INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Talks', '{"en": "Tell me about some of your highlights and lowlights from this past year.", "da": "Fortæl mig om nogle af dine højdepunkter og lavpunkter fra det sidste år.", "de": "Erzähl mir von einigen deiner Highlights und Tiefpunkte aus dem letzten Jahr.", "es": "Cuéntame algunos de tus momentos altos y bajos de este último año.", "pt": "Fala-me de alguns dos teus altos e baixos do último ano.", "ro": "Povestește-mi câteva dintre momentele tale bune și rele din ultimul an.", "fr": "Parle-moi de quelques-uns de tes hauts et bas de l’année passée.", "it": "Raccontami alcuni dei tuoi momenti salienti e dei tuoi momenti difficili di quest''anno passato.", "fi": "Kerro minulle joitakin kohokohtia ja alhaisia hetkiä kuluneelta vuodelta.", "nl": "Vertel me over enkele van je hoogte- en dieptepunten van het afgelopen jaar.", "no": "Fortell meg om noen av høydepunktene og lavpunktene fra det siste året ditt.", "sv": "Berätta om några av dina höjdpunkter och lågvattenmärken från det gångna året.", "pl": "Opowiedz mi o swoich najważniejszych i najtrudniejszych momentach z minionego roku."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Talks', '{"en": "What’s a memory of us, you believe will still make you smile when you’re 90?", "da": "Hvilket minde om os tror du stadig vil få dig til at smile, når du er 90?", "de": "Welche Erinnerung an uns wird dich deiner Meinung nach noch zum Lächeln bringen, wenn du 90 bist?", "es": "¿Qué recuerdo nuestro crees que todavía te hará sonreír cuando tengas 90 años?", "pt": "Qual lembrança nossa você acredita que ainda vai fazer você sorrir quando tiver 90 anos?", "ro": "Care este o amintire cu noi despre care crezi că încă te va face să zâmbești când vei avea 90 de ani?", "fr": "Quel souvenir de nous crois-tu te fera encore sourire quand tu auras 90 ans ?", "it": "Qual è un ricordo di noi che pensi ti farà ancora sorridere quando avrai 90 anni?", "fi": "Mikä muisto meistä saa sinut edelleen hymyilemään, kun olet 90-vuotias?", "nl": "Wat is een herinnering aan ons waarvan je denkt dat die je nog steeds zal laten glimlachen als je 90 bent?", "no": "Hva er et minne om oss som du tror fortsatt vil få deg til å smile når du er 90?", "sv": "Vilket minne av oss tror du fortfarande kommer få dig att le när du är 90?", "pl": "Jakie wspomnienie o nas, twoim zdaniem, nadal sprawi, że się uśmiechniesz, gdy będziesz miał 90 lat?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Talks', '{"en": "Aside from my current job, what''s an alternative carrier you think I would be amazing at?", "da": "Bortset fra mit nuværende job, hvilket alternativt karrierevalg tror du, jeg ville være fantastisk til?", "de": "Abgesehen von meinem jetzigen Job: Welcher alternative Beruf wäre deiner Meinung nach wie für mich gemacht?", "es": "Aparte de mi trabajo actual, ¿en qué otra carrera crees que sería increíble?", "pt": "Além do meu trabalho atual, que carreira alternativa achas que eu faria maravilhas?", "ro": "În afară de jobul meu actual, la ce altă carieră crezi că aș fi grozav(ă)?", "fr": "En dehors de mon travail actuel, quel autre métier penses-tu que je ferais à merveille ?", "it": "A parte il mio attuale lavoro, quale carriera alternativa pensi che sarei straordinario?", "fi": "Mikä olisi vaihtoehtoinen ura, jossa uskot, että olisin mahtava nykyisen työni lisäksi?", "nl": "Afgezien van mijn huidige baan, wat is een alternatieve carrière waarvan jij denkt dat ik er geweldig in zou zijn?", "no": "Bortsett fra min nåværende jobb, hva er en alternativ karriere du tror jeg ville vært fantastisk i?", "sv": "Förutom mitt nuvarande jobb, vilken alternativ karriär tror du att jag skulle vara fantastisk på?", "pl": "Poza moją obecną pracą, w jakiej alternatywnej karierze myślisz, że byłbym niesamowity?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Talks', '{"en": "What helps you open up when you’re feeling closed or isolated?", "da": "Hvad hjælper dig med at åbne op, når du føler dig lukket eller isoleret?", "de": "Was hilft dir, dich zu öffnen, wenn du dich verschlossen oder isoliert fühlst?", "es": "¿Qué te ayuda a abrirte cuando te sientes cerrado/a o aislado/a?", "pt": "O que te ajuda a abrir-te quando te sentes fechado/a ou isolado/a?", "ro": "Ce te ajută să te deschizi când te simți închis(ă) sau izolat(ă)?", "fr": "Qu’est-ce qui t’aide à t’ouvrir quand tu te sens fermé(e) ou isolé(e) ?", "it": "Cosa ti aiuta ad aprirti quando ti senti chiuso o isolato?", "fi": "Mikä auttaa sinua avautumaan, kun tunnet itsesi sulkeutuneeksi tai eristyneeksi?", "nl": "Wat helpt jou om je open te stellen als je je gesloten of geïsoleerd voelt?", "no": "Hva hjelper deg med å åpne deg når du føler deg lukket eller isolert?", "sv": "Vad hjälper dig att öppna upp när du känner dig stängd eller isolerad?", "pl": "Co pomaga ci się otworzyć, gdy czujesz się zamknięty lub odizolowany?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
