INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Silly Talks', '{"en": "You get a teleport button that only works to very specific places. What two places do you choose?", "da": "Du får en teleport-knap der kun virker til meget specifikke steder. Hvilke to steder vælger du?", "de": "Du bekommst einen Teleport-Knopf, der nur an sehr spezifische Orte funktioniert. Welche zwei Orte wählst du?", "es": "Te dan un botón de teletransporte que solo funciona hacia lugares muy específicos. ¿Qué dos lugares eliges?", "pt": "Recebes um botão de teleporte que só funciona para locais muito específicos. Que dois locais escolhes?", "ro": "Primeşti un buton de teleport care funcţionează doar către locuri foarte specifice. Ce două locuri alegi?", "fr": "Tu obtiens un bouton de téléportation qui ne fonctionne que pour des lieux très spécifiques. Quels deux lieux choisis-tu ?", "it": "Ricevi un pulsante di teletrasporto che funziona solo per posti molto specifici. Quali due posti scegli?", "fi": "Saat teleportointipainikkeen, joka toimii vain hyvin tietyissä paikoissa. Mitkä kaksi paikkaa valitset?", "nl": "Je krijgt een teleportknop die alleen werkt naar zeer specifieke plaatsen. Welke twee plaatsen kies je?", "no": "Du får en teleportknapp som bare fungerer til veldig spesifikke steder. Hvilke to steder velger du?", "sv": "Du får en teleportknapp som bara fungerar till mycket specifika platser. Vilka två platser väljer du?", "pl": "Dostajesz przycisk teleportu, który działa tylko w bardzo konkretnych miejscach. Jakie dwa miejsca wybierasz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
