VALUES ('Car Talks', '{"en": "What’s the longest journey you’ve ever taken?", "da": "Hvad er den længste rejse, du nogensinde har taget?", "de": "Was ist die längste Reise, die du je gemacht hast?", "es": "¿Cuál es el viaje más largo que has hecho?", "pt": "Qual é a viagem mais longa que você já fez?", "ro": "Care este cea mai lungă călătorie pe care ai făcut-o?", "fr": "Quel est le plus long voyage que tu aies jamais fait ?", "it": "Qual è il viaggio più lungo che tu abbia mai fatto?", "fi": "Mikä on pisin matka, jonka olet koskaan tehnyt?", "nl": "Wat is de langste reis die je ooit hebt gemaakt?", "no": "Hva er den lengste reisen du noen gang har tatt?", "sv": "Vilken är den längsta resa du någonsin har gjort?", "pl": "Jaka jest najdłuższa podróż, jaką kiedykolwiek odbyłeś/odbyłaś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If pets could text, what would yours send you at 2 a.m.?", "da": "Hvis kæledyr kunne skrive beskeder, hvad ville dit sende dig kl. 2 om natten?", "de": "Wenn Haustiere schreiben könnten, was würde dir deins um 2 Uhr nachts schicken?", "es": "Si las mascotas pudieran mandar mensajes, ¿qué te enviaría la tuya a las 2 a.m.?", "pt": "Se os pets pudessem mandar mensagens, o que o seu te mandaria às 2 da manhã?", "ro": "Dacă animalele de companie ar putea trimite mesaje, ce ți-ar trimite al tău la 2 dimineața?", "fr": "Si les animaux pouvaient envoyer des textos, que t’enverrait le tien à 2 h du matin ?", "it": "Se gli animali domestici potessero mandare messaggi, cosa ti scriverebbe il tuo alle 2 di notte?", "fi": "Jos lemmikit voisivat lähettää viestejä, mitä omasi lähettäisi sinulle klo 2 yöllä?", "nl": "Als huisdieren konden appen, wat zou die van jou je om 2 uur ’s nachts sturen?", "no": "Hvis kjæledyr kunne sende meldinger, hva ville ditt sendt deg klokka 2 om natta?", "sv": "Om husdjur kunde sms:a, vad skulle ditt skicka till dig kl. 2 på natten?", "pl": "Gdyby zwierzaki mogły pisać wiadomości, co twoje wysłałoby ci o 2 w nocy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "Which city would you move to for one year – and why?", "da": "Hvilken by ville du flytte til i et år – og hvorfor?", "de": "In welche Stadt würdest du für ein Jahr ziehen – und warum?", "es": "¿A qué ciudad te mudarías por un año – y por qué?", "pt": "Para qual cidade você se mudaria por um ano – e por quê?", "ro": "În ce oraș te-ai muta pentru un an – și de ce?", "fr": "Dans quelle ville irais-tu vivre pendant un an – et pourquoi ?", "it": "In quale città ti trasferiresti per un anno, e perché?", "fi": "Mihin kaupunkiin muuttaisit vuodeksi – ja miksi?", "nl": "Naar welke stad zou je voor één jaar verhuizen – en waarom?", "no": "Hvilken by ville du flyttet til for ett år – og hvorfor?", "sv": "Till vilken stad skulle du flytta i ett år – och varför?", "pl": "Do jakiego miasta przeprowadził(a)byś się na rok – i dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "Have you ever had a “this is not what I expected” travel moment — good or bad?", "da": "Har du nogensinde haft en “det var ikke som forventet”-rejseoplevelse — god eller dårlig?", "de": "Hattest du jemals einen „das habe ich nicht erwartet“-Reisemoment – gut oder schlecht?", "es": "¿Alguna vez tuviste un momento de viaje de “esto no era lo que esperaba”, bueno o malo?", "pt": "Você já teve um momento de viagem “não era o que eu esperava” — bom ou ruim?", "ro": "Ai avut vreodată un moment de călătorie „nu era ce credeam” — bun sau rău?", "fr": "As-tu déjà eu un moment de voyage du type « ce n’était pas ce à quoi je m’attendais » — bon ou mauvais ?", "it": "Hai mai avuto in viaggio un momento del tipo “non è come me lo aspettavo”, in bene o in male?", "fi": "Onko sinulle matkalla tullut hetkeä, jolloin ajattelit: “tätä en odottanut” – hyvässä tai pahassa?", "nl": "Heb je ooit op reis zo’n “dit is niet wat ik verwachtte”-moment gehad – positief of negatief?", "no": "Har du noen gang hatt et «dette var ikke som jeg hadde forventet»-øyeblikk på reise – på godt eller vondt?", "sv": "Har du någonsin haft ett ”det här var inte vad jag väntade mig”-ögonblick på resa – bra eller dåligt?", "pl": "Czy miałeś/miałaś kiedyś w podróży moment „tego się nie spodziewałem/am” – na plus lub na minus?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "Have you ever been scared of a sound in the wild — and did you ever find out what it was?", "da": "Har du nogensinde været bange for en lyd i naturen — og fandt du ud af, hvad det var?", "de": "Hattest du jemals Angst vor einem Geräusch in der Wildnis – und hast du herausgefunden, was es war?", "es": "¿Alguna vez te asustó un sonido en la naturaleza — y descubriste qué era?", "pt": "Você já se assustou com um som na natureza — e descobriu o que era?", "ro": "Ți s-a întâmplat vreodată să te sperii de un sunet în natură — și să afli ce era?", "fr": "T’es-tu déjà fait peur avec un bruit dans la nature — et as-tu découvert ce que c’était ?", "it": "Ti sei mai spaventato/a per un rumore in natura – e hai poi scoperto cos’era?", "fi": "Oletko koskaan pelästynyt ääntä luonnossa – ja saitko koskaan selville, mitä se oli?", "nl": "Ben je ooit bang geweest voor een geluid in de natuur – en kwam je er ooit achter wat het was?", "no": "Har du noen gang blitt redd av en lyd ute i naturen – og fant du noen gang ut hva det var?", "sv": "Har du någonsin blivit rädd för ett ljud ute i naturen – och tog du någonsin reda på vad det var?", "pl": "Czy kiedykolwiek przestraszył(a) cię jakiś dźwięk na łonie natury – i czy dowiedziałeś/dowiedziałaś się, co to było?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
