INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What skill instantly makes traveling with someone easier?", "da": "Hvilken evne gør det øjeblikkeligt lettere at rejse med nogen?", "de": "Welche Fähigkeit macht das Reisen mit jemandem sofort einfacher?", "es": "¿Qué habilidad hace que viajar con alguien sea más fácil?", "pt": "Que habilidade torna viajar com alguém instantaneamente mais fácil?", "ro": "Ce abilitate face călătoria cu cineva imediat mai ușoară?", "fr": "Quelle compétence rend instantanément un voyage à deux plus facile ?", "it": "Quale abilità rende immediatamente più facile viaggiare con qualcuno?", "fi": "Mikä taito tekee matkustamisesta jonkun kanssa heti helpompaa?", "nl": "Welke vaardigheid maakt het meteen makkelijker om met iemand te reizen?", "no": "Hvilken ferdighet gjør det med en gang lettere å reise sammen med noen?", "sv": "Vilken förmåga gör det direkt lättare att resa med någon?", "pl": "Jaka umiejętność od razu ułatwia podróżowanie z kimś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "Which friend or relative would make this drive hilarious—and why?", "da": "Hvilken ven eller slægtning ville gøre denne køretur hylende morsom — og hvorfor?", "de": "Welcher Freund oder Verwandte würde diese Fahrt urkomisch machen – und warum?", "es": "¿Qué amigo o familiar haría este viaje divertidísimo — y por qué?", "pt": "Que amigo ou parente tornaria essa viagem hilária — e por quê?", "ro": "Ce prieten sau rudă ar face această călătorie amuzantă — și de ce?", "fr": "Quel ami ou membre de la famille rendrait ce trajet hilarant — et pourquoi ?", "it": "Quale amico o parente renderebbe questo viaggio in auto esilarante, e perché?", "fi": "Mikä ystävä tai sukulainen tekisi tästä ajomatkasta hulvattoman – ja miksi?", "nl": "Welke vriend of welk familielid zou deze rit hysterisch grappig maken – en waarom?", "no": "Hvilken venn eller slektning ville gjort denne bilturen hysterisk morsom – og hvorfor?", "sv": "Vilken vän eller släkting skulle göra den här bilresan hysteriskt rolig – och varför?", "pl": "Który przyjaciel albo krewny sprawiłby, że ta przejażdżka byłaby przezabawna – i dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If you could time-capsule one everyday moment, which would you save from today?", "da": "Hvis du kunne tidskapsle ét hverdagsøjeblik, hvilket ville du gemme fra i dag?", "de": "Wenn du einen Alltagsmoment in einer Zeitkapsel bewahren könntest, welchen würdest du heute speichern?", "es": "Si pudieras guardar un momento cotidiano en una cápsula del tiempo, ¿cuál elegirías hoy?", "pt": "Se você pudesse guardar um momento comum de hoje em uma cápsula do tempo, qual seria?", "ro": "Dacă ai putea păstra un moment obișnuit de azi într-o capsulă a timpului, care ar fi?", "fr": "Si tu pouvais mettre un moment ordinaire d’aujourd’hui dans une capsule temporelle, lequel choisirais-tu ?", "it": "Se potessi mettere in una capsula del tempo un momento qualsiasi di oggi, quale salveresti?", "fi": "Jos voisit laittaa aikakapseliin yhden arkisen hetken, minkä tämän päivän hetken valitsisit?", "nl": "Als je één alledaags moment in een tijdcapsule kon stoppen, welk moment van vandaag zou je kiezen?", "no": "Hvis du kunne legge ett hverdagsøyeblikk i en tidskapsel, hvilket fra i dag ville du valgt?", "sv": "Om du kunde tidskapsla ett vardagsögonblick, vilket från just idag skulle du spara?", "pl": "Gdybyś mógł/mogła zamknąć w kapsule czasu jeden zwykły moment, który z dzisiejszego dnia byś wybrał(a)?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What’s one item you always pack, even if you never end up using it?", "da": "Hvilken ting pakker du altid, selvom du aldrig ender med at bruge den?", "de": "Welchen Gegenstand packst du immer ein, auch wenn du ihn nie benutzt?", "es": "¿Qué objeto siempre empacas, aunque nunca lo uses?", "pt": "Que item você sempre leva, mesmo sem nunca usar?", "ro": "Ce obiect iei mereu cu tine, chiar dacă nu-l folosești niciodată?", "fr": "Quel objet emportes-tu toujours, même si tu ne l’utilises jamais ?", "it": "Qual è l’oggetto che metti sempre in valigia, anche se poi non lo usi mai?", "fi": "Mikä on yksi tavara, jonka pakkaat aina, vaikka et ikinä päätyisi käyttämään sitä?", "nl": "Welk item neem je altijd mee, ook al gebruik je het uiteindelijk nooit?", "no": "Hvilken ting pakker du alltid, selv om du nesten aldri ender opp med å bruke den?", "sv": "Vilken sak packar du alltid, även om du aldrig använder den?", "pl": "Jaki przedmiot zawsze pakujesz, nawet jeśli i tak go nie używasz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If you caught a shooting star, what wish would you send with it?", "da": "Hvis du så et stjerneskud, hvilket ønske ville du sende med det?", "de": "Wenn du eine Sternschnuppe siehst, welchen Wunsch würdest du damit senden?", "es": "Si vieras una estrella fugaz, ¿qué deseo pedirías?", "pt": "Se você visse uma estrela cadente, que desejo faria?", "ro": "Dacă ai vedea o stea căzătoare, ce dorință ți-ai pune?", "fr": "Si tu voyais une étoile filante, quel vœu ferais-tu ?", "it": "Se potessi “acchiappare” una stella cadente, quale desiderio le affideresti?", "fi": "Jos saisit kiinni tähdenlennon, minkä toiveen lähettäisit sen mukana?", "nl": "Als je een vallende ster kon vangen, welke wens zou je ermee meesturen?", "no": "Hvis du kunne fange et stjerneskudd, hvilket ønske ville du sendt med det?", "sv": "Om du kunde fånga ett stjärnfall, vilken önskan skulle du skicka med det?", "pl": "Gdyby udało ci się złapać spadającą gwiazdę, jakie życzenie byś z nią wysłał(a)?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "Where were you the first time you saw the ocean?", "da": "Hvor var du første gang, du så havet?", "de": "Wo warst du, als du das Meer zum ersten Mal gesehen hast?", "es": "¿Dónde estabas la primera vez que viste el mar?", "pt": "Onde você estava quando viu o mar pela primeira vez?", "ro": "Unde erai prima dată când ai văzut marea?", "fr": "Où étais-tu la première fois que tu as vu la mer ?", "it": "Dove eri la prima volta che hai visto l’oceano?", "fi": "Missä olit, kun näit meren ensimmäisen kerran?", "nl": "Waar was je de eerste keer dat je de oceaan zag?", "no": "Hvor var du første gang du så havet?", "sv": "Var befann du dig första gången du såg havet?", "pl": "Gdzie byłeś/byłaś, gdy po raz pierwszy zobaczyłeś/zobaczyłaś ocean?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "Tell a travel mishap you can laugh about now.", "da": "Fortæl om en rejsefejl, du nu kan grine af.", "de": "Erzähl von einem Reisepatzer, über den du jetzt lachen kannst.", "es": "Cuéntanos un error de viaje del que ahora puedas reírte.", "pt": "Conte um erro de viagem do qual você ri hoje.", "ro": "Povestește o greșeală de călătorie de care poți râde acum.", "fr": "Raconte une mésaventure de voyage dont tu peux rire maintenant.", "it": "Racconta un imprevisto di viaggio su cui ora riesci a ridere.", "fi": "Kerro matkakömmähdys, jolle voit nykyään jo nauraa.", "nl": "Vertel over een reisblunder waar je nu om kunt lachen.", "no": "Fortell om en reisetabbe du kan le av nå.", "sv": "Berätta om ett rese-misstag som du kan skratta åt nu.", "pl": "Opowiedz o wpadce z podróży, z której dziś możesz się śmiać."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What’s a tiny detour you’ll always take—view, bakery, weird statue?", "da": "Hvilken lille omvej vil du altid tage — udsigt, bageri, mærkelig statue?", "de": "Welchen kleinen Umweg nimmst du immer – Aussicht, Bäckerei, seltsame Statue?", "es": "¿Qué pequeño desvío siempre tomas — una vista, panadería o estatua rara?", "pt": "Que pequeno desvio você sempre faz — vista, padaria, estátua estranha?", "ro": "Ce mic ocol faci mereu — o priveliște, o brutărie, o statuie ciudată?", "fr": "Quel petit détour fais-tu toujours — vue, boulangerie, statue étrange ?", "it": "Quale piccola deviazione farai sempre volentieri – per un bel panorama, una panetteria, una statua strana…?", "fi": "Minkä pienen ylimääräisen koukkauksen teet aina – näköalapaikan, leipomon vai jonkin oudon patsaan takia?", "nl": "Wat is een kleine omweg die je altijd maakt – voor uitzicht, bakkerij, of een raar standbeeld?", "no": "Hvilken liten omvei tar du alltid – for utsikten, et bakeri, en rar statue?", "sv": "Vilken liten omväg tar du alltid – för utsikten, ett bageri, en konstig staty?", "pl": "Jaką małą nadkładkę drogi zawsze zrobisz – dla widoku, piekarni, dziwnego pomnika?"}'::jsonb, false, true, true);
