INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If we ran a tiny bed-and-breakfast, what would guests remember most?", "da": "Hvis vi drev et lille bed-and-breakfast, hvad ville gæsterne huske bedst?", "de": "Wenn wir ein kleines Bed-and-Breakfast hätten, woran würden sich die Gäste am meisten erinnern?", "es": "Si dirigiéramos un pequeño bed and breakfast, ¿qué recordarían más los huéspedes?", "pt": "Se tivéssemos uma pousada, do que os hóspedes se lembrariam mais?", "ro": "Dacă am avea un mic bed-and-breakfast, ce ar ține minte oaspeții?", "fr": "Si nous tenions une petite chambre d’hôtes, de quoi les invités se souviendraient-ils le plus ?", "it": "Se gestissimo un piccolo bed & breakfast, che cosa ricorderebbero di più gli ospiti?", "fi": "Jos pitäisimme pientä bed & breakfastia, minkä vieraat muistaisivat meistä parhaiten?", "nl": "Als wij een klein bed & breakfast zouden runnen, wat zouden gasten zich dan het meest van ons herinneren?", "no": "Hvis vi drev et lite bed & breakfast, hva tror du gjestene ville husket best?", "sv": "Om vi drev ett litet bed and breakfast, vad skulle gästerna minnas mest?", "pl": "Gdybyśmy prowadzili mały pensjonat typu bed & breakfast, co goście zapamiętaliby najbardziej?"}'::jsonb, false, true, true);
