INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If we detour two hours for an epic meal – would you think it’s worth it? Why?", "da": "Hvis vi tager en omvej på to timer for et episk måltid – synes du, det er det værd? Hvorfor?", "de": "Wenn wir zwei Stunden Umweg für ein episches Essen machen – würdest du denken, es lohnt sich? Warum?", "es": "Si desviamos dos horas por una comida épica, ¿crees que valdría la pena? ¿Por qué?", "pt": "Se fizermos um desvio de duas horas por uma refeição épica, achas que vale a pena? Porquê?", "ro": "Dacă am face un ocol de două ore pentru o masă epică, ai crede că merită? De ce?", "fr": "Si on faisait un détour de deux heures pour un repas épique – tu penserais que ça en vaut la peine ? Pourquoi ?", "it": "Se facessimo una deviazione di due ore per un pasto epico, penseresti che ne vale la pena? Perché?", "fi": "Jos kiertäisimme kaksi tuntia paremman aterian takia, olisiko se sinusta sen arvoista? Miksi?", "nl": "Als we twee uur zouden omrijden voor een legendarische maaltijd – zou jij vinden dat het de moeite waard is? Waarom?", "no": "Hvis vi hadde kjørt to timer ekstra for et episk måltid – hadde du syntes det var verdt det? Hvorfor?", "sv": "Om vi gjorde en två timmar lång omväg för en episk måltid – skulle du tycka att det var värt det? Varför?", "pl": "Gdybyśmy nadłożyli dwie godziny drogi dla epickiego posiłku – uznał(a)byś, że było warto? Dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What was your most peaceful travel moment?", "da": "Hvad var dit mest fredfyldte rejseøjeblik?", "de": "Was war dein friedlichster Reisemoment?", "es": "¿Cuál fue tu momento de viaje más tranquilo?", "pt": "Qual foi o teu momento de viagem mais pacífico?", "ro": "Care a fost cel mai liniștit moment al tău dintr-o călătorie?", "fr": "Quel a été ton moment de voyage le plus paisible ?", "it": "Qual è stato il tuo momento di viaggio più pacifico?", "fi": "Mikä on rauhallisin matkamuistosi?", "nl": "Wat was je meest vredige reis-moment?", "no": "Hva var ditt mest fredfulle reiseminne?", "sv": "Vad var ditt mest fridfulla reseögonblick?", "pl": "Jaki był twój najbardziej spokojny moment w podróży?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What movie or book do you wish you could erase from memory just to experience again for the first time?", "da": "Hvilken film eller bog ville du ønske, du kunne slette fra hukommelsen bare for at opleve den igen for første gang?", "de": "Welchen Film oder welches Buch würdest du gern aus deinem Gedächtnis löschen, um es zum ersten Mal wieder zu erleben?", "es": "¿Qué película o libro desearías poder borrar de tu memoria para experimentarlo de nuevo por primera vez?", "pt": "Que filme ou livro gostarias de apagar da memória para o viver novamente pela primeira vez?", "ro": "Ce film sau carte ți-ai dori să o ștergi din memorie doar pentru a o retrăi pentru prima dată?", "fr": "Quel film ou livre aimerais-tu effacer de ta mémoire pour le revivre comme la première fois ?", "it": "Quale film o libro vorresti poter cancellare dalla memoria solo per riviverlo di nuovo per la prima volta?", "fi": "Minkä elokuvan tai kirjan pyyhkisit mielestäsi vain siksi, että saisit kokea sen uudestaan ensimmäistä kertaa?", "nl": "Welke film of welk boek zou je uit je geheugen willen wissen, alleen maar om het nog één keer voor het eerst te ervaren?", "no": "Hvilken film eller bok skulle du ønske du kunne slette fra minnet bare for å oppleve den på nytt for første gang?", "sv": "Vilken film eller bok skulle du vilja radera ur minnet bara för att få uppleva den igen för första gången?", "pl": "Jaki film albo jaką książkę chciał(a)byś wymazać z pamięci tylko po to, żeby przeżyć ją jeszcze raz po raz pierwszy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If we had to stop and live in one of the towns we pass, what would convince you to stay?", "da": "Hvis vi skulle stoppe og bo i en af byerne, vi passerer, hvad ville overbevise dig om at blive?", "de": "Wenn wir in einer der Städte, an denen wir vorbeifahren, leben müssten – was würde dich überzeugen zu bleiben?", "es": "Si tuviéramos que parar y vivir en una de las ciudades por las que pasamos, ¿qué te convencería de quedarte?", "pt": "Se tivéssemos de parar e viver numa das cidades por onde passamos, o que te convenceria a ficar?", "ro": "Dacă ar trebui să ne oprim și să trăim într-unul dintre orașele prin care trecem, ce te-ar convinge să rămâi?", "fr": "Si on devait s’arrêter et vivre dans une des villes qu’on traverse, qu’est-ce qui te convaincrait d’y rester ?", "it": "Se dovessimo fermarci a vivere in una delle città che attraversiamo, cosa ti convincerebbe a restare?", "fi": "Jos meidän pitäisi pysähtyä ja jäädä asumaan johonkin ohittamaamme kaupunkiin, mikä saisi sinut jäämään?", "nl": "Als we moesten stoppen en in één van de plaatsen gaan wonen waar we langs komen, wat zou jou overtuigen om te blijven?", "no": "Hvis vi måtte stoppe og bo i en av byene vi kjører gjennom, hva skulle til for at du ble?", "sv": "Om vi var tvungna att stanna och bo i en av städerna vi passerar, vad skulle få dig att vilja stanna kvar?", "pl": "Gdybyśmy musieli zatrzymać się i zamieszkać w jednym z mijanych miasteczek, co przekonałoby cię, żeby zostać?"}'::jsonb, false, true, true);
