INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What’s something you’ve done once that you’ll never do again — but you’re glad you tried?", "da": "Hvad er noget, du kun har gjort én gang, som du aldrig vil gøre igen – men er glad for, at du prøvede?", "de": "Was hast du einmal getan, das du nie wieder tun würdest – aber froh bist, dass du es versucht hast?", "es": "¿Qué has hecho una vez que nunca volverías a hacer, pero te alegra haberlo intentado?", "pt": "O que fizeste uma vez que nunca mais farias — mas ficaste feliz por ter tentado?", "ro": "Ce ai făcut o dată și nu ai mai face niciodată — dar ești bucuros că ai încercat?", "fr": "Qu’as-tu fait une fois que tu ne referais jamais — mais dont tu es content de l’avoir tenté ?", "it": "Qual è qualcosa che hai fatto una volta e non farai mai più – ma sei contento/a di averci provato?", "fi": "Mikä on juttu, jonka teit kerran etkä enää koskaan tekisi – mutta olet silti iloinen, että kokeilit?", "nl": "Wat is iets dat je één keer hebt gedaan en nooit meer zult doen – maar waar je toch blij om bent dat je het hebt geprobeerd?", "no": "Hva er noe du har gjort én gang som du aldri gjør igjen – men du er glad du prøvde?", "sv": "Vad är något du gjort en gång och aldrig gör igen – men du är glad att du testade?", "pl": "Co takiego zrobiłeś/zrobiłaś raz i nigdy więcej nie zrobisz – ale cieszysz się, że spróbowałeś/spróbowałaś?"}'::jsonb, false, true, true);
