INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "Airport edition: are you a “arrive super early” or “cutting it close” traveler—and why?", "da": "Lufthavns-udgave: Er du en “komme i rigtig god tid”-rejsende eller en “kommer i sidste øjeblik”-type – og hvorfor?", "de": "Flughafen-Edition: Bist du eher der Typ „viel zu früh da“ oder „knapp dran“ – und warum?", "es": "Edición aeropuerto: ¿eres de llegar súper temprano o de ir apurando el tiempo hasta el final? ¿Por qué?", "pt": "Edição aeroporto: você é do time “chegar bem cedo” ou “deixar para a última hora” – e por quê?", "ro": "Ediția aeroport: ești genul care ajunge super devreme sau cel care vine la limită – și de ce?", "fr": "Édition aéroport : tu es plutôt du genre à arriver très en avance ou à arriver tout juste à temps – et pourquoi ?", "it": "Versione aeroporto: sei un viaggiatore da “arrivo super in anticipo” o da “arrivo all’ultimo minuto” – e perché?", "fi": "Lentokenttäversio: oletko “tulen paikalle tosi ajoissa” vai “vedän melkein viime tipassa” -matkailija – ja miksi?", "nl": "Luchthaven-editie: ben jij een “heel vroeg aankomen” of een “net op het nippertje” reiziger – en waarom?", "no": "Flyplass-versjon: er du en “kommer supertidlig” eller “kommer i siste liten”-reisende – og hvorfor?", "sv": "Flygplats-version: är du en “kommer supertidigt”- eller “kommer i sista stund”-resenär – och varför?", "pl": "Wersja lotniskowa: jesteś typem „przyjeżdżam bardzo wcześnie” czy „docieram na styk” – i dlaczego?"}'::jsonb, false, true, true);
