VALUES ('Do-you-dare Talks', '{"en": "What’s something people often misunderstand about you?", "da": "Hvad misforstår folk ofte om dig?", "de": "Was missverstehen die Leute oft an dir?", "es": "¿Qué es algo que la gente suele malinterpretar sobre ti?", "pt": "O que é algo que as pessoas geralmente interpretam mal sobre ti?", "ro": "Ce crezi că oamenii înțeleg greșit despre tine?", "fr": "Qu’est-ce que les gens comprennent souvent mal à ton sujet ?", "it": "Qualcosa che le persone fraintendono spesso su di te?", "fi": "Mikä on jotain, mitä ihmiset usein väärin ymmärtävät sinusta?", "nl": "Wat is iets dat mensen vaak verkeerd begrijpen over jou?", "no": "Hva er noe folk ofte misforstår om deg?", "sv": "Vad är något som folk ofta missförstår om dig?", "pl": "Coś, co ludzie często źle rozumieją na twój temat?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something you’d love for my parents to know about you someday?", "da": "Hvad er noget, du gerne vil have, mine forældre skal vide om dig en dag?", "de": "Was möchtest du, dass meine Eltern eines Tages über dich wissen?", "es": "¿Qué te gustaría que mis padres supieran sobre ti algún día?", "pt": "O que gostarias que os meus pais soubessem sobre ti um dia?", "ro": "Ce ți-ar plăcea ca părinții mei să afle despre tine într-o zi?", "fr": "Qu’aimerais-tu que mes parents sachent de toi un jour ?", "it": "Qualcosa che ti piacerebbe che i miei genitori sapessero di te un giorno?", "fi": "Mikä on jotain, jonka haluaisit vanhempieni tietävän sinusta joskus?", "nl": "Wat is iets dat je zou willen dat mijn ouders ooit over jou weten?", "no": "Hva er noe du gjerne skulle ønsket at foreldrene mine visste om deg en dag?", "sv": "Vad är det som du skulle vilja att mina föräldrar fick veta om dig någon gång?", "pl": "Coś, co chciałbyś, aby moi rodzice kiedyś o tobie wiedzieli?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Hand me your phone — I get to scroll through your photos for ten seconds.", "da": "Giv mig din telefon — jeg får lov til at rulle gennem dine billeder i ti sekunder.", "de": "Gib mir dein Handy – ich darf zehn Sekunden lang durch deine Fotos scrollen.", "es": "Dame tu teléfono — puedo desplazarme por tus fotos durante diez segundos.", "pt": "Dá-me o teu telemóvel — posso ver as tuas fotos por dez segundos.", "ro": "Dă-mi telefonul tău — pot derula prin fotografiile tale zece secunde.", "fr": "Passe-moi ton téléphone — j’ai dix secondes pour faire défiler tes photos.", "it": "Passami il tuo telefono — posso scorrere le tue foto per dieci secondi.", "fi": "Anna puhelimesi — saan selata valokuviasi kymmenen sekuntia.", "nl": "Geef me je telefoon — ik mag tien seconden door je foto''s scrollen.", "no": "Gi meg telefonen din — jeg får bla gjennom bildene dine i ti sekunder.", "sv": "Ge mig din telefon — jag får bläddra igenom dina bilder i tio sekunder.", "pl": "Podaj mi swój telefon — mogę przeglądać twoje zdjęcia przez dziesięć sekund."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Have you ever felt judged for something you believe in?", "da": "Har du nogensinde følt dig dømt for noget, du tror på?", "de": "Hast du dich jemals für etwas, woran du glaubst, verurteilt gefühlt?", "es": "¿Alguna vez te has sentido juzgado por algo en lo que crees?", "pt": "Alguma vez sentiste que foste julgado por algo em que acreditas?", "ro": "Te-ai simțit vreodată judecat pentru ceva în care crezi?", "fr": "T’es-tu déjà senti jugé pour quelque chose en quoi tu crois ?", "it": "Hai mai sentito di essere giudicato per qualcosa in cui credi?", "fi": "Oletko koskaan tuntenut, että sinua on arvosteltu jostain, mihin uskot?", "nl": "Heb je ooit het gevoel gehad beoordeeld te worden om iets waar je in gelooft?", "no": "Har du noen gang følt deg dømt for noe du tror på?", "sv": "Har du någonsin känt dig dömd för något du tror på?", "pl": "Czy kiedykolwiek czułeś się oceniany za coś, w co wierzysz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something you think I don’t give myself enough credit for?", "da": "Hvad tror du, jeg ikke giver mig selv nok ros for?", "de": "Was glaubst du, wofür ich mir selbst nicht genug Anerkennung gebe?", "es": "¿Por qué cosa crees que no me doy suficiente crédito?", "pt": "Pelo que achas que não me dou crédito suficiente?", "ro": "Pentru ce crezi că nu îmi dau suficient merit?", "fr": "Pour quoi penses-tu que je ne me donne pas assez de crédit ?", "it": "Qual è una cosa per cui pensi che non mi dia abbastanza credito?", "fi": "Mikä on jotain, mistä luulet, etten anna itselleni tarpeeksi krediittiä?", "nl": "Wat is iets waarvan je denkt dat ik mezelf niet genoeg krediet geef?", "no": "Hva er noe du mener jeg ikke gir meg selv nok kreditt for?", "sv": "Vad är något du tycker att jag inte ger mig själv tillräckligt med beröm för?", "pl": "Coś, za co myślisz, że nie doceniam siebie wystarczająco?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something you’d love us to try together but haven’t yet?", "da": "Hvad er noget, du gerne vil have, vi prøver sammen, men endnu ikke har gjort?", "de": "Was würdest du gerne mit mir ausprobieren, was wir noch nicht getan haben?", "es": "¿Qué te gustaría que probáramos juntos pero aún no hemos hecho?", "pt": "O que gostarias que tentássemos juntos mas ainda não fizemos?", "ro": "Ce ai vrea să încercăm împreună, dar nu am făcut încă?", "fr": "Qu’aimerais-tu que nous essayions ensemble mais que nous n’avons pas encore fait ?", "it": "Qualcosa che ti piacerebbe provare insieme ma non abbiamo ancora fatto?", "fi": "Mikä on jotain, mitä haluaisit meidän kokeilevan yhdessä, mutta emme ole vielä tehneet?", "nl": "Wat is iets dat je graag samen met ons zou willen proberen, maar dat we nog niet hebben gedaan?", "no": "Hva er noe du gjerne vil at vi skal prøve sammen, men som vi ikke har gjort ennå?", "sv": "Vad är något du skulle vilja att vi provar tillsammans men inte har gjort än?", "pl": "Coś, co chcielibyśmy spróbować razem, ale jeszcze tego nie zrobiliśmy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you think your exes would describe you as a partner?", "da": "Hvordan tror du, dine ekser ville beskrive dig som partner?", "de": "Wie würden dich deine Ex-Partner als Partner beschreiben?", "es": "¿Cómo crees que tus ex te describirían como pareja?", "pt": "Como achas que os teus ex te descreveriam como parceiro?", "ro": "Cum crezi că te-ar descrie foștii tăi ca partener?", "fr": "Comment penses-tu que tes ex te décriraient comme partenaire ?", "it": "Come pensi che i tuoi ex ti descriverebbero come partner?", "fi": "Miten luulet, että eksäsi kuvailisivat sinua kumppanina?", "nl": "Hoe denk je dat je exen jou als partner zouden beschrijven?", "no": "Hvordan tror du eksene dine ville beskrevet deg som partner?", "sv": "Hur tror du att dina ex skulle beskriva dig som partner?", "pl": "Jak myślisz, jak twoje byłe partnerki/partnerzy opisaliby cię jako partnera?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you feel about getting older?", "da": "Hvordan har du det med at blive ældre?", "de": "Wie fühlst du dich, wenn du älter wirst?", "es": "¿Cómo te sientes respecto a envejecer?", "pt": "Como te sentes em relação a envelhecer?", "ro": "Cum te simți în legătură cu îmbătrânirea?", "fr": "Comment te sens-tu à propos du fait de vieillir ?", "it": "Come ti senti riguardo all''invecchiare?", "fi": "Miltä sinusta tuntuu vanhenemisesta?", "nl": "Hoe voel je je over ouder worden?", "no": "Hvordan føler du om å bli eldre?", "sv": "Hur känner du inför att bli äldre?", "pl": "Jak się czujesz z powodu starzenia się?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Stare into my eyes for thirty seconds without laughing or looking away.", "da": "Stir mig i øjnene i tredive sekunder uden at grine eller kigge væk.", "de": "Schau mir dreißig Sekunden lang in die Augen, ohne zu lachen oder wegzuschauen.", "es": "Mírame a los ojos durante treinta segundos sin reírte ni mirar hacia otro lado.", "pt": "Olha-me nos olhos durante trinta segundos sem rir nem desviar o olhar.", "ro": "Privește-mă în ochi treizeci de secunde fără să râzi sau să te uiți în altă parte.", "fr": "Regarde-moi dans les yeux pendant trente secondes sans rire ni détourner le regard.", "it": "Guarda nei miei occhi per trenta secondi senza ridere o distogliere lo sguardo.", "fi": "Tuijota silmiini kolmekymmentä sekuntia nauramatta tai kääntämättä katsetta pois.", "nl": "Kijk dertig seconden in mijn ogen zonder te lachen of weg te kijken.", "no": "Se inn i øynene mine i tretti sekunder uten å le eller se bort.", "sv": "Stir in mina ögon i trettio sekunder utan att skratta eller titta bort.", "pl": "Patrz mi w oczy przez trzydzieści sekund, nie śmiejąc się i nie odwracając wzroku."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s one little thing that makes you jealous?", "da": "Hvad er en lille ting, der gør dig jaloux?", "de": "Was ist eine kleine Sache, die dich eifersüchtig macht?", "es": "¿Qué pequeña cosa te pone celoso?", "pt": "Que pequena coisa te deixa com ciúmes?", "ro": "Ce lucru mic te face gelos?", "fr": "Quelle petite chose te rend jaloux ?", "it": "Qual è una piccola cosa che ti fa sentire geloso?", "fi": "Mikä on yksi pieni asia, joka saa sinut kateelliseksi?", "nl": "Wat is één klein dingetje dat je jaloers maakt?", "no": "Hva er en liten ting som gjør deg sjalu?", "sv": "Vad är en liten sak som gör dig avundsjuk?", "pl": "Co to za mała rzecz, która cię zazdrości?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s your guiltiest pleasure?", "da": "Hvad er din skyldigste fornøjelse?", "de": "Was ist dein heimlichstes Vergnügen?", "es": "¿Cuál es tu placer más culpable?", "pt": "Qual é o teu prazer mais culpado?", "ro": "Care este plăcerea ta vinovată?", "fr": "Quel est ton plaisir coupable ?", "it": "Qual è il tuo piacere più colpevole?", "fi": "Mikä on suurin syyllisyyden aiheuttava nautintosi?", "nl": "Wat is jouw grootste schuldgevoel genot?", "no": "Hva er din største skyldfølelse glede?", "sv": "Vad är din största skuldfria njutning?", "pl": "Jakie jest twoje największe guilty pleasure?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "When did you realize you liked me more than just a friend?", "da": "Hvornår gik det op for dig, at du kunne lide mig mere end bare som ven?", "de": "Wann hast du gemerkt, dass du mich mehr magst als nur als Freund?", "es": "¿Cuándo te diste cuenta de que te gustaba más que solo como amigo?", "pt": "Quando percebeste que gostavas de mim mais do que apenas como amigo?", "ro": "Când ți-ai dat seama că mă placi mai mult decât ca prieten?", "fr": "Quand as-tu réalisé que tu m’aimais plus que comme un ami ?", "it": "Quando hai capito che mi piacevi più di un semplice amico?", "fi": "Milloin tajusit, että pidät minusta enemmän kuin vain ystävänä?", "nl": "Wanneer besefte je dat je me meer leuk vond dan alleen een vriend?", "no": "Når innså du at du likte meg mer enn bare som en venn?", "sv": "När insåg du att du gillade mig mer än bara som en vän?", "pl": "Kiedy zdałeś sobie sprawę, że lubisz mnie bardziej niż tylko jako przyjaciela?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you feel when people talk about politics or religion at social gatherings?", "da": "Hvordan har du det, når folk taler om politik eller religion til sociale sammenkomster?", "de": "Wie fühlst du dich, wenn Menschen bei gesellschaftlichen Treffen über Politik oder Religion sprechen?", "es": "¿Cómo te sientes cuando la gente habla de política o religión en reuniones sociales?", "pt": "Como te sentes quando as pessoas falam de política ou religião em encontros sociais?", "ro": "Cum te simți când oamenii vorbesc despre politică sau religie la întâlniri sociale?", "fr": "Que ressens-tu quand les gens parlent de politique ou de religion lors d’événements sociaux ?", "it": "Come ti senti quando le persone parlano di politica o religione durante gli incontri sociali?", "fi": "Miltä sinusta tuntuu, kun ihmiset puhuvat politiikasta tai uskonnosta sosiaalisissa tilaisuuksissa?", "nl": "Hoe voel je je als mensen op sociale bijeenkomsten over politiek of religie praten?", "no": "Hvordan føler du deg når folk snakker om politikk eller religion på sosiale sammenkomster?", "sv": "Hur känner du när folk pratar om politik eller religion vid sociala tillställningar?", "pl": "Jak się czujesz, gdy ludzie rozmawiają o polityce lub religii na spotkaniach towarzyskich?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Give me a genuine compliment that has nothing to do with looks.", "da": "Giv mig et oprigtigt kompliment, der ikke handler om udseende.", "de": "Gib mir ein echtes Kompliment, das nichts mit Aussehen zu tun hat.", "es": "Dime un cumplido sincero que no tenga que ver con la apariencia.", "pt": "Dá-me um elogio genuíno que não tenha a ver com a aparência.", "ro": "Spune-mi un compliment sincer care nu are legătură cu aspectul fizic.", "fr": "Fais-moi un vrai compliment qui n’a rien à voir avec l’apparence.", "it": "Dammi un complimento sincero che non abbia nulla a che fare con l''aspetto.", "fi": "Anna minulle aito kehu, joka ei liity ulkonäköön.", "nl": "Geef me een oprecht compliment dat niets met uiterlijk te maken heeft.", "no": "Gi meg et ekte kompliment som ikke har noe med utseende å gjøre.", "sv": "Ge mig en äkta komplimang som inte har något att göra med utseende.", "pl": "Daj mi szczery komplement, który nie ma nic wspólnego z wyglądem."}'::jsonb, false, true, true);
