VALUES ('Do-you-dare Talks', '{"en": "What instantly makes you lose interest in someone?", "da": "Hvad får dig øjeblikkeligt til at miste interessen for nogen?", "de": "Was lässt dich sofort das Interesse an jemandem verlieren?", "es": "¿Qué te hace perder el interés por alguien al instante?", "pt": "O que te faz perder o interesse por alguém instantaneamente?", "ro": "Ce te face să îți pierzi instantaneu interesul pentru cineva?", "fr": "Qu’est-ce qui te fait instantanément perdre tout intérêt pour quelqu’un ?", "it": "Cosa ti fa perdere subito interesse in qualcuno?", "fi": "Mikä saa sinut heti menettämään kiinnostuksesi johonkin?", "nl": "Wat maakt dat je meteen je interesse in iemand verliest?", "no": "Hva får deg til å miste interessen for noen med en gang?", "sv": "Vad får dig att omedelbart tappa intresset för någon?", "pl": "Co natychmiast sprawia, że tracisz zainteresowanie kimś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something you value so much, you’d do anything to protect it?", "da": "Hvad værdsætter du så højt, at du ville gøre alt for at beskytte det?", "de": "Was schätzt du so sehr, dass du alles tun würdest, um es zu schützen?", "es": "¿Qué valoras tanto que harías cualquier cosa para protegerlo?", "pt": "O que valorizas tanto que farias tudo para o proteger?", "ro": "Ce prețuiești atât de mult încât ai face orice să îl protejezi?", "fr": "Qu’est-ce que tu apprécies tellement que tu ferais tout pour le protéger ?", "it": "Qualcosa che valorizzi così tanto da fare qualsiasi cosa per proteggerlo?", "fi": "Mikä on sellainen asia, jota arvostat niin paljon, että tekisit mitä tahansa suojellaksesi sitä?", "nl": "Wat is iets dat je zo waardeert dat je alles zou doen om het te beschermen?", "no": "Hva er noe du verdsetter så mye at du ville gjort hva som helst for å beskytte det?", "sv": "Vad är något du värderar så mycket att du skulle göra vad som helst för att skydda det?", "pl": "Coś, co cenisz tak bardzo, że zrobisz wszystko, aby to chronić?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
