INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What makes me different from everyone you’ve dated before?", "da": "Hvad gør mig anderledes end alle, du har datet før?", "de": "Was macht mich anders als alle, die du zuvor gedatet hast?", "es": "¿Qué me hace diferente de todas las personas con las que has salido antes?", "pt": "O que me torna diferente de todas as pessoas com quem já namoraste?", "ro": "Ce mă face diferit(ă) de toți cei cu care ai fost înainte?", "fr": "Qu’est-ce qui me rend différent(e) de toutes les personnes que tu as fréquentées avant ?", "it": "Cosa mi rende diverso da tutti quelli con cui hai avuto una relazione prima?", "fi": "Mikä tekee minusta erilaisen kuin kaikki ne, joiden kanssa olet aiemmin seurustellut?", "nl": "Wat maakt mij anders dan iedereen met wie je eerder hebt gedate?", "no": "Hva gjør meg annerledes enn alle du har datet før?", "sv": "Vad gör mig annorlunda från alla du har dejtat tidigare?", "pl": "Co sprawia, że różnię się od wszystkich, z którymi wcześniej się spotykałeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
