INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What do you want our next chapter together to feel like?", "da": "Hvordan vil du have, at vores næste kapitel sammen skal føles?", "de": "Wie möchtest du, dass sich unser nächstes Kapitel zusammen anfühlt?", "es": "¿Cómo te gustaría que se sintiera nuestro próximo capítulo juntos?", "pt": "Como gostarias que fosse o nosso próximo capítulo juntos?", "ro": "Cum ai vrea să fie următorul nostru capitol împreună?", "fr": "Comment aimerais-tu que notre prochain chapitre ensemble se sente ?", "it": "Come vuoi che si senta il nostro prossimo capitolo insieme?", "fi": "Miltä haluat, että seuraava yhteinen luvumme tuntuu?", "nl": "Hoe wil je dat ons volgende hoofdstuk samen aanvoelt?", "no": "Hvordan vil du at vårt neste kapittel sammen skal føles?", "sv": "Hur vill du att vårt nästa kapitel tillsammans ska kännas?", "pl": "Jak chcesz, aby nasz następny rozdział razem się czuł?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What do you believe happens when we die — and does that belief comfort or challenge you?", "da": "Hvad tror du sker, når vi dør — og trøster eller udfordrer den tro dig?", "de": "Was glaubst du, passiert, wenn wir sterben – und tröstet oder fordert dich dieser Glaube heraus?", "es": "¿Qué crees que sucede cuando morimos — y esa creencia te consuela o te desafía?", "pt": "O que acreditas que acontece quando morremos — e essa crença conforta-te ou desafia-te?", "ro": "Ce crezi că se întâmplă când murim — și credința asta te liniștește sau te provoacă?", "fr": "Que penses-tu qu’il se passe quand on meurt — et cette croyance te réconforte-t-elle ou te met-elle au défi ?", "it": "Cosa credi che accada quando moriamo — e questa convinzione ti conforta o ti sfida?", "fi": "Mitä uskot tapahtuvan, kun kuolemme — ja lohduttaako vai haastaa tämä uskomus sinua?", "nl": "Wat geloof je dat er gebeurt als we sterven — en biedt dat geloof je troost of daagt het je uit?", "no": "Hva tror du skjer når vi dør — og gir den troen deg trøst eller utfordrer den deg?", "sv": "Vad tror du händer när vi dör — och ger den tron dig tröst eller utmanar den dig?", "pl": "Co sądzisz, co się dzieje, gdy umieramy — i czy ta wiara cię pociesza, czy raczej wyzwala?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What moral value or principle matters most to you in a relationship?", "da": "Hvilken moralsk værdi eller princip betyder mest for dig i et forhold?", "de": "Welcher moralische Wert oder welches Prinzip ist dir in einer Beziehung am wichtigsten?", "es": "¿Qué valor moral o principio es más importante para ti en una relación?", "pt": "Que valor moral ou princípio é mais importante para ti num relacionamento?", "ro": "Ce valoare morală sau principiu contează cel mai mult pentru tine într-o relație?", "fr": "Quelle valeur morale ou principe compte le plus pour toi dans une relation ?", "it": "Quale valore morale o principio è più importante per te in una relazione?", "fi": "Mikä moraalinen arvo tai periaate on sinulle tärkein suhteessa?", "nl": "Welke morele waarde of principe is voor jou het belangrijkst in een relatie?", "no": "Hvilken moralsk verdi eller prinsipp betyr mest for deg i et forhold?", "sv": "Vilken moralisk värdering eller princip betyder mest för dig i en relation?", "pl": "Jaką wartość moralną lub zasadę uważasz za najważniejszą w związku?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you decide what’s forgivable and what’s not?", "da": "Hvordan beslutter du, hvad der kan tilgives, og hvad der ikke kan?", "de": "Wie entscheidest du, was verzeihbar ist und was nicht?", "es": "¿Cómo decides qué se puede perdonar y qué no?", "pt": "Como decides o que é perdoável e o que não é?", "ro": "Cum decizi ce poate fi iertat și ce nu?", "fr": "Comment décides-tu ce qui est pardonnable et ce qui ne l’est pas ?", "it": "Come decidi cosa è perdonabile e cosa non lo è?", "fi": "Miten päätät, mikä on anteeksiannettavaa ja mikä ei?", "nl": "Hoe beslis je wat vergeven kan worden en wat niet?", "no": "Hvordan bestemmer du hva som er tilgivelig og hva som ikke er det?", "sv": "Hur bestämmer du vad som är förlåtligt och vad som inte är det?", "pl": "Jak decydujesz, co jest do wybaczenia, a co nie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Have you ever struggled to understand someone’s beliefs that were very different from your own?", "da": "Har du nogensinde haft svært ved at forstå nogen, hvis overbevisninger var meget forskellige fra dine egne?", "de": "Hattest du jemals Schwierigkeiten, den Glauben eines anderen zu verstehen, der sich stark von deinem unterschied?", "es": "¿Alguna vez te costó entender las creencias de alguien muy diferentes a las tuyas?", "pt": "Alguma vez tiveste dificuldade em entender as crenças de alguém muito diferentes das tuas?", "ro": "Ți-a fost vreodată greu să înțelegi credințele cuiva foarte diferite de ale tale?", "fr": "As-tu déjà eu du mal à comprendre les croyances de quelqu’un de très différent de toi ?", "it": "Hai mai avuto difficoltà a comprendere le credenze di qualcuno che erano molto diverse dalle tue?", "fi": "Oletko koskaan kamppaillut ymmärtääksesi jonkun uskomuksia, jotka olivat hyvin erilaisia kuin omasi?", "nl": "Heb je ooit moeite gehad om de overtuigingen van iemand te begrijpen die heel anders waren dan de jouwe?", "no": "Har du noen gang hatt problemer med å forstå noens tro som var veldig forskjellig fra din egen?", "sv": "Har du någonsin haft svårt att förstå någon annans övertygelser som var väldigt olika dina egna?", "pl": "Czy kiedykolwiek miałeś trudności ze zrozumieniem czyichś przekonań, które były bardzo różne od twoich?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you feel when you hear someone talk openly about their past relationships or sexual history?", "da": "Hvordan har du det, når du hører nogen tale åbent om deres tidligere forhold eller seksuelle historie?", "de": "Wie fühlst du dich, wenn jemand offen über seine früheren Beziehungen oder seine sexuelle Vergangenheit spricht?", "es": "¿Cómo te sientes cuando alguien habla abiertamente de sus relaciones o historia sexual pasada?", "pt": "Como te sentes quando alguém fala abertamente sobre as suas relações ou passado sexual?", "ro": "Cum te simți când auzi pe cineva vorbind deschis despre relațiile sau trecutul său sexual?", "fr": "Que ressens-tu quand quelqu’un parle ouvertement de ses anciennes relations ou de son passé sexuel ?", "it": "Come ti senti quando senti qualcuno parlare apertamente delle proprie relazioni passate o della propria storia sessuale?", "fi": "Miltä sinusta tuntuu, kun kuulet jonkun puhuvan avoimesti menneistä suhteistaan tai seksuaalisesta historiastaan?", "nl": "Hoe voel je je als je iemand openlijk hoort praten over hun vorige relaties of seksuele geschiedenis?", "no": "Hvordan føler du deg når du hører noen snakke åpent om sine tidligere forhold eller seksuelle erfaringer?", "sv": "Hur känner du när du hör någon prata öppet om sina tidigare relationer eller sin sexuella historia?", "pl": "Jak się czujesz, gdy słyszysz, jak ktoś otwarcie mówi o swoich przeszłych związkach lub historii seksualnej?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Do you think people are judged too harshly for the number of people they’ve been with?", "da": "Synes du, folk bliver dømt for hårdt for antallet af personer, de har været sammen med?", "de": "Denkst du, Menschen werden zu hart dafür beurteilt, mit wie vielen sie zusammen waren?", "es": "¿Crees que la gente es juzgada demasiado severamente por la cantidad de personas con las que ha estado?", "pt": "Achas que as pessoas são julgadas com demasiada dureza pelo número de pessoas com quem estiveram?", "ro": "Crezi că oamenii sunt judecați prea aspru pentru numărul de persoane cu care au fost?", "fr": "Penses-tu que les gens sont trop jugés pour le nombre de partenaires qu’ils ont eus ?", "it": "Pensi che le persone vengano giudicate troppo severamente per il numero di persone con cui sono state?", "fi": "Mietitkö, että ihmisiä tuomitaan liian ankarasti sen perusteella, kuinka monen kanssa he ovat olleet?", "nl": "Denk je dat mensen te hard beoordeeld worden op het aantal mensen met wie ze geweest zijn?", "no": "Tror du folk blir dømt for strengt for antallet de har vært sammen med?", "sv": "Tycker du att människor döms för hårt för antalet personer de har varit med?", "pl": "Czy uważasz, że ludzie są zbyt surowo oceniani za liczbę osób, z którymi byli?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something you’ve learned to be more tolerant or open-minded about?", "da": "Hvad har du lært at blive mere tolerant eller åben overfor?", "de": "Was hast du gelernt, toleranter oder offener zu sein?", "es": "¿Qué has aprendido a tolerar o aceptar más abiertamente?", "pt": "O que aprendeste a tolerar ou a ser mais aberto?", "ro": "Ce ai învățat să tolerezi mai ușor sau să accepți mai deschis?", "fr": "Qu’as-tu appris à être plus tolérant ou plus ouvert à propos de ?", "it": "Qualcosa su cui hai imparato ad essere più tollerante o aperto di mente?", "fi": "Mikä on jotain, jonka olet oppinut olemaan suvaitsevaisempi tai avaramielisempi?", "nl": "Wat is iets waar je meer tolerant of open-minded over bent geworden?", "no": "Hva er noe du har lært å være mer tolerant eller åpen for?", "sv": "Vad är något du har lärt dig att vara mer tolerant eller öppen för?", "pl": "Czego nauczyłeś się, aby być bardziej tolerancyjny lub otwarty?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
