INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Do you think honesty can sometimes sound like judgment?", "da": "Tror du, ærlighed nogle gange kan lyde som dømmende?", "de": "Denkst du, Ehrlichkeit kann manchmal wie ein Urteil klingen?", "es": "¿Crees que la honestidad a veces puede sonar como un juicio?", "pt": "Acreditas que a honestidade às vezes soa como julgamento?", "ro": "Crezi că sinceritatea poate suna uneori ca o judecată?", "fr": "Penses-tu que l’honnêteté peut parfois ressembler à un jugement ?", "it": "Pensi che l''onestà a volte possa sembrare un giudizio?", "fi": "Luuleeko, että rehellisyys voi joskus kuulostaa tuomiolta?", "nl": "Denk je dat eerlijkheid soms als oordeel kan klinken?", "no": "Tror du at ærlighet noen ganger kan høres ut som dømming?", "sv": "Tycker du att ärlighet ibland kan låta som dömande?", "pl": "Czy myślisz, że szczerość czasami może brzmieć jak osąd?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s a political or social issue you feel strongly about but rarely talk about?", "da": "Hvilket politisk eller socialt emne føler du stærkt for, men taler sjældent om?", "de": "Welches politische oder soziale Thema liegt dir am Herzen, über das du selten sprichst?", "es": "¿Qué tema político o social te importa mucho pero del que rara vez hablas?", "pt": "Que tema político ou social te importa muito mas raramente falas sobre ele?", "ro": "Ce subiect politic sau social te preocupă, dar rar vorbești despre el?", "fr": "Quel sujet politique ou social te tient à cœur mais dont tu parles rarement ?", "it": "Qual è un problema politico o sociale su cui hai forti opinioni ma di cui parli raramente?", "fi": "Mikä on poliittinen tai sosiaalinen kysymys, josta tunnet voimakkaasti, mutta josta harvoin puhut?", "nl": "Wat is een politiek of sociaal probleem waar je sterk over denkt, maar zelden over praat?", "no": "Hva er et politisk eller sosialt spørsmål du føler sterkt for, men sjelden snakker om?", "sv": "Vilket politiskt eller socialt ämne känner du starkt för men sällan pratar om?", "pl": "Jaki jest problem polityczny lub społeczny, na którym ci zależy, ale rzadko o nim rozmawiasz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What do you think is harder today: being authentic or being private?", "da": "Hvad tror du er sværere i dag: at være autentisk eller at være privat?", "de": "Was glaubst du, ist heute schwieriger: authentisch oder privat zu sein?", "es": "¿Qué crees que es más difícil hoy en día: ser auténtico o ser reservado?", "pt": "O que achas que é mais difícil hoje: ser autêntico ou ser reservado?", "ro": "Ce crezi că e mai greu azi: să fii autentic sau să fii discret?", "fr": "Que crois-tu qu’il soit plus difficile aujourd’hui : être authentique ou être réservé ?", "it": "Cosa pensi sia più difficile oggi: essere autentici o essere riservati?", "fi": "Mitä mieltä olet, mikä on tänä päivänä vaikeampaa: olla aito vai olla yksityinen?", "nl": "Wat denk je dat vandaag moeilijker is: authentiek zijn of privé zijn?", "no": "Hva tror du er vanskeligere i dag: å være autentisk eller å være privat?", "sv": "Vad tycker du är svårare idag: att vara äkta eller att vara privat?", "pl": "Co myślisz, co jest dzisiaj trudniejsze: bycie autentycznym czy bycie prywatnym?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you feel about influencers or people who share every detail of their lives publicly?", "da": "Hvordan har du det med influencere eller folk, der deler alle detaljer af deres liv offentligt?", "de": "Wie stehst du zu Influencern oder Menschen, die jedes Detail ihres Lebens öffentlich teilen?", "es": "¿Qué opinas de los influencers o de las personas que comparten cada detalle de su vida públicamente?", "pt": "O que pensas dos influenciadores ou pessoas que partilham todos os detalhes da sua vida?", "ro": "Ce părere ai despre influencerii sau persoanele care își împărtășesc public fiecare detaliu al vieții?", "fr": "Que penses-tu des influenceurs ou des gens qui partagent chaque détail de leur vie publiquement ?", "it": "Cosa ne pensi degli influencer o delle persone che condividono ogni dettaglio della loro vita pubblicamente?", "fi": "Mitä mieltä olet vaikuttajista tai ihmisistä, jotka jakavat jokaisen yksityiskohdan elämästään julkisesti?", "nl": "Hoe voel je je over influencers of mensen die elk detail van hun leven openbaar delen?", "no": "Hva synes du om influensere eller folk som deler hver detalj av livene sine offentlig?", "sv": "Vad tycker du om influencers eller personer som delar varje detalj av sina liv offentligt?", "pl": "Jak się czujesz w związku z influencerami lub osobami, które publicznie dzielą się każdym szczegółem swojego życia?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Have you ever taken something that wasn’t yours? How did you feel afterward?", "da": "Har du nogensinde taget noget, der ikke var dit? Hvordan havde du det bagefter?", "de": "Har du nogensinde følt dig genert over at spørge om det, du vil have?", "es": "Hast du dich jemals geschämt, nach dem zu fragen, was du willst?", "pt": "Já pegaste algo que não era teu? Como te sentiste depois?", "ro": "Ai luat vreodată ceva ce nu era al tău? Cum te-ai simțit după?", "fr": "As-tu déjà pris quelque chose qui ne t’appartenait pas ? Comment t’es-tu senti après ?", "it": "Hai mai preso qualcosa che non era tuo? Come ti sei sentito dopo?", "fi": "Oletko koskaan ottanut jotain, mikä ei ollut sinun? Miltä sinusta tuntui sen jälkeen?", "nl": "Heb je ooit iets genomen dat niet van jou was? Hoe voelde je je daarna?", "no": "Har du noen gang tatt noe som ikke var ditt? Hvordan følte du deg etterpå?", "sv": "Har du någonsin tagit något som inte var ditt? Hur kände du dig efteråt?", "pl": "Czy kiedykolwiek wziąłeś coś, co nie było twoje? Jak się wtedy czułeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What helps you feel free while still feeling loved?", "da": "Hvad hjælper dig med at føle dig fri, men stadig elsket?", "de": "Was hilft dir, dich frei zu fühlen und trotzdem geliebt zu werden?", "es": "¿Qué te ayuda a sentirte libre y al mismo tiempo amado?", "pt": "O que te ajuda a sentir-te livre e ainda assim amado?", "ro": "Ce te ajută să te simți liber, dar totuși iubit?", "fr": "Qu’est-ce qui t’aide à te sentir libre tout en te sentant aimé ?", "it": "Cosa ti aiuta a sentirti libero pur continuando a sentirti amato?", "fi": "Mikä auttaa sinua tuntemaan itsesi vapaaksi samalla kun tunnet itsesi rakastetuksi?", "nl": "Wat helpt jou om je vrij te voelen terwijl je je toch geliefd voelt?", "no": "Hva hjelper deg å føle deg fri samtidig som du føler deg elsket?", "sv": "Vad hjälper dig att känna dig fri samtidigt som du känner dig älskad?", "pl": "Co pomaga ci czuć się wolnym, jednocześnie czując się kochanym?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What helps you feel confident and attractive in your own skin?", "da": "Hvad hjælper dig med at føle dig selvsikker og attraktiv i din egen hud?", "de": "Was hilft dir, dich selbstbewusst und attraktiv in deiner eigenen Haut zu fühlen?", "es": "¿Qué te ayuda a sentirte seguro y atractivo en tu propia piel?", "pt": "O que te ajuda a sentires-te confiante e atraente na tua própria pele?", "ro": "Ce te ajută să te simți încrezător și atrăgător în pielea ta?", "fr": "Qu’est-ce qui t’aide à te sentir confiant et attirant dans ta propre peau ?", "it": "Cosa ti aiuta a sentirti sicuro e attraente nella tua pelle?", "fi": "Mikä auttaa sinua tuntemaan itsesi itsevarmaksi ja viehättäväksi omassa kehossasi?", "nl": "Wat helpt jou om je zelfverzekerd en aantrekkelijk te voelen in je eigen huid?", "no": "Hva hjelper deg med å føle deg selvsikker og attraktiv i din egen hud?", "sv": "Vad hjälper dig att känna dig självsäker och attraktiv i din egen hud?", "pl": "Co pomaga ci czuć się pewnie i atrakcyjnie we własnej skórze?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s something I say that always makes you feel loved?", "da": "Hvad er noget, jeg siger, som altid får dig til at føle dig elsket?", "de": "Was sage ich, das dich immer geliebt fühlen lässt?", "es": "¿Qué es algo que digo que siempre te hace sentir amado/a?", "pt": "O que eu digo que sempre te faz sentir amado(a)?", "ro": "Ce spun eu mereu care te face să te simți iubit(ă)?", "fr": "Qu’est-ce que je dis qui te fait toujours te sentir aimé(e) ?", "it": "Qualcosa che dico sempre che ti fa sentire amato?", "fi": "Mikä on jotain, mitä sanon, joka aina saa sinut tuntemaan itsesi rakastetuksi?", "nl": "Wat is iets dat ik zeg dat je altijd geliefd laat voelen?", "no": "Hva er noe jeg sier som alltid får deg til å føle deg elsket?", "sv": "Vad är något jag säger som alltid får dig att känna dig älskad?", "pl": "Coś, co mówię, co zawsze sprawia, że czujesz się kochany?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What kind of words mean the most to you when you’ve had a rough day?", "da": "Hvilke slags ord betyder mest for dig, når du har haft en hård dag?", "de": "Welche Art von Worten bedeutet dir am meisten, wenn du einen harten Tag hattest?", "es": "¿Qué tipo de palabras significan más para ti cuando has tenido un mal día?", "pt": "Que tipo de palavras significam mais para ti quando tiveste um dia difícil?", "ro": "Ce fel de cuvinte înseamnă cel mai mult pentru tine când ai avut o zi grea?", "fr": "Quels mots comptent le plus pour toi quand tu as passé une mauvaise journée ?", "it": "Che tipo di parole significano di più per te quando hai avuto una giornata difficile?", "fi": "Minkälaiset sanat merkitsevät sinulle eniten, kun olet ollut huonolla tuulella?", "nl": "Welke woorden betekenen het meest voor jou als je een zware dag hebt gehad?", "no": "Hvilke ord betyr mest for deg når du har hatt en tøff dag?", "sv": "Vilka slags ord betyder mest för dig när du har haft en tuff dag?", "pl": "Jakie słowa mają dla Ciebie największe znaczenie, gdy miałeś trudny dzień?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What do you wish I said to you more often?", "da": "Hvad ville du ønske, jeg sagde til dig oftere?", "de": "Was wünschst du dir, dass ich dir öfter sage?", "es": "¿Qué te gustaría que te dijera más a menudo?", "pt": "O que gostarias que eu te dissesse mais vezes?", "ro": "Ce ți-ai dori să-ți spun mai des?", "fr": "Qu’aimerais-tu que je te dise plus souvent ?", "it": "Cosa vorresti che ti dicessi più spesso?", "fi": "Mitä toivoisit, että sanoisin sinulle useammin?", "nl": "Wat zou je willen dat ik vaker tegen je zei?", "no": "Hva ønsker du at jeg sa til deg oftere?", "sv": "Vad önskar du att jag sa till dig oftare?", "pl": "Co chciałbyś, żebym częściej ci mówił?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When was the last time something I said really stuck with you — in a good way?", "da": "Hvornår var sidste gang, noget jeg sagde, virkelig blev hos dig – på en god måde?", "de": "Wann war das letzte Mal, dass etwas, das ich sagte, dir im Gedächtnis blieb – auf gute Weise?", "es": "¿Cuándo fue la última vez que algo que dije realmente se te quedó grabado, de buena manera?", "pt": "Quando foi a última vez que algo que eu disse realmente ficou contigo – de uma forma boa?", "ro": "Când a fost ultima dată când ceva ce am spus ți-a rămas în minte – într-un mod bun?", "fr": "Quand est-ce que quelque chose que j’ai dit t’est vraiment resté – d’une bonne façon ?", "it": "Quando è stata l''ultima volta che qualcosa che ho detto ti è rimasto impresso — in un modo positivo?", "fi": "Milloin viimeksi jokin, mitä sanoin, jäi sinulle todella mieleen — hyvällä tavalla?", "nl": "Wanneer was de laatste keer dat iets wat ik zei echt bij je is gebleven — op een goede manier?", "no": "Når var sist gang noe jeg sa virkelig festet seg hos deg — på en god måte?", "sv": "När var senaste gången något jag sa verkligen stannade kvar hos dig — på ett bra sätt?", "pl": "Kiedy ostatni raz coś, co powiedziałem, naprawdę zapadło ci w pamięć — w dobry sposób?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you like me to cheer you on when you’re nervous or unsure?", "da": "Hvordan kan jeg bedst opmuntre dig, når du er nervøs eller usikker?", "de": "Wie kann ich dich am besten aufmuntern, wenn du nervös oder unsicher bist?", "es": "¿Cómo te gusta que te anime cuando estás nervioso/a o inseguro/a?", "pt": "Como gostas que eu te anime quando estás nervoso(a) ou inseguro(a)?", "ro": "Cum îți place să te încurajez când ești nervos(ă) sau nesigur(ă)?", "fr": "Comment aimes-tu que je t’encourage quand tu es nerveux(se) ou incertain(e) ?", "it": "Come ti piace che ti incoraggi quando sei nervoso o insicuro?", "fi": "Miten haluaisit, että kannustan sinua, kun olet hermostunut tai epävarma?", "nl": "Hoe vind je het fijn dat ik je aanmoedig als je nerveus of onzeker bent?", "no": "Hvordan liker du at jeg heier på deg når du er nervøs eller usikker?", "sv": "Hur vill du att jag hejar på dig när du är nervös eller osäker?", "pl": "Jak lubisz, żeby cię dopingować, gdy jesteś zdenerwowany lub niepewny?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What kind of compliments feel most real to you?", "da": "Hvilke slags komplimenter føles mest ægte for dig?", "de": "Welche Art von Komplimenten fühlt sich für dich am ehrlichsten an?", "es": "¿Qué tipo de cumplidos se sienten más reales para ti?", "pt": "Que tipo de elogios te parecem mais sinceros?", "ro": "Ce fel de complimente ți se par cele mai autentice?", "fr": "Quels types de compliments te semblent les plus sincères ?", "it": "Quali tipi di complimenti ti sembrano più autentici?", "fi": "Minkälaiset kohteliaisuudet tuntuvat sinusta kaikkein aidoimmilta?", "nl": "Welke soort complimenten voelen voor jou het meest oprecht?", "no": "Hvilke typer komplimenter føles mest ekte for deg?", "sv": "Vilka typer av komplimanger känns mest äkta för dig?", "pl": "Jakie komplementy wydają ci się najbardziej autentyczne?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When you apologize, what do you need to hear from me to feel forgiven?", "da": "Når du undskylder, hvad har du brug for at høre fra mig for at føle dig tilgivet?", "de": "Wenn du dich entschuldigst, was musst du von mir hören, um dich vergeben zu fühlen?", "es": "Cuando te disculpas, ¿qué necesitas oír de mí para sentirte perdonado/a?", "pt": "Quando pedes desculpa, o que precisas de ouvir de mim para te sentires perdoado(a)?", "ro": "Când îți ceri scuze, ce ai nevoie să auzi de la mine ca să te simți iertat(ă)?", "fr": "Quand tu t’excuses, qu’as-tu besoin d’entendre de ma part pour te sentir pardonné(e) ?", "it": "Quando ti scuso, cosa hai bisogno di sentire da me per sentirti perdonato?", "fi": "Kun pyydät anteeksi, mitä sinun tarvitsee kuulla minulta, jotta tuntisit itsesi anteeksiannetuksi?", "nl": "Wanneer je je verontschuldigt, wat heb je van mij nodig om je vergeven te voelen?", "no": "Når du ber om unnskyldning, hva trenger du å høre fra meg for å føle deg tilgitt?", "sv": "När du ber om ursäkt, vad behöver du höra från mig för att känna dig förlåten?", "pl": "Kiedy przepraszasz, co musisz usłyszeć ode mnie, aby poczuć się wybaczonym?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s a sweet message or text from me that would make your whole day better?", "da": "Hvad er en sød besked eller sms fra mig, der ville gøre hele din dag bedre?", "de": "Welche süße Nachricht oder SMS von mir würde deinen ganzen Tag besser machen?", "es": "¿Qué mensaje o texto dulce mío haría que tu día fuera mejor?", "pt": "Que mensagem ou texto carinhoso meu tornaria o teu dia melhor?", "ro": "Care este un mesaj sau un text drăguț de la mine care ți-ar face ziua mai bună?", "fr": "Quel message ou texto doux de ma part rendrait ta journée meilleure ?", "it": "Qual è un messaggio dolce o un testo da parte mia che renderebbe la tua giornata migliore?", "fi": "Mikä olisi suloinen viesti tai teksti minulta, joka tekisi koko päiväsi paremmaksi?", "nl": "Wat is een lief bericht of tekst van mij die je hele dag beter zou maken?", "no": "Hva er en søt melding eller tekst fra meg som ville gjort hele dagen din bedre?", "sv": "Vad är ett sött meddelande eller sms från mig som skulle göra hela din dag bättre?", "pl": "Jaka słodka wiadomość lub tekst ode mnie poprawiłby ci cały dzień?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you like me to express admiration for you?", "da": "Hvordan vil du have, jeg viser beundring for dig?", "de": "Wie möchtest du, dass ich Bewunderung für dich ausdrücke?", "es": "¿Cómo te gusta que exprese mi admiración por ti?", "pt": "Como gostas que eu expresse a minha admiração por ti?", "ro": "Cum îți place să îți arăt admirația mea?", "fr": "Comment aimes-tu que j’exprime mon admiration pour toi ?", "it": "Come ti piace che esprima ammirazione per te?", "fi": "Miten haluaisit, että ilmaisisin ihailuani sinua kohtaan?", "nl": "Hoe vind je het dat ik mijn bewondering voor jou uitdruk?", "no": "Hvordan liker du at jeg uttrykker beundring for deg?", "sv": "Hur vill du att jag ska uttrycka beundran för dig?", "pl": "Jak lubisz, żebym wyrażał podziw dla ciebie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When do my words matter the most to you — when you’re celebrating or when you’re hurting?", "da": "Hvornår betyder mine ord mest for dig – når du fejrer, eller når du har det svært?", "de": "Wann bedeuten meine Worte dir am meisten – wenn du feierst oder wenn du leidest?", "es": "¿Cuándo mis palabras significan más para ti: cuando celebras o cuando sufres?", "pt": "Quando é que as minhas palavras significam mais para ti — quando celebras ou quando sofres?", "ro": "Când cuvintele mele contează cel mai mult pentru tine — când te bucuri sau când suferi?", "fr": "Quand mes mots comptent-ils le plus pour toi — quand tu célèbres ou quand tu souffres ?", "it": "Quando le mie parole contano di più per te — quando stai festeggiando o quando stai soffrendo?", "fi": "Milloin sanani merkitsevät sinulle eniten — kun juhlit vai kun kärsit?", "nl": "Wanneer betekenen mijn woorden het meest voor jou - als je aan het vieren bent of als je pijn hebt?", "no": "Når betyr ordene mine mest for deg — når du feirer eller når du har det vondt?", "sv": "När betyder mina ord mest för dig – när du firar eller när du har ont?", "pl": "Kiedy moje słowa mają dla ciebie największe znaczenie — gdy świętujesz, czy gdy cierpisz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What could I say that would make you feel truly seen right now?", "da": "Hvad kunne jeg sige lige nu, der ville få dig til at føle dig virkelig set?", "de": "Was könnte ich jetzt sagen, damit du dich wirklich gesehen fühlst?", "es": "¿Qué podría decir ahora que te haría sentir realmente visto/a?", "pt": "O que eu poderia dizer agora que te faria sentir-te verdadeiramente visto(a)?", "ro": "Ce aș putea spune acum ca să te simți cu adevărat văzut(ă)?", "fr": "Que pourrais-je dire maintenant pour que tu te sentes vraiment vu(e) ?", "it": "Cosa potrei dire che ti farebbe sentire veramente visto in questo momento?", "fi": "Mitä voisin sanoa, jotta tuntisit itsesi todella nähdyksi juuri nyt?", "nl": "Wat zou ik kunnen zeggen waardoor je je op dit moment echt gezien zou voelen?", "no": "Hva kunne jeg si som virkelig ville få deg til å føle deg sett akkurat nå?", "sv": "Vad skulle jag kunna säga som verkligen skulle få dig att känna dig sedd just nu?", "pl": "Co mógłbym powiedzieć, żebyś poczuł się naprawdę dostrzegany w tej chwili?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you feel when I tease or joke with you — do you take it as play or something else?", "da": "Hvordan har du det, når jeg driller eller laver sjov med dig – tager du det som leg eller noget andet?", "de": "Wie fühlst du dich, wenn ich dich necke oder mit dir scherze – nimmst du es als Spiel oder etwas anderes?", "es": "¿Cómo te sientes cuando bromeo contigo, lo tomas como juego o como algo más?", "pt": "Como te sentes quando eu brinco contigo — levas como brincadeira ou outra coisa?", "ro": "Cum te simți când glumesc cu tine — o iei ca pe o joacă sau altceva?", "fr": "Comment te sens-tu quand je plaisante avec toi — tu le prends comme un jeu ou autrement ?", "it": "Come ti senti quando ti prendo in giro o scherzo con te — lo prendi come un gioco o qualcos''altro?", "fi": "Miltä sinusta tuntuu, kun kiusoittelen tai vitsailen kanssasi — otatko sen leikkinä vai jotain muuta?", "nl": "Hoe voel je je als ik je plaag of grappen met je maak — beschouw je het als spel of iets anders?", "no": "Hvordan føler du deg når jeg tuller eller spøker med deg — tar du det som lek eller noe annet?", "sv": "Hur känner du dig när jag retas eller skämtar med dig – uppfattar du det som lek eller något annat?", "pl": "Jak się czujesz, gdy żartuję lub droczę się z tobą — traktujesz to jako zabawę, czy może coś innego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When you’re upset, do you want me to listen quietly or say something reassuring right away?", "da": "Når du er ked af det, vil du have mig til at lytte stille eller sige noget beroligende med det samme?", "de": "Wenn du traurig bist, soll ich still zuhören oder gleich etwas Beruhigendes sagen?", "es": "Cuando estás molesto/a, ¿quieres que escuche en silencio o que diga algo reconfortante de inmediato?", "pt": "Quando estás chateado(a), queres que eu ouça em silêncio ou diga algo reconfortante logo?", "ro": "Când ești supărat(ă), vrei să te ascult în tăcere sau să spun ceva liniștitor imediat?", "fr": "Quand tu es contrarié(e), veux-tu que j’écoute en silence ou que je dise quelque chose de rassurant tout de suite ?", "it": "Quando sei turbato, preferisci che ti ascolti in silenzio o che dica subito qualcosa di rassicurante?", "fi": "Kun olet järkyttynyt, haluatko, että kuuntelen hiljaa vai sanonko heti jotain lohduttavaa?", "nl": "Wanneer je van streek bent, wil je dan dat ik stil luister of meteen iets geruststellends zeg?", "no": "Når du er opprørt, vil du at jeg skal lytte stille eller si noe beroligende med en gang?", "sv": "När du är upprörd, vill du att jag ska lyssna tyst eller säga något lugnande direkt?", "pl": "Kiedy jesteś zdenerwowany, wolisz, żebym słuchał cicho, czy od razu powiedział coś pocieszającego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s one compliment or kind word that always feels awkward to you — and why?", "da": "Hvilket kompliment eller venligt ord føles altid akavet for dig – og hvorfor?", "de": "Welches Kompliment oder freundliche Wort fühlt sich für dich immer unangenehm an – und warum?", "es": "¿Qué cumplido o palabra amable siempre te resulta incómodo, y por qué?", "pt": "Que elogio ou palavra gentil te soa sempre estranho — e por quê?", "ro": "Ce compliment sau cuvânt amabil ți se pare mereu ciudat – și de ce?", "fr": "Quel compliment ou mot gentil te met toujours mal à l’aise — et pourquoi ?", "it": "Qual è un complimento o una parola gentile che ti fa sempre sentire a disagio — e perché?", "fi": "Mikä on yksi kehu tai ystävällinen sana, joka tuntuu aina vaivaannuttavalta — ja miksi?", "nl": "Wat is één compliment of vriendelijk woord dat je altijd ongemakkelijk vindt — en waarom?", "no": "Hva er et kompliment eller et vennlig ord som alltid føles ubehagelig for deg — og hvorfor?", "sv": "Vilken komplimang eller snäll kommentar känns alltid awkward för dig — och varför?", "pl": "Jakie jest jedno komplement lub miłe słowo, które zawsze wydaje ci się niezręczne — i dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Do you like when you get comments on your appearance?", "da": "Kan du lide, når du får kommentarer om dit udseende?", "de": "Magst du es, wenn du Kommentare zu deinem Aussehen bekommst?", "es": "¿Te gusta cuando recibes comentarios sobre tu apariencia?", "pt": "Gostas quando recebes comentários sobre a tua aparência?", "ro": "Îți place când primești comentarii despre aspectul tău?", "fr": "Aimes-tu quand on commente ton apparence ?", "it": "Ti piace quando ricevi commenti sul tuo aspetto?", "fi": "Pidätkö siitä, kun saat kommentteja ulkonäöstäsi?", "nl": "Vind je het leuk als je opmerkingen krijgt over je uiterlijk?", "no": "Liker du når du får kommentarer om utseendet ditt?", "sv": "Tycker du om när du får kommentarer om ditt utseende?", "pl": "Czy lubisz, gdy dostajesz komentarze na temat swojego wyglądu?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When do you feel like you have my full attention?", "da": "Hvornår føler du, at du har min fulde opmærksomhed?", "de": "Wann fühlst du, dass du meine volle Aufmerksamkeit hast?", "es": "¿Cuándo sientes que tienes toda mi atención?", "pt": "Quando sentes que tens toda a minha atenção?", "ro": "Când simți că ai toată atenția mea?", "fr": "Quand sens-tu que tu as toute mon attention ?", "it": "Quando senti di avere la mia piena attenzione?", "fi": "Milloin tunnet, että minulla on täysi huomiosi?", "nl": "Wanneer voel je dat je mijn volledige aandacht hebt?", "no": "Når føler du at du har min fulle oppmerksomhet?", "sv": "När känner du att du har min fulla uppmärksamhet?", "pl": "Kiedy czujesz, że mam twoją pełną uwagę?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s one hobby or activity you’d love for us to try as a couple?", "da": "Hvilken hobby eller aktivitet vil du gerne have, vi prøver som par?", "de": "Welches Hobby oder welche Aktivität würdest du gerne als Paar ausprobieren?", "es": "¿Qué afición o actividad te gustaría que probáramos como pareja?", "pt": "Que hobby ou atividade gostarias que experimentássemos como casal?", "ro": "Ce hobby sau activitate ai vrea să încercăm ca cuplu?", "fr": "Quel passe-temps ou activité aimerais-tu que nous essayions en couple ?", "it": "Qual è un hobby o un''attività che ti piacerebbe provare insieme come coppia?", "fi": "Mikä on yksi harrastus tai aktiviteetti, jonka haluaisit meidän kokeilevan parina?", "nl": "Wat is een hobby of activiteit die je graag zou willen dat we samen als stel proberen?", "no": "Hva er en hobby eller aktivitet du gjerne vil at vi skal prøve som par?", "sv": "Vilken hobby eller aktivitet skulle du vilja att vi provar som ett par?", "pl": "Jakie jedno hobby lub aktywność chciałbyś, żebyśmy spróbowali jako para?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What does “quality time” look like to you — what do we do, how does it feel?", "da": "Hvordan ser “kvalitetstid” ud for dig – hvad laver vi, hvordan føles det?", "de": "Wie sieht „Quality Time“ für dich aus – was machen wir, wie fühlt es sich an?", "es": "¿Cómo se ve el “tiempo de calidad” para ti — qué hacemos, cómo se siente?", "pt": "Como é o “tempo de qualidade” para ti — o que fazemos, como se sente?", "ro": "Cum arată pentru tine “timpul de calitate” — ce facem, cum se simte?", "fr": "À quoi ressemble pour toi un “moment de qualité” — que faisons-nous, comment cela se ressent-il ?", "it": "Cosa significa per te \"tempo di qualità\" — cosa facciamo, come ci ci sentiamo?", "fi": "Miltä \"laadukas aika\" näyttää sinulle - mitä teemme, miltä se tuntuu?", "nl": "Wat betekent “kwaliteitstijd” voor jou — wat doen we, hoe voelt het?", "no": "Hva ser \"kvalitetstid\" ut for deg — hva gjør vi, hvordan føles det?", "sv": "Vad innebär \"kvalitetstid\" för dig — vad gör vi, hur känns det?", "pl": "Jak dla ciebie wygląda „jakościowy czas” — co robimy, jak się czujemy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How can I make our time together feel more intentional?", "da": "Hvordan kan jeg få vores tid sammen til at føles mere bevidst og meningsfuld?", "de": "Wie kann ich unsere gemeinsame Zeit bewusster und bedeutungsvoller gestalten?", "es": "¿Cómo puedo hacer que nuestro tiempo juntos se sienta más intencional y significativo?", "pt": "Como posso tornar o nosso tempo juntos mais intencional e significativo?", "ro": "Cum pot face timpul nostru împreună mai intenționat și semnificativ?", "fr": "Comment puis-je rendre notre temps ensemble plus intentionnel et significatif ?", "it": "Come posso fare in modo che il nostro tempo insieme si senta più intenzionale?", "fi": "Miten voin tehdä yhteisestä ajastamme tarkoituksellisempaa?", "nl": "Hoe kan ik onze tijd samen meer doelbewust laten aanvoelen?", "no": "Hvordan kan jeg gjøre tiden vår sammen mer meningsfull?", "sv": "Hur kan jag få vår tid tillsammans att kännas mer avsiktlig?", "pl": "Jak mogę sprawić, aby nasz wspólny czas był bardziej świadomy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you want us to handle distractions when we’re together — phones, work, or people pulling us away?", "da": "Hvordan vil du have, at vi håndterer distraktioner, når vi er sammen – telefoner, arbejde eller folk, der trækker os væk?", "de": "Wie möchtest du, dass wir mit Ablenkungen umgehen, wenn wir zusammen sind – Telefonen, Arbeit oder Menschen, die uns ablenken?", "es": "¿Cómo quieres que manejemos las distracciones cuando estamos juntos — teléfonos, trabajo o gente que nos interrumpe?", "pt": "Como queres que lidemos com distrações quando estamos juntos — telemóveis, trabalho ou pessoas que nos afastam?", "ro": "Cum vrei să gestionăm distragerile când suntem împreună — telefoane, muncă sau oameni care ne întrerup?", "fr": "Comment veux-tu que nous gérions les distractions quand nous sommes ensemble — téléphones, travail ou gens qui nous éloignent ?", "it": "Come vuoi che gestiamo le distrazioni quando siamo insieme — telefoni, lavoro o persone che ci allontanano?", "fi": "Miten haluat, että käsittelemme häiriötekijöitä, kun olemme yhdessä — puhelimet, työ tai ihmiset, jotka vetävät meitä pois?", "nl": "Hoe wil je dat we met afleidingen omgaan als we samen zijn — telefoons, werk of mensen die ons afleiden?", "no": "Hvordan ønsker du at vi skal håndtere distraksjoner når vi er sammen — telefoner, arbeid eller folk som trekker oss bort?", "sv": "Hur vill du att vi ska hantera distraktioner när vi är tillsammans — telefoner, arbete eller människor som drar oss bort?", "pl": "Jak chcesz, żebyśmy radzili sobie z rozproszeniami, gdy jesteśmy razem — telefonami, pracą czy ludźmi, którzy nas odciągają?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you feel when we do chores or errands together — like teamwork or just getting stuff done?", "da": "Hvordan har du det, når vi laver husarbejde eller ærinder sammen – som teamwork eller bare for at få ting gjort?", "de": "Wie fühlst du dich, wenn wir Hausarbeit oder Besorgungen zusammen machen – wie Teamarbeit oder einfach, um Dinge zu erledigen?", "es": "¿Cómo te sientes cuando hacemos tareas o mandados juntos — como trabajo en equipo o solo para hacer cosas?", "pt": "Como te sentes quando fazemos tarefas ou recados juntos — como trabalho de equipa ou só para tratar de coisas?", "ro": "Cum te simți când facem treburi sau comisioane împreună — ca o echipă sau doar pentru a termina treaba?", "fr": "Comment te sens-tu quand nous faisons des tâches ou des courses ensemble — comme une équipe ou juste pour accomplir les choses ?", "it": "Come ti senti quando facciamo faccende o commissioni insieme — come un lavoro di squadra o semplicemente per portare a termine le cose?", "fi": "Miltä sinusta tuntuu, kun teemme kotitöitä tai asioita yhdessä – onko se enemmän tiimityötä vai vain asioiden hoitamista?", "nl": "Hoe voel je je als we samen klusjes of boodschappen doen — meer als teamwork of gewoon dingen afhandelen?", "no": "Hvordan føler du det når vi gjør husarbeid eller ærender sammen — som teamarbeid eller bare for å få ting gjort?", "sv": "Hur känner du när vi gör sysslor eller ärenden tillsammans – som teamwork eller bara för att få saker gjorda?", "pl": "Jak się czujesz, gdy robimy razem obowiązki domowe lub załatwiamy sprawy — jak zespół czy po prostu załatwiając sprawy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you like me to check in with you during the day?", "da": "Hvordan vil du have, jeg tjekker ind hos dig i løbet af dagen?", "de": "Wie möchtest du, dass ich mich im Laufe des Tages bei dir melde?", "es": "¿Cómo te gusta que te contacte durante el día?", "pt": "Como gostas que eu fale contigo ao longo do dia?", "ro": "Cum ai vrea să iau legătura cu tine pe parcursul zilei?", "fr": "Comment aimes-tu que je prenne de tes nouvelles pendant la journée ?", "it": "Come preferisci che ti contatti durante il giorno?", "fi": "Miten haluaisit, että tarkistan kuulumisesi päivän aikana?", "nl": "Hoe wil je dat ik gedurende de dag contact met je opneem?", "no": "Hvordan vil du at jeg skal sjekke inn med deg i løpet av dagen?", "sv": "Hur vill du att jag ska kolla in med dig under dagen?", "pl": "Jak wolisz, żebym się z tobą kontaktował w ciągu dnia?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "If we had a full day just for each other, what would you want to do?", "da": "Hvis vi havde en hel dag kun for hinanden, hvad ville du så gerne lave?", "de": "Wenn wir einen ganzen Tag nur für uns hätten, was würdest du gerne tun?", "es": "Si tuviéramos un día completo solo para nosotros, ¿qué te gustaría hacer?", "pt": "Se tivéssemos um dia inteiro só para nós, o que gostarias de fazer?", "ro": "Dacă am avea o zi întreagă doar pentru noi, ce ai vrea să facem?", "fr": "Si nous avions une journée entière juste pour nous, que voudrais-tu faire ?", "it": "Se avessimo un''intera giornata solo per noi, cosa ti piacerebbe fare?", "fi": "Jos meillä olisi koko päivä vain toisillemme, mitä haluaisit tehdä?", "nl": "Als we een hele dag alleen voor elkaar hadden, wat zou je dan willen doen?", "no": "Hvis vi hadde en hel dag bare for hverandre, hva ville du ønsket å gjøre?", "sv": "Om vi hade en hel dag bara för varandra, vad skulle du vilja göra?", "pl": "Jeśli mielibyśmy cały dzień tylko dla siebie, co chciałbyś robić?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What kind of conversation makes you feel closest to me?", "da": "Hvilken slags samtale får dig til at føle dig tættest på mig?", "de": "Welche Art von Gespräch lässt dich mir am nächsten fühlen?", "es": "¿Qué tipo de conversación te hace sentir más cerca de mí?", "pt": "Que tipo de conversa te faz sentir-te mais próximo(a) de mim?", "ro": "Ce fel de conversație te face să te simți cel mai aproape de mine?", "fr": "Quel type de conversation te fait te sentir le plus proche de moi ?", "it": "Che tipo di conversazione ti fa sentire più vicino a me?", "fi": "Minkälainen keskustelu saa sinut tuntemaan itsesi lähimmäksi minua?", "nl": "Wat voor soort gesprek laat je je het dichtst bij mij voelen?", "no": "Hvilken type samtale får deg til å føle deg nærmest meg?", "sv": "Vilken typ av samtal får dig att känna dig närmast mig?", "pl": "Jaki rodzaj rozmowy sprawia, że czujesz się ze mną najbliżej?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you like to reconnect after we’ve been apart or distracted?", "da": "Hvordan kan du bedst lide at genoprette forbindelsen efter, at vi har været adskilt eller distraherede?", "de": "Wie verbindest du dich am liebsten wieder, nachdem wir getrennt oder abgelenkt waren?", "es": "¿Cómo te gusta reconectar después de que hemos estado separados o distraídos?", "pt": "Como gostas de reconectar depois de termos estado separados ou distraídos?", "ro": "Cum îți place să te reconectezi după ce am fost despărțiți sau distrași?", "fr": "Comment aimes-tu te reconnecter après que nous avons été séparés ou distraits ?", "it": "Come ti piace riconnetterti dopo che siamo stati separati o distratti?", "fi": "Miten tykkäät jälleen yhdistyä, kun olemme olleet eristyksissä tai häiriintyneitä?", "nl": "Hoe vind je het fijn om weer contact te maken nadat we een tijd apart zijn geweest of afgeleid zijn?", "no": "Hvordan liker du å gjennopprette kontakten etter at vi har vært fra hverandre eller distrahert?", "sv": "Hur gillar du att återknyta kontakten efter att vi har varit åtskilda eller distraherade?", "pl": "Jak lubisz się ponownie połączyć po tym, jak byliśmy oddaleni lub rozproszeni?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What do I do that makes you feel like I’m really there with you?", "da": "Hvad gør jeg, der får dig til at føle, at jeg virkelig er til stede sammen med dig?", "de": "Was mache ich, das dich fühlen lässt, dass ich wirklich bei dir bin?", "es": "¿Qué hago que te hace sentir que realmente estoy contigo?", "pt": "O que eu faço que te faz sentir que estou mesmo presente contigo?", "ro": "Ce fac eu care te face să simți că sunt cu adevărat alături de tine?", "fr": "Qu’est-ce que je fais qui te fait sentir que je suis vraiment avec toi ?", "it": "Cosa faccio che ti fa sentire come se fossi davvero lì con te?", "fi": "Mitä teen, mikä saa sinut tuntemaan, että olen todella siellä kanssasi?", "nl": "Wat doe ik waardoor je het gevoel hebt dat ik echt bij je ben?", "no": "Hva gjør jeg som får deg til å føle at jeg virkelig er der med deg?", "sv": "Vad gör jag som får dig att känna att jag verkligen är där med dig?", "pl": "Co robię, co sprawia, że czujesz, że naprawdę jestem z tobą?"}'::jsonb, false, true, true);
