INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What kind of date night feels most like “us”?", "da": "Hvilken slags dateaften føles mest som “os”?", "de": "Welche Art von Date-Abend fühlt sich am meisten wie „wir“ an?", "es": "¿Qué tipo de cita se siente más como “nosotros”?", "pt": "Que tipo de encontro se sente mais como “nós”?", "ro": "Ce fel de întâlnire pare cel mai mult „noi”?", "fr": "Quel genre de soirée en amoureux te semble le plus « nous » ?", "it": "Che tipo di serata romantica ci fa sentire più \"noi\"?", "fi": "Minkälainen treffi-ilta tuntuu eniten \"meiltä\"?", "nl": "Wat voor soort date night voelt het meest als \"wij\"?", "no": "Hva slags date night føles mest som \"oss\"?", "sv": "Vilken typ av dejtkväll känns mest som \"vi\"?", "pl": "Jaki rodzaj randki najbardziej przypomina „nas”?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do I accidentally make you feel like I’m not fully present — and how could I do better?", "da": "Hvordan får jeg dig ved en fejl til at føle, at jeg ikke er helt til stede – og hvordan kunne jeg blive bedre?", "de": "Wie bringe ich dich aus Versehen dazu zu fühlen, dass ich nicht ganz präsent bin – und wie könnte ich es besser machen?", "es": "¿Cómo hago que accidentalmente sientas que no estoy completamente presente, y cómo podría mejorar?", "pt": "Como é que, sem querer, te faço sentir que não estou totalmente presente — e como posso melhorar?", "ro": "Cum te fac, fără să vreau, să simți că nu sunt complet prezent — și cum aș putea fi mai bun(ă)?", "fr": "Comment est-ce que je te fais, sans le vouloir, sentir que je ne suis pas complètement présent(e) — et comment pourrais-je faire mieux ?", "it": "Come faccio a farti sentire che non sono completamente presente — e come potrei fare meglio?", "fi": "Miten onnistun vahingossa saamaan sinut tuntemaan, että en ole täysin läsnä — ja miten voisin parantaa?", "nl": "Hoe maak ik je per ongeluk het gevoel dat ik er niet helemaal ben — en hoe kan ik het beter doen?", "no": "Hvordan får jeg deg til å føle at jeg ikke er helt til stede — og hvordan kan jeg gjøre det bedre?", "sv": "Hur gör jag så att du oavsiktligt känner att jag inte är helt närvarande — och hur kan jag göra bättre?", "pl": "Jak mogę przypadkowo sprawić, że poczujesz, że nie jestem w pełni obecny — i jak mogę to poprawić?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s your favorite memory of us just spending time together?", "da": "Hvad er dit yndlingsminde om os, bare når vi tilbringer tid sammen?", "de": "Was ist deine Lieblings­erinnerung an uns, wenn wir einfach Zeit miteinander verbringen?", "es": "¿Cuál es tu recuerdo favorito de nosotros pasando tiempo juntos?", "pt": "Qual é a tua lembrança favorita de nós a passar tempo juntos?", "ro": "Care este amintirea ta preferată cu noi petrecând timp împreună?", "fr": "Quel est ton souvenir préféré de nous passant du temps ensemble ?", "it": "Qual è il tuo ricordo preferito di noi che passiamo del tempo insieme?", "fi": "Mikä on lempimuistosi siitä, kun vietimme aikaa yhdessä?", "nl": "Wat is je favoriete herinnering aan de tijd die we samen doorbrachten?", "no": "Hva er ditt favorittminne av oss når vi bare tilbringer tid sammen?", "sv": "Vad är ditt favoritminne av oss när vi bara tillbringade tid tillsammans?", "pl": "Jakie jest twoje ulubione wspomnienie z czasów, kiedy po prostu spędzaliśmy razem czas?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "If we had 30 minutes right now, what would you want to do with me?", "da": "Hvis vi havde 30 minutter lige nu, hvad ville du gerne lave med mig?", "de": "Wenn wir jetzt 30 Minuten hätten, was würdest du gerne mit mir machen?", "es": "Si tuviéramos 30 minutos ahora, ¿qué te gustaría hacer conmigo?", "pt": "Se tivéssemos 30 minutos agora, o que gostarias de fazer comigo?", "ro": "Dacă am avea 30 de minute acum, ce ai vrea să facem împreună?", "fr": "Si nous avions 30 minutes maintenant, que voudrais-tu faire avec moi ?", "it": "Se avessimo 30 minuti in questo momento, cosa ti piacerebbe fare con me?", "fi": "Jos meillä olisi nyt 30 minuuttia, mitä haluaisit tehdä kanssani?", "nl": "Als we nu 30 minuten hadden, wat zou je dan met mij willen doen?", "no": "Hvis vi hadde 30 minutter akkurat nå, hva ville du ønsket å gjøre med meg?", "sv": "Om vi hade 30 minuter just nu, vad skulle du vilja göra med mig?", "pl": "Gdybyśmy mieli teraz 30 minut, co chciałbyś ze mną zrobić?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When it comes to chores or responsibilities, what do you naturally take on — and what do you secretly wish I’d help with more?", "da": "Når det gælder pligter eller ansvar, hvad tager du naturligt på dig – og hvad ville du ønske, jeg hjalp mere med?", "de": "Wenn es um Pflichten oder Verantwortung geht, was übernimmst du natürlich – und wobei wünschst du dir, dass ich mehr helfe?", "es": "Cuando se trata de tareas o responsabilidades, ¿qué sueles asumir tú y con qué desearías que te ayudara más?", "pt": "Quando se trata de tarefas ou responsabilidades, o que assumes naturalmente — e com o que gostarias que eu ajudasse mais?", "ro": "Când vine vorba de treburi sau responsabilități, ce preiei tu în mod natural — și cu ce ți-ai dori să te ajut mai mult?", "fr": "Quand il s’agit de tâches ou de responsabilités, que prends-tu naturellement en charge — et avec quoi aimerais-tu que je t’aide davantage ?", "it": "Quando si tratta di faccende o responsabilità, cosa ti prendi naturalmente in carico — e con cosa desideri segretamente che io ti aiuti di più?", "fi": "Kun on kyse kotitöistä tai vastuista, mitä otat luonnostaan hoitaaksesi — ja mihin toivoisit salaa, että auttaisin enemmän?", "nl": "Als het gaat om huishoudelijke taken of verantwoordelijkheden, wat neem je van nature op je — en waar zou je stiekem willen dat ik je meer mee help?", "no": "Når det gjelder husarbeid eller ansvar, hva tar du naturlig på deg — og hva ønsker du hemmelig at jeg skulle hjelpe mer med?", "sv": "När det kommer till sysslor eller ansvar, vad tar du naturligt på dig — och vad önskar du i hemlighet att jag skulle hjälpa till med mer?", "pl": "Kiedy mowa o obowiązkach domowych lub odpowiedzialnościach, co zazwyczaj bierzesz na siebie — a co w tajemnicy chciałbyś, żebym bardziej ci w tym pomógł?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s one thing I could do this week that would make you feel loved?", "da": "Hvad er én ting, jeg kunne gøre denne uge, som ville få dig til at føle dig elsket?", "de": "Was ist eine Sache, die ich diese Woche tun könnte, damit du dich geliebt fühlst?", "es": "¿Qué podría hacer esta semana que te haría sentir amado/a?", "pt": "O que eu poderia fazer esta semana que te fizesse sentir amado(a)?", "ro": "Care e un lucru pe care l-aș putea face săptămâna asta ca să te simți iubit(ă)?", "fr": "Quelle est une chose que je pourrais faire cette semaine pour que tu te sentes aimé(e) ?", "it": "Qual è una cosa che potrei fare questa settimana che ti farebbe sentire amato?", "fi": "Mikä on yksi asia, jonka voisin tehdä tällä viikolla, jotta tuntisit itsesi rakastetuksi?", "nl": "Wat is één ding dat ik deze week kan doen waardoor je je geliefd voelt?", "no": "Hva er én ting jeg kan gjøre denne uken som ville fått deg til å føle deg elsket?", "sv": "Vad är en sak jag skulle kunna göra den här veckan som skulle få dig att känna dig älskad?", "pl": "Coś, co mógłbyś zrobić w tym tygodniu, co sprawiłoby, że poczułbym się kochany?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Did you grow up seeing one person do most of the housework or planning? How do you think that shaped what you expect in us?", "da": "Så du som barn én person tage sig af det meste husarbejde eller planlægning? Hvordan tror du, det har formet dine forventninger til os?", "de": "Bist du aufgewachsen, indem du gesehen hast, dass eine Person den Großteil der Hausarbeit oder Planung gemacht hat? Wie hat das deiner Meinung nach beeinflusst, was du von uns erwartest?", "es": "¿Creciste viendo a una persona hacer la mayor parte de las tareas o la planificación? ¿Cómo crees que eso influyó en lo que esperas de nosotros?", "pt": "Cresceste a ver uma pessoa fazer a maior parte das tarefas domésticas ou do planeamento? Como achas que isso moldou o que esperas de nós?", "ro": "Ai crescut văzând o persoană făcând cea mai mare parte din treburile casei sau planificare? Cum crezi că asta ți-a influențat așteptările față de noi?", "fr": "As-tu grandi en voyant une seule personne faire la plupart des tâches ménagères ou de la planification ? Comment penses-tu que cela a influencé ce que tu attends de nous ?", "it": "Sei cresciuto vedendo una persona fare la maggior parte dei lavori domestici o della pianificazione? Come pensi che questo abbia influenzato le tue aspettative nei nostri confronti?", "fi": "Kasvoitko niin, että yksi henkilö teki suurimman osan kotitöistä tai suunnittelusta? Miten luulet sen muovanneen odotuksiasi meistä?", "nl": "Ben je opgegroeid met het zien van één persoon die het meeste van het huishoudelijke werk of de planning deed? Hoe denk je dat dat heeft gevormd wat je van ons verwacht?", "no": "Vokste du opp med å se én person gjøre mesteparten av husarbeidet eller planleggingen? Hvordan tror du det har formet hva du forventer av oss?", "sv": "Växte du upp med att se en person göra det mesta av hushållsarbetet eller planeringen? Hur tror du att det har format vad du förväntar dig av oss?", "pl": "Czy dorastałeś, widząc, że jedna osoba wykonuje większość prac domowych lub planowania? Jak myślisz, jak to wpłynęło na to, czego oczekujesz od nas?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you like me to show up when you’re overwhelmed — jump in and help, or give you space first?", "da": "Hvordan vil du have, jeg reagerer, når du er overvældet – skal jeg springe ind og hjælpe, eller give dig plads først?", "de": "Wie möchtest du, dass ich reagiere, wenn du überfordert bist – soll ich einspringen und helfen oder dir zuerst Raum geben?", "es": "¿Cómo te gusta que actúe cuando estás abrumado/a — que intervenga y ayude, o que te dé espacio primero?", "pt": "Como queres que eu aja quando estás sobrecarregado(a) — ajudar de imediato ou dar-te espaço primeiro?", "ro": "Cum vrei să reacționez când ești copleșit(ă) — să sar să te ajut sau să-ți dau spațiu mai întâi?", "fr": "Comment veux-tu que j’agisse quand tu es dépassé(e) — que j’intervienne pour aider ou que je te laisse d’abord de l’espace ?", "it": "Come preferisci che io mi comporti quando sei sopraffatto — intervenire e aiutarti, o darti prima dello spazio?", "fi": "Miten haluaisit, että ilmestyn paikalle, kun olet ylivoimainen — hyppään mukaan auttamaan vai annan ensin tilaa?", "nl": "Hoe wil je dat ik reageer als je overweldigd bent — meteen helpen of eerst wat ruimte geven?", "no": "Hvordan vil du at jeg skal være der for deg når du føler deg overveldet — skal jeg hoppe inn og hjelpe, eller gi deg litt plass først?", "sv": "Hur vill du att jag ska agera när du känner dig överväldigad — hoppa in och hjälpa till, eller ge dig utrymme först?", "pl": "Jak wolisz, żebym się zachował, gdy jesteś przytłoczony — wskoczyć i pomóc, czy najpierw dać ci przestrzeń?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What kind of help means the most when you’re stressed?", "da": "Hvilken slags hjælp betyder mest for dig, når du er stresset?", "de": "Welche Art von Hilfe bedeutet dir am meisten, wenn du gestresst bist?", "es": "¿Qué tipo de ayuda significa más para ti cuando estás estresado/a?", "pt": "Que tipo de ajuda significa mais para ti quando estás stressado(a)?", "ro": "Ce fel de ajutor contează cel mai mult pentru tine când ești stresat(ă)?", "fr": "Quel type d’aide compte le plus pour toi quand tu es stressé(e) ?", "it": "Che tipo di aiuto significa di più quando sei stressato?", "fi": "Minkälaista apua arvostat eniten, kun olet stressaantunut?", "nl": "Welke soort hulp betekent het meest voor jou als je gestrest bent?", "no": "Hvilken type hjelp betyr mest når du er stresset?", "sv": "Vilken typ av hjälp betyder mest när du är stressad?", "pl": "Jaki rodzaj pomocy jest dla Ciebie najważniejszy, gdy jesteś zestresowany?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s one “gender role” or old habit we could unlearn together when it comes to helping each other?", "da": "Hvilken “kønsrolle” eller gammel vane kunne vi aflære sammen, når det gælder at hjælpe hinanden?", "de": "Welche „Geschlechterrolle“ oder alte Gewohnheit könnten wir gemeinsam verlernen, wenn es darum geht, uns gegenseitig zu helfen?", "es": "¿Qué “rol de género” o viejo hábito podríamos desaprender juntos cuando se trata de ayudarnos mutuamente?", "pt": "Que “papel de género” ou velho hábito poderíamos desaprender juntos quando se trata de ajudar um ao outro?", "ro": "Ce „rol de gen” sau obicei vechi am putea dezvăța împreună când vine vorba să ne ajutăm unul pe altul?", "fr": "Quel « rôle de genre » ou vieille habitude pourrions-nous désapprendre ensemble quand il s’agit de s’entraider ?", "it": "Qual è un \"ruolo di genere\" o un''abitudine vecchia che potremmo disimparare insieme quando si tratta di aiutarci a vicenda?", "fi": "Mikä on yksi \"sukupuolirooli\" tai vanha tapa, jonka voisimme unohtaa yhdessä auttaessamme toisiamme?", "nl": "Wat is één \"genderrol\" of oude gewoonte die we samen zouden kunnen afleren als het gaat om elkaar helpen?", "no": "Hva er en \"kjønnrolle\" eller gammel vane vi kan avlære sammen når det gjelder å hjelpe hverandre?", "sv": "Vilken \"könsroll\" eller gammal vana skulle vi kunna avlära oss tillsammans när det gäller att hjälpa varandra?", "pl": "Jaką jedną „rolę płci” lub stary nawyk moglibyśmy wspólnie oduczyć się, jeśli chodzi o wzajemną pomoc?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What little thing do I do that makes your life easier?", "da": "Hvilken lille ting gør jeg, der gør dit liv lettere?", "de": "Welche kleine Sache mache ich, die dein Leben leichter macht?", "es": "¿Qué pequeña cosa hago que hace tu vida más fácil?", "pt": "Que pequena coisa eu faço que torna a tua vida mais fácil?", "ro": "Ce mic lucru fac eu care îți face viața mai ușoară?", "fr": "Quelle petite chose est-ce que je fais qui rend ta vie plus facile ?", "it": "Quale piccola cosa faccio che rende la tua vita più facile?", "fi": "Mikä pieni asia, jonka teen, helpottaa elämääsi?", "nl": "Wat is een klein ding dat ik doe dat jouw leven gemakkelijker maakt?", "no": "Hva er det lille jeg gjør som gjør livet ditt lettere?", "sv": "Vilken liten sak gör jag som gör ditt liv enklare?", "pl": "Jaka mała rzecz, którą robię, ułatwia ci życie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you want me to show that I’ve got your back?", "da": "Hvordan vil du have, jeg viser, at jeg støtter dig?", "de": "Wie möchtest du, dass ich zeige, dass ich hinter dir stehe?", "es": "¿Cómo quieres que te demuestre que te apoyo?", "pt": "Como queres que eu mostre que estou do teu lado?", "ro": "Cum vrei să îți arăt că te susțin?", "fr": "Comment veux-tu que je montre que je suis là pour toi ?", "it": "Come vuoi che ti dimostri che ti sostengo?", "fi": "Miten haluat, että näytän, että olen tukenasi?", "nl": "Hoe wil je dat ik laat zien dat ik je steun?", "no": "Hvordan vil du at jeg skal vise at jeg støtter deg?", "sv": "Hur vill du att jag ska visa att jag stöttar dig?", "pl": "Jak chcesz, żebym pokazał, że cię wspieram?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s a small gesture that always feels big to you?", "da": "Hvilken lille gestus føles altid stor for dig?", "de": "Welche kleine Geste fühlt sich für dich immer groß an?", "es": "¿Qué pequeño gesto siempre se siente grande para ti?", "pt": "Que pequeno gesto te parece sempre importante?", "ro": "Ce mic gest ți se pare mereu important?", "fr": "Quel petit geste te semble toujours grand ?", "it": "Qual è un piccolo gesto che per te sembra sempre grande?", "fi": "Mikä on pieni ele, joka tuntuu aina suurelta sinulle?", "nl": "Wat is een klein gebaar dat altijd groot voor je aanvoelt?", "no": "Hva er en liten gest som alltid føles stor for deg?", "sv": "Vilken liten gest känns alltid stor för dig?", "pl": "Jaki mały gest zawsze wydaje ci się wielki?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When you’re exhausted, how do you like me to step in?", "da": "Når du er udmattet, hvordan vil du have, jeg træder til?", "de": "Wenn du erschöpft bist, wie möchtest du, dass ich einspringe?", "es": "Cuando estás agotado/a, ¿cómo te gusta que intervenga?", "pt": "Quando estás exausto(a), como gostas que eu intervenha?", "ro": "Când ești epuizat(ă), cum ai vrea să intervin?", "fr": "Quand tu es épuisé(e), comment veux-tu que j’intervienne ?", "it": "Quando sei esausto, come ti piace che io intervenga?", "fi": "Kun olet uupunut, miten haluaisit minun astuvan mukaan?", "nl": "Wanneer je uitgeput bent, hoe wil je dat ik inspring?", "no": "Når du er utslitt, hvordan liker du at jeg trår til?", "sv": "När du är utmattad, hur vill du att jag ska hjälpa till?", "pl": "Kiedy jesteś wyczerpany, jak chciałbyś, żebym ci pomógł?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What would make you feel more like a team with me?", "da": "Hvad ville få dig til at føle dig mere som et team sammen med mig?", "de": "Was würde dich mehr wie ein Team mit mir fühlen lassen?", "es": "¿Qué te haría sentir más como un equipo conmigo?", "pt": "O que te faria sentir mais como uma equipa comigo?", "ro": "Ce te-ar face să te simți mai mult ca o echipă cu mine?", "fr": "Qu’est-ce qui te ferait sentir davantage comme une équipe avec moi ?", "it": "Cosa ti farebbe sentire più parte di un team con me?", "fi": "Mikä saisi sinut tuntemaan itsesi enemmän tiimiksi kanssani?", "nl": "Wat zou je meer het gevoel geven dat we een team zijn?", "no": "Hva ville fått deg til å føle deg mer som et team med meg?", "sv": "Vad skulle få dig att känna dig mer som ett team med mig?", "pl": "Co sprawiłoby, że poczułbyś się bardziej częścią zespołu ze mną?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you feel when I try to help but don’t do it “your way”?", "da": "Hvordan har du det, når jeg prøver at hjælpe, men ikke gør det “på din måde”?", "de": "Wie fühlst du dich, wenn ich versuche zu helfen, es aber nicht „auf deine Art“ mache?", "es": "¿Cómo te sientes cuando intento ayudar pero no lo hago “a tu manera”?", "pt": "Como te sentes quando eu tento ajudar mas não o faço “à tua maneira”?", "ro": "Cum te simți când încerc să ajut dar nu o fac “în felul tău”?", "fr": "Comment te sens-tu quand j’essaie d’aider mais que je ne le fais pas « à ta façon » ?", "it": "Come ti senti quando cerco di aiutarti ma non lo faccio \"a modo tuo\"?", "fi": "Miltä sinusta tuntuu, kun yritän auttaa, mutta en tee sitä \"sinun tavallasi\"?", "nl": "Hoe voel je je als ik probeer te helpen maar het niet op \"jouw manier\" doe?", "no": "Hvordan føler du det når jeg prøver å hjelpe, men ikke gjør det \"din måte\"?", "sv": "Hur känner du när jag försöker hjälpa till men inte gör det \"på ditt sätt\"?", "pl": "Jak się czujesz, gdy próbuję pomóc, ale nie robię tego „po twojemu”?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s something I do (or could do) that quietly says “I care about you”?", "da": "Hvad gør jeg (eller kunne jeg gøre), der stille siger “jeg holder af dig”?", "de": "Was tue ich (oder könnte ich tun), das leise sagt „Ich kümmere mich um dich“?", "es": "¿Qué hago (o podría hacer) que diga en silencio “me importas”?", "pt": "O que eu faço (ou poderia fazer) que diga silenciosamente “importas para mim”?", "ro": "Ce fac (sau aș putea face) care spune în tăcere „îmi pasă de tine”?", "fr": "Que fais-je (ou pourrais-je faire) qui dit silencieusement « je tiens à toi » ?", "it": "Qualcosa che faccio (o potrei fare) che dice in silenzio \"mi importa di te\"?", "fi": "Mikä on jokin asia, jonka teen (tai voisin tehdä), joka hiljaa sanoo \"välitän sinusta\"?", "nl": "Wat is iets dat ik doe (of zou kunnen doen) dat stilletjes zegt \"ik geef om je\"?", "no": "Hva er noe jeg gjør (eller kan gjøre) som stille sier \"jeg bryr meg om deg\"?", "sv": "Vad är något jag gör (eller skulle kunna göra) som tyst säger \"jag bryr mig om dig\"?", "pl": "Coś, co robię (lub mógłbym zrobić), co cicho mówi „zależy mi na tobie”?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How can I better notice when you need help — without you asking?", "da": "Hvordan kan jeg bedre lægge mærke til, når du har brug for hjælp – uden at du spørger?", "de": "Wie kann ich besser erkennen, wenn du Hilfe brauchst – ohne dass du fragst?", "es": "¿Cómo puedo notar mejor cuándo necesitas ayuda — sin que lo pidas?", "pt": "Como posso perceber melhor quando precisas de ajuda — sem pedires?", "ro": "Cum pot observa mai bine când ai nevoie de ajutor — fără să ceri?", "fr": "Comment puis-je mieux remarquer quand tu as besoin d’aide — sans que tu le demandes ?", "it": "Come posso accorgermi meglio quando hai bisogno di aiuto, senza che tu debba chiedere?", "fi": "Miten voisin paremmin huomata, kun tarvitset apua — ilman että sinun tarvitsee pyytää?", "nl": "Hoe kan ik beter opmerken wanneer je hulp nodig hebt — zonder dat je het vraagt?", "no": "Hvordan kan jeg bedre legge merke til når du trenger hjelp — uten at du ber om det?", "sv": "Hur kan jag bättre märka när du behöver hjälp — utan att du ber om det?", "pl": "Jak mogę lepiej zauważać, kiedy potrzebujesz pomocy — bez pytania?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s something I could do more often that would take weight off your shoulders?", "da": "Hvad kunne jeg gøre oftere, som ville lette noget af vægten fra dine skuldre?", "de": "Was könnte ich öfter tun, um dir etwas Last von den Schultern zu nehmen?", "es": "¿Qué podría hacer más a menudo que te quite peso de los hombros?", "pt": "O que eu poderia fazer mais vezes que te tirasse um peso dos ombros?", "ro": "Ce aș putea face mai des ca să-ți iau o parte din greutate de pe umeri?", "fr": "Que pourrais-je faire plus souvent pour alléger un peu le poids sur tes épaules ?", "it": "Cosa potrei fare più spesso che allevierebbe un po'' il tuo peso?", "fi": "Mikä on jotain, mitä voisin tehdä useammin, mikä keventäisi taakkaasi?", "nl": "Wat is iets dat ik vaker zou kunnen doen om je ontlasten?", "no": "Hva er noe jeg kunne gjort oftere som ville lett byrden din?", "sv": "Vad är något jag skulle kunna göra oftare som skulle lätta på dina axlar?", "pl": "Coś, co mógłbym robić częściej, co zdjęłoby ci ciężar z ramion?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you usually react when someone gives you a gift — excited, shy, awkward, emotional? What do you wish people understood about that?", "da": "Hvordan reagerer du normalt, når nogen giver dig en gave – begejstret, genert, akavet, rørt? Hvad ville du ønske, folk forstod om det?", "de": "Wie reagierst du normalerweise, wenn dir jemand ein Geschenk gibt – aufgeregt, schüchtern, unbeholfen, emotional? Was wünschst du dir, dass die Leute darüber verstehen?", "es": "¿Cómo sueles reaccionar cuando alguien te da un regalo — emocionado/a, tímido/a, incómodo/a, emocionado/a? ¿Qué te gustaría que la gente entendiera sobre eso?", "pt": "Como costumas reagir quando alguém te oferece um presente — entusiasmado(a), tímido(a), atrapalhado(a), emocionado(a)? O que gostarias que as pessoas entendessem sobre isso?", "ro": "Cum reacționezi de obicei când cineva îți oferă un cadou — entuziasmat(ă), timid(ă), stânjenit(ă), emoționat(ă)? Ce ți-ai dori să înțeleagă oamenii despre asta?", "fr": "Comment réagis-tu d’habitude quand quelqu’un t’offre un cadeau — excité(e), timide, maladroit(e), ému(e) ? Qu’aimerais-tu que les gens comprennent à ce sujet ?", "it": "Come reagisci di solito quando qualcuno ti fa un regalo — con entusiasmo, timidezza, imbarazzo, emozione? Cosa vorresti che le persone capissero riguardo a questo?", "fi": "Miten yleensä reagoit, kun joku antaa sinulle lahjan — innokkaasti, ujoisesti, vaivaantuneesti, tunteellisesti? Mitä toivot ihmisten ymmärtävän siitä?", "nl": "Hoe reageer je meestal als iemand je een cadeau geeft — enthousiast, verlegen, ongemakkelijk, emotioneel? Wat zou je willen dat mensen daarover begrijpen?", "no": "Hvordan pleier du å reagere når noen gir deg en gave — spent, sjenert, klønete, følelsesladet? Hva ønsker du at folk forsto om det?", "sv": "Hur brukar du reagera när någon ger dig en present — entusiastisk, blyg, klumpig, känslosam? Vad önskar du att folk förstod om det?", "pl": "Jak zazwyczaj reagujesz, gdy ktoś daje ci prezent — z ekscytacją, nieśmiałością, niezręcznie, emocjonalnie? Co chciałbyś, aby ludzie zrozumieli na ten temat?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What makes a gift feel truly meaningful to you?", "da": "Hvad får en gave til virkelig at føles meningsfuld for dig?", "de": "Was lässt ein Geschenk für dich wirklich bedeutungsvoll fühlen?", "es": "¿Qué hace que un regalo se sienta verdaderamente significativo para ti?", "pt": "O que faz um presente parecer verdadeiramente significativo para ti?", "ro": "Ce face ca un cadou să ți se pară cu adevărat semnificativ?", "fr": "Qu’est-ce qui rend un cadeau vraiment significatif pour toi ?", "it": "Cosa rende un regalo davvero significativo per te?", "fi": "Mikä tekee lahjasta sinulle todella merkityksellisen?", "nl": "Wat maakt een cadeau voor jou echt betekenisvol?", "no": "Hva gjør en gave virkelig meningsfull for deg?", "sv": "Vad gör en gåva verkligen meningsfull för dig?", "pl": "Co sprawia, że prezent wydaje się dla Ciebie naprawdę znaczący?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Do you like surprises, or do you prefer to know something’s coming?", "da": "Kan du lide overraskelser, eller foretrækker du at vide, at noget er på vej?", "de": "Magst du Überraschungen oder weißt du lieber im Voraus, dass etwas kommt?", "es": "¿Te gustan las sorpresas o prefieres saber que algo viene?", "pt": "Gostas de surpresas ou preferes saber que algo vem aí?", "ro": "Îți plac surprizele sau preferi să știi dinainte că urmează ceva?", "fr": "Aimes-tu les surprises ou préfères-tu savoir à l’avance que quelque chose arrive ?", "it": "Ti piacciono le sorprese, o preferisci sapere che sta per arrivare qualcosa?", "fi": "Pidätkö yllätyksistä, vai suositko tietää etukäteen, mitä on tulossa?", "nl": "Hou je van verrassingen, of geef je er de voorkeur aan om te weten dat er iets aankomt?", "no": "Liker du overraskelser, eller foretrekker du å vite at noe kommer?", "sv": "Tycker du om överraskningar, eller föredrar du att veta att något är på väg?", "pl": "Czy lubisz niespodzianki, czy wolisz wiedzieć, że coś nadchodzi?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s the most thoughtful gift I’ve ever given you — and why?", "da": "Hvad er den mest betænksomme gave, jeg nogensinde har givet dig – og hvorfor?", "de": "Was ist das aufmerksamste Geschenk, das ich dir je gemacht habe – und warum?", "es": "¿Cuál es el regalo más considerado que te he dado — y por qué?", "pt": "Qual foi o presente mais atencioso que já te dei — e por quê?", "ro": "Care este cel mai atent cadou pe care ți l-am oferit — și de ce?", "fr": "Quel est le cadeau le plus attentionné que je t’aie jamais offert — et pourquoi ?", "it": "Qual è il regalo più premuroso che ti abbia mai fatto — e perché?", "fi": "Mikä on ajatuksellisin lahja, jonka olen koskaan antanut sinulle — ja miksi?", "nl": "Wat is het meest doordachte cadeau dat ik je ooit heb gegeven — en waarom?", "no": "Hva er den mest gjennomtenkte gaven jeg noen gang har gitt deg — og hvorfor?", "sv": "Vad är den mest omtänksamma presenten jag någonsin har gett dig — och varför?", "pl": "Jaki był najbardziej przemyślany prezent, jaki kiedykolwiek ci dałem — i dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you feel when I surprise you with something small?", "da": "Hvordan har du det, når jeg overrasker dig med noget lille?", "de": "Wie fühlst du dich, wenn ich dich mit etwas Kleinem überrasche?", "es": "¿Cómo te sientes cuando te sorprendo con algo pequeño?", "pt": "Como te sentes quando eu te surpreendo com algo pequeno?", "ro": "Cum te simți când te surprind cu ceva mic?", "fr": "Comment te sens-tu quand je te surprends avec quelque chose de petit ?", "it": "Come ti senti quando ti sorprendo con qualcosa di piccolo?", "fi": "Miltä sinusta tuntuu, kun yllätän sinut jollakin pienellä?", "nl": "Hoe voel je je als ik je verras met iets kleins?", "no": "Hvordan føler du deg når jeg overrasker deg med noe lite?", "sv": "Hur känner du dig när jag överraskar dig med något litet?", "pl": "Jak się czujesz, gdy zaskakuję cię czymś małym?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How important is it to you that a gift feels personal or symbolic?", "da": "Hvor vigtigt er det for dig, at en gave føles personlig eller symbolsk?", "de": "Wie wichtig ist es dir, dass ein Geschenk persönlich oder symbolisch wirkt?", "es": "¿Qué tan importante es para ti que un regalo se sienta personal o simbólico?", "pt": "Quão importante é para ti que um presente pareça pessoal ou simbólico?", "ro": "Cât de important este pentru tine ca un cadou să fie personal sau simbolic?", "fr": "Quelle importance accordes-tu au fait qu’un cadeau soit personnel ou symbolique ?", "it": "Quanto è importante per te che un regalo sembri personale o simbolico?", "fi": "Kuinka tärkeää sinulle on, että lahja tuntuu henkilökohtaiselta tai symboliselta?", "nl": "Hoe belangrijk is het voor jou dat een cadeau persoonlijk of symbolisch aanvoelt?", "no": "Hvor viktig er det for deg at en gave føles personlig eller symbolsk?", "sv": "Hur viktigt är det för dig att en present känns personlig eller symbolisk?", "pl": "Jak ważne jest dla Ciebie, aby prezent był osobisty lub symboliczny?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s a small token from me that would make you smile every time you saw it?", "da": "Hvilken lille ting fra mig ville få dig til at smile hver gang du så den?", "de": "Welches kleine Andenken von mir würde dich jedes Mal zum Lächeln bringen, wenn du es siehst?", "es": "¿Qué pequeño detalle mío te haría sonreír cada vez que lo vieras?", "pt": "Que pequeno gesto meu te faria sorrir sempre que o visses?", "ro": "Ce mic simbol de la mine te-ar face să zâmbești de fiecare dată când l-ai vedea?", "fr": "Quel petit souvenir de moi te ferait sourire chaque fois que tu le verrais ?", "it": "Qual è un piccolo regalo da parte mia che ti farebbe sorridere ogni volta che lo vedessi?", "fi": "Mikä olisi pieni muistoesine minulta, joka saisi sinut hymyilemään joka kerta, kun näkisit sen?", "nl": "Wat is een klein aandenken van mij dat je elke keer zou laten glimlachen als je het zag?", "no": "Hva er en liten gave fra meg som ville fått deg til å smile hver gang du så den?", "sv": "Vad är en liten gåva från mig som skulle få dig att le varje gång du såg den?", "pl": "Jaki mały upominek ode mnie sprawiłby, że za każdym razem, gdy na niego spojrzysz, uśmiechniesz się?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Do you think people misunderstand what it means to have “gifts” as a love language? What does it actually mean to you?", "da": "Tror du, folk misforstår, hvad det betyder at have “gaver” som kærlighedssprog? Hvad betyder det egentlig for dig?", "de": "Denkst du, dass Menschen missverstehen, was es bedeutet, „Geschenke“ als Liebessprache zu haben? Was bedeutet es wirklich für dich?", "es": "¿Crees que la gente malinterpreta lo que significa tener los “regalos” como lenguaje del amor? ¿Qué significa realmente para ti?", "pt": "Achas que as pessoas interpretam mal o que significa ter “presentes” como linguagem do amor? O que isso realmente significa para ti?", "ro": "Crezi că oamenii înțeleg greșit ce înseamnă să ai „cadouri” ca limbaj al iubirii? Ce înseamnă asta cu adevărat pentru tine?", "fr": "Penses-tu que les gens comprennent mal ce que cela signifie d’avoir les « cadeaux » comme langage de l’amour ? Qu’est-ce que cela signifie vraiment pour toi ?", "it": "Pensi che le persone fraintendano cosa significhi avere i \"regali\" come linguaggio dell''amore? Cosa significa realmente per te?", "fi": "Mietitkö, että ihmiset ymmärtävät väärin, mitä tarkoittaa, että \"lahjat\" ovat rakkauden kieli? Mitä se oikeastaan tarkoittaa sinulle?", "nl": "Denk je dat mensen verkeerd begrijpen wat het betekent om \"gaven\" als liefdestaal te hebben? Wat betekent het eigenlijk voor jou?", "no": "Tror du folk misforstår hva det betyr å ha \"gaver\" som kjærlighetsspråk? Hva betyr det egentlig for deg?", "sv": "Tycker du att folk missförstår vad det innebär att ha \"gåvor\" som kärleksspråk? Vad betyder det egentligen för dig?", "pl": "Czy uważasz, że ludzie źle rozumieją, co oznacza posiadanie „darów” jako języka miłości? Co to dla Ciebie właściwie oznacza?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What makes a gift feel shallow to you — and what makes it feel thoughtful and genuine?", "da": "Hvad får en gave til at føles overfladisk for dig – og hvad får den til at føles betænksom og ægte?", "de": "Was lässt ein Geschenk für dich oberflächlich wirken – und was lässt es durchdacht und echt erscheinen?", "es": "¿Qué hace que un regalo se sienta superficial para ti — y qué lo hace considerado y genuino?", "pt": "O que faz um presente parecer superficial para ti — e o que o torna atencioso e genuíno?", "ro": "Ce face ca un cadou să pară superficial pentru tine — și ce îl face atent și sincer?", "fr": "Qu’est-ce qui fait qu’un cadeau te semble superficiel — et qu’est-ce qui le rend attentionné et sincère ?", "it": "Cosa rende un regalo superficiale per te — e cosa lo rende invece pensato e genuino?", "fi": "Mikä tekee lahjasta mielestäsi pinnallisen — ja mikä tekee siitä ajatuksellisen ja aito?", "nl": "Wat maakt een cadeau voor jou oppervlakkig — en wat maakt het doordacht en oprecht?", "no": "Hva får en gave til å føles overfladisk for deg — og hva gjør at den føles gjennomtenkt og ekte?", "sv": "Vad får en gåva att kännas ytlig för dig — och vad får den att kännas omtänksam och äkta?", "pl": "Co sprawia, że prezent wydaje się płytki, a co sprawia, że jest przemyślany i szczery?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Do you prefer gifts that are practical or sentimental?", "da": "Foretrækker du gaver, der er praktiske eller sentimentale?", "de": "Bevorzugst du Geschenke, die praktisch oder sentimental sind?", "es": "¿Prefieres regalos que sean prácticos o sentimentales?", "pt": "Preferes presentes práticos ou sentimentais?", "ro": "Preferi cadouri practice sau sentimentale?", "fr": "Préfères-tu les cadeaux pratiques ou sentimentaux ?", "it": "Preferisci regali che siano pratici o sentimentali?", "fi": "Pidätkö enemmän käytännöllisistä vai tunteellisista lahjoista?", "nl": "Geef je de voorkeur aan praktische of sentimentele cadeaus?", "no": "Foretrekker du gaver som er praktiske eller sentimentale?", "sv": "Föredrar du presenter som är praktiska eller sentimentala?", "pl": "Czy wolisz prezenty praktyczne czy sentymentalne?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you feel when I remember small details about what you like?", "da": "Hvordan har du det, når jeg husker små detaljer om, hvad du kan lide?", "de": "Wie fühlst du dich, wenn ich mich an kleine Details erinnere, die du magst?", "es": "¿Cómo te sientes cuando recuerdo pequeños detalles sobre lo que te gusta?", "pt": "Como te sentes quando eu me lembro de pequenos detalhes sobre o que gostas?", "ro": "Cum te simți când îmi amintesc mici detalii despre ce îți place?", "fr": "Que ressens-tu quand je me souviens des petits détails de ce que tu aimes ?", "it": "Come ti senti quando ricordo piccoli dettagli su ciò che ti piace?", "fi": "Miltä sinusta tuntuu, kun muistan pieniä yksityiskohtia siitä, mistä pidät?", "nl": "Hoe voel je je als ik kleine details herinner over wat je leuk vindt?", "no": "Hvordan føler du deg når jeg husker små detaljer om hva du liker?", "sv": "Hur känner du dig när jag kommer ihåg små detaljer om vad du gillar?", "pl": "Jak się czujesz, gdy pamiętam o małych szczegółach dotyczących tego, co lubisz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s one thing I could give you that has no price tag but huge meaning?", "da": "Hvad er én ting, jeg kunne give dig, som ikke har en prisseddel, men enorm betydning?", "de": "Was ist eine Sache, die ich dir geben könnte, die keinen Preis hat, aber große Bedeutung?", "es": "¿Qué podría darte que no tenga precio pero signifique mucho?", "pt": "O que eu poderia dar-te que não tivesse preço mas tivesse um enorme significado?", "ro": "Ce ți-aș putea oferi care nu are preț, dar are o mare însemnătate?", "fr": "Quelle chose pourrais-je t’offrir qui n’a pas de prix mais une grande signification ?", "it": "Qual è una cosa che potrei darti che non ha un prezzo ma ha un grande significato?", "fi": "Mikä on yksi asia, jonka voisin antaa sinulle, jolla ei ole hintalappua mutta valtava merkitys?", "nl": "Wat is één ding dat ik je zou kunnen geven dat geen prijs heeft, maar enorme betekenis heeft?", "no": "Hva er én ting jeg kunne gi deg som ikke har prislapp, men stor betydning?", "sv": "Vad är en sak jag skulle kunna ge dig som inte har något prislapp men stor betydelse?", "pl": "Coś, co mógłbym ci dać, co nie ma ceny, ale ma ogromne znaczenie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s something physical that instantly reminds you of our love?", "da": "Hvad er noget fysisk, der straks minder dig om vores kærlighed?", "de": "Was ist etwas Physisches, das dich sofort an unsere Liebe erinnert?", "es": "¿Qué objeto físico te recuerda instantáneamente a nuestro amor?", "pt": "Que objeto físico te lembra instantaneamente do nosso amor?", "ro": "Ce obiect fizic îți amintește imediat de dragostea noastră?", "fr": "Quel objet physique te rappelle instantanément notre amour ?", "it": "Qualcosa di fisico che ti ricorda immediatamente il nostro amore?", "fi": "Mikä on jokin fyysinen asia, joka heti tuo mieleesi rakkautemme?", "nl": "Wat is iets fysieks dat je meteen aan onze liefde doet denken?", "no": "Hva er noe fysisk som umiddelbart minner deg om vår kjærlighet?", "sv": "Vad är något fysiskt som genast påminner dig om vår kärlek?", "pl": "Coś fizycznego, co od razu przypomina ci o naszej miłości?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Do you think gifts should always be a surprise, or is it okay to say what you want and make it easy for each other?", "da": "Tror du, at gaver altid skal være en overraskelse, eller er det okay at sige, hvad man ønsker, og gøre det nemt for hinanden?", "de": "Denkst du, Geschenke sollten immer eine Überraschung sein, oder ist es in Ordnung zu sagen, was man will, um es einander leichter zu machen?", "es": "¿Crees que los regalos siempre deben ser una sorpresa, o está bien decir lo que uno quiere y hacerlo fácil para ambos?", "pt": "Achas que os presentes devem ser sempre uma surpresa, ou é aceitável dizer o que se quer e facilitar um ao outro?", "ro": "Crezi că darurile ar trebui să fie mereu o surpriză, sau e în regulă să spui ce îți dorești și să fie mai simplu pentru amândoi?", "fr": "Penses-tu que les cadeaux devraient toujours être une surprise, ou est-il acceptable de dire ce que l’on veut pour se faciliter les choses ?", "it": "Pensi che i regali debbano sempre essere una sorpresa, o va bene dire cosa si desidera e facilitare le cose a vicenda?", "fi": "Mietitkö, että lahjojen pitäisi aina olla yllätyksiä, vai onko ihan ok sanoa, mitä haluaa ja helpottaa toisten elämää?", "nl": "Denk je dat cadeaus altijd een verrassing moeten zijn, of is het oké om te zeggen wat je wilt en het elkaar gemakkelijk te maken?", "no": "Tror du gaver alltid bør være en overraskelse, eller er det greit å si hva man ønsker og gjøre det enklere for hverandre?", "sv": "Tycker du att presenter alltid ska vara en överraskning, eller är det okej att säga vad man vill ha och göra det lättare för varandra?", "pl": "Czy uważasz, że prezenty powinny zawsze być niespodzianką, czy może warto powiedzieć, czego się chce, aby ułatwić sobie nawzajem?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When’s the last time you felt surprised — in a good way — by me?", "da": "Hvornår var sidste gang, du blev overrasket – på en god måde – af mig?", "de": "Wann war das letzte Mal, dass du – auf gute Weise – von mir überrascht wurdest?", "es": "¿Cuándo fue la última vez que te sorprendí — de una buena manera?", "pt": "Quando foi a última vez que te surpreendi — de uma forma boa?", "ro": "Când a fost ultima dată când te-am surprins — într-un mod plăcut?", "fr": "Quand as-tu été surpris(e) pour la dernière fois — d’une bonne manière — par moi ?", "it": "Quando è stata l''ultima volta che ti sei sentito sorpreso — in modo positivo — da me?", "fi": "Milloin viimeksi tunsit itsesi yllätetyksi — hyvällä tavalla — minun ansiostani?", "nl": "Wanneer was de laatste keer dat je op een goede manier door mij verrast was?", "no": "Når var siste gang du følte deg overrasket — på en god måte — av meg?", "sv": "När var senaste gången du kände dig överraskad — på ett bra sätt — av mig?", "pl": "Kiedy ostatni raz poczułeś się zaskoczony — w pozytywny sposób — przeze mnie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Do you see touch more as a way to show love, to feel loved, or both?", "da": "Ser du berøring mere som en måde at vise kærlighed på, at føle sig elsket – eller begge dele?", "de": "Siehst du Berührung eher als eine Art, Liebe zu zeigen, Liebe zu fühlen – oder beides?", "es": "¿Ves el contacto físico más como una forma de mostrar amor, de sentirte amado/a, o ambas cosas?", "pt": "Vês o toque mais como uma forma de mostrar amor, de te sentires amado(a), ou ambos?", "ro": "Vezi atingerea mai mult ca o modalitate de a arăta iubire, de a te simți iubit(ă) sau ambele?", "fr": "Vois-tu le contact physique plutôt comme une manière de montrer l’amour, de te sentir aimé(e), ou les deux ?", "it": "Vedi il contatto fisico più come un modo per mostrare amore, per sentirsi amati, o entrambi?", "fi": "Näetkö kosketuksen enemmän tapana osoittaa rakkautta, tuntea itsesi rakastetuksi vai molempina?", "nl": "Zie je aanraking meer als een manier om liefde te tonen, om je geliefd te voelen, of beide?", "no": "Ser du berøring mer som en måte å vise kjærlighet på, å føle seg elsket, eller begge deler?", "sv": "Ser du beröring mer som ett sätt att visa kärlek, att känna sig älskad, eller båda?", "pl": "Czy postrzegasz dotyk bardziej jako sposób na okazanie miłości, poczucie się kochanym, czy może jedno i drugie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How important is physical closeness to you in everyday life?", "da": "Hvor vigtigt er fysisk nærhed for dig i hverdagen?", "de": "Wie wichtig ist körperliche Nähe für dich im Alltag?", "es": "¿Qué tan importante es la cercanía física para ti en la vida cotidiana?", "pt": "Quão importante é a proximidade física para ti no dia a dia?", "ro": "Cât de importantă este apropierea fizică pentru tine în viața de zi cu zi?", "fr": "Quelle importance a la proximité physique pour toi au quotidien ?", "it": "Quanto è importante per te la vicinanza fisica nella vita quotidiana?", "fi": "Kuinka tärkeää fyysinen läheisyys on sinulle arjessa?", "nl": "Hoe belangrijk is fysieke nabijheid voor jou in het dagelijks leven?", "no": "Hvor viktig er fysisk nærhet for deg i hverdagen?", "sv": "Hur viktigt är fysisk närhet för dig i vardagen?", "pl": "Jak ważna jest dla Ciebie bliskość fizyczna w codziennym życiu?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Did you grow up in a family that showed affection easily, or was touch more private or rare? How do you think that shaped you?", "da": "Voksede du op i en familie, der viste kærlighed let, eller var berøring mere privat eller sjælden? Hvordan tror du, det har formet dig?", "de": "Bist du in einer Familie aufgewachsen, die Zuneigung leicht zeigte, oder war Berührung privater oder seltener? Wie glaubst du, hat dich das geprägt?", "es": "¿Creciste en una familia que mostraba afecto fácilmente, o donde el contacto era más privado o raro? ¿Cómo crees que eso te formó?", "pt": "Cresceste numa família que mostrava afeto facilmente, ou onde o toque era mais privado ou raro? Como achas que isso te moldou?", "ro": "Ai crescut într-o familie care își arăta ușor afecțiunea, sau atingerea era mai privată ori rară? Cum crezi că te-a influențat asta?", "fr": "As-tu grandi dans une famille qui montrait facilement de l’affection, ou où le contact était plus privé ou rare ? Comment penses-tu que cela t’a façonné(e) ?", "it": "Sei cresciuto in una famiglia che mostrava affetto facilmente, o il contatto fisico era più privato o raro? Come pensi che questo ti abbia influenzato?", "fi": "Kasvoitko perheessä, jossa osoitettiin hellyyttä helposti, vai oliko kosketus enemmän yksityistä tai harvinaista? Miten luulet sen muovanneen sinua?", "nl": "Ben je opgegroeid in een gezin dat gemakkelijk genegenheid toonde, of was aanraking meer privé of zeldzaam? Hoe denk je dat dat jou gevormd heeft?", "no": "Vokste du opp i en familie som viste kjærlighet lett, eller var berøring mer privat eller sjelden? Hvordan tror du det har formet deg?", "sv": "Växte du upp i en familj som visade kärlek lätt, eller var beröring mer privat eller sällsynt? Hur tror du att det formade dig?", "pl": "Czy dorastałeś w rodzinie, która łatwo okazywała uczucia, czy może dotyk był bardziej prywatny lub rzadki? Jak myślisz, jak to wpłynęło na Ciebie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you like to be comforted physically when you’re upset?", "da": "Hvordan kan jeg bedst trøste dig fysisk, når du er ked af det?", "de": "Wie kann ich dich körperlich am besten trösten, wenn du traurig bist?", "es": "¿Cómo te gusta que te consuele físicamente cuando estás triste?", "pt": "Como posso confortar-te fisicamente quando estás triste?", "ro": "Cum pot să te alin fizic când ești supărat(ă)?", "fr": "Comment puis-je te réconforter physiquement quand tu es triste ?", "it": "Come ti piace essere confortato fisicamente quando sei giù di morale?", "fi": "Miten tykkäät, että sinua lohdutetaan fyysisesti, kun olet surullinen?", "nl": "Hoe word je het liefst fysiek getroost als je van streek bent?", "no": "Hvordan liker du å bli trøstet fysisk når du er opprørt?", "sv": "Hur gillar du att bli tröstad fysiskt när du är upprörd?", "pl": "Jak lubisz być pocieszany fizycznie, gdy jesteś zdenerwowany?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s your favorite way for me to hold or hug you?", "da": "Hvad er din yndlingsmåde for mig at holde eller kramme dig på?", "de": "Was ist deine Lieblingsart, wie ich dich halte oder umarme?", "es": "¿Cuál es tu forma favorita de que te abrace o te sostenga?", "pt": "Qual é a tua maneira favorita de eu te abraçar ou segurar?", "ro": "Care este modul tău preferat de a te ține sau îmbrățișa?", "fr": "Quelle est ta manière préférée pour que je te tienne ou t’embrasse ?", "it": "Qual è il tuo modo preferito di abbracciarmi o tenermi?", "fi": "Mikä on suosikkitapasi, miten haluaisit minun pitävän tai halivan sinua?", "nl": "Wat is jouw favoriete manier waarop ik je kan vasthouden of omarmen?", "no": "Hva er din favorittmåte for meg å holde eller klemme deg på?", "sv": "Vad är ditt favorit sätt för mig att hålla om dig eller krama dig?", "pl": "Jaki jest twój ulubiony sposób, w jaki mogę cię przytulić lub objąć?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you like me to show affection during the day — quick touches, hugs, kisses?", "da": "Hvordan vil du have, jeg viser hengivenhed i løbet af dagen – hurtige berøringer, kram, kys?", "de": "Wie möchtest du, dass ich tagsüber Zuneigung zeige – kurze Berührungen, Umarmungen, Küsse?", "es": "¿Cómo te gusta que te muestre cariño durante el día — toques rápidos, abrazos, besos?", "pt": "Como queres que eu mostre carinho durante o dia — toques rápidos, abraços, beijos?", "ro": "Cum vrei să îți arăt afecțiunea pe parcursul zilei — atingeri scurte, îmbrățișări, săruturi?", "fr": "Comment veux-tu que je montre de l’affection pendant la journée — gestes rapides, câlins, baisers ?", "it": "Come ti piace che mostri affetto durante il giorno — tocchi veloci, abbracci, baci?", "fi": "Miten haluaisit, että osoitan rakkautta päivän aikana — nopeilla kosketuksilla, haleilla, suukkoilla?", "nl": "Hoe vind je het fijn dat ik genegenheid toon gedurende de dag — snelle aanrakingen, knuffels, kussen?", "no": "Hvordan liker du at jeg viser kjærlighet i løpet av dagen — raske berøringer, klemmer, kyss?", "sv": "Hur vill du att jag ska visa tillgivenhet under dagen — snabba beröringar, kramar, kyssar?", "pl": "Jak lubisz, żebym okazywał uczucia w ciągu dnia — szybkie dotknięcia, przytulenia, pocałunki?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What kind of physical affection helps you feel secure?", "da": "Hvilken form for fysisk hengivenhed får dig til at føle dig tryg?", "de": "Welche Art von körperlicher Zuneigung lässt dich sicher fühlen?", "es": "¿Qué tipo de afecto físico te hace sentir seguro/a?", "pt": "Que tipo de afeto físico te faz sentir seguro(a)?", "ro": "Ce fel de afecțiune fizică te face să te simți în siguranță?", "fr": "Quel type d’affection physique te fait te sentir en sécurité ?", "it": "Quale tipo di affetto fisico ti fa sentire sicuro?", "fi": "Minkälaista fyysistä läheisyyttä tarvitset tunteaksesi itsesi turvalliseksi?", "nl": "Welke vorm van fysieke genegenheid helpt je je veilig te voelen?", "no": "Hvilken type fysisk nærhet får deg til å føle deg trygg?", "sv": "Vilken typ av fysisk närhet får dig att känna dig trygg?", "pl": "Jakiego rodzaju fizyczna bliskość sprawia, że czujesz się bezpiecznie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "When do you crave closeness with me the most?", "da": "Hvornår længes du mest efter at være tæt på mig?", "de": "Wann sehnst du dich am meisten danach, mir nahe zu sein?", "es": "¿Cuándo anhelas más estar cerca de mí?", "pt": "Quando é que mais desejas estar perto de mim?", "ro": "Când îți dorești cel mai mult să fii aproape de mine?", "fr": "Quand désires-tu le plus être proche de moi ?", "it": "Quando hai più voglia di vicinanza con me?", "fi": "Milloin kaipaat läheisyyttä kanssani eniten?", "nl": "Wanneer heb je het meest behoefte aan nabijheid met mij?", "no": "Når lengter du mest etter nærhet med meg?", "sv": "När längtar du mest efter närhet med mig?", "pl": "Kiedy najbardziej pragniesz bliskości ze mną?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you like me to initiate affection?", "da": "Hvordan vil du have, jeg tager initiativ til hengivenhed?", "de": "Wie möchtest du, dass ich Zuneigung initiiere?", "es": "¿Cómo te gusta que inicie el afecto contigo?", "pt": "Como gostas que eu inicie o carinho?", "ro": "Cum îți place să inițiez afecțiunea?", "fr": "Comment veux-tu que je prenne l’initiative de l’affection ?", "it": "Come ti piace che io inizi a mostrare affetto?", "fi": "Miten haluaisit, että aloitan hellyyden?", "nl": "Hoe wil je dat ik genegenheid initieer?", "no": "Hvordan liker du at jeg viser omtanke?", "sv": "Hur vill du att jag ska visa min tillgivenhet?", "pl": "Jak lubisz, żebym okazywał uczucia?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Do you think people misunderstand this love language — assuming it’s only about sex, when it’s really about comfort and connection?", "da": "Tror du, folk misforstår dette kærlighedssprog – og tror, det kun handler om sex, når det i virkeligheden handler om trøst og forbindelse?", "de": "Denkst du, dass Menschen diese Liebessprache missverstehen – und denken, sie ginge nur um Sex, obwohl es in Wahrheit um Trost und Verbindung geht?", "es": "¿Crees que la gente malinterpreta este lenguaje del amor — pensando que se trata solo de sexo, cuando realmente es de consuelo y conexión?", "pt": "Achas que as pessoas interpretam mal esta linguagem do amor — achando que é só sobre sexo, quando na verdade é sobre conforto e ligação?", "ro": "Crezi că oamenii înțeleg greșit acest limbaj al iubirii — crezând că e doar despre sex, când de fapt e despre confort și conexiune?", "fr": "Penses-tu que les gens comprennent mal ce langage de l’amour — pensant qu’il s’agit seulement de sexe alors qu’il s’agit de réconfort et de connexion ?", "it": "Pensi che le persone fraintendano questo linguaggio dell''amore, assumendo che riguardi solo il sesso, quando in realtà si tratta di conforto e connessione?", "fi": "Luuleeko, että ihmiset ymmärtävät tämän rakkauden kielen väärin — olettaen, että se on vain seksiä, kun se oikeastaan liittyy mukavuuteen ja yhteyteen?", "nl": "Denk je dat mensen deze liefdestaal verkeerd begrijpen — ervan uitgaande dat het alleen om seks gaat, terwijl het eigenlijk om comfort en verbinding draait?", "no": "Tror du folk misforstår dette kjærlighetsspråket — og antar at det kun handler om sex, når det egentlig handler om komfort og forbindelse?", "sv": "Tror du att folk missförstår detta kärleksspråk — och antar att det bara handlar om sex, när det egentligen handlar om komfort och koppling?", "pl": "Czy myślisz, że ludzie źle rozumieją ten język miłości — zakładając, że chodzi tylko o seks, podczas gdy tak naprawdę chodzi o komfort i połączenie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How do you feel about affection in public?", "da": "Hvad synes du om hengivenhed i det offentlige rum?", "de": "Wie fühlst du dich bei Zuneigung in der Öffentlichkeit?", "es": "¿Qué piensas del afecto en público?", "pt": "O que achas de mostrar carinho em público?", "ro": "Ce părere ai despre afecțiunea în public?", "fr": "Que penses-tu de l’affection en public ?", "it": "Cosa ne pensi dell''affetto in pubblico?", "fi": "Miten suhtaudut julkiseen hellyyteen?", "nl": "Hoe voel je je over genegenheid in het openbaar?", "no": "Hvordan føler du om kjærlighet i offentligheten?", "sv": "Hur känner du för ömhet på offentliga platser?", "pl": "Jak się czujesz w kwestii okazywania uczuć publicznie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "Do you ever feel pressure to be affectionate a certain way — like to hug, cuddle, or initiate — even when you’re not in the mood?", "da": "Føler du nogensinde pres for at vise hengivenhed på en bestemt måde – som at kramme, nusse eller tage initiativ – selv når du ikke er i humør?", "de": "Fühlst du dich jemals unter Druck, auf eine bestimmte Weise Zuneigung zu zeigen – zu umarmen, zu kuscheln oder den ersten Schritt zu machen – auch wenn du nicht in Stimmung bist?", "es": "¿Alguna vez sientes presión para mostrar afecto de cierta manera — abrazar, acurrucarte o iniciar — incluso cuando no tienes ganas?", "pt": "Alguma vez sentes pressão para mostrar afeto de certa forma — abraçar, aconchegar ou iniciar — mesmo quando não estás com vontade?", "ro": "Simți vreodată presiune să arăți afecțiune într-un anumit fel — să îmbrățișezi, să te alint sau să inițiezi — chiar și când nu ai chef?", "fr": "Ressens-tu parfois une pression pour montrer ton affection d’une certaine façon — étreindre, câliner ou initier — même quand tu n’en as pas envie ?", "it": "Ti capita mai di sentirti sotto pressione per essere affettuoso in un certo modo — come abbracciare, coccolare o iniziare — anche quando non sei dell''umore giusto?", "fi": "Tunnetko koskaan painetta olla affektiivinen tietyllä tavalla — kuten halata, kietoutua tai aloittaa — vaikka et olisi siihen mielentilassa?", "nl": "Voel je ooit druk om op een bepaalde manier affectie te tonen — zoals knuffelen, omhelzen of het initiatief nemen — zelfs als je daar niet in de stemming voor bent?", "no": "Føler du noen gang press for å være kjærlig på en bestemt måte — som å klemme, kose eller ta initiativ — selv når du ikke er i humør?", "sv": "Känner du någonsin press att vara kärleksfull på ett visst sätt — som att kramas, mysa eller ta initiativ — även när du inte är på humör?", "pl": "Czy kiedykolwiek czujesz presję, aby okazywać uczucia w określony sposób — na przykład przytulać, tulić się lub inicjować — nawet gdy nie masz na to ochoty?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "How can we both ask for touch — or space — without anyone feeling rejected or guilty?", "da": "Hvordan kan vi begge bede om berøring – eller plads – uden at nogen føler sig afvist eller skyldig?", "de": "Wie können wir beide um Berührung – oder Raum – bitten, ohne dass sich jemand abgelehnt oder schuldig fühlt?", "es": "¿Cómo podemos ambos pedir contacto — o espacio — sin que nadie se sienta rechazado o culpable?", "pt": "Como podemos ambos pedir toque — ou espaço — sem que ninguém se sinta rejeitado ou culpado?", "ro": "Cum putem cere amândoi atingere — sau spațiu — fără ca cineva să se simtă respins sau vinovat?", "fr": "Comment pouvons-nous demander tous deux du contact — ou de l’espace — sans que personne ne se sente rejeté ou coupable ?", "it": "Come possiamo entrambi chiedere contatto — o spazio — senza che nessuno si senta rifiutato o in colpa?", "fi": "Miten voimme molemmat pyytää kosketusta — tai tilaa — ilman, että kukaan tuntee itseään hylätyksi tai syylliseksi?", "nl": "Hoe kunnen we allebei om aanraking — of ruimte — vragen zonder dat iemand zich afgewezen of schuldig voelt?", "no": "Hvordan kan vi begge be om berøring — eller plass — uten at noen føler seg avvist eller skyldig?", "sv": "Hur kan vi båda be om beröring — eller utrymme — utan att någon känner sig avvisad eller skyldig?", "pl": "Jak możemy oboje prosić o dotyk — lub przestrzeń — tak, aby nikt nie czuł się odrzucony lub winny?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Languages Remix Talks', '{"en": "What’s one small gesture I could do more often to say “I love you” without words?", "da": "Hvilken lille gestus kunne jeg gøre oftere for at sige “jeg elsker dig” uden ord?", "de": "Welche kleine Geste könnte ich öfter tun, um „Ich liebe dich“ ohne Worte zu sagen?", "es": "¿Qué pequeño gesto podría hacer más a menudo para decir “te amo” sin palabras?", "pt": "Que pequeno gesto eu poderia fazer mais vezes para dizer “amo-te” sem palavras?", "ro": "Ce mic gest aș putea face mai des pentru a spune “te iubesc” fără cuvinte?", "fr": "Quel petit geste pourrais-je faire plus souvent pour dire « je t’aime » sans mots ?", "it": "Qual è un piccolo gesto che potrei fare più spesso per dire \"ti amo\" senza parole?", "fi": "Mikä on yksi pieni ele, jota voisin tehdä useammin sanoakseni \"rakastan sinua\" ilman sanoja?", "nl": "Wat is één klein gebaar dat ik vaker zou kunnen doen om \"Ik hou van je\" zonder woorden te zeggen?", "no": "Hva er en liten gest jeg kunne gjort oftere for å si \"jeg elsker deg\" uten ord?", "sv": "Vilken liten gest skulle jag kunna göra oftare för att säga \"jag älskar dig\" utan ord?", "pl": "Jaki mały gest mógłbym robić częściej, aby powiedzieć „kocham cię” bez słów?"}'::jsonb, false, true, true);
