INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Unpopular Opinions XL ', '{"en": "“Tech companies should have a ‘karma score’ for how good or bad they are for humanity.”", "da": "Teknologivirksomheder burde have en “karma-score” for, hvor gode eller dårlige de er for menneskeheden.", "de": "Tech-Unternehmen sollten eine „Karma-Bewertung“ haben, wie gut oder schlecht sie für die Menschheit sind.", "es": "Las empresas tecnológicas deberían tener una “puntuación kármica” según lo buenas o malas que sean para la humanidad.", "pt": "As empresas de tecnologia deveriam ter uma “pontuação de karma” pelo bem ou mal que fazem à humanidade.", "ro": "Companiile tehnologice ar trebui să aibă un „scor karmic” pentru cât de bune sau rele sunt pentru omenire.", "fr": "Les entreprises technologiques devraient avoir un « score de karma » selon leur impact sur l’humanité.", "it": "\"Le aziende tecnologiche dovrebbero avere un ''punteggio karma'' per quanto siano buone o cattive per l''umanità.\"", "fi": "\"Teknologiayrityksillä pitäisi olla ''karma-pisteet'' siitä, kuinka hyviä tai huonoja ne ovat ihmiskunnalle.\"", "nl": "\"Technologiebedrijven zouden een ''karma-score'' moeten hebben voor hoe goed of slecht ze zijn voor de mensheid.\"", "no": "\"Teknologiselskaper bør ha en ''karma-score'' for hvor gode eller dårlige de er for menneskeheten.\"", "sv": "\"Teknikföretag borde ha en ''karma-poäng'' för hur bra eller dåliga de är för mänskligheten.\"", "pl": "„Firmy technologiczne powinny mieć ''wynik karmy'' oceniający, jak dobre lub złe są dla ludzkości.”"}'::jsonb, false, true, true);
