INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You’re in love, but your friends hate your partner. Do you listen to them or follow your heart?", "da": "Du er forelsket, men dine venner hader din partner. Lytter du til dem eller følger dit hjerte?", "de": "Du bist verliebt, aber deine Freunde hassen deinen Partner. Hörst du auf sie oder folgst du deinem Herzen?", "es": "Estás enamorado/a, pero tus amigos odian a tu pareja. ¿Les haces caso o sigues tu corazón?", "pt": "Estás apaixonado(a), mas os teus amigos odeiam o teu parceiro. Ouves-os ou segues o teu coração?", "ro": "Ești îndrăgostit(ă), dar prietenii tăi îți urăsc partenerul. Îi asculți sau îți urmezi inima?", "fr": "Tu es amoureux(se), mais tes amis détestent ton partenaire. Tu les écoutes ou tu suis ton cœur ?", "it": "Sei innamorato, ma i tuoi amici odiano il tuo partner. Ascolti loro o segui il tuo cuore?", "fi": "Olet rakastunut, mutta ystäväsi vihaavat kumppaniasi. Kuunteletko heitä vai seuraatko sydäntäsi?", "nl": "Je bent verliefd, maar je vrienden haten je partner. Luister je naar hen of volg je je hart?", "no": "Du er forelsket, men vennene dine hater partneren din. Lytter du til dem eller følger du hjertet ditt?", "sv": "Du är kär, men dina vänner hatar din partner. Lyssnar du på dem eller följer du ditt hjärta?", "pl": "Jesteś zakochany, ale twoi przyjaciele nienawidzą twojego partnera. Słuchasz ich, czy podążasz za swoim sercem?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
