INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You win a free vacation — but you can only bring one friend from this room. Who’s your pick and why?", "da": "Du vinder en gratis ferie — men du må kun tage én ven fra dette rum. Hvem vælger du og hvorfor?", "de": "Du gewinnst einen kostenlosen Urlaub – aber du darfst nur einen Freund aus diesem Raum mitnehmen. Wen wählst du und warum?", "es": "Ganas unas vacaciones gratis — pero solo puedes llevar a un amigo de esta sala. ¿A quién eliges y por qué?", "pt": "Ganhas umas férias grátis — mas só podes levar um amigo desta sala. Quem escolhes e porquê?", "ro": "Câștigi o vacanță gratuită — dar poți lua doar un prieten din această cameră. Pe cine alegi și de ce?", "fr": "Tu gagnes des vacances gratuites — mais tu ne peux emmener qu’un ami d’ici. Qui choisis-tu et pourquoi ?", "it": "Vinci una vacanza gratis — ma puoi portare solo un amico di questa stanza. Chi scegli e perché?", "fi": "Voitat ilmaisen loman — mutta voit tuoda vain yhden ystävän tästä huoneesta. Kuka on valintasi ja miksi?", "nl": "Je wint een gratis vakantie — maar je mag maar één vriend uit deze kamer meenemen. Wie kies je en waarom?", "no": "Du vinner en gratis ferie — men du kan bare ta med deg én venn fra dette rommet. Hvem velger du og hvorfor?", "sv": "Du vinner en gratis semester — men du kan bara ta med en vän från det här rummet. Vem väljer du och varför?", "pl": "Wygrywasz darmowe wakacje — ale możesz zabrać tylko jednego przyjaciela z tego pokoju. Kogo wybierasz i dlaczego?"}'::jsonb, false, true, true);
