INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "One of us has to fake-date you for a year for money — who do you pick?", "da": "En af os skal lade som om vi dater dig i et år for penge — hvem vælger du?", "de": "Einer von uns muss für ein Jahr so tun, als würde er mit dir ausgehen – für Geld. Wen wählst du?", "es": "Uno de nosotros tiene que fingir salir contigo por un año por dinero — ¿a quién eliges?", "pt": "Um de nós tem de fingir que namora contigo durante um ano por dinheiro — quem escolhes?", "ro": "Unul dintre noi trebuie să se prefacă că e într-o relație cu tine un an pentru bani — pe cine alegi?", "fr": "L’un de nous doit faire semblant de sortir avec toi pendant un an pour de l’argent — qui choisis-tu ?", "it": "Uno di noi deve fingere di uscire con te per un anno per soldi — chi scegli?", "fi": "Kenen valitset, jos jonkun meistä täytyy teeskennellä deittailua kanssasi vuoden ajan rahan takia?", "nl": "Een van ons moet jou een jaar lang voor geld faken, wie kies je?", "no": "En av oss må late som vi dater deg i et år for penger — hvem velger du?", "sv": "En av oss måste låtsasdejta dig i ett år för pengar — vem väljer du?", "pl": "Jedna z nas musi udawać, że się z tobą spotyka przez rok za pieniądze — kogo wybierasz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who would be the best partner in a “two truths and a lie” scam?", "da": "Hvem ville være den bedste partner i et “to sandheder og en løgn”-scam?", "de": "Wer wäre der beste Partner in einem „Zwei Wahrheiten und eine Lüge“-Spiel?", "es": "¿Quién sería el mejor compañero en un juego de “dos verdades y una mentira”?", "pt": "Quem seria o melhor parceiro num jogo de “duas verdades e uma mentira”?", "ro": "Cine ar fi cel mai bun partener într-un joc de “două adevăruri și o minciună”?", "fr": "Qui serait le meilleur partenaire dans un jeu de “deux vérités et un mensonge” ?", "it": "Chi sarebbe il miglior partner in un gioco di \"due verità e una bugia\"?", "fi": "Kuka olisi paras kumppani \"kaksi totuutta ja yksi valhe\" -pelissä?", "nl": "Wie zou de beste partner zijn in een \"twee waarheden en een leugen\" oplichterij?", "no": "Hvem ville vært den beste partneren i en \"to sannheter og en løgn\"-svindel?", "sv": "Vem skulle vara den bästa partnern i en \"två sanningar och en lögn\"-bluff?", "pl": "Kto byłby najlepszym partnerem w oszustwie „dwie prawdy i kłamstwo”?"}'::jsonb, false, true, true);
