INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You have to choose one forever: physical chemistry or emotional connection. Which wins?", "da": "Du skal vælge én for evigt: fysisk kemi eller følelsesmæssig forbindelse. Hvilken vinder?", "de": "Du musst dich für immer entscheiden: körperliche Chemie oder emotionale Verbindung. Was gewinnt?", "es": "Tienes que elegir una para siempre: ¿química física o conexión emocional?", "pt": "Tens de escolher uma para sempre: química física ou ligação emocional.", "ro": "Trebuie să alegi una pentru totdeauna: chimie fizică sau conexiune emoțională. Care câștigă?", "fr": "Tu dois en choisir une pour toujours : alchimie physique ou connexion émotionnelle. Laquelle gagne ?", "it": "Devi scegliere uno per sempre: chimica fisica o connessione emotiva. Quale vince?", "fi": "Sinun täytyy valita yksi ikuisesti: fysikaalinen kemia vai emotionaalinen yhteys. Kumpi voittaa?", "nl": "Je moet er één voor altijd kiezen: fysische chemie of emotionele verbinding. Welke wint?", "no": "Du må velge én for alltid: fysisk kjemi eller følelsesmessig tilknytning. Hvilken vinner?", "sv": "Du måste välja en för alltid: fysikalisk kemi eller känslomässig koppling. Vilken vinner?", "pl": "Musisz wybrać jedno na zawsze: chemia fizyczna czy emocjonalne połączenie. Co wygrywa?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who’s most likely to accidentally text the wrong person something dramatic?", "da": "Hvem er mest tilbøjelig til ved et uheld at sende en dramatisk besked til den forkerte person?", "de": "Wer wird am ehesten versehentlich eine dramatische Nachricht an die falsche Person senden?", "es": "¿Quién es más probable que envíe por error un mensaje dramático a la persona equivocada?", "pt": "Quem é mais provável de enviar por engano uma mensagem dramática à pessoa errada?", "ro": "Cine e cel mai probabil să trimită din greșeală un mesaj dramatic persoanei greșite?", "fr": "Qui est le plus susceptible d’envoyer par erreur un message dramatique à la mauvaise personne ?", "it": "Chi è più probabile che invii per sbaglio un messaggio drammatico alla persona sbagliata?", "fi": "Kuka todennäköisimmin lähettää vahingossa dramaattisen viestin väärälle henkilölle?", "nl": "Wie is het meest waarschijnlijk om per ongeluk iets dramatisch naar de verkeerde persoon te sms''en?", "no": "Hvem er mest sannsynlig til å sende en dramatisk melding til feil person ved et uhell?", "sv": "Vem är mest benägen att av misstag skicka ett dramatiskt meddelande till fel person?", "pl": "Kto najprawdopodobniej przypadkowo wyśle coś dramatycznego do niewłaściwej osoby?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who would you call if you needed to hide something — and who would absolutely snitch?", "da": "Hvem ville du ringe til, hvis du havde brug for at skjule noget — og hvem ville helt sikkert sladre?", "de": "Wen würdest du anrufen, wenn du etwas verstecken müsstest – und wer würde auf jeden Fall petzen?", "es": "¿A quién llamarías si necesitaras esconder algo — y quién absolutamente delataría?", "pt": "A quem ligarias se precisasses esconder algo — e quem contaria tudo?", "ro": "Pe cine ai suna dacă ai avea nevoie să ascunzi ceva — și cine ar spune sigur?", "fr": "Qui appellerais-tu si tu devais cacher quelque chose — et qui trahirait sûrement ?", "it": "Chi chiameresti se avessi bisogno di nascondere qualcosa — e chi sicuramente farebbe la spia?", "fi": "Keneä soittaisit, jos sinun pitäisi piilottaa jotain — ja kuka varmasti kertoisi siitä eteenpäin?", "nl": "Wie zou je bellen als je iets moest verbergen — en wie zou absoluut verklikken?", "no": "Hvem ville du ringe til hvis du trengte å skjule noe — og hvem ville absolutt sladre?", "sv": "Vem skulle du ringa om du behövde gömma något — och vem skulle absolut skvallra?", "pl": "Kogo byś zadzwonił, gdybyś musiał coś ukryć — a kto na pewno by doniósł?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You have to give one of us your phone for an hour. Who’s least likely to ruin your life?", "da": "Du skal give en af os din telefon i en time. Hvem er mindst tilbøjelig til at ødelægge dit liv?", "de": "Du musst einem von uns dein Handy für eine Stunde geben. Wer würde dein Leben am wenigsten ruinieren?", "es": "Tienes que darle tu teléfono a uno de nosotros por una hora. ¿Quién es menos probable que arruine tu vida?", "pt": "Tens de dar o teu telemóvel a um de nós por uma hora. Quem é menos provável de arruinar a tua vida?", "ro": "Trebuie să-i dai telefonul tău unuia dintre noi pentru o oră. Cine are cele mai mici șanse să-ți strice viața?", "fr": "Tu dois donner ton téléphone à l’un de nous pour une heure. Qui est le moins susceptible de ruiner ta vie ?", "it": "Devi dare il tuo telefono a uno di noi per un''ora. Chi è il meno probabile che rovini la tua vita?", "fi": "Kenen teistä pitäisi saada puhelimensa käyttöön tunniksi? Kuka on vähiten todennäköinen pilaamaan elämääsi?", "nl": "Je moet een van ons je telefoon voor een uur geven. Wie is het minst waarschijnlijk om je leven te verwoesten?", "no": "Du må gi en av oss telefonen din i en time. Hvem er minst sannsynlig til å ødelegge livet ditt?", "sv": "Du måste ge en av oss din telefon i en timme. Vem är minst sannolikt att förstöra ditt liv?", "pl": "Musisz dać jednemu z nas swój telefon na godzinę. Kto jest najmniej prawdopodobny, żeby zrujnować ci życie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You find out two of your close friends were hanging out without inviting you — what’s your honest reaction?", "da": "Du finder ud af, at to af dine tætte venner hang ud uden dig — hvad er din ærlige reaktion?", "de": "Du findest heraus, dass zwei deiner engen Freunde ohne dich abgehangen haben – was ist deine ehrliche Reaktion?", "es": "Descubres que dos de tus amigos cercanos salieron sin ti — ¿cuál es tu reacción honesta?", "pt": "Descobres que dois dos teus amigos próximos saíram sem ti — qual é a tua reação honesta?", "ro": "Află că doi dintre prietenii tăi apropiați s-au întâlnit fără tine — care e reacția ta sinceră?", "fr": "Tu découvres que deux de tes amis proches sont sortis sans toi — quelle est ta réaction honnête ?", "it": "Scopri che due dei tuoi amici più stretti si sono incontrati senza invitarti — qual è la tua reazione onesta?", "fi": "Saat tietää, että kaksi läheistä ystävääsi vietti aikaa yhdessä ilman, että kutsuivat sinua — mikä on rehellinen reaktiosi?", "nl": "Je ontdekt dat twee van je goede vrienden samen waren zonder jou uit te nodigen — wat is je eerlijke reactie?", "no": "Du finner ut at to av dine nære venner hang ut uten å invitere deg — hva er din ærlige reaksjon?", "sv": "Du får reda på att två av dina nära vänner hängde med varandra utan att bjuda in dig — vad är din ärliga reaktion?", "pl": "Dowiadujesz się, że dwóch twoich bliskich przyjaciół spędzało czas razem, nie zapraszając cię — jaka jest twoja szczera reakcja?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who in this room would handle a breakup the most dramatically?", "da": "Hvem her ville håndtere et brud mest dramatisk?", "de": "Wer hier würde eine Trennung am dramatischsten verarbeiten?", "es": "¿Quién aquí manejaría una ruptura de la forma más dramática?", "pt": "Quem aqui lidaria com uma separação da forma mais dramática?", "ro": "Cine de aici ar reacționa cel mai dramatic la o despărțire?", "fr": "Qui ici vivrait une rupture de la manière la plus dramatique ?", "it": "Chi in questa stanza gestirebbe una rottura in modo più drammatico?", "fi": "Kuka tässä huoneessa käsittelisi eron kaikkein dramaattisimmin?", "nl": "Wie in deze kamer zou een breakup het meest dramatisch verwerken?", "no": "Hvem i dette rommet ville håndtert et brudd mest dramatisk?", "sv": "Vem i det här rummet skulle hantera en breakup mest dramatiskt?", "pl": "Kto w tym pomieszczeniu najdramaturgiczniej poradziłby sobie z rozstaniem?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who do you think has the most secrets no one knows about?", "da": "Hvem tror du har flest hemmeligheder, som ingen kender til?", "de": "Wer, glaubst du, hat die meisten Geheimnisse, von denen niemand weiß?", "es": "¿Quién crees que tiene más secretos que nadie conoce?", "pt": "Quem achas que tem mais segredos que ninguém sabe?", "ro": "Cine crezi că are cele mai multe secrete pe care nimeni nu le știe?", "fr": "Qui, selon toi, a le plus de secrets que personne ne connaît ?", "it": "Chi pensi abbia i più segreti che nessuno conosce?", "fi": "Kuka mielestäsi on se, jolla on eniten salaisuuksia, joista kukaan ei tiedä?", "nl": "Wie denk je dat de meeste geheimen heeft die niemand weet?", "no": "Hvem tror du har de fleste hemmelighetene ingen vet om?", "sv": "Vem tror du har flest hemligheter som ingen känner till?", "pl": "Kto twoim zdaniem ma najwięcej sekretów, o których nikt nie wie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "If you could upgrade one friend’s personality trait and downgrade another, who and what would it be?", "da": "Hvis du kunne opgradere én vens personlighedstræk og nedgradere en anden, hvem og hvad ville det være?", "de": "Wenn du ein Persönlichkeitsmerkmal eines Freundes verbessern und ein anderes verschlechtern könntest, wer und was wäre das?", "es": "Si pudieras mejorar un rasgo de personalidad de un amigo y empeorar otro, ¿quién y cuál sería?", "pt": "Se pudesses melhorar um traço de personalidade de um amigo e piorar outro, quem e qual seria?", "ro": "Dacă ai putea îmbunătăți un atribut al unui prieten și înrăutăți altul, cine și care ar fi?", "fr": "Si tu pouvais améliorer un trait de personnalité d’un ami et en affaiblir un autre, qui et lequel choisirais-tu ?", "it": "Se potessi migliorare un tratto della personalità di un amico e peggiorarne un altro, chi e quale sarebbe?", "fi": "Jos voisit parantaa yhden ystäväsi luonteenpiirteen ja heikentää toisen, kuka ja mikä se olisi?", "nl": "Als je één persoonlijkheidskenmerk van een vriend zou kunnen verbeteren en dat van een ander zou kunnen verslechteren, wie en wat zou het zijn?", "no": "Hvis du kunne oppgradere en venns personlighetstrekk og nedgradere et annet, hvem ville det vært og hva ville det vært?", "sv": "Om du kunde uppgradera en väns personlighetsegenskap och nedgradera en annan, vem skulle det vara och vad skulle det vara?", "pl": "Gdybyś mógł poprawić jedną cechę osobowości przyjaciela, a obniżyć inną u innego, kogo byś wybrał i co by to było?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You’re given the power to erase one embarrassing moment for a friend — whose would you erase?", "da": "Du får magten til at slette ét pinligt øjeblik for en ven — hvis ville du slette?", "de": "Du hast die Macht, einen peinlichen Moment eines Freundes zu löschen – wessen würdest du löschen?", "es": "Tienes el poder de borrar un momento vergonzoso de un amigo — ¿cuál borrarías?", "pt": "Tens o poder de apagar um momento embaraçoso de um amigo — qual apagarias?", "ro": "Ai puterea să ștergi un moment jenant al unui prieten — pe al cui l-ai șterge?", "fr": "Tu as le pouvoir d’effacer un moment embarrassant d’un ami — lequel effacerais-tu ?", "it": "Ti è data la possibilità di cancellare un momento imbarazzante per un amico — quale cancelleresti?", "fi": "Sinulle annetaan valta pyyhkiä yksi nolostuttava hetki ystävältäsi — minkä hetken pyyhkisit?", "nl": "Je krijgt de macht om één gênant moment voor een vriend te wissen — wiens zou je wissen?", "no": "Du får makten til å slette ett pinlig øyeblikk for en venn — hvilket ville du slette?", "sv": "Du får makten att radera ett pinsamt ögonblick för en vän — vilket skulle du radera?", "pl": "Dostajesz moc, aby wymazać jeden wstydliwy moment dla przyjaciela — który byś wymazał?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You can only keep one: the friend who always shows up late, or the one who always cancels last minute?", "da": "Du kan kun beholde én: vennen der altid kommer for sent, eller den der altid aflyser i sidste øjeblik?", "de": "Du kannst nur einen behalten: den Freund, der immer zu spät kommt, oder den, der immer im letzten Moment absagt?", "es": "Solo puedes quedarte con uno: ¿el amigo que siempre llega tarde o el que siempre cancela a último momento?", "pt": "Só podes ficar com um: o amigo que está sempre atrasado ou o que cancela à última hora?", "ro": "Poți păstra doar unul: prietenul care întârzie mereu sau cel care anulează în ultimul moment?", "fr": "Tu ne peux en garder qu’un : celui qui est toujours en retard ou celui qui annule toujours à la dernière minute ?", "it": "Puoi tenere solo uno: l''amico che arriva sempre in ritardo, o quello che cancella sempre all''ultimo minuto?", "fi": "Voit pitää vain yhden: ystävän, joka aina saapuu myöhässä, vai sen, joka aina peruu viime hetkellä?", "nl": "Je mag er maar één houden: de vriend die altijd te laat komt, of degene die altijd op het laatste moment afzegt?", "no": "Du kan bare beholde én: vennen som alltid kommer for sent, eller den som alltid avlyser i siste liten?", "sv": "Du kan bara behålla en: vännen som alltid kommer för sent, eller den som alltid avbokar i sista minuten?", "pl": "Możesz zatrzymać tylko jednego: przyjaciela, który zawsze się spóźnia, czy tego, który zawsze odwołuje w ostatniej chwili?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You find out your friend has been lying about something small for years — what do you do?", "da": "Du finder ud af, at din ven har løjet om noget småt i årevis — hvad gør du?", "de": "Du erfährst, dass dein Freund jahrelang über etwas Kleines gelogen hat – was tust du?", "es": "Descubres que tu amigo ha estado mintiendo sobre algo pequeño durante años — ¿qué haces?", "pt": "Descobres que o teu amigo mentiu sobre algo pequeno durante anos — o que fazes?", "ro": "Află că prietenul tău a mințit ani de zile despre ceva mic — ce faci?", "fr": "Tu découvres que ton ami ment depuis des années sur un petit détail — que fais-tu ?", "it": "Scopri che il tuo amico ha mentito su qualcosa di piccolo per anni — cosa fai?", "fi": "Saat tietää, että ystäväsi on valehdellut jostain pienestä jo vuosia — mitä teet?", "nl": "Je ontdekt dat je vriend al jaren over iets kleins liegt — wat doe je?", "no": "Du finner ut at vennen din har løyet om noe lite i flere år — hva gjør du?", "sv": "Du får reda på att din vän har ljugit om något litet i flera år – vad gör du?", "pl": "Dowiadujesz się, że twój przyjaciel kłamał o czymś małym przez lata — co robisz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You find your partner on a dating app “just to look.” Is that cheating or harmless curiosity?", "da": "Du finder din partner på en datingapp “bare for at kigge.” Er det utroskab eller uskyldig nysgerrighed?", "de": "Du findest deinen Partner auf einer Dating-App „nur zum Schauen“. Ist das Betrug oder harmlose Neugier?", "es": "Encuentras a tu pareja en una app de citas “solo mirando”. ¿Es engaño o curiosidad inofensiva?", "pt": "Encontras o teu parceiro numa app de encontros “só a ver”. É traição ou curiosidade inofensiva?", "ro": "Îți găsești partenerul pe o aplicație de dating „doar să vadă”. E înșelat sau doar curiozitate nevinovată?", "fr": "Tu découvres ton partenaire sur une appli de rencontres « juste pour regarder ». C’est de la tromperie ou de la curiosité ?", "it": "Trovi il tuo partner su un''app di incontri \"solo per curiosità\". È tradimento o semplice curiosità?", "fi": "Löydät kumppanisi deittisovelluksesta \"vain katsomassa\". Onko se pettämistä vai harmitonta uteliaisuutta?", "nl": "Je vindt je partner op een datingapp \"gewoon om te kijken.\" Is dat bedrog of onschuldige nieuwsgierigheid?", "no": "Du finner partneren din på en datingapp \"bare for å se.\" Er det utroskap eller harmløs nysgjerrighet?", "sv": "Du hittar din partner på en dejtingapp \"bara för att kolla.\" Är det otrohet eller harmlös nyfikenhet?", "pl": "Znalazłeś swojego partnera na aplikacji randkowej „tylko do oglądania”. Czy to zdrada, czy nieszkodliwa ciekawość?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
