INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You notice a small red light behind a vent. Do you cover it or call staff?", "da": "Du ser et lille rødt lys bag en ventilationsrist. Dækker du det eller ringer du til personalet?", "de": "Du bemerkst ein kleines rotes Licht hinter einem Lüftungsschlitz. Deckst du es ab oder rufst du das Personal?", "es": "Ves una pequeña luz roja detrás de una rejilla. ¿La cubres o llamas al personal?", "pt": "Vês uma pequena luz vermelha atrás de uma ventilação. Cobres ou chamas os funcionários?", "ro": "Observi o mică lumină roșie în spatele unui orificiu de ventilație. O acoperi sau chemi personalul?", "fr": "Tu remarques une petite lumière rouge derrière une grille d’aération. Tu la couvres ou tu appelles le personnel ?", "it": "Noti una piccola luce rossa dietro una grata. La copri o chiami il personale?", "fi": "Huomaat pienen punaisen valon ritilän takana. Peitätkö sen vai kutsutko henkilökuntaa?", "nl": "Je ziet een klein rood lampje achter een rooster. Dek je het af of roep je personeel?", "no": "Du legger merke til et lite rødt lys bak en ventil. Dekker du det til eller ringer du på personalet?", "sv": "Du ser ett litet rött ljus bakom ett ventilationsgaller. Täckar du över det eller ringer du på personal?", "pl": "Zauważasz małe czerwone światełko za kratką wentylacyjną. Zasłaniasz je czy wołasz obsługę?"}'::jsonb, true, true, false);
