INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "If our home were haunted, which room would it choose and why?", "da": "Hvis vores hjem var hjemsøgt, hvilket rum ville det vælge og hvorfor?", "de": "Wenn unser Haus heimgesucht wäre, welches Zimmer würde es wählen und warum?", "es": "Si nuestra casa estuviera encantada, ¿qué habitación elegiría y por qué?", "pt": "Se a nossa casa fosse assombrada, que quarto escolheria e porquê?", "ro": "Dacă casa noastră ar fi bântuită, ce cameră ar alege și de ce?", "fr": "Si notre maison était hantée, quelle pièce choisirait-elle et pourquoi ?", "it": "Se la nostra casa fosse infestata, quale stanza sceglierebbe e perché?", "fi": "Jos kotimme olisi kummitustalo, minkä huoneen se valitsisi ja miksi?", "nl": "Als ons huis zou spoken, welke kamer zou het kiezen en waarom?", "no": "Hvis hjemmet vårt var hjemsøkt, hvilket rom ville det velge og hvorfor?", "sv": "Om vårt hem var hemsökt, vilket rum skulle det välja och varför?", "pl": "Gdyby nasz dom był nawiedzony, który pokój by wybrał i dlaczego?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What superstition do you follow “just in case”?", "da": "Hvilken overtro følger du “bare for en sikkerheds skyld”?", "de": "Welchen Aberglauben befolgst du „nur für alle Fälle“?", "es": "¿Qué superstición sigues “por si acaso”?", "pt": "Que superstição segues “só por precaução”?", "ro": "Ce superstiție urmezi „doar ca să fii sigur”?", "fr": "Quelle superstition suis-tu « juste au cas où » ?", "it": "Quale superstizione segui \"per sicurezza\"?", "fi": "Mitä taikauskoa noudatat varmuuden vuoksi \"ihan vain jos\"?", "nl": "Welke bijgeloof volg je, \"voor de zekerheid\"?", "no": "Hvilken overtro følger du \"bare for sikkerhets skyld\"?", "sv": "Vilken vidskepelse följer du \"för säkerhets skull\"?", "pl": "Jakiego przesądu trzymasz się \"na wszelki wypadek\"?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Your pet stares at an empty corner for a full minute. What could it be?", "da": "Dit kæledyr stirrer på et tomt hjørne i et helt minut. Hvad kan det være?", "de": "Dein Haustier starrt eine leere Ecke eine Minute lang an. Was könnte es sein?", "es": "Tu mascota mira fijamente una esquina vacía durante un minuto. ¿Qué podría ser?", "pt": "O teu animal de estimação encara um canto vazio por um minuto. O que será?", "ro": "Animalul tău de companie se uită fix într-un colț gol timp de un minut. Ce ar putea fi?", "fr": "Ton animal regarde un coin vide pendant une minute entière. Qu’est-ce que ça pourrait être ?", "it": "Il tuo animale domestico fissa un angolo vuoto per un minuto intero. Cosa potrebbe essere?", "fi": "Lemmikkisi tuijottaa tyhjää nurkkaa kokonaisen minuutin. Mitä se voisi olla?", "nl": "Je huisdier staart een volle minuut naar een lege hoek. Wat zou het kunnen zijn?", "no": "Kjæledyret ditt stirrer på et tomt hjørne i et helt minutt. Hva kan det være?", "sv": "Ditt husdjur stirrar på ett tomt hörn i en hel minut. Vad kan det vara?", "pl": "Twój pupil przez całą minutę gapi się w pusty kąt. Co to może być?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Pick one to witness: UFO, time traveler, or a ghost – which one do you choose?", "da": "Du må vælge én ting at se: UFO, tidsrejsende eller et spøgelse – hvad vælger du?", "de": "Du darfst eines sehen: UFO, Zeitreisenden oder Geist – was wählst du?", "es": "Puedes elegir presenciar uno: un OVNI, un viajero del tiempo o un fantasma. ¿Cuál eliges?", "pt": "Podes escolher ver um: OVNI, viajante no tempo ou fantasma – qual escolhes?", "ro": "Poți alege să vezi un singur lucru: un OZN, un călător în timp sau o fantomă – ce alegi?", "fr": "Tu peux en voir un seul : OVNI, voyageur du temps ou fantôme – lequel choisis-tu ?", "it": "Scegline uno da vedere: UFO, viaggiatore nel tempo o fantasma – quale scegli?", "fi": "Valitse yksi nähtäväksi: UFO, aikamatkustaja vai kummitus – minkä otat?", "nl": "Kies er één om mee te maken: UFO, tijdreiziger of geest – welke kies je?", "no": "Velg én å være vitne til: UFO, tidsreisende eller et spøkelse – hvilken velger du?", "sv": "Välj en att få bevittna: UFO, tidsresenär eller ett spöke – vilken väljer du?", "pl": "Wybierz jedno, czego chcesz być świadkiem: UFO, podróżnik w czasie czy duch – co wybierasz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A date knows a private detail you never shared. Do you ask how, or end the date?", "da": "En date ved en privat detalje, du aldrig har delt. Spørger du hvordan, eller stopper du daten?", "de": "Ein Date kennt ein privates Detail, das du nie geteilt hast. Fragst du, wie er/sie das weiß, oder beendest du das Date?", "es": "Una cita sabe un detalle privado que nunca compartiste. ¿Preguntas cómo lo sabe o terminas la cita?", "pt": "Um encontro sabe um detalhe privado que nunca contaste. Perguntas como ou acabas o encontro?", "ro": "O persoană cu care ești la o întâlnire știe un detaliu privat pe care nu l-ai împărtășit niciodată. Întrebi cum știe sau închei întâlnirea?", "fr": "Un rendez-vous connaît un détail privé que tu n’as jamais partagé. Tu demandes comment ou tu mets fin au rendez-vous ?", "it": "Un appuntamento sa un dettaglio privato che non hai mai raccontato. Chiedi come fa a saperlo o chiudi l’appuntamento?", "fi": "Treffi tietää yksityiskohdan, jota et ole koskaan kertonut. Kysytkö miten hän sen tietää vai lopetatko treffit?", "nl": "Een date weet een privé-detail dat je nooit hebt verteld. Vraag je hoe hij/zij dat weet, of beëindig je de date?", "no": "En date vet en privat detalj du aldri har delt. Spør du hvordan, eller avslutter du daten?", "sv": "En dejt vet en privat detalj du aldrig berättat. Frågar du hur hen vet det, eller avslutar du dejten?", "pl": "Osoba na randce zna prywatny szczegół, którego nigdy nie zdradziłeś/-aś. Pytasz, skąd wie, czy kończysz randkę?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "If you could ask a ghost one question, what would it be?", "da": "Hvis du kunne stille et spøgelse ét spørgsmål, hvad ville det være?", "de": "Wenn du einem Geist eine Frage stellen könntest, welche wäre das?", "es": "Si pudieras hacerle una pregunta a un fantasma, ¿cuál sería?", "pt": "Se pudesses fazer uma pergunta a um fantasma, qual seria?", "ro": "Dacă ai putea pune o singură întrebare unei fantome, care ar fi?", "fr": "Si tu pouvais poser une question à un fantôme, laquelle serait-ce ?", "it": "Se potessi fare una domanda a un fantasma, quale sarebbe?", "fi": "Jos voisit kysyä kummitukselta yhden kysymyksen, mikä se olisi?", "nl": "Als je een geest één vraag kon stellen, welke zou dat zijn?", "no": "Hvis du kunne stille et spøkelse ett spørsmål, hva ville det vært?", "sv": "Om du kunde ställa ett spöke en fråga, vad skulle du fråga?", "pl": "Gdybyś mógł/mogła zadać duchowi jedno pytanie, jakie by to było?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A rideshare driver locks the doors and says the route “just changed.” What do you do next?", "da": "En chauffør låser dørene og siger, at ruten “lige har ændret sig.” Hvad gør du?", "de": "Ein Fahrdienstfahrer verriegelt die Türen und sagt, die Route „hat sich gerade geändert.“ Was tust du?", "es": "Un conductor cierra las puertas y dice que la ruta “acaba de cambiar”. ¿Qué haces?", "pt": "Um motorista tranca as portas e diz que a rota “acabou de mudar”. O que fazes?", "ro": "Șoferul de rideshare încuie ușile și spune că ruta „tocmai s-a schimbat.” Ce faci?", "fr": "Un chauffeur verrouille les portes et dit que l’itinéraire « vient de changer ». Que fais-tu ?", "it": "Un autista di rideshare chiude le portiere e dice che il percorso \"è appena cambiato\". Cosa fai dopo?", "fi": "Kyytipalvelun kuski lukitsee ovet ja sanoo reitin \"juuri muuttuneen\". Mitä teet seuraavaksi?", "nl": "Een rideshare-chauffeur doet de deuren op slot en zegt dat de route \"net is veranderd\". Wat doe je daarna?", "no": "En samkjøringssjåfør låser dørene og sier at ruta \"nettopp ble endret\". Hva gjør du nå?", "sv": "En samåkningschaufför låser dörrarna och säger att rutten \"just ändrades\". Vad gör du sedan?", "pl": "Kierowca przejazdu z aplikacji rygluje drzwi i mówi, że trasa \"właśnie się zmieniła\". Co robisz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What’s your family’s oddest good-luck ritual?", "da": "Hvad er din families mærkeligste lykkebringende ritual?", "de": "Was ist das seltsamste Glücksritual deiner Familie?", "es": "¿Cuál es el ritual de buena suerte más extraño de tu familia?", "pt": "Qual é o ritual de sorte mais estranho da tua família?", "ro": "Care este cel mai ciudat ritual de noroc al familiei tale?", "fr": "Quel est le rituel porte-bonheur le plus étrange de ta famille ?", "it": "Qual è il rito portafortuna più strano della tua famiglia?", "fi": "Mikä on perheesi oudoin onnea tuova rituaali?", "nl": "Wat is het vreemdste geluksritueel van jouw familie?", "no": "Hva er familiens rareste lykkeritual?", "sv": "Vad är din familjs konstigaste tur-ritual?", "pl": "Jaki jest najdziwniejszy rytuał na szczęście w twojej rodzinie?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Share a dream you remember too clearly. Any meaning?", "da": "Del en drøm, du husker alt for tydeligt. Nogen betydning?", "de": "Teile einen Traum, an den du dich zu gut erinnerst. Irgendeine Bedeutung?", "es": "Comparte un sueño que recuerdes con demasiada claridad. ¿Algún significado?", "pt": "Partilha um sonho que te lembras demasiado bem. Tem algum significado?", "ro": "Povestește un vis pe care ți-l amintești prea clar. Ce înțeles are?", "fr": "Partage un rêve dont tu te souviens trop bien. Une signification ?", "it": "Condividi un sogno che ricordi fin troppo bene. Che significato potrebbe avere?", "fi": "Kerro unesta, jonka muistat liian tarkasti. Onko sillä mielestäsi jokin merkitys?", "nl": "Deel een droom die je te goed herinnert. Zie je er een betekenis in?", "no": "Del en drøm du husker altfor godt. Noen mening i den?", "sv": "Dela en dröm du minns alldeles för tydligt. Någon betydelse?", "pl": "Podziel się snem, który pamiętasz aż za dobrze. Ma dla ciebie jakieś znaczenie?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A crime case you can’t stop thinking about—why this one?", "da": "En kriminalsag, du ikke kan glemme – hvorfor netop den?", "de": "Ein Kriminalfall, über den du nicht aufhören kannst nachzudenken – warum dieser?", "es": "Un caso criminal que no puedes dejar de pensar – ¿por qué ese?", "pt": "Um caso criminal que não consegues esquecer – porquê esse?", "ro": "Un caz criminal de care nu te poți desprinde – de ce tocmai acesta?", "fr": "Une affaire criminelle à laquelle tu penses encore – pourquoi celle-là ?", "it": "Un caso di cronaca nera a cui non riesci a smettere di pensare: perché proprio questo?", "fi": "Rikostapaus, jota et saa mielestäsi – miksi juuri tämä?", "nl": "Een strafzaak waar je maar aan blijft denken – waarom juist deze?", "no": "En kriminalsak du ikke klarer å slutte å tenke på – hvorfor akkurat denne?", "sv": "Ett kriminalfall du inte kan sluta tänka på – varför just det?", "pl": "Sprawa kryminalna, o której nie możesz przestać myśleć – dlaczego właśnie ta?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Footsteps outside your door, peephole blocked – what do you do?", "da": "Fodtrin udenfor din dør, kighul blokeret – hvad gør du?", "de": "Schritte vor deiner Tür, Spion blockiert – was tust du?", "es": "Pasos fuera de tu puerta, mirilla bloqueada – ¿qué haces?", "pt": "Passos fora da tua porta, olho mágico bloqueado – o que fazes?", "ro": "Se aud pași în fața ușii tale, vizorul este blocat – ce faci?", "fr": "Des pas devant ta porte, judas bloqué – que fais-tu ?", "it": "Passi fuori dalla porta, spioncino coperto – cosa fai?", "fi": "Askeleita oven ulkopuolella, ovisilmä peitetty – mitä teet?", "nl": "Voetstappen buiten je deur, deurspion afgeplakt – wat doe je?", "no": "Fottrinn utenfor døren din, dørspionen er blokkert – hva gjør du?", "sv": "Fotsteg utanför din dörr, titthålet är blockerat – vad gör du?", "pl": "Kroki za drzwiami, wizjer zasłonięty – co robisz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Motion sensor lights turn on in the empty next room. How do you clear the space?", "da": "Bevægelsessensorlys tænder i det tomme rum ved siden af. Hvordan tjekker du det?", "de": "Bewegungsmelderlicht geht im leeren Nebenraum an. Wie prüfst du den Raum?", "es": "Las luces con sensor se encienden en la habitación vacía de al lado. ¿Cómo revisas el lugar?", "pt": "A luz do sensor de movimento acende-se no quarto ao lado. Como verificas?", "ro": "Lumina cu senzor de mișcare se aprinde în camera goală de alături. Cum verifici spațiul?", "fr": "Les lumières à capteur de mouvement s’allument dans la pièce voisine vide. Comment vérifies-tu ?", "it": "Le luci con sensore di movimento si accendono nella stanza vuota accanto. Come \"ripulisci\" lo spazio?", "fi": "Liiketunnistinvalot syttyvät tyhjään viereiseen huoneeseen. Miten \"puhdistat\" tilan?", "nl": "Bewegingssensorlampen gaan aan in de lege kamer naast je. Hoe \"reinig\" je de ruimte?", "no": "Bevegelsessensorlysene slår seg på i det tomme rommet ved siden av. Hvordan \"renser\" du stedet?", "sv": "Rörelsesensorlampor tänds i det tomma rummet bredvid. Hur \"rensar\" du platsen?", "pl": "Czujniki ruchu zapalają światło w pustym pokoju obok. Jak \"oczyszczasz\" tę przestrzeń?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You can ask a spirit one yes/no question. What do you ask?", "da": "Du kan stille en ånd ét ja/nej-spørgsmål. Hvad spørger du om?", "de": "Du darfst einem Geist eine Ja/Nein-Frage stellen. Was fragst du?", "es": "Puedes hacerle una pregunta de sí/no a un espíritu. ¿Qué preguntas?", "pt": "Podes fazer uma pergunta de sim/não a um espírito. O que perguntas?", "ro": "Poți pune unei spirite o întrebare la care răspunde doar „da” sau „nu”. Ce întrebi?", "fr": "Tu peux poser une question par oui/non à un esprit. Que demandes-tu ?", "it": "Puoi fare a uno spirito una sola domanda a cui si risponde sì/no. Cosa chiedi?", "fi": "Voit kysyä hengeltä yhden kyllä/ei -kysymyksen. Mitä kysyt?", "nl": "Je mag één ja/nee-vraag aan een geest stellen. Wat vraag je?", "no": "Du kan stille en ånd ett ja/nei-spørsmål. Hva spør du om?", "sv": "Du får ställa en ja/nej-fråga till en ande. Vad frågar du?", "pl": "Możesz zadać duchowi jedno pytanie z odpowiedzią tak/nie. O co pytasz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You find an AirTag/tracker alert on your phone after a night out. What do you do?", "da": "Du får en AirTag/Tracker-advarsel på din telefon efter en aften ude. Hvad gør du?", "de": "Du erhältst nach einer Nacht draußen eine AirTag/Tracker-Warnung auf deinem Handy. Was tust du?", "es": "Recibes una alerta de AirTag/rastreador en tu teléfono después de una noche fuera. ¿Qué haces?", "pt": "Recebes um alerta de AirTag/rastreador no teu telemóvel depois de uma noite fora. O que fazes?", "ro": "Primești o alertă pe telefon despre un AirTag/tracker după o noapte în oraș. Ce faci?", "fr": "Tu reçois une alerte AirTag/suivi sur ton téléphone après une soirée dehors. Que fais-tu ?", "it": "Trovi una notifica di AirTag/tracker sul telefono dopo una serata fuori. Cosa fai?", "fi": "Saat AirTag-/seurantalaitteen hälytyksen puhelimeesi illanvieton jälkeen. Mitä teet?", "nl": "Je krijgt na een avond uit een AirTag-/tracker-melding op je telefoon. Wat doe je?", "no": "Du får varsel om en AirTag/tracker på telefonen etter en kveld ute. Hva gjør du?", "sv": "Du får en AirTag-/tracker-varning på mobilen efter en utekväll. Vad gör du?", "pl": "Po wieczornym wyjściu dostajesz na telefon powiadomienie o AirTagu/trackerze. Co robisz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Rate your belief in ghosts, 0–10, and defend it.", "da": "Bedøm din tro på spøgelser fra 0–10, og forsvar den.", "de": "Bewerte deinen Glauben an Geister von 0 bis 10 und verteidige ihn.", "es": "Califica tu creencia en fantasmas del 0 al 10 y defiéndela.", "pt": "Avalia a tua crença em fantasmas de 0 a 10 e defende-a.", "ro": "Evaluează-ți credința în fantome de la 0 la 10 și apără-ți răspunsul.", "fr": "Évalue ta croyance aux fantômes de 0 à 10 et défends-la.", "it": "Valuta quanto credi ai fantasmi da 0 a 10 e difendi il tuo voto.", "fi": "Arvioi uskosi kummituksiin asteikolla 0–10 ja perustele se.", "nl": "Geef je geloof in geesten een cijfer van 0–10 en verdedig het.", "no": "Ranger troen din på spøkelser fra 0–10, og forklar.", "sv": "Betygsätt din tro på spöken, 0–10, och försvara betyget.", "pl": "Oceń swoją wiarę w duchy w skali 0–10 i ją uzasadnij."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A child in a store waves behind you and says “bye.” There’s no one there. How do you react?", "da": "Et barn i en butik vinker bag dig og siger “hej hej”. Der er ingen der. Hvad gør du?", "de": "Ein Kind im Laden winkt dir hinterher und sagt „Tschüss“. Niemand ist da. Wie reagierst du?", "es": "Un niño en una tienda te saluda y dice “adiós”. No hay nadie allí. ¿Cómo reaccionas?", "pt": "Uma criança numa loja acena-te e diz “tchau”. Não há ninguém. Como reages?", "ro": "Un copil într-un magazin îți face cu mâna și spune „pa.” Nu e nimeni acolo. Cum reacționezi?", "fr": "Un enfant dans un magasin te fait signe et dit « au revoir ». Il n’y a personne. Comment réagis-tu ?", "it": "Un bambino in un negozio saluta dietro di te e dice \"ciao\". Non c’è nessuno. Come reagisci?", "fi": "Lapsi kaupassa vilkuttaa selkäsi takana ja sanoo \"hei hei\". Ketään ei näy. Miten reagoit?", "nl": "Een kind in een winkel zwaait achter je en zegt \"doei\". Er is niemand. Hoe reageer je?", "no": "Et barn i en butikk vinker bak deg og sier \"ha det\". Det er ingen der. Hvordan reagerer du?", "sv": "Ett barn i en butik vinkar bakom dig och säger \"hej då\". Det finns ingen där. Hur reagerar du?", "pl": "Dziecko w sklepie macha za twoimi plecami i mówi \"pa\". Nikogo tam nie ma. Jak reagujesz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
