INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "If mirrors recorded one minute a day, which minute would you replay?", "da": "Hvis spejle optog ét minut om dagen, hvilket minut ville du gense?", "de": "Wenn Spiegel eine Minute pro Tag aufzeichnen würden, welche Minute würdest du wieder abspielen?", "es": "Si los espejos grabaran un minuto al día, ¿qué minuto volverías a ver?", "pt": "Se os espelhos gravassem um minuto por dia, qual voltarias a ver?", "ro": "Dacă oglinzile ar înregistra un minut pe zi, ce minut ai revedea?", "fr": "Si les miroirs enregistraient une minute par jour, laquelle rejouerais-tu ?", "it": "Se gli specchi registrassero un minuto al giorno, quale minuto vorresti rivedere?", "fi": "Jos peilit tallentaisivat yhden minuutin päivässä, minkä minuutin haluaisit katsoa uudelleen?", "nl": "Als spiegels één minuut per dag zouden opnemen, welke minuut zou jij terugkijken?", "no": "Hvis speil tok opp ett minutt om dagen, hvilket minutt ville du spolt tilbake til?", "sv": "Om speglar spelade in en minut om dagen, vilken minut skulle du spela upp igen?", "pl": "Gdyby lustra nagrywały jedną minutę dziennie, którą minutę chciał(a)byś odtworzyć?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A stranger knows your name and favorite snack. Explain it rationally.", "da": "En fremmed kender dit navn og din yndlingssnack. Forklar det rationelt.", "de": "Ein Fremder kennt deinen Namen und deinen Lieblingssnack. Erkläre es rational.", "es": "Un desconocido sabe tu nombre y tu aperitivo favorito. Explícalo racionalmente.", "pt": "Um estranho sabe o teu nome e o teu lanche favorito. Explica racionalmente.", "ro": "Un străin îți știe numele și gustarea preferată. Explică logic.", "fr": "Un inconnu connaît ton nom et ton encas préféré. Explique rationnellement.", "it": "Uno sconosciuto conosce il tuo nome e il tuo snack preferito. Spiegalo in modo razionale.", "fi": "Tuntematon tietää nimesi ja lempievääsi. Selitä tämä järkevästi.", "nl": "Een onbekende kent je naam en je favoriete snack. Verklaar het op een rationele manier.", "no": "En fremmed vet navnet ditt og favorittsnacken din. Forklar det på en rasjonell måte.", "sv": "En främling kan ditt namn och ditt favoritsnack. Förklara det rationellt.", "pl": "Obca osoba zna twoje imię i ulubioną przekąskę. Wyjaśnij to racjonalnie."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You’re walking home and all the streetlights go out at once. What’s your next move?", "da": "Du går hjem, og alle gadelygter slukker på én gang. Hvad gør du?", "de": "Du gehst nach Hause, und alle Straßenlaternen gehen gleichzeitig aus. Was tust du?", "es": "Caminas a casa y todas las farolas se apagan a la vez. ¿Qué haces?", "pt": "Caminhas para casa e todos os candeeiros da rua apagam-se de uma vez. O que fazes?", "ro": "Mergi spre casă și toate felinarele se sting deodată. Ce faci?", "fr": "Tu rentres chez toi et tous les lampadaires s’éteignent d’un coup. Que fais-tu ?", "it": "Stai tornando a casa a piedi e tutti i lampioni si spengono all’improvviso. Qual è la tua prossima mossa?", "fi": "Kävelet kotiin ja kaikki katuvalot sammuvat yhtä aikaa. Mitä teet seuraavaksi?", "nl": "Je loopt naar huis en alle lantaarnpalen gaan tegelijk uit. Wat is je volgende stap?", "no": "Du går hjem, og alle gatelysene slukker samtidig. Hva gjør du da?", "sv": "Du går hem och alla gatlyktor slocknar samtidigt. Vad gör du sen?", "pl": "Idziesz pieszo do domu i nagle wszystkie latarnie gasną. Jaki jest twój następny krok?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What smell would make a place instantly haunted to you?", "da": "Hvilken lugt ville få et sted til straks at føles hjemsøgt?", "de": "Welcher Geruch würde einen Ort sofort spukhaft machen?", "es": "¿Qué olor haría que un lugar se sintiera inmediatamente encantado?", "pt": "Que cheiro faria um lugar parecer imediatamente assombrado?", "ro": "Ce miros ar face un loc să pară instantaneu bântuit?", "fr": "Quelle odeur rendrait un endroit instantanément hanté ?", "it": "Quale odore renderebbe un luogo istantaneamente infestato, per te?", "fi": "Mikä haju saisi paikan tuntumaan heti kuin siellä kummittelisi?", "nl": "Welke geur zou een plek voor jou meteen spookachtig maken?", "no": "Hvilken lukt ville fått et sted til å føles umiddelbart hjemsøkt for deg?", "sv": "Vilken doft skulle få en plats att kännas direkt hemsökt för dig?", "pl": "Jaki zapach sprawiłby, że miejsce od razu wydałoby ci się nawiedzone?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Déjà vu hits hard—but you know the next 10 seconds. Do you follow the script or try to break it?", "da": "Déjà vu rammer hårdt – men du kender de næste 10 sekunder. Følger du scriptet eller bryder det?", "de": "Déjà-vu trifft dich hart – aber du kennst die nächsten 10 Sekunden. Folgst du dem Ablauf oder brichst du ihn?", "es": "El déjà vu golpea fuerte, pero sabes los próximos 10 segundos. ¿Sigues el guion o lo rompes?", "pt": "O déjà vu atinge-te forte – mas sabes os próximos 10 segundos. Segues o guião ou quebras?", "ro": "Déjà vu te lovește puternic – dar știi următoarele 10 secunde. Urmezi scenariul sau îl rupi?", "fr": "Le déjà-vu te frappe fort – mais tu connais les dix prochaines secondes. Tu suis le script ou tu le brises ?", "it": "Il déjà vu è fortissimo, ma tu conosci i prossimi 10 secondi. Segui il copione o provi a romperlo?", "fi": "Déjà-vu iskee kovaa – mutta tiedät seuraavat 10 sekuntia. Seuraatko käsikirjoitusta vai yritätkö rikkoa sen?", "nl": "Heftig déjà vu – maar je weet de komende 10 seconden. Volg je het script of probeer je het te doorbreken?", "no": "Deja vu treffer skikkelig, men du vet hva som skjer de neste 10 sekundene. Følger du manus eller prøver du å bryte det?", "sv": "Kraftig déjà vu – men du vet vad som händer de nästa 10 sekunderna. Följer du manus eller försöker du bryta det?", "pl": "Mocne déjà vu – ale znasz następne 10 sekund. Trzymasz się scenariusza czy próbujesz go przerwać?"}'::jsonb, true, true, false);
