INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You can remove one fear forever—what do you choose?", "da": "Du kan fjerne én frygt for altid – hvilken vælger du?", "de": "Du kannst eine Angst für immer beseitigen – welche wählst du?", "es": "Puedes eliminar un miedo para siempre – ¿cuál eliges?", "pt": "Podes eliminar um medo para sempre – qual escolhes?", "ro": "Poți elimina o frică pentru totdeauna – care ar fi?", "fr": "Tu peux supprimer une peur pour toujours – laquelle choisis-tu ?", "it": "Puoi eliminare per sempre una paura: quale scegli?", "fi": "Voit poistaa yhden pelon ikuisesti – minkä valitset?", "nl": "Je mag één angst voor altijd wegnemen – welke kies je?", "no": "Du kan fjerne én frykt for alltid – hvilken velger du?", "sv": "Du kan ta bort en rädsla för alltid – vilken väljer du?", "pl": "Możesz usunąć jeden lęk na zawsze – który wybierasz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A stranger insists they know you from a city you’ve never visited—and name a bar you “liked.” Next step?", "da": "En fremmed insisterer på, at de kender dig fra en by, du aldrig har besøgt – og nævner en bar, du “kunne lide.” Hvad gør du?", "de": "Ein Fremder besteht darauf, dich aus einer Stadt zu kennen, die du nie besucht hast – und nennt eine Bar, die du „mochtest“. Was tust du?", "es": "Un extraño insiste en que te conoce de una ciudad que nunca visitaste, y nombra un bar que “te gustaba”. ¿Qué haces?", "pt": "Um estranho insiste que te conhece de uma cidade onde nunca estiveste – e menciona um bar de que “gostavas”. E agora?", "ro": "Un străin insistă că te cunoaște dintr-un oraș în care n-ai fost niciodată – și menționează un bar care „ți-a plăcut”. Ce faci?", "fr": "Un inconnu insiste sur le fait qu’il te connaît d’une ville où tu n’es jamais allé – et nomme un bar que tu « aimais ». Que fais-tu ?", "it": "Uno sconosciuto insiste di conoscerti da una città in cui non sei mai stato/a e nomina un bar che ti “piaceva”. Prossima mossa?", "fi": "Tuntematon väittää tuntevansa sinut kaupungista, jossa et ole koskaan käynyt – ja mainitsee baarin, josta “pidit”. Mitä teet seuraavaksi?", "nl": "Een onbekende beweert je te kennen uit een stad waar je nooit bent geweest – en noemt een bar die je zogenaamd “leuk vond”. Wat nu?", "no": "En fremmed insisterer på at de kjenner deg fra en by du aldri har vært i – og nevner en bar du visstnok “likte”. Hva gjør du nå?", "sv": "En främling påstår att hen känner dig från en stad du aldrig varit i – och nämner en bar du ”gillade”. Vad gör du sen?", "pl": "Obca osoba upiera się, że zna cię z miasta, w którym nigdy nie byłeś/byłaś – i wymienia bar, który rzekomo „lubiłeś/-aś”. Co dalej?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What’s the creepiest nice thing someone could do for you?", "da": "Hvad er den uhyggeligste venlige ting, nogen kunne gøre for dig?", "de": "Was ist das gruseligste nette Ding, das jemand für dich tun könnte?", "es": "¿Cuál es la cosa amable más escalofriante que alguien podría hacer por ti?", "pt": "Qual é a coisa mais assustadoramente gentil que alguém poderia fazer por ti?", "ro": "Care este cel mai înfiorător gest drăguț pe care cineva l-ar putea face pentru tine?", "fr": "Quelle est la chose gentille la plus effrayante que quelqu’un pourrait faire pour toi ?", "it": "Qual è la cosa più inquietante ma gentile che qualcuno potrebbe fare per te?", "fi": "Mikä olisi karmivin mutta silti “kiltti” teko, jonka joku voisi tehdä puolestasi?", "nl": "Wat is het griezeligste aardige ding dat iemand voor je zou kunnen doen?", "no": "Hva er det skumleste, men egentlig snille, noen kunne gjøre for deg?", "sv": "Vad är det läskigaste snälla någon skulle kunna göra för dig?", "pl": "Jaka jest najbardziej creepowa, ale miła rzecz, jaką ktoś mógłby dla ciebie zrobić?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You must spend the night in a museum. Which type would creep you out most (wax figures, medical curiosities, taxidermy, dolls, etc.)—and why?", "da": "Du skal tilbringe natten på et museum. Hvilken type ville skræmme dig mest (voksmennesker, medicinske kuriositeter, udstoppede dyr, dukker osv.) – og hvorfor?", "de": "Du musst die Nacht in einem Museum verbringen. Welche Art würde dich am meisten gruseln (Wachspuppen, medizinische Kuriositäten, Tierpräparate, Puppen usw.) – und warum?", "es": "Debes pasar la noche en un museo. ¿Qué tipo te asustaría más (figuras de cera, curiosidades médicas, taxidermia, muñecas, etc.) y por qué?", "pt": "Tens de passar a noite num museu. Que tipo te assustaria mais (figuras de cera, curiosidades médicas, taxidermia, bonecas, etc.) – e porquê?", "ro": "Trebuie să petreci noaptea într-un muzeu. Care tip te-ar speria cel mai tare (figuri de ceară, curiozități medicale, animale împăiate, păpuși etc.) – și de ce?", "fr": "Tu dois passer la nuit dans un musée. Quel type te ferait le plus peur (cire, curiosités médicales, animaux empaillés, poupées, etc.) – et pourquoi ?", "it": "Devi passare la notte in un museo. Quale tipo ti inquieterebbe di più (figure di cera, curiosità mediche, tassidermia, bambole, ecc.) e perché?", "fi": "Sinun on vietettävä yö museossa. Millainen museo pelottaisi eniten (vahanuket, lääketieteelliset kuriositeetit, täytetyt eläimet, nuket jne.) – ja miksi?", "nl": "Je moet een nacht in een museum doorbrengen. Welk type zou je het meest laten griezelen (wasfiguren, medische curiosa, opgezette dieren, poppen, etc.) – en waarom?", "no": "Du må tilbringe natten på et museum. Hvilken type ville skremt deg mest (voksfigurer, medisinske kuriositeter, utstoppede dyr, dukker osv.) – og hvorfor?", "sv": "Du måste tillbringa natten på ett museum. Vilken typ skulle kännas mest obehaglig (vaxdockor, medicinska kuriositeter, uppstoppade djur, dockor osv.) – och varför?", "pl": "Musisz spędzić noc w muzeum. Jaki typ przeraziłby cię najbardziej (figury woskowe, ciekawostki medyczne, preparaty, lalki itp.) – i dlaczego?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You find a duplicate of your house key in your drawer, newly cut. Do you change locks?", "da": "Du finder en kopi af din husnøgle i din skuffe, nyklippet. Skifter du lås?", "de": "Du findest einen Duplikatschlüssel deines Hauses in deiner Schublade, frisch geschnitten. Wechselst du die Schlösser?", "es": "Encuentras una copia de la llave de tu casa en tu cajón, recién hecha. ¿Cambias las cerraduras?", "pt": "Encontras uma cópia da chave de tua casa na gaveta, recém cortada. Mudarias as fechaduras?", "ro": "Găsești o copie a cheii casei tale în sertar, proaspăt tăiată. Schimbi yala?", "fr": "Tu trouves un double de ta clé de maison dans ton tiroir, fraîchement taillé. Tu changes les serrures ?", "it": "Trovi un duplicato della chiave di casa nel cassetto, appena fatto. Cambi la serratura?", "fi": "Löydät laatikosta vastikään tehdyn vara-avaimen kotiisi. Vaihdatko lukot?", "nl": "Je vindt in je lade een nieuw bijgemaakte kopie van je huissleutel. Verwissel je de sloten?", "no": "Du finner en nykopiert husnøkkel i skuffen din. Skifter du låser?", "sv": "Du hittar en nygjord kopia av din husnyckel i en låda. Byter du lås?", "pl": "W szufladzie znajdujesz świeżo dorobiony duplikat klucza do domu. Wymieniasz zamki?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Name a hilltop, tunnel, or alley that has ✨bad vibes✨.", "da": "Nævn en bakketop, tunnel eller gyde med ✨dårlig energi✨.", "de": "Nenne einen Hügel, Tunnel oder eine Gasse mit ✨schlechter Energie✨.", "es": "Nombra una colina, túnel o callejón con ✨malas vibras✨.", "pt": "Nomeia uma colina, túnel ou viela com ✨más energias✨.", "ro": "Numește un deal, tunel sau gang cu ✨vibrații rele✨.", "fr": "Nomme une colline, un tunnel ou une ruelle avec ✨de mauvaises ondes✨.", "it": "Nomina una cima, un tunnel o un vicolo che ha ✨brutte vibrazioni✨.", "fi": "Nimeä mäenhuippu, tunneli tai kuja, josta tulee ✨paha fiilis✨.", "nl": "Noem een heuveltop, tunnel of steeg die ✨bad vibes✨ heeft.", "no": "Nevn en bakketopp, tunnel eller bakgate som har ✨dårlig vibber✨.", "sv": "Nämn en kulle, tunnel eller gränd som har ✨dålig vibbe✨.", "pl": "Podaj nazwę wzgórza, tunelu lub zaułka, który ma ✨złą energię✨."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Maps suggests a “faster route” through a road that doesn’t exist. Do you follow it?", "da": "Kort foreslår en “hurtigere rute” gennem en vej, der ikke findes. Følger du den?", "de": "Maps schlägt eine „schnellere Route“ über eine Straße vor, die nicht existiert. Folgst du ihr?", "es": "El mapa sugiere una “ruta más rápida” por un camino que no existe. ¿La sigues?", "pt": "O mapa sugere uma “rota mais rápida” por uma estrada que não existe. Segues?", "ro": "Harta îți sugerează o „rută mai rapidă” printr-un drum care nu există. O urmezi?", "fr": "Le GPS te propose un « itinéraire plus rapide » par une route qui n’existe pas. Tu y vas ?", "it": "Maps ti suggerisce un “percorso più veloce” attraverso una strada che non esiste. Lo segui?", "fi": "Karttasovellus ehdottaa “nopeampaa reittiä” tietä pitkin, jota ei ole olemassa. Lähdetkö mukaan?", "nl": "Maps stelt een “snellere route” voor via een weg die niet bestaat. Volg je die?", "no": "Kart-appen foreslår en «raskere rute» via en vei som ikke finnes. Følger du den?", "sv": "Kartappen föreslår en ”snabbare rutt” via en väg som inte finns. Följer du den?", "pl": "Mapy proponują „szybszą trasę” drogą, która nie istnieje. Jedziesz nią?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Your shadow lags by two seconds. Now what?", "da": "Din skygge er to sekunder bagud. Hvad nu?", "de": "Dein Schatten hinkt zwei Sekunden hinterher. Was jetzt?", "es": "Tu sombra se retrasa dos segundos. ¿Y ahora qué?", "pt": "A tua sombra atrasa-se dois segundos. E agora?", "ro": "Umbra ta întârzie două secunde. Ce faci?", "fr": "Ton ombre a deux secondes de retard. Et maintenant ?", "it": "La tua ombra è in ritardo di due secondi. E adesso?", "fi": "Varjosi laahaa kaksi sekuntia perässä. Mitä nyt?", "nl": "Je schaduw loopt twee seconden achter. Wat doe je?", "no": "Skyggen din henger to sekunder etter. Hva gjør du?", "sv": "Din skugga ligger två sekunder efter. Vad gör du?", "pl": "Twój cień spóźnia się o dwie sekundy. I co teraz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Something keeps “going wrong” around one person. Do you believe in the evil eye or coincidence? Defend it.", "da": "Noget går altid galt omkring én person. Tror du på det onde øje eller tilfældighed? Forklar.", "de": "Um eine Person geht immer etwas schief. Glaubst du an den bösen Blick oder Zufall? Erkläre.", "es": "Algo siempre sale mal alrededor de una persona. ¿Crees en el mal de ojo o en la coincidencia? Defiéndelo.", "pt": "Algo dá sempre errado à volta de uma pessoa. Acreditas no mau-olhado ou em coincidência? Defende.", "ro": "În jurul unei persoane totul merge prost. Crezi în deochi sau în coincidență? Apără-ți opinia.", "fr": "Quelque chose tourne toujours mal autour d’une personne. Tu crois au mauvais œil ou à la coïncidence ? Défends ton point de vue.", "it": "Intorno a una certa persona va sempre tutto “storto”. Credi nel malocchio o nelle coincidenze? Difendi la tua risposta.", "fi": "Jonkun ympärillä jokin menee jatkuvasti “pieleen”. Uskotko pahaan silmään vai sattumaan? Perustele.", "nl": "Rond één persoon blijft er steeds iets “misgaan”. Geloof je in het boze oog of in toeval? Verdedig je keuze.", "no": "Rundt én person går ting stadig «galt». Tror du på det onde øyet eller på tilfeldigheter? Begrunn.", "sv": "Runt en viss person verkar saker hela tiden gå ”fel”. Tror du på onda ögat eller på slumpen? Förklara.", "pl": "Wokół jednej osoby ciągle coś „idzie nie tak”. Wierzysz w złe oko czy w przypadek? Uzasadnij."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Share a folk tale or saying from your culture that’s a bit spooky.", "da": "Del et folkeeventyr eller ordsprog fra din kultur, der er lidt uhyggeligt.", "de": "Teile ein Volksmärchen oder Sprichwort aus deiner Kultur, das etwas unheimlich ist.", "es": "Comparte un cuento popular o dicho de tu cultura que sea un poco espeluznante.", "pt": "Partilha um conto ou ditado da tua cultura que seja um pouco assustador.", "ro": "Spune o legendă sau un proverb din cultura ta care e puțin înfricoșător.", "fr": "Partage un conte ou un dicton de ta culture qui est un peu effrayant.", "it": "Condividi una fiaba popolare o un detto della tua cultura che sia un po’ inquietante.", "fi": "Jaa kansansatu tai sanonta omasta kulttuuristasi, joka on hieman pelottava.", "nl": "Deel een volksverhaal of uitdrukking uit jouw cultuur die een beetje griezelig is.", "no": "Del et folkeeventyr eller et uttrykk fra kulturen din som er litt skummelt.", "sv": "Dela en folksaga eller ett uttryck från din kultur som är lite kusligt.", "pl": "Podziel się ludową opowieścią lub powiedzonkiem z twojej kultury, które jest trochę straszne."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What’s your personal early-warning sign that a place isn’t safe?", "da": "Hvad er dit personlige tidlige advarselstegn på, at et sted ikke er sikkert?", "de": "Was ist dein persönliches Frühwarnzeichen, dass ein Ort nicht sicher ist?", "es": "¿Cuál es tu señal de advertencia personal de que un lugar no es seguro?", "pt": "Qual é o teu sinal pessoal de que um lugar não é seguro?", "ro": "Care este semnul tău personal că un loc nu e sigur?", "fr": "Quel est ton signe avant-coureur personnel qu’un lieu n’est pas sûr ?", "it": "Qual è il tuo segnale personale d’allarme che ti dice che un posto non è sicuro?", "fi": "Mikä on oma varoitusmerkkisi siitä, että paikka ei ole turvallinen?", "nl": "Wat is voor jou het vroegste waarschuwingssignaal dat een plek niet veilig is?", "no": "Hva er ditt personlige tidlige varsel om at et sted ikke er trygt?", "sv": "Vad är din personliga tidiga varningssignal på att en plats inte är säker?", "pl": "Jaki jest twój osobisty wczesny sygnał, że miejsce nie jest bezpieczne?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What do you think happens after we die—one honest sentence.", "da": "Hvad tror du, der sker, når vi dør – én ærlig sætning.", "de": "Was glaubst du, passiert, wenn wir sterben – ein ehrlicher Satz.", "es": "¿Qué crees que sucede cuando morimos? Una frase honesta.", "pt": "O que achas que acontece depois de morrermos – uma frase honesta.", "ro": "Ce crezi că se întâmplă după ce murim – o propoziție sinceră.", "fr": "Que penses-tu qu’il arrive après la mort – une phrase honnête.", "it": "Cosa pensi che succeda dopo la morte? Una frase sincera.", "fi": "Mitä luulet tapahtuvan kuoleman jälkeen – yksi rehellinen lause.", "nl": "Wat denk je dat er gebeurt nadat we sterven – één eerlijke zin.", "no": "Hva tror du skjer etter at vi dør – én ærlig setning.", "sv": "Vad tror du händer efter att vi dör – en ärlig mening.", "pl": "Jak myślisz, co się dzieje po śmierci – jedno szczere zdanie."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You hear your name from an empty room. Investigate or nope?", "da": "Du hører dit navn fra et tomt rum. Undersøg eller gå væk?", "de": "Du hörst deinen Namen aus einem leeren Raum. Untersuchen oder gehen?", "es": "Oyes tu nombre desde una habitación vacía. ¿Investigas o te vas?", "pt": "Ouves o teu nome vindo de uma sala vazia. Investigarias ou saías?", "ro": "Auzi cum ți se spune numele dintr-o cameră goală. Cercetezi sau pleci?", "fr": "Tu entends ton nom venir d’une pièce vide. Tu enquêtes ou tu pars ?", "it": "Senti il tuo nome provenire da una stanza vuota. Vai a vedere o lasci stare?", "fi": "Kuulet nimesi tyhjästä huoneesta. Tutkitko vai etkö todellakaan?", "nl": "Je hoort je naam uit een lege kamer komen. Ga je kijken of echt niet?", "no": "Du hører navnet ditt bli ropt fra et tomt rom. Går du for å sjekke, eller nei takk?", "sv": "Du hör ditt namn ropas från ett tomt rum. Går du och kollar eller nope?", "pl": "Słyszysz swoje imię z pustego pokoju. Idziesz sprawdzić czy absolutnie nie?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Reincarnation is real: what skill or habit would you carry over?", "da": "Reinkarnation er virkelig: hvilken færdighed eller vane ville du tage med dig?", "de": "Reinkarnation ist real: Welche Fähigkeit oder Gewohnheit würdest du mitnehmen?", "es": "La reencarnación es real: ¿qué habilidad o hábito llevarías contigo?", "pt": "A reencarnação é real: que habilidade ou hábito levarias contigo?", "ro": "Reîncarnarea e reală: ce abilitate sau obicei ai duce mai departe?", "fr": "La réincarnation est réelle : quelle compétence ou habitude emporterais-tu ?", "it": "La reincarnazione è reale: quale abilità o abitudine vorresti portare con te?", "fi": "Reinkarnaatio on totta: minkä taidon tai tavan ottaisit mukaasi seuraavaan elämään?", "nl": "Reïncarnatie is echt: welke vaardigheid of gewoonte zou je meenemen?", "no": "Reinkarnasjon er ekte: hvilken ferdighet eller vane ville du tatt med deg videre?", "sv": "Reinkarnation är på riktigt: vilken färdighet eller vana skulle du ta med dig?", "pl": "Reinkarnacja jest prawdziwa: jaką umiejętność lub jaki nawyk zabrał(a)byś ze sobą?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What’s the safest place on earth at midnight?", "da": "Hvad er det sikreste sted på jorden ved midnat?", "de": "Was ist der sicherste Ort auf der Erde um Mitternacht?", "es": "¿Cuál es el lugar más seguro del mundo a medianoche?", "pt": "Qual é o lugar mais seguro da Terra à meia-noite?", "ro": "Care este cel mai sigur loc de pe Pământ la miezul nopții?", "fr": "Quel est l’endroit le plus sûr sur Terre à minuit ?", "it": "Qual è il posto più sicuro sulla Terra a mezzanotte?", "fi": "Mikä on maailman turvallisin paikka keskiyöllä?", "nl": "Wat is de veiligste plek op aarde om middernacht?", "no": "Hva er det tryggeste stedet på jorden ved midnatt?", "sv": "Vilken är den säkraste platsen på jorden vid midnatt?", "pl": "Jakie miejsce na świecie jest najbezpieczniejsze o północy?"}'::jsonb, true, true, false);
