INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You see someone place a bag under a bench and walk away fast. Do you follow, report, or wait?", "da": "Du ser nogen lægge en taske under en bænk og gå hurtigt væk. Følger du efter, anmelder du, eller venter du?", "de": "Du siehst jemanden eine Tasche unter eine Bank legen und schnell weggehen. Folgst du, meldest du es oder wartest du?", "es": "Ves a alguien dejar una bolsa bajo un banco y alejarse rápido. ¿Sigues, reportas o esperas?", "pt": "Vês alguém deixar uma mala debaixo de um banco e sair depressa. Segues, denuncias ou esperas?", "ro": "Vezi pe cineva punând o geantă sub o bancă și plecând repede. Urmărești, anunți sau aștepți?", "fr": "Tu vois quelqu’un poser un sac sous un banc et partir vite. Tu suis, tu signales ou tu attends ?", "it": "Vedi qualcuno mettere una borsa sotto una panchina e allontanarsi in fretta. Lo segui, lo segnali o aspetti?", "fi": "Näet jonkun laittavan laukun penkin alle ja kävelevän nopeasti pois. Seuraatko, ilmoitatko vai odotatko?", "nl": "Je ziet iemand een tas onder een bank neerzetten en snel weglopen. Volg je, meld je het, of wacht je af?", "no": "Du ser noen legge en veske under en benk og gå raskt bort. Følger du etter, varsler du, eller venter du?", "sv": "Du ser någon lägga en väska under en bänk och gå därifrån snabbt. Följer du efter, larmar du eller väntar du?", "pl": "Widzisz, jak ktoś chowa torbę pod ławką i szybko odchodzi. Idziesz za nim, zgłaszasz to czy czekasz?"}'::jsonb, true, true, false);
