INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A friend tracks your location “for safety”… and comments on your stops. Is that care or control?", "da": "En ven sporer din placering “for sikkerhedens skyld”… og kommenterer dine stop. Omsorg eller kontrol?", "de": "Ein Freund verfolgt deinen Standort „zur Sicherheit“… und kommentiert deine Stopps. Sorge oder Kontrolle?", "es": "Un amigo rastrea tu ubicación “por seguridad”… y comenta tus paradas. ¿Cuidado o control?", "pt": "Um amigo rastreia a tua localização “por segurança”… e comenta as tuas paragens. Cuidado ou controlo?", "ro": "Un prieten îți urmărește locația „pentru siguranță”… și comentează opririle tale. Grijă sau control?", "fr": "Un ami suit ta position « pour ta sécurité »… et commente tes arrêts. Prendre soin ou contrôler ?", "it": "Un amico traccia la tua posizione “per sicurezza”… e commenta tutte le tue soste. È cura o controllo?", "fi": "Ystävä seuraa sijaintiasi ”turvallisuuden vuoksi”… ja kommentoi pysähdyksiäsi. Onko se välittämistä vai kontrollia?", "nl": "Een vriend volgt je locatie “voor je veiligheid”… en geeft commentaar op je stops. Is dat zorg of controle?", "no": "En venn sporer posisjonen din «for sikkerhets skyld»… og kommenterer stoppene dine. Er det omsorg eller kontroll?", "sv": "En vän spårar din plats ”för säkerhetens skull”… och kommenterar dina stopp. Är det omtanke eller kontroll?", "pl": "Przyjaciel śledzi twoją lokalizację „dla bezpieczeństwa”… i komentuje twoje przystanki. To troska czy kontrola?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What’s the eeriest kind of weather?", "da": "Hvad er den mest uhyggelige slags vejr?", "de": "Was ist die unheimlichste Art von Wetter?", "es": "¿Cuál es el tipo de clima más inquietante?", "pt": "Qual é o tipo de clima mais assustador?", "ro": "Care este cel mai înfiorător tip de vreme?", "fr": "Quel est le type de temps le plus inquiétant ?", "it": "Che tipo di tempo atmosferico ti sembra più inquietante?", "fi": "Millainen sää on mielestäsi karmivin?", "nl": "Wat voor soort weer vind jij het meest spookachtig?", "no": "Hva er den mest uhyggelige typen vær?", "sv": "Vilken sorts väder är ruggigast enligt dig?", "pl": "Jaki rodzaj pogody jest dla ciebie najbardziej upiorny?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "If the walls could talk, what rumor would they spread?", "da": "Hvis væggene kunne tale, hvilket rygte ville de sprede?", "de": "Wenn die Wände sprechen könnten, welches Gerücht würden sie verbreiten?", "es": "Si las paredes pudieran hablar, ¿qué rumor propagarían?", "pt": "Se as paredes pudessem falar, que rumor espalhariam?", "ro": "Dacă pereții ar putea vorbi, ce zvon ar răspândi?", "fr": "Si les murs pouvaient parler, quelle rumeur propageraient-ils ?", "it": "Se i muri potessero parlare, quale pettegolezzo diffonderebbero?", "fi": "Jos seinät voisivat puhua, mitä juorua ne levittäisivät?", "nl": "Als de muren konden praten, welk gerucht zouden ze verspreiden?", "no": "Hvis veggene kunne snakke, hvilket rykte ville de spre?", "sv": "Om väggarna kunde prata, vilket rykte skulle de sprida?", "pl": "Gdyby ściany mogły mówić, jaką plotkę by rozpuściły?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A neighbor moves in but never opens curtains. What’s your theory?", "da": "En nabo flytter ind, men åbner aldrig gardinerne. Hvad er din teori?", "de": "Ein Nachbar zieht ein, aber öffnet nie die Vorhänge. Was ist deine Theorie?", "es": "Un vecino se muda pero nunca abre las cortinas. ¿Cuál es tu teoría?", "pt": "Um vizinho muda-se mas nunca abre as cortinas. Qual é a tua teoria?", "ro": "Un vecin se mută, dar nu deschide niciodată perdelele. Care e teoria ta?", "fr": "Un voisin emménage mais n’ouvre jamais les rideaux. Quelle est ta théorie ?", "it": "Un vicino si trasferisce ma non apre mai le tende. Qual è la tua teoria?", "fi": "Naapuri muuttaa viereen, mutta ei koskaan avaa verhoja. Mikä on teoriasi?", "nl": "Een nieuwe buur trekt in maar doet nooit de gordijnen open. Wat is jouw theorie?", "no": "En ny nabo flytter inn, men åpner aldri gardinene. Hva er teorien din?", "sv": "En granne flyttar in men öppnar aldrig gardinerna. Vad är din teori?", "pl": "Nowy sąsiad się wprowadza, ale nigdy nie odsłania zasłon. Jaka jest twoja teoria?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Lab-grown meat tastes identical and costs less. Do you switch 100%? Why or why not?", "da": "Laboratoriekød smager identisk og koster mindre. Skifter du 100%? Hvorfor eller hvorfor ikke?", "de": "Laborfleisch schmeckt gleich und kostet weniger. Steigst du komplett um? Warum oder warum nicht?", "es": "La carne cultivada sabe igual y cuesta menos. ¿Cambias al 100%? ¿Por qué sí o no?", "pt": "Carne cultivada sabe igual e custa menos. Mudarias 100%? Porquê?", "ro": "Carnea crescută în laborator are același gust și costă mai puțin. Treci complet la ea? De ce da sau nu?", "fr": "La viande cultivée en laboratoire a le même goût et coûte moins cher. Tu passes à 100 % ? Pourquoi ou pourquoi pas ?", "it": "La carne coltivata in laboratorio ha lo stesso sapore e costa meno. Passi al 100% a quella? Perché sì o perché no?", "fi": "Laboratoriossa kasvatettu liha maistuu täysin samalta ja on halvempaa. Vaihdatko siihen kokonaan? Miksi tai miksi et?", "nl": "Kweekvlees smaakt precies hetzelfde en is goedkoper. Stap je er 100% op over? Waarom wel of niet?", "no": "Laboratoriedyrket kjøtt smaker helt likt og koster mindre. Går du over til det 100 %? Hvorfor eller hvorfor ikke?", "sv": "Labodlat kött smakar likadant och kostar mindre. Går du över helt? Varför eller varför inte?", "pl": "Mięso z laboratorium smakuje identycznie i jest tańsze. Przechodzisz na nie w 100%? Dlaczego tak lub dlaczego nie?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Who is the last person you would like to bump into, around the corner in the middle of the night?", "da": "Hvem er den sidste person, du gerne ville støde ind i på gaden midt om natten?", "de": "Wer ist die letzte Person, der du mitten in der Nacht begegnen möchtest?", "es": "¿Quién es la última persona con la que querrías cruzarte en medio de la noche?", "pt": "Quem é a última pessoa que gostarias de encontrar no meio da noite?", "ro": "Cine este ultima persoană pe care ai vrea s-o întâlnești la colț, noaptea?", "fr": "Quelle est la dernière personne que tu voudrais croiser au coin de la rue, au milieu de la nuit ?", "it": "Chi è l’ultima persona che vorresti incontrare dietro l’angolo nel cuore della notte?", "fi": "Kenen kanssa et todellakaan haluaisi törmätä yöllä nurkan takana?", "nl": "Wie is de laatste persoon tegen wie je ’s nachts achter een hoekje zou willen aanlopen?", "no": "Hvem er den siste personen du vil møte rundt et hjørne midt på natten?", "sv": "Vem är den sista personen du vill springa in i runt hörnet mitt i natten?", "pl": "Kogo najbardziej nie chciał(a)byś spotkać za rogiem w środku nocy?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You wake up with a person standing in the doorframe – what do you do?", "da": "Du vågner, og der står en person i dørkarmen – hvad gør du?", "de": "Du wachst auf, und jemand steht im Türrahmen – was tust du?", "es": "Te despiertas y hay alguien en el marco de la puerta – ¿qué haces?", "pt": "Acordas e há alguém na moldura da porta – o que fazes?", "ro": "Te trezești și vezi o persoană stând în tocul ușii – ce faci?", "fr": "Tu te réveilles et quelqu’un se tient dans l’encadrement de la porte – que fais-tu ?", "it": "Ti svegli e c’è una persona in piedi nella cornice della porta: cosa fai?", "fi": "Heräät ja näet henkilön seisomassa ovenaukossa – mitä teet?", "nl": "Je wordt wakker en er staat iemand in de deuropening – wat doe je?", "no": "Du våkner og det står en person i døråpningen – hva gjør du?", "sv": "Du vaknar och det står en person i dörröppningen – vad gör du?", "pl": "Budzisz się, a w futrynie drzwi stoi jakaś osoba – co robisz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Your smart lock app shows someone tried four PINs at 2:41 a.m. What do you do?", "da": "Din smart lock-app viser, at nogen prøvede fire PINs kl. 2:41. Hvad gør du?", "de": "Deine Smart-Lock-App zeigt, dass jemand um 2:41 Uhr vier PINs versucht hat. Was tust du?", "es": "Tu app de cerradura muestra que alguien intentó cuatro PINs a las 2:41 a.m. ¿Qué haces?", "pt": "A tua app da fechadura mostra que alguém tentou quatro PINs às 2:41. O que fazes?", "ro": "Aplicația ta de încuietoare arată că cineva a încercat patru coduri la 2:41 dimineața. Ce faci?", "fr": "Ton application de serrure connectée montre que quelqu’un a essayé quatre codes à 2h41. Que fais-tu ?", "it": "L’app della serratura smart mostra che qualcuno ha provato quattro PIN alle 2:41 di notte. Cosa fai?", "fi": "Älylukkosovellus näyttää, että joku kokeili neljää PIN-koodia klo 2.41 yöllä. Mitä teet?", "nl": "Je slimme-deurslot-app laat zien dat iemand om 2.41 uur vier pincodes heeft geprobeerd. Wat doe je?", "no": "Appen til smartlåsen viser at noen prøvde fire PIN-koder kl. 02.41. Hva gjør du?", "sv": "Din smartlås-app visar att någon provade fyra koder kl. 2.41 på natten. Vad gör du?", "pl": "Aplikacja od twojego smart-zamka pokazuje, że ktoś o 2:41 w nocy próbował czterech PIN-ów. Co robisz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "If Mom and Dad had superhero powers, what would their powers be?", "da": "Hvis mor og far havde superkræfter, hvilke kræfter ville de så have?", "de": "Wenn Mama und Papa Superkräfte hätten, welche Kräfte hätten sie dann?", "es": "Si mamá y papá tuvieran superpoderes, ¿cuáles serían sus poderes?", "pt": "Se a mamãe e o papai tivessem superpoderes, quais seriam seus poderes?", "ro": "Dacă mama și tata ar avea superputeri, care ar fi puterile lor?", "fr": "Si maman et papa avaient des superpouvoirs, quels seraient leurs pouvoirs ?", "it": "L’app della serratura smart mostra che qualcuno ha provato quattro PIN alle 2:41 di notte. Cosa fai?", "fi": "Älylukkosovellus näyttää, että joku kokeili neljää PIN-koodia klo 2.41 yöllä. Mitä teet?", "nl": "Je slimme-deurslot-app laat zien dat iemand om 2.41 uur vier pincodes heeft geprobeerd. Wat doe je?", "no": "Appen til smartlåsen viser at noen prøvde fire PIN-koder kl. 02.41. Hva gjør du?", "sv": "Din smartlås-app visar att någon provade fyra koder kl. 2.41 på natten. Vad gör du?", "pl": "Aplikacja od twojego smart-zamka pokazuje, że ktoś o 2:41 w nocy próbował czterech PIN-ów. Co robisz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "Would you rather have wings or a tail? Why?", "da": "Ville du helst have vinger eller en hale? Hvorfor?", "de": "Würdest du lieber Flügel oder einen Schwanz haben? Warum?", "es": "¿Preferirías tener alas o una cola? ¿Por qué?", "pt": "Preferias ter asas ou uma cauda? Por quê?", "ro": "Ai prefera să ai aripi sau o coadă? De ce?", "fr": "Préférerais-tu avoir des ailes ou une queue ? Pourquoi ?", "it": "Preferiresti avere ali o una coda? Perché?", "fi": "Haluaisitko mieluummin siivet vai hännän? Miksi?", "nl": "Zou je liever vleugels of een staart hebben? Waarom?", "no": "Ville du heller hatt vinger eller en hale? Hvorfor?", "sv": "Skulle du hellre ha vingar eller en svans? Varför?", "pl": "Czy wolałbyś mieć skrzydła czy ogon? Dlaczego?"}'::jsonb, false, true, true);
