VALUES ('Tiny Talks', '{"en": "Who do you like to sit next to, and why?", "da": "Hvem kan du bedst lide at sidde ved siden af, og hvorfor?", "de": "Neben wem sitzt du gern, und warum?", "es": "¿Al lado de quién te gusta sentarte y por qué?", "pt": "Ao lado de quem gostas de te sentar e por quê?", "ro": "Lângă cine îți place să stai la școală și de ce?", "fr": "À côté de qui aimes-tu t’asseoir et pourquoi ?", "it": "Chi ti piace avere accanto e perché?", "fi": "Kenen vieressä tykkäät istua, ja miksi?", "nl": "Wie zit je het liefst naast, en waarom?", "no": "Hvem liker du å sitte ved siden av, og hvorfor?", "sv": "Vem gillar du att sitta bredvid, och varför?", "pl": "Kogo lubisz siedzieć obok i dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
