INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What do you think is harder — being a kid or being a parent? Why?", "da": "Hvad tror du er sværere — at være barn eller at være forælder? Hvorfor?", "de": "Was ist deiner Meinung nach schwieriger – ein Kind oder ein Elternteil zu sein? Warum?", "es": "¿Qué crees que es más difícil: ser niño o ser padre? ¿Por qué?", "pt": "O que achas que é mais difícil — ser criança ou ser pai/mãe? Por quê?", "ro": "Ce crezi că este mai greu — să fii copil sau părinte? De ce?", "fr": "Qu’est-ce qui est plus difficile à ton avis — être enfant ou être parent ? Pourquoi ?", "it": "Cosa pensi sia più difficile — essere un bambino o essere un genitore? Perché?", "fi": "Mitä mieltä olet, mikä on vaikeampaa — olla lapsi vai olla vanhempi? Miksi?", "nl": "Wat denk je dat moeilijker is — een kind zijn of een ouder zijn? Waarom?", "no": "Hva tror du er vanskeligere — å være barn eller å være forelder? Hvorfor?", "sv": "Vad tycker du är svårare — att vara barn eller att vara förälder? Varför?", "pl": "Co myślisz, co jest trudniejsze — być dzieckiem czy być rodzicem? Dlaczego?"}'::jsonb, false, true, true);
