INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "If your best friend turned into a superhero for a day, what would their power be?", "da": "Hvis din bedste ven blev til en superhelt for en dag, hvilken kraft ville de have?", "de": "Wenn dein bester Freund für einen Tag ein Superheld wäre, welche Kraft hätte er?", "es": "Si tu mejor amigo se convirtiera en superhéroe por un día, ¿cuál sería su poder?", "pt": "Se o teu melhor amigo se tornasse um super-herói por um dia, qual seria o seu poder?", "ro": "Dacă cel mai bun prieten al tău ar deveni supererou pentru o zi, ce putere ar avea?", "fr": "Si ton meilleur ami devenait un super-héros pour une journée, quel serait son pouvoir ?", "it": "Se il tuo migliore amico diventasse un supereroe per un giorno, quale sarebbe il suo potere?", "fi": "Jos paras ystäväsi muuttuisi supersankariksi päiväksi, mikä heidän voimansa olisi?", "nl": "Als je beste vriend voor een dag in een superheld zou veranderen, welke kracht zou hij dan hebben?", "no": "Hvis din beste venn ble en superhelt for en dag, hva ville deres superkraft vært?", "sv": "Om din bästa vän blev en superhjälte för en dag, vilken superkraft skulle de ha?", "pl": "Gdyby twój najlepszy przyjaciel zamienił się w superbohatera na jeden dzień, jaką moc by miał?"}'::jsonb, false, true, true);
