INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "If you could give your parents a gift that doesn’t cost money, what would it be?", "da": "Hvis du kunne give dine forældre en gave, der ikke koster penge, hvad ville det være?", "de": "Wenn du deinen Eltern ein Geschenk machen könntest, das kein Geld kostet, was wäre das?", "es": "Si pudieras darles a tus padres un regalo que no cuesta dinero, ¿qué sería?", "pt": "Se pudesses dar aos teus pais um presente que não custasse dinheiro, qual seria?", "ro": "Dacă ai putea oferi părinților un cadou care nu costă bani, care ar fi?", "fr": "Si tu pouvais offrir à tes parents un cadeau qui ne coûte rien, lequel serait-ce ?", "it": "Se potessi fare un regalo ai tuoi genitori che non costa soldi, quale sarebbe?", "fi": "Jos voisit antaa vanhemmillesi lahjan, joka ei maksa rahaa, mikä se olisi?", "nl": "Als je je ouders een cadeau zou kunnen geven dat geen geld kost, wat zou dat dan zijn?", "no": "Hvis du kunne gi foreldrene dine en gave som ikke koster penger, hva ville det vært?", "sv": "Om du kunde ge dina föräldrar en gåva som inte kostar pengar, vad skulle det vara?", "pl": "Gdybyś mógł dać swoim rodzicom prezent, który nie kosztuje pieniędzy, co by to było?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What do you think raindrops talk about when they fall?", "da": "Hvad tror du, regndråber taler om, når de falder?", "de": "Worüber reden Regentropfen wohl, wenn sie fallen?", "es": "¿De qué crees que hablan las gotas de lluvia cuando caen?", "pt": "Sobre o que achas que as gotas de chuva falam quando caem?", "ro": "Despre ce crezi că vorbesc picăturile de ploaie când cad?", "fr": "De quoi penses-tu que parlent les gouttes de pluie quand elles tombent ?", "it": "Cosa pensi che dicano le gocce di pioggia quando cadono?", "fi": "Mitä mieltä olet, mistä sadepisarat puhuvat pudotessaan?", "nl": "Waarover denken regendruppels te praten als ze vallen?", "no": "Hva tror du regndråper snakker om når de faller?", "sv": "Vad tror du regndroppar pratar om när de faller?", "pl": "O czym myślisz, że krople deszczu rozmawiają, gdy spadają?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "Which animal would be the funniest to see wearing shoes?", "da": "Hvilket dyr ville se sjovest ud med sko på?", "de": "Welches Tier wäre am lustigsten mit Schuhen?", "es": "¿Qué animal sería el más divertido de ver con zapatos?", "pt": "Que animal seria mais engraçado de ver com sapatos?", "ro": "Ce animal ar fi cel mai amuzant de văzut purtând pantofi?", "fr": "Quel animal serait le plus drôle à voir porter des chaussures ?", "it": "Quale animale sarebbe il più divertente da vedere con le scarpe?", "fi": "Mikä eläin olisi hauskin nähdä kengät jalassa?", "nl": "welk dier zou het grappigst zijn om in schoenen te zien?", "no": "Hvilket dyr ville vært det morsomste å se iført sko?", "sv": "Vilket djur skulle vara roligast att se i skor?", "pl": "Które zwierzę byłoby najzabawniejsze w butach?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What would happen if trees could walk around?", "da": "Hvad ville der ske, hvis træer kunne gå rundt?", "de": "Was würde passieren, wenn Bäume herumlaufen könnten?", "es": "¿Qué pasaría si los árboles pudieran caminar?", "pt": "O que aconteceria se as árvores pudessem andar?", "ro": "Ce s-ar întâmpla dacă arborii ar putea merge?", "fr": "Que se passerait-il si les arbres pouvaient marcher ?", "it": "Cosa succederebbe se gli alberi potessero camminare?", "fi": "Mitä tapahtuisi, jos puut voisivat kävellä ympäriinsä?", "nl": "Wat zou er gebeuren als bomen konden rondlopen?", "no": "Hva ville skjedd hvis trær kunne gå rundt?", "sv": "Vad skulle hända om träd kunde gå omkring?", "pl": "Co by się stało, gdyby drzewa mogły chodzić?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "If you could give every animal a job, what job would you give a giraffe?", "da": "Hvis du kunne give alle dyr et job, hvilket job ville du give en giraf?", "de": "Wenn du jedem Tier einen Job geben könntest, welchen würdest du einer Giraffe geben?", "es": "Si pudieras darle un trabajo a cada animal, ¿qué trabajo le darías a una jirafa?", "pt": "Se pudesses dar um trabalho a cada animal, que trabalho darias a uma girafa?", "ro": "Dacă ai putea da un loc de muncă fiecărui animal, ce slujbă i-ai da unei girafe?", "fr": "Si tu pouvais donner un travail à chaque animal, quel travail donnerais-tu à une girafe ?", "it": "Se potessi dare a ogni animale un lavoro, che lavoro daresti a una giraffa?", "fi": "Jos voisit antaa jokaiselle eläimelle työn, minkä työn antaisit kirahville?", "nl": "Als je elk dier een baan zou kunnen geven, welke baan zou je dan aan een giraffe geven?", "no": "Hvis du kunne gi hvert dyr en jobb, hvilken jobb ville du gitt en giraff?", "sv": "Om du kunde ge varje djur ett jobb, vilket jobb skulle du ge en giraff?", "pl": "Gdybyś mógł nadać każdemu zwierzęciu pracę, jaką pracę dałbyś żyrafie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What kind of cookies do you think Santa likes the most?", "da": "Hvilken slags småkager tror du, julemanden bedst kan lide?", "de": "Welche Kekse mag der Weihnachtsmann deiner Meinung nach am liebsten?", "es": "¿Qué tipo de galletas crees que le gustan más a Santa?", "pt": "Que tipo de bolachas achas que o Pai Natal mais gosta?", "ro": "Ce fel de biscuiți crezi că îi plac cel mai mult lui Moș Crăciun?", "fr": "Quels biscuits penses-tu que le Père Noël préfère ?", "it": "Quale tipo di biscotti pensi che piaccia di più a Babbo Natale?", "fi": "Minkälaisista kekseistä luulet Joulupukin pitävän eniten?", "nl": "Welke soort koekjes denkt u dat de Kerstman het lekkerst vindt?", "no": "Hvilken type småkaker tror du julenissen liker best?", "sv": "Vilken sorts kakor tror du att Tomten gillar mest?", "pl": "Jakie ciastka myślisz, że Santa lubi najbardziej?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What’s something you think your parents work hard to do every day?", "da": "Hvad er noget, du tror, dine forældre arbejder hårdt på hver dag?", "de": "Woran arbeiten deine Eltern deiner Meinung nach jeden Tag hart?", "es": "¿En qué crees que trabajan duro tus padres cada día?", "pt": "Em que achas que os teus pais trabalham arduamente todos os dias?", "ro": "La ce crezi că muncesc din greu părinții tăi în fiecare zi?", "fr": "À quoi penses-tu que tes parents travaillent dur chaque jour ?", "it": "Qual è una cosa che pensi i tuoi genitori si impegnino a fare ogni giorno?", "fi": "Mikä on jotain, minkä uskot vanhempiesi tekevän kovasti joka päivä?", "nl": "Wat is iets waarvan je denkt dat je ouders elke dag hard voor werken?", "no": "Hva er noe du tror foreldrene dine jobber hardt for å gjøre hver dag?", "sv": "Vad är något du tror att dina föräldrar arbetar hårt för att göra varje dag?", "pl": "Co myślisz, że twoi rodzice codziennie ciężko pracują, aby to zrobić?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What is something you’re really good at?", "da": "Hvad er noget, du er virkelig god til?", "de": "Worin bist du richtig gut?", "es": "¿En qué eres realmente bueno?", "pt": "No que és realmente bom?", "ro": "La ce ești foarte bun?", "fr": "En quoi es-tu vraiment bon ?", "it": "Qual è una cosa in cui sei davvero bravo?", "fi": "Mikä on jotain, missä olet todella hyvä?", "nl": "Wat is iets waar je echt goed in bent?", "no": "Hva er noe du er virkelig god til?", "sv": "Vad är något du verkligen är bra på?", "pl": "Coś, w czym naprawdę jesteś dobry?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What do you think the Tooth Fairy does with all those teeth?", "da": "Hvad tror du, Tandfeen gør med alle de tænder?", "de": "Was glaubst du, was die Zahnfee mit all den Zähnen macht?", "es": "¿Qué crees que hace el Hada de los Dientes con todos esos dientes?", "pt": "O que achas que a Fada dos Dentes faz com todos esses dentes?", "ro": "Ce crezi că face Zâna Măseluță cu toți acei dinți?", "fr": "Que penses-tu que fait la Petite Souris avec toutes ces dents ?", "it": "Cosa pensi che faccia la Fatina dei Denti con tutti quei denti?", "fi": "Mitä mieltä olet, mitä Hammaskeiju tekee kaikilla niillä hampailla?", "nl": "Wat denk je dat de Tandfee met al die tanden doet?", "no": "Hva tror du Tandfeen gjør med alle de tennene?", "sv": "Vad tror du Tandfen gör med alla de där tänderna?", "pl": "Co myślisz, co Wróżka Zębuszka robi ze wszystkimi tymi zębami?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What do you like better — group work or working alone? Why?", "da": "Hvad kan du bedst lide — gruppearbejde eller at arbejde alene? Hvorfor?", "de": "Was magst du lieber – Gruppenarbeit oder allein arbeiten? Warum?", "es": "¿Qué prefieres: trabajar en grupo o solo? ¿Por qué?", "pt": "O que preferes — trabalhar em grupo ou sozinho? Por quê?", "ro": "Ce îți place mai mult — să lucrezi în grup sau singur? De ce?", "fr": "Que préfères-tu — le travail en groupe ou travailler seul ? Pourquoi ?", "it": "Cosa ti piace di più — lavorare in gruppo o lavorare da solo? Perché?", "fi": "Mistä pidät enemmän — ryhmätyöstä vai yksin työskentelystä? Miksi?", "nl": "Wat vind je beter — samenwerken in een groep of alleen werken? Waarom?", "no": "Hva liker du bedre — gruppearbeid eller å jobbe alene? Hvorfor?", "sv": "Vad tycker du är bättre — grupparbete eller att arbeta ensam? Varför?", "pl": "Co wolisz — pracę w grupie czy pracę samodzielną? Dlaczego?"}'::jsonb, false, true, true);
