INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "If we could swap ages for one day, what’s the first thing you’d do?", "da": "Hvis vi kunne bytte alder for en dag, hvad er det første, du ville gøre?", "de": "Wenn wir für einen Tag das Alter tauschen könnten, was würdest du als Erstes tun?", "es": "Si pudiéramos intercambiar edades por un día, ¿qué sería lo primero que harías?", "pt": "Se pudéssemos trocar de idade por um dia, o que farias primeiro?", "ro": "Dacă am putea schimba vârstele pentru o zi, care ar fi primul lucru pe care l-ai face?", "fr": "Si nous pouvions échanger nos âges pour une journée, quelle serait la première chose que tu ferais ?", "it": "Se potessimo scambiarci l''età per un giorno, qual è la prima cosa che faresti?", "fi": "Jos voisimme vaihtaa ikäämme päiväksi, mikä olisi ensimmäinen asia, jonka tekisit?", "nl": "Als we van leeftijd konden wisselen voor één dag, wat zou je dan als eerste doen?", "no": "Hvis vi kunne bytte alder i en dag, hva ville det første du gjorde vært?", "sv": "Om vi kunde byta ålder för en dag, vad skulle du göra först?", "pl": "Gdybyśmy mogli zamienić się wiekiem na jeden dzień, co byś zrobił jako pierwsze?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to do that doesn’t make sense today?", "da": "Hvad er noget, folk plejede at gøre, som ikke giver mening i dag?", "de": "Was haben die Leute früher gemacht, das heute keinen Sinn mehr ergibt?", "es": "¿Qué es algo que la gente solía hacer que hoy no tiene sentido?", "pt": "O que é algo que as pessoas costumavam fazer que hoje não faz sentido?", "ro": "Ce este ceva ce oamenii obișnuiau să facă și nu mai are sens azi?", "fr": "Quelle est une chose que les gens faisaient autrefois qui n’a plus de sens aujourd’hui ?", "it": "Qualcosa che le persone facevano in passato e che oggi non ha senso?", "fi": "Mikä on jotain, mitä ihmiset ennen tekivät, mutta mikä ei tänä päivänä enää tunnu järkevältä?", "nl": "Wat is iets dat mensen vroeger deden dat vandaag de dag geen zin meer heeft?", "no": "Hva er noe folk pleide å gjøre som ikke gir mening i dag?", "sv": "Vad är något som folk brukade göra som inte känns rimligt idag?", "pl": "Coś, co ludzie kiedyś robili, a dzisiaj nie ma sensu?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing from the past you wish would come back?", "da": "Hvad er én ting fra fortiden, du ville ønske kom tilbage?", "de": "Was ist eine Sache aus der Vergangenheit, die du dir zurückwünschst?", "es": "¿Qué cosa del pasado te gustaría que volviera?", "pt": "O que do passado gostarias que voltasse?", "ro": "Ce lucru din trecut ai vrea să revină?", "fr": "Quelle chose du passé aimerais-tu revoir revenir ?", "it": "Qual è una cosa del passato che vorresti tornasse?", "fi": "Mikä on yksi asia menneisyydestä, jonka toivoisit palaavan?", "nl": "Wat is één ding uit het verleden dat je zou willen dat het terugkwam?", "no": "Hva er én ting fra fortiden du skulle ønske kom tilbake?", "sv": "Vad är en sak från det förflutna som du önskar skulle komma tillbaka?", "pl": "Coś z przeszłości, co chciałbyś, aby wróciło?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something from today you think is much better than before?", "da": "Hvad er noget fra i dag, som du synes er meget bedre end før?", "de": "Was ist etwas von heute, das du viel besser findest als früher?", "es": "¿Qué cosa de hoy crees que es mucho mejor que antes?", "pt": "O que de hoje achas muito melhor do que antes?", "ro": "Ce lucru din prezent crezi că este mult mai bun decât înainte?", "fr": "Quelle chose d’aujourd’hui trouves-tu bien meilleure qu’avant ?", "it": "Qualcosa di oggi che pensi sia molto meglio di prima?", "fi": "Mikä on jotain tänään, mikä mielestäsi on paljon parempaa kuin ennen?", "nl": "Wat is iets van vandaag dat je denkt dat het veel beter is dan vroeger?", "no": "Hva er noe fra i dag som du mener er mye bedre enn før?", "sv": "Vad är något från idag som du tycker är mycket bättre än tidigare?", "pl": "Coś, co dzisiaj uważasz za znacznie lepsze niż wcześniej?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a household chore that used to take forever?", "da": "Hvilken husopgave tog engang evigheder at lave?", "de": "Welche Hausarbeit hat früher ewig gedauert?", "es": "¿Qué tarea doméstica solía tardar una eternidad?", "pt": "Que tarefa doméstica costumava demorar imenso tempo?", "ro": "Ce treabă casnică obișnuia să dureze o veșnicie?", "fr": "Quelle corvée domestique prenait autrefois une éternité ?", "it": "Quale faccenda domestica richiedeva un sacco di tempo?", "fi": "Mikä kotityö kesti ennen ikuisuuden?", "nl": "Wat is een huishoudelijke taak die vroeger eeuwig leek te duren?", "no": "Hva er en husarbeidoppgave som pleide å ta en evighet?", "sv": "Vilket hushållsarbete tog en evighet att göra tidigare?", "pl": "Jakie domowe obowiązki kiedyś zajmowały wieczność?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something kids today will never understand about your childhood?", "da": "Hvad er noget, børn i dag aldrig vil forstå fra din barndom?", "de": "Was werden Kinder heute nie über deine Kindheit verstehen?", "es": "¿Qué es algo de tu infancia que los niños de hoy nunca entenderán?", "pt": "O que é algo da tua infância que as crianças de hoje nunca compreenderão?", "ro": "Ce este ceva din copilăria ta pe care copiii de azi nu-l vor înțelege niciodată?", "fr": "Quelle est une chose de ton enfance que les enfants d’aujourd’hui ne comprendront jamais ?", "it": "Qualcosa che i bambini di oggi non capiranno mai della tua infanzia?", "fi": "Mikä on jotain, mitä lapset tänä päivänä eivät koskaan ymmärrä lapsuudestasi?", "nl": "Wat is iets dat kinderen van vandaag de dag nooit zullen begrijpen over jouw kindertijd?", "no": "Hva er noe barn i dag aldri vil forstå om barndommen din?", "sv": "Vad är något som barn idag aldrig kommer att förstå om din barndom?", "pl": "Coś, czego dzieci dzisiaj nigdy nie zrozumieją o twoim dzieciństwie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people had to wait a long time for, that we barely have patience for today?", "da": "Hvad er noget, folk plejede at vente længe på, som vi næsten ikke har tålmodighed til i dag?", "de": "Worauf mussten die Menschen früher lange warten, wofür wir heute kaum Geduld haben?", "es": "¿Qué era algo por lo que la gente tenía que esperar mucho tiempo, pero hoy apenas tenemos paciencia?", "pt": "O que as pessoas antigamente tinham de esperar muito tempo e hoje quase não temos paciência?", "ro": "Pentru ce trebuiau oamenii să aștepte mult timp înainte, dar azi nu mai avem răbdare?", "fr": "Pour quoi les gens devaient-ils attendre longtemps autrefois et pour lequel nous n’avons presque plus de patience aujourd’hui ?", "it": "Qualcosa per cui le persone hanno dovuto aspettare a lungo, per cui noi oggi abbiamo a malapena pazienza?", "fi": "Mikä on jotain, mitä ihmiset joutuivat odottamaan pitkään, mutta mitä meillä on nykyään tuskin kärsivällisyyttä odottaa?", "nl": "Wat is iets waar mensen vroeger lang op moesten wachten, waar we vandaag de dag nauwelijks geduld voor hebben?", "no": "Hva er noe folk måtte vente lenge på, som vi knapt har tålmodighet til i dag?", "sv": "Vad är något som folk fick vänta länge på, som vi knappt har tålamod för idag?", "pl": "Coś, na co ludzie musieli czekać długo, a na co my ledwo mamy cierpliwość dzisiaj?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that’s changed for the better in your lifetime?", "da": "Hvad er noget, der er blevet bedre i din levetid?", "de": "Was hat sich in deinem Leben zum Besseren verändert?", "es": "¿Qué cosa ha cambiado para mejor en tu vida?", "pt": "O que mudou para melhor na tua vida?", "ro": "Ce s-a schimbat în bine în timpul vieții tale?", "fr": "Qu’est-ce qui s’est amélioré au cours de ta vie ?", "it": "Qualcosa che è cambiato in meglio nella tua vita?", "fi": "Mikä on jotain, mikä on muuttunut paremmaksi elämäsi aikana?", "nl": "Wat is er in jouw leven veranderd ten goede?", "no": "Hva er noe som har endret seg til det bedre i løpet av livet ditt?", "sv": "Vad är något som har förändrats till det bättre under din livstid?", "pl": "Coś, co zmieniło się na lepsze w twoim życiu?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What was your favorite way to spend a Saturday before phones or tablets?", "da": "Hvad var din yndlingsmåde at bruge en lørdag på før telefoner og tablets?", "de": "Wie hast du samstags am liebsten Zeit verbracht, bevor es Handys und Tablets gab?", "es": "¿Cómo te gustaba pasar los sábados antes de los teléfonos y tabletas?", "pt": "Como gostavas de passar os sábados antes dos telemóveis e tablets?", "ro": "Cum îți plăcea să-ți petreci sâmbetele înainte de telefoane și tablete?", "fr": "Quelle était ta façon préférée de passer le samedi avant les téléphones et les tablettes ?", "it": "Qual era il tuo modo preferito di trascorrere un sabato prima dei telefoni o dei tablet?", "fi": "Mikä oli lempitapasi viettää lauantaita ennen puhelimia tai tabletteja?", "nl": "Wat was jouw favoriete manier om een zaterdag door te brengen voordat er telefoons of tablets waren?", "no": "Hva var din favorittmåte å tilbringe en lørdag på før telefoner eller nettbrett?", "sv": "Vad var ditt favorit sätt att spendera en lördag på innan telefoner eller surfplattor?", "pl": "Jaki był twój ulubiony sposób spędzania soboty przed pojawieniem się telefonów komórkowych lub tabletów?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "Tell me about your first date or your first big crush.", "da": "Fortæl mig om din første date eller din første store forelskelse.", "de": "Erzähl mir von deinem ersten Date oder deiner ersten großen Liebe.", "es": "Cuéntame sobre tu primera cita o tu primer gran amor.", "pt": "Conta-me sobre o teu primeiro encontro ou a tua primeira grande paixão.", "ro": "Povestește-mi despre prima ta întâlnire sau prima ta mare iubire.", "fr": "Raconte-moi ton premier rendez-vous ou ton premier grand amour.", "it": "Parlami del tuo primo appuntamento o della tua prima grande cotta.", "fi": "Kerro minulle ensimmäisestä treffeistäsi tai ensimmäisestä suuresta ihastuksestasi.", "nl": "Vertel me over je eerste date of je eerste grote verliefdheid.", "no": "Fortell meg om din første date eller din første store forelskelse.", "sv": "Berätta om din första dejt eller din första stora förälskelse.", "pl": "Opowiedz mi o swojej pierwszej randce lub swojej pierwszej wielkiej miłości."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you think was harder in “the good old days”?", "da": "Hvad tror du var sværere i “de gode gamle dage”?", "de": "Was glaubst du, war schwieriger in den „guten alten Zeiten“?", "es": "¿Qué crees que era más difícil en “los viejos tiempos”?", "pt": "O que achas que era mais difícil nos “bons velhos tempos”?", "ro": "Ce crezi că era mai greu în „vremurile bune de altădată”?", "fr": "Qu’est-ce qui était plus difficile selon toi dans “le bon vieux temps” ?", "it": "Qualcosa che pensi fosse più difficile nei \"bei vecchi tempi\"?", "fi": "Mikä on jotain, mitä ajattelet olleen vaikeampaa \"hyvinä vanhoina aikoina\"?", "nl": "Wat is iets waarvan je denkt dat het moeilijker was in \"de goede oude tijd\"?", "no": "Hva er noe du mener var vanskeligere i \"de gode gamle dager\"?", "sv": "Vad är något du tycker var svårare \"på den gamla goda tiden\"?", "pl": "Co myślisz, że było trudniejsze w \"dawnych dobrych czasach\"?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you miss about how people used to talk to each other?", "da": "Hvad savner du ved, hvordan folk plejede at tale med hinanden?", "de": "Was vermisst du daran, wie Menschen früher miteinander gesprochen haben?", "es": "¿Qué extrañas de cómo la gente solía hablar entre sí?", "pt": "O que sentes falta na forma como as pessoas costumavam conversar?", "ro": "Ce îți lipsește din felul în care oamenii obișnuiau să vorbească unii cu alții?", "fr": "Qu’est-ce qui te manque dans la façon dont les gens se parlaient autrefois ?", "it": "Qual è una cosa che ti manca di come le persone parlavano tra di loro?", "fi": "Mikä on jotain, mitä kaipaat siitä, miten ihmiset puhuivat toisilleen ennen?", "nl": "Wat is iets dat je mist aan hoe mensen vroeger met elkaar praatten?", "no": "Hva er noe du savner med hvordan folk pleide å snakke med hverandre?", "sv": "Vad är det som du saknar med hur folk brukade prata med varandra?", "pl": "Coś, co tęsknisz za tym, jak ludzie kiedyś rozmawiali ze sobą?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What was your favorite snack or drink growing up — does it still exist?", "da": "Hvad var din yndlingssnack eller drik, da du voksede op — findes den stadig?", "de": "Was war dein Lieblingssnack oder -getränk als Kind – gibt es das noch?", "es": "¿Cuál era tu merienda o bebida favorita cuando eras niño — todavía existe?", "pt": "Qual era o teu lanche ou bebida favorita em criança — ainda existe?", "ro": "Care era gustarea sau băutura ta preferată când erai mic — mai există?", "fr": "Quelle était ta collation ou boisson préférée en grandissant — existe-t-elle encore ?", "it": "Qual era il tuo snack o drink preferito da bambino — esiste ancora?", "fi": "Mikä oli lempivälipalas tai juomasi lapsena — onko se yhä olemassa?", "nl": "Wat was je favoriete snack of drankje toen je opgroeide — bestaat het nog steeds?", "no": "Hva var din favorittsnack eller drikke da du vokste opp — finnes det fortsatt?", "sv": "Vad var ditt favorit-snack eller dryck när du växte upp — finns det fortfarande?", "pl": "Jakie było twoje ulubione przekąski lub napój w dzieciństwie — czy nadal istnieje?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that’s gotten too complicated today?", "da": "Hvad er noget, der er blevet alt for kompliceret i dag?", "de": "Was ist heute viel zu kompliziert geworden?", "es": "¿Qué cosa se ha vuelto demasiado complicada hoy en día?", "pt": "O que ficou demasiado complicado hoje em dia?", "ro": "Ce lucru a devenit prea complicat azi?", "fr": "Qu’est-ce qui est devenu trop compliqué de nos jours ?", "it": "Qual è qualcosa che è diventato troppo complicato oggi?", "fi": "Mikä on jotain, mikä on tänään mennyt liian monimutkaiseksi?", "nl": "Wat is iets dat vandaag te ingewikkeld is geworden?", "no": "Hva er noe som har blitt for komplisert i dag?", "sv": "Vad är något som har blivit för komplicerat idag?", "pl": "Coś, co stało się dzisiaj zbyt skomplikowane?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something new that makes life easier now?", "da": "Hvad er noget nyt, der gør livet lettere nu?", "de": "Was ist etwas Neues, das das Leben heute einfacher macht?", "es": "¿Qué cosa nueva hace la vida más fácil ahora?", "pt": "O que há de novo que torna a vida mais fácil agora?", "ro": "Ce lucru nou face viața mai ușoară acum?", "fr": "Quelle nouveauté rend la vie plus facile aujourd’hui ?", "it": "Qualcosa di nuovo che rende la vita più facile adesso?", "fi": "Mikä on jotain uutta, mikä tekee elämästä helpompaa nyt?", "nl": "Wat is iets nieuws dat het leven nu gemakkelijker maakt?", "no": "Hva er noe nytt som gjør livet enklere nå?", "sv": "Vad är något nytt som gör livet enklare nu?", "pl": "Coś nowego, co ułatwia życie teraz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What was your first big purchase — and what did it cost?", "da": "Hvad var dit første store køb — og hvad kostede det?", "de": "Was war dein erster großer Kauf – und was hat er gekostet?", "es": "¿Cuál fue tu primera gran compra — y cuánto costó?", "pt": "Qual foi a tua primeira grande compra — e quanto custou?", "ro": "Care a fost prima ta achiziție mare — și cât a costat?", "fr": "Quel a été ton premier grand achat — et combien cela a coûté ?", "it": "Qual è stato il tuo primo grande acquisto — e quanto è costato?", "fi": "Mikä oli ensimmäinen iso ostoksesi — ja mitä se maksoi?", "nl": "Wat was je eerste grote aankoop — en wat kostte het?", "no": "Hva var ditt første store kjøp — og hva kostet det?", "sv": "Vad var ditt första stora köp — och vad kostade det?", "pl": "Jakie było twoje pierwsze duże zakupy — i ile to kosztowało?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "Tell me about a time when you had to be really brave.", "da": "Fortæl mig om en tid, hvor du måtte være virkelig modig.", "de": "Erzähl mir von einer Zeit, in der du sehr mutig sein musstest.", "es": "Cuéntame de una vez que tuviste que ser muy valiente.", "pt": "Conta-me sobre uma vez em que tiveste de ser muito corajoso.", "ro": "Povestește-mi despre o dată când a trebuit să fii foarte curajos.", "fr": "Raconte-moi un moment où tu as dû être vraiment courageux.", "it": "Raccontami di un momento in cui hai dovuto essere davvero coraggioso.", "fi": "Kerro minulle ajasta, jolloin sinun piti olla todella rohkea.", "nl": "Vertel me over een moment waarop je echt moedig moest zijn.", "no": "Fortell meg om en gang da du måtte være virkelig modig.", "sv": "Berätta om en gång när du var tvungen att vara riktigt modig.", "pl": "Opowiedz mi o czasie, kiedy musiałeś być naprawdę odważny."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What kind of toys or games were popular when you were little?", "da": "Hvilke legetøj eller spil var populære, da du var lille?", "de": "Welche Spielzeuge oder Spiele waren beliebt, als du klein warst?", "es": "¿Qué juguetes o juegos eran populares cuando eras pequeño?", "pt": "Que brinquedos ou jogos eram populares quando eras pequeno?", "ro": "Ce jucării sau jocuri erau populare când erai mic?", "fr": "Quels jouets ou jeux étaient populaires quand tu étais petit ?", "it": "Quali tipi di giocattoli o giochi erano popolari quando eri piccolo?", "fi": "Mitä leluja tai pelejä oli suosittuja, kun olit pieni?", "nl": "Welke soorten speelgoed of spellen waren populair toen je klein was?", "no": "Hvilke typer leker eller spill var populære da du var liten?", "sv": "Vilka typer av leksaker eller spel var populära när du var liten?", "pl": "Jakie zabawki lub gry były popularne, gdy byłeś mały?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What did “having fun” look like before video games or TV?", "da": "Hvordan så “at have det sjovt” ud, før videospil eller tv fandtes?", "de": "Wie sah „Spaß haben“ aus, bevor es Videospiele oder Fernsehen gab?", "es": "¿Cómo era “divertirse” antes de los videojuegos o la televisión?", "pt": "Como era “divertir-se” antes dos videojogos ou da televisão?", "ro": "Cum arăta „distracția” înainte de jocurile video sau televiziune?", "fr": "À quoi ressemblait “s’amuser” avant les jeux vidéo ou la télévision ?", "it": "Com''era \"divertirsi\" prima dei videogiochi o della TV?", "fi": "Miltä \"hauskanpito\" näytti ennen videopelejä tai televisiota?", "nl": "Hoe zag \"plezier hebben\" eruit voordat er videospellen of tv waren?", "no": "Hvordan så \"å ha det moro\" ut før videospill eller TV?", "sv": "Hur såg \"att ha roligt\" ut innan videospel eller TV?", "pl": "Jak wyglądała zabawa przed pojawieniem się gier wideo lub telewizji?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you remember breaking and having to fix by hand?", "da": "Hvad er noget, du husker at have ødelagt og måtte reparere i hånden?", "de": "Was erinnerst du dich, kaputt gemacht und von Hand repariert zu haben?", "es": "¿Qué recuerdas haber roto y tenido que arreglar a mano?", "pt": "O que te lembras de ter partido e consertado à mão?", "ro": "Ce îți amintești că ai stricat și a trebuit să repari cu mâna?", "fr": "De quoi te souviens-tu avoir cassé et dû réparer à la main ?", "it": "Qualcosa che ricordi di aver rotto e di aver dovuto riparare a mano?", "fi": "Mikä on jotain, jonka muistat rikkoneesi ja joutuneesi korjaamaan käsin?", "nl": "Wat is iets dat je je herinnert dat je hebt gebroken en met de hand moest repareren?", "no": "Hva er noe du husker at du brakk og måtte fikse for hånd?", "sv": "Vad är något du minns att du bröt och var tvungen att laga för hand?", "pl": "Coś, co pamiętasz, że zepsułeś i musiałeś naprawić ręcznie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you learned to live without back then that everyone has now?", "da": "Hvad er noget, du lærte at leve uden dengang, som alle har nu?", "de": "Was hast du gelernt, früher ohne zu leben, was heute jeder hat?", "es": "¿Qué aprendiste a vivir sin tener antes, que ahora todos tienen?", "pt": "O que aprendeste a viver sem, que agora todos têm?", "ro": "Ce ai învățat să trăiești fără atunci, dar toată lumea are acum?", "fr": "De quoi as-tu appris à te passer autrefois que tout le monde a maintenant ?", "it": "Qualcosa che hai imparato a vivere senza all''epoca che tutti hanno adesso?", "fi": "Mikä on jotain, mitä opit elämään ilman silloin, mutta mitä kaikilla on nyt?", "nl": "Wat is iets waar je toen leerde zonder te leven dat iedereen nu heeft?", "no": "Hva er noe du lærte å leve uten den gang som alle har nå?", "sv": "Vad är något du lärde dig att leva utan då som alla har nu?", "pl": "Coś, czego nauczyłeś się żyć bez wtedy, co teraz ma każdy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What was shopping like before online stores?", "da": "Hvordan var det at handle, før der fandtes onlinebutikker?", "de": "Wie war Einkaufen, bevor es Online-Shops gab?", "es": "¿Cómo era ir de compras antes de las tiendas en línea?", "pt": "Como era fazer compras antes das lojas online?", "ro": "Cum era cumpărăturile înainte de magazinele online?", "fr": "Comment faisait-on du shopping avant les magasins en ligne ?", "it": "Com''era fare shopping prima dei negozi online?", "fi": "Millainen ostaminen oli ennen verkkokauppoja?", "nl": "Hoe was winkelen voordat er online winkels waren?", "no": "Hvordan var shopping før nettbutikker?", "sv": "Hur var shopping innan nätbutiker?", "pl": "Jak wyglądały zakupy przed pojawieniem się sklepów internetowych?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "Tell me about how you met the love of your life.", "da": "Fortæl mig, hvordan du mødte kærligheden i dit liv.", "de": "Erzähl mir, wie du die Liebe deines Lebens getroffen hast.", "es": "Cuéntame cómo conociste al amor de tu vida.", "pt": "Conta-me como conheceste o amor da tua vida.", "ro": "Spune-mi cum ți-ai întâlnit dragostea vieții tale.", "fr": "Raconte-moi comment tu as rencontré l’amour de ta vie.", "it": "Raccontami di come hai incontrato l''amore della tua vita.", "fi": "Kerro minulle, miten tapasit elämäsi rakkauden.", "nl": "Vertel me hoe je de liefde van je leven hebt ontmoet.", "no": "Fortell meg om hvordan du møtte kjærligheten i livet ditt.", "sv": "Berätta om hur du träffade kärleken i ditt liv.", "pl": "Opowiedz mi, jak poznałeś miłość swojego życia."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
