INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "Tell me about a moment that changed the direction of your life.", "da": "Fortæl mig om et øjeblik, der ændrede retningen i dit liv.", "de": "Erzähl mir von einem Moment, der die Richtung deines Lebens verändert hat.", "es": "Cuéntame sobre un momento que cambió el rumbo de tu vida.", "pt": "Conta-me sobre um momento que mudou a direção da tua vida.", "ro": "Spune-mi despre un moment care ți-a schimbat direcția vieții.", "fr": "Raconte-moi un moment qui a changé la direction de ta vie.", "it": "Raccontami di un momento che ha cambiato la direzione della tua vita.", "fi": "Kerro minulle hetkestä, joka muutti elämäsi suuntaa.", "nl": "Vertel me over een moment dat de richting van je leven heeft veranderd.", "no": "Fortell meg om et øyeblikk som endret retningen i livet ditt.", "sv": "Berätta om ett ögonblick som förändrade riktningen i ditt liv.", "pl": "Opowiedz mi o momencie, który zmienił kierunek twojego życia."}'::jsonb, false, true, true);
