INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people collected before photos lived on phones?", "da": "Hvad samlede folk på, før billeder levede på telefoner?", "de": "Was sammelten die Leute, bevor Fotos auf Handys gespeichert wurden?", "es": "¿Qué coleccionaba la gente antes de que las fotos vivieran en los teléfonos?", "pt": "O que as pessoas colecionavam antes de as fotos viverem nos telemóveis?", "ro": "Ce colecționau oamenii înainte ca fotografiile să fie pe telefoane?", "fr": "Que collectionnaient les gens avant que les photos vivent sur les téléphones ?", "it": "Qualcosa che le persone collezionavano prima che le foto vivessero sui telefoni?", "fi": "Mitä asioita ihmiset keräsivät ennen kuin valokuvat olivat puhelimissa?", "nl": "Wat is iets dat mensen verzamelden voordat foto''s op telefoons stonden?", "no": "Hva er noe folk samlet på før bilder ble lagret på telefoner?", "sv": "Vad är något som folk samlade på innan foton fanns på telefoner?", "pl": "Coś, co ludzie zbierali, zanim zdjęcia zaczęły być przechowywane w telefonach?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you remember hearing on the radio all the time?", "da": "Hvad kan du huske, du hørte i radioen hele tiden?", "de": "Was erinnerst du dich, ständig im Radio gehört zu haben?", "es": "¿Qué recuerdas haber escuchado en la radio todo el tiempo?", "pt": "O que te lembras de ouvir na rádio o tempo todo?", "ro": "Ce îți amintești că auzeai mereu la radio?", "fr": "Que te souviens-tu d’avoir entendu tout le temps à la radio ?", "it": "Qualcosa che ricordi di aver sentito alla radio tutto il tempo?", "fi": "Mikä on jotain, mitä muistat kuulleesi radiosta koko ajan?", "nl": "Wat is iets dat je je herinnert dat je vaak op de radio hoorde?", "no": "Hva er noe du husker å ha hørt på radioen hele tiden?", "sv": "Vad är något du minns att du hörde på radion hela tiden?", "pl": "Coś, co pamiętasz, że słyszałeś w radiu cały czas?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you had to memorize because you couldn’t just look it up?", "da": "Hvad skulle du lære udenad, fordi du ikke bare kunne slå det op?", "de": "Was musstest du auswendig lernen, weil du es nicht einfach nachschlagen konntest?", "es": "¿Qué tenías que memorizar porque no podías simplemente buscarlo?", "pt": "O que tinhas de memorizar porque não podias simplesmente procurar?", "ro": "Ce trebuia să memorezi pentru că nu puteai doar să cauți?", "fr": "Qu’est-ce que tu devais mémoriser parce que tu ne pouvais pas simplement le chercher ?", "it": "Qualcosa che hai dovuto memorizzare perché non potevi semplicemente cercarlo?", "fi": "Mikä on jotain, jonka jouduit muistamaan, koska et voinut vain etsiä sitä?", "nl": "Wat is iets dat je moest onthouden omdat je het niet gewoon kon opzoeken?", "no": "Hva er noe du måtte lære deg utenat fordi du ikke kunne bare slå det opp?", "sv": "Vad är något du var tvungen att memorera eftersom du inte bara kunde slå upp det?", "pl": "Coś, co musiałeś zapamiętać, ponieważ nie mogłeś po prostu tego znaleźć?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you think technology has made less personal?", "da": "Hvad tror du, teknologien har gjort mindre personlig?", "de": "Was hat die Technologie deiner Meinung nach unpersönlicher gemacht?", "es": "¿Qué crees que la tecnología ha hecho menos personal?", "pt": "O que achas que a tecnologia tornou menos pessoal?", "ro": "Ce crezi că a făcut tehnologia mai puțin personal?", "fr": "Qu’est-ce que la technologie a rendu moins personnel selon toi ?", "it": "Qualcosa che pensi la tecnologia abbia reso meno personale?", "fi": "Mikä on jotain, jonka ajattelet teknologian tehneen vähemmän henkilökohtaiseksi?", "nl": "Wat is iets waarvan je denkt dat technologie het minder persoonlijk heeft gemaakt?", "no": "Hva er noe du mener at teknologi har gjort mindre personlig?", "sv": "Vad är något som du tycker att teknologin har gjort mindre personligt?", "pl": "Coś, co myślisz, że technologia uczyniła mniej osobistym?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing from today you wish had existed long ago?", "da": "Hvad er én ting fra i dag, du ville ønske havde eksisteret for længe siden?", "de": "Was ist eine Sache von heute, die du dir schon lange gewünscht hättest?", "es": "¿Qué cosa de hoy desearías que hubiera existido hace mucho tiempo?", "pt": "O que de hoje gostarias que tivesse existido há muito tempo?", "ro": "Ce lucru din prezent ți-ai fi dorit să existe de mult?", "fr": "Quelle chose d’aujourd’hui aurais-tu aimé qu’elle existe il y a longtemps ?", "it": "Qual è una cosa di oggi che avresti voluto esistesse tanto tempo fa?", "fi": "Mikä on yksi asia tänään, jonka toivoisit olevan olemassa jo kauan sitten?", "nl": "Wat is één ding van vandaag dat je had gewild dat het lang geleden al bestond?", "no": "Hva er én ting fra i dag du skulle ønske hadde eksistert for lenge siden?", "sv": "Vad är en sak från idag som du önskar hade funnits för länge sedan?", "pl": "Coś, co dzisiaj istnieje, co chciałbyś, żeby istniało już dawno temu?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that cost only a few coins when you were young?", "da": "Hvad kostede kun få mønter, da du var ung?", "de": "Was hat nur ein paar Münzen gekostet, als du jung warst?", "es": "¿Qué costaba solo unas pocas monedas cuando eras joven?", "pt": "O que custava apenas umas moedas quando eras jovem?", "ro": "Ce costa doar câteva monede când erai tânăr?", "fr": "Qu’est-ce qui ne coûtait que quelques pièces quand tu étais jeune ?", "it": "Qualcosa che costava solo qualche moneta quando eri giovane?", "fi": "Mikä on jotain, joka maksoi vain muutaman kolikon, kun olit nuori?", "nl": "Wat is iets dat maar een paar muntjes kostte toen je jong was?", "no": "Hva er noe som kostet bare noen få mynter da du var ung?", "sv": "Vad var något som kostade bara några mynt när du var ung?", "pl": "Coś, co kosztowało tylko kilka monet, kiedy byłeś młody?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something about “the good old days” that actually wasn’t so good?", "da": "Hvad ved “de gode gamle dage” var faktisk ikke så godt?", "de": "Was an den „guten alten Zeiten“ war eigentlich nicht so gut?", "es": "¿Qué de “los viejos tiempos” en realidad no era tan bueno?", "pt": "O que dos “bons velhos tempos” afinal não era assim tão bom?", "ro": "Ce era în „vremurile bune de altădată” care de fapt nu era așa grozav?", "fr": "Qu’est-ce qui, dans “le bon vieux temps”, n’était pas si bien que ça ?", "it": "Qualcosa dei \"bei tempi andati\" che in realtà non era poi così buono?", "fi": "Mikä on jotain \"hyvistä vanhoista päivistä\", joka ei oikeastaan ollut niin hyvää?", "nl": "Wat is iets aan \"de goede oude tijd\" dat eigenlijk niet zo goed was?", "no": "Hva er noe med \"de gode gamle dager\" som faktisk ikke var så bra?", "sv": "Vad är något om \"de goda gamla dagarna\" som faktiskt inte var så bra?", "pl": "Coś, co w \"dawnych dobrych czasach\" nie było aż tak dobre?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people worried about back then that seems funny now?", "da": "Hvad bekymrede folk sig om dengang, som virker sjovt nu?", "de": "Worum haben sich die Leute damals Sorgen gemacht, was heute lustig erscheint?", "es": "¿De qué se preocupaba la gente antes que ahora parece gracioso?", "pt": "Com o que as pessoas se preocupavam antes e que agora parece engraçado?", "ro": "De ce își făceau griji oamenii atunci, dar acum pare amuzant?", "fr": "De quoi les gens s’inquiétaient-ils autrefois et qui paraît drôle aujourd’hui ?", "it": "Qual è qualcosa di cui le persone si preoccupavano all''epoca che ora sembra divertente?", "fi": "Mikä on jotain, mistä ihmiset olivat huolissaan silloin, mikä tuntuu nyt hassulta?", "nl": "Wat is iets waar mensen zich toen zorgen over maakten dat nu grappig lijkt?", "no": "Hva er noe folk bekymret seg for den gang som virker morsomt nå?", "sv": "Vad är något som folk oroade sig för då som verkar lustigt nu?", "pl": "Coś, co ludzie się wtedy martwili, a teraz wydaje się zabawne?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a rule your parents had that nobody follows anymore?", "da": "Hvad var en regel, dine forældre havde, som ingen følger mere?", "de": "Welche Regel hatten deine Eltern, die heute niemand mehr befolgt?", "es": "¿Qué regla tenían tus padres que ya nadie sigue?", "pt": "Que regra tinham os teus pais que já ninguém segue?", "ro": "Ce regulă aveau părinții tăi pe care nimeni nu o mai respectă azi?", "fr": "Quelle règle tes parents avaient-ils que plus personne ne suit maintenant ?", "it": "Qual è una regola che i tuoi genitori avevano e che nessuno segue più?", "fi": "Mikä on sääntö, jonka vanhempasi asettivat, mutta jota kukaan ei enää noudata?", "nl": "Wat is een regel die je ouders hadden die niemand meer volgt?", "no": "Hva er en regel foreldrene dine hadde som ingen følger lenger?", "sv": "Vilken regel hade dina föräldrar som ingen följer längre?", "pl": "Jakie zasady mieli twoi rodzice, których nikt już nie przestrzega?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a piece of advice people said all the time when you were growing up?", "da": "Hvad var et råd, folk altid sagde, da du voksede op?", "de": "Welchen Ratschlag haben die Leute ständig gegeben, als du aufgewachsen bist?", "es": "¿Qué consejo decía la gente todo el tiempo cuando crecías?", "pt": "Que conselho as pessoas diziam sempre quando cresceste?", "ro": "Ce sfat spuneau oamenii mereu când erai copil?", "fr": "Quel conseil les gens donnaient-ils tout le temps quand tu grandissais ?", "it": "Qual è un consiglio che le persone dicevano sempre quando eri giovane?", "fi": "Mikä on neuvo, jota ihmiset sanoivat koko ajan, kun kasvoit?", "nl": "Wat is een advies dat mensen vaak zeiden toen je opgroeide?", "no": "Hva er et råd folk sa hele tiden da du vokste opp?", "sv": "Vilket råd sa folk hela tiden när du växte upp?", "pl": "Jaką radę ludzie często powtarzali, gdy dorastałeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people did for fun without spending money?", "da": "Hvad gjorde folk for sjov uden at bruge penge?", "de": "Was haben die Leute zum Spaß gemacht, ohne Geld auszugeben?", "es": "¿Qué hacía la gente para divertirse sin gastar dinero?", "pt": "O que as pessoas faziam para se divertir sem gastar dinheiro?", "ro": "Ce făceau oamenii pentru distracție fără să cheltuiască bani?", "fr": "Que faisaient les gens pour s’amuser sans dépenser d’argent ?", "it": "Qual è una cosa che le persone facevano per divertirsi senza spendere soldi?", "fi": "Mikä on jotain, mitä ihmiset tekivät hauskaa ilman, että he käyttivät rahaa?", "nl": "Wat is iets dat mensen deden voor plezier zonder geld uit te geven?", "no": "Hva er noe folk gjorde for moro skyld uten å bruke penger?", "sv": "Vad är något som folk gjorde för skojs skull utan att spendera pengar?", "pl": "Coś, co ludzie robili dla zabawy, nie wydając pieniędzy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you remember from road trips before GPS?", "da": "Hvad husker du fra bilture før GPS fandtes?", "de": "Was erinnerst du dich an Autofahrten, bevor es GPS gab?", "es": "¿Qué recuerdas de los viajes por carretera antes del GPS?", "pt": "O que te lembras das viagens de carro antes do GPS?", "ro": "Ce îți amintești din călătoriile cu mașina înainte de GPS?", "fr": "Que te rappelles-tu des voyages en voiture avant le GPS ?", "it": "Qual è una cosa che ricordi dei viaggi in auto prima del GPS?", "fi": "Mikä on jotain, mitä muistat automatkoilta ennen GPS:ää?", "nl": "Wat is iets wat je je herinnert van roadtrips voordat er GPS was?", "no": "Hva er noe du husker fra bilturer før GPS?", "sv": "Vad är något du minns från bilresor innan GPS fanns?", "pl": "Coś, co pamiętasz z podróży samochodowych sprzed czasów GPS?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to repair instead of replacing?", "da": "Hvad plejede folk at reparere i stedet for at erstatte?", "de": "Was haben die Leute früher repariert, anstatt es zu ersetzen?", "es": "¿Qué solían reparar las personas antes en lugar de reemplazarlo?", "pt": "O que as pessoas consertavam antes em vez de substituir?", "ro": "Ce obișnuiau oamenii să repare înainte în loc să înlocuiască?", "fr": "Que réparaient les gens autrefois au lieu de remplacer ?", "it": "Qualcosa che le persone riparavano invece di sostituire?", "fi": "Mikä on jotain, mitä ihmiset ennen korjasivat sen sijaan, että olisivat vaihtaneet?", "nl": "Wat is iets dat mensen vroeger repareerden in plaats van het te vervangen?", "no": "Hva er noe folk pleide å reparere i stedet for å erstatte?", "sv": "Vad är något som folk brukade laga istället för att byta ut?", "pl": "Coś, co ludzie kiedyś naprawiali zamiast wymieniać?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you used to write by hand that’s typed now?", "da": "Hvad plejede du at skrive i hånden, som nu bliver skrevet på computer?", "de": "Was hast du früher von Hand geschrieben, das jetzt getippt wird?", "es": "¿Qué solías escribir a mano que ahora se escribe a máquina?", "pt": "O que costumavas escrever à mão que agora é digitado?", "ro": "Ce scriai de mână înainte și acum tastezi?", "fr": "Que écrivais-tu autrefois à la main et qui est tapé maintenant ?", "it": "Qualcosa che scrivevi a mano e che ora digiti?", "fi": "Mikä on jotain, mitä kirjoitit aiemmin käsin, mutta joka on nyt kirjoitettu tietokoneella?", "nl": "Wat is iets dat je vroeger met de hand schreef en nu getypt is?", "no": "Hva er noe du pleide å skrive for hånd som nå er skrevet på datamaskin?", "sv": "Vad är något du brukade skriva för hand som nu är typat?", "pl": "Coś, co kiedyś pisałeś ręcznie, a teraz piszesz na komputerze?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a fashion trend you actually miss?", "da": "Hvilken modetrend savner du faktisk?", "de": "Welchen Modetrend vermisst du wirklich?", "es": "¿Qué tendencia de moda realmente extrañas?", "pt": "Que tendência de moda realmente sentes falta?", "ro": "Ce tendință de modă chiar îți lipsește?", "fr": "Quelle tendance de mode te manque vraiment ?", "it": "Qual è una tendenza di moda che ti manca davvero?", "fi": "Mikä muotitrendi on sellainen, jota oikeasti kaipaat?", "nl": "Welke mode trend mis je eigenlijk?", "no": "Hvilken motetrend savner du faktisk?", "sv": "Vilken modetrend saknar du faktiskt?", "pl": "Jaką modową tendencję rzeczywiście tęsknisz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that made childhood simpler — or tougher — back then?", "da": "Hvad gjorde barndommen enklere — eller sværere — dengang?", "de": "Was machte die Kindheit damals einfacher – oder schwieriger?", "es": "¿Qué hacía que la infancia fuera más fácil — o más difícil — en ese entonces?", "pt": "O que tornava a infância mais simples — ou mais difícil — naquela altura?", "ro": "Ce făcea copilăria mai ușoară — sau mai grea — atunci?", "fr": "Qu’est-ce qui rendait l’enfance plus simple — ou plus difficile — à l’époque ?", "it": "Qualcosa che ha reso l''infanzia più semplice — o più difficile — all''epoca?", "fi": "Mikä oli jotain, mikä teki lapsuudesta yksinkertaisempaa — tai vaikeampaa — silloin?", "nl": "Wat is iets dat de kindertijd toen eenvoudiger — of moeilijker — maakte?", "no": "Hva er noe som gjorde barndommen enklere — eller tøffere — den gangen?", "sv": "Vad var något som gjorde barndomen enklare — eller svårare — då?", "pl": "Coś, co sprawiło, że dzieciństwo było prostsze — lub trudniejsze — w tamtych czasach?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing you think the older generation did better than today’s?", "da": "Hvad tror du, den ældre generation gjorde bedre end i dag?", "de": "Was glaubst du, hat die ältere Generation besser gemacht als heute?", "es": "¿Qué crees que la generación mayor hacía mejor que la de hoy?", "pt": "O que achas que a geração mais velha fazia melhor do que a de hoje?", "ro": "Ce crezi că făcea generația mai veche mai bine decât cea de azi?", "fr": "Que penses-tu que l’ancienne génération faisait mieux que celle d’aujourd’hui ?", "it": "Qual è una cosa che pensi che la generazione precedente facesse meglio di quella di oggi?", "fi": "Mikä on yksi asia, jonka ajattelet vanhemman sukupolven tehneen paremmin kuin nykyajan?", "nl": "Wat is één ding waarvan je denkt dat de oudere generatie dat beter deed dan vandaag de dag?", "no": "Hva er én ting du mener den eldre generasjonen gjorde bedre enn dagens?", "sv": "Vad är en sak som du tycker att den äldre generationen gjorde bättre än dagens?", "pl": "Co uważasz, że starsze pokolenie robiło lepiej niż dzisiejsze?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing today’s generation definitely does better than the older?", "da": "Hvad er én ting, som dagens generation helt sikkert gør bedre end den ældre?", "de": "Was ist eine Sache, die die heutige Generation definitiv besser macht als die ältere?", "es": "¿Qué es algo que la generación actual definitivamente hace mejor que la anterior?", "pt": "O que é algo que a geração atual faz melhor do que a antiga?", "ro": "Ce este ceva ce generația de azi face cu siguranță mai bine decât cea veche?", "fr": "Quelle chose la génération d’aujourd’hui fait-elle définitivement mieux que l’ancienne ?", "it": "Qual è una cosa che la generazione di oggi fa sicuramente meglio delle generazioni precedenti?", "fi": "Mikä on yksi asia, jonka tämän päivän sukupolvi ehdottomasti tekee paremmin kuin vanhempi sukupolvi?", "nl": "Wat is één ding dat de huidige generatie zeker beter doet dan de oudere?", "no": "Hva er én ting dagens generasjon definitivt gjør bedre enn den eldre?", "sv": "Vad är en sak som dagens generation definitivt gör bättre än den äldre?", "pl": "Coś, co dzisiejsze pokolenie zdecydowanie robi lepiej niż starsze?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people did for entertainment before everyone had screens?", "da": "Hvad gjorde folk for underholdning, før alle havde skærme?", "de": "Was haben die Leute zur Unterhaltung gemacht, bevor alle Bildschirme hatten?", "es": "¿Qué hacía la gente para entretenerse antes de que todos tuvieran pantallas?", "pt": "O que as pessoas faziam para se entreter antes de todos terem ecrãs?", "ro": "Ce făceau oamenii pentru divertisment înainte ca toți să aibă ecrane?", "fr": "Que faisaient les gens pour se divertir avant que tout le monde ait des écrans ?", "it": "Qual è una cosa che le persone facevano per divertirsi prima che tutti avessero degli schermi?", "fi": "Mitä ihmiset tekivät viihdykkeeksi ennen kuin kaikilla oli näyttöjä?", "nl": "Wat deden mensen voor vermaak voordat iedereen schermen had?", "no": "Hva var noe folk gjorde for underholdning før alle hadde skjermer?", "sv": "Vad gjorde folk för underhållning innan alla hade skärmar?", "pl": "Co ludzie robili dla rozrywki, zanim wszyscy mieli ekrany?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing you think was safer back then — and one thing that wasn’t?", "da": "Hvad tror du, var sikrere dengang — og hvad var det ikke?", "de": "Was glaubst du, war damals sicherer – und was nicht?", "es": "¿Qué crees que era más seguro antes — y qué no lo era?", "pt": "O que achas que era mais seguro antes — e o que não era?", "ro": "Ce crezi că era mai sigur înainte — și ce nu era?", "fr": "Qu’est-ce qui, selon toi, était plus sûr autrefois — et quoi ne l’était pas ?", "it": "Qual è una cosa che pensi fosse più sicura allora — e una cosa che non lo era?", "fi": "Mikä on yksi asia, jonka ajattelet olleen turvallisempaa ennen — ja yksi asia, joka ei ollut?", "nl": "Wat is één ding dat je denkt dat veiliger was toen — en één ding dat dat niet was?", "no": "Hva er én ting du mener var tryggere før — og én ting som ikke var det?", "sv": "Vad är en sak som du tycker var säkrare då — och en sak som inte var det?", "pl": "Co według ciebie było jedną rzeczą, która była bezpieczniejsza w tamtych czasach — a co nie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to plan weeks ahead that we do last-minute now?", "da": "Hvad plejede folk at planlægge uger i forvejen, som vi nu gør i sidste øjeblik?", "de": "Was haben die Leute früher wochenlang im Voraus geplant, das wir jetzt in letzter Minute machen?", "es": "¿Qué planificaban las personas con semanas de anticipación que ahora hacemos a último momento?", "pt": "O que as pessoas costumavam planear semanas antes e agora fazemos em cima da hora?", "ro": "Ce planificau oamenii cu săptămâni înainte, dar acum facem pe ultima sută de metri?", "fr": "Que planifiaient autrefois les gens des semaines à l’avance et que nous faisons à la dernière minute maintenant ?", "it": "Qualcosa che le persone pianificavano settimane in anticipo e che ora facciamo all''ultimo minuto?", "fi": "Mikä on jotain, mitä ihmiset ennen suunnittelivat viikkoja etukäteen, mutta teemme nyt viime hetkellä?", "nl": "Wat is iets waar mensen vroeger weken van tevoren plannen voor maakten, maar wat we nu op het laatste moment doen?", "no": "Hva er noe folk pleide å planlegge uker i forveien, som vi nå gjør i siste liten?", "sv": "Vad är något som folk brukade planera veckor i förväg som vi gör i sista minuten nu?", "pl": "Coś, co ludzie kiedyś planowali z wyprzedzeniem, a teraz robimy na ostatnią chwilę?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that used to last longer back then, compared to nowadays?", "da": "Hvad plejede at holde længere dengang, sammenlignet med nu?", "de": "Was hat früher länger gehalten als heute?", "es": "¿Qué duraba más antes comparado con ahora?", "pt": "O que durava mais antes comparado com agora?", "ro": "Ce ținea mai mult înainte, comparativ cu acum?", "fr": "Qu’est-ce qui durait plus longtemps autrefois comparé à aujourd’hui ?", "it": "Qual è qualcosa che durava di più una volta, rispetto ai giorni nostri?", "fi": "Mikä on jotain, mikä kesti aikaisemmin pidempään verrattuna nykypäivään?", "nl": "Wat is iets dat vroeger langer meeging dan tegenwoordig?", "no": "Hva er noe som pleide å vare lenger før, sammenlignet med i dag?", "sv": "Vad är något som brukade hålla längre förr i tiden, jämfört med idag?", "pl": "Coś, co kiedyś trwało dłużej w porównaniu do dzisiaj?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing from the “good old days” you’d want the next generation to experience?", "da": "Hvad er én ting fra “de gode gamle dage”, du gerne vil have, at næste generation oplever?", "de": "Was aus den „guten alten Zeiten“ würdest du der nächsten Generation wünschen zu erleben?", "es": "¿Qué cosa de “los viejos tiempos” quisieras que experimentara la próxima generación?", "pt": "O que dos “bons velhos tempos” gostarias que a próxima geração experimentasse?", "ro": "Ce lucru din „vremurile bune” ai vrea să-l trăiască și următoarea generație?", "fr": "Quelle chose du “bon vieux temps” aimerais-tu que la prochaine génération connaisse ?", "it": "Qual è una cosa dei \"bei tempi andati\" che vorresti che la prossima generazione sperimentasse?", "fi": "Mikä on yksi asia \"hyviltä vanhoilta ajoilta\", jonka haluaisit seuraavan sukupolven kokevan?", "nl": "Wat is één ding uit de \"goede oude tijd\" dat je de volgende generatie zou willen laten ervaren?", "no": "Hva er én ting fra \"de gode gamle dager\" du ville ønsket at neste generasjon skulle oppleve?", "sv": "Vad är en sak från de \"gamla goda dagarna\" som du skulle vilja att nästa generation får uppleva?", "pl": "Coś, co z \"dobrych starych czasów\" chciałbyś, aby następne pokolenie doświadczyło?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to argue about that no one cares about today?", "da": "Hvad plejede folk at skændes om, som ingen bekymrer sig om nu?", "de": "Worüber haben sich die Leute früher gestritten, was heute niemanden mehr interessiert?", "es": "¿De qué discutía la gente antes que hoy a nadie le importa?", "pt": "Sobre o que as pessoas discutiam antes que hoje ninguém se importa?", "ro": "Despre ce se certau oamenii înainte și acum nu mai pasă nimănui?", "fr": "De quoi les gens se disputaient-ils autrefois et dont tout le monde se fiche aujourd’hui ?", "it": "Qualcosa su cui le persone litigavano un tempo e di cui oggi a nessuno importa?", "fi": "Mikä on jotain, mistä ihmiset aiemmin riitelivät, mutta josta kukaan ei välitä tänään?", "nl": "Wat is iets waar mensen vroeger over discussieerden, maar waar tegenwoordig niemand zich meer druk om maakt?", "no": "Hva er noe folk pleide å krangle om som ingen bryr seg om i dag?", "sv": "Vad är något som folk brukade bråka om men som ingen bryr sig om idag?", "pl": "O co ludzie kiedyś się kłócili, a dziś nikogo to nie obchodzi?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
