INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Talks', '{"en": "What’s a memory of us, you believe will still make you smile when you’re 90?", "da": "Hvilket minde om os tror du stadig vil få dig til at smile, når du er 90?", "de": "Welche Erinnerung an uns wird dich deiner Meinung nach noch zum Lächeln bringen, wenn du 90 bist?", "es": "¿Qué recuerdo nuestro crees que todavía te hará sonreír cuando tengas 90 años?", "pt": "Qual lembrança nossa você acredita que ainda vai fazer você sorrir quando tiver 90 anos?", "ro": "Care este o amintire cu noi despre care crezi că încă te va face să zâmbești când vei avea 90 de ani?", "fr": "Quel souvenir de nous crois-tu te fera encore sourire quand tu auras 90 ans ?", "it": "Qual è un ricordo di noi che pensi ti farà ancora sorridere quando avrai 90 anni?", "fi": "Mikä muisto meistä saa sinut edelleen hymyilemään, kun olet 90-vuotias?", "nl": "Wat is een herinnering aan ons waarvan je denkt dat die je nog steeds zal laten glimlachen als je 90 bent?", "no": "Hva er et minne om oss som du tror fortsatt vil få deg til å smile når du er 90?", "sv": "Vilket minne av oss tror du fortfarande kommer få dig att le när du är 90?", "pl": "Jakie wspomnienie o nas, twoim zdaniem, nadal sprawi, że się uśmiechniesz, gdy będziesz miał 90 lat?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
