INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Love Talks', '{"en": "What helps you open up when you’re feeling closed or isolated?", "da": "Hvad hjælper dig med at åbne op, når du føler dig lukket eller isoleret?", "de": "Was hilft dir, dich zu öffnen, wenn du dich verschlossen oder isoliert fühlst?", "es": "¿Qué te ayuda a abrirte cuando te sientes cerrado/a o aislado/a?", "pt": "O que te ajuda a abrir-te quando te sentes fechado/a ou isolado/a?", "ro": "Ce te ajută să te deschizi când te simți închis(ă) sau izolat(ă)?", "fr": "Qu’est-ce qui t’aide à t’ouvrir quand tu te sens fermé(e) ou isolé(e) ?", "it": "Cosa ti aiuta ad aprirti quando ti senti chiuso o isolato?", "fi": "Mikä auttaa sinua avautumaan, kun tunnet itsesi sulkeutuneeksi tai eristyneeksi?", "nl": "Wat helpt jou om je open te stellen als je je gesloten of geïsoleerd voelt?", "no": "Hva hjelper deg med å åpne deg når du føler deg lukket eller isolert?", "sv": "Vad hjälper dig att öppna upp när du känner dig stängd eller isolerad?", "pl": "Co pomaga ci się otworzyć, gdy czujesz się zamknięty lub odizolowany?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
