INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If our car could talk, what do you think it would say about us?", "da": "Hvis vores bil kunne tale, hvad tror du, den ville sige om os?", "de": "Wenn unser Auto sprechen könnte, was glaubst du, würde es über uns sagen?", "es": "Si nuestro coche pudiera hablar, ¿qué crees que diría sobre nosotros?", "pt": "Se o nosso carro pudesse falar, o que achas que ele diria sobre nós?", "ro": "Dacă mașina noastră ar putea vorbi, ce crezi că ar spune despre noi?", "fr": "Si notre voiture pouvait parler, que penses-tu qu’elle dirait de nous ?", "it": "Se la nostra macchina potesse parlare, cosa pensi direbbe di noi?", "fi": "Jos meidän automme osaisi puhua, mitä luulet sen sanovan meistä?", "nl": "Als onze auto kon praten, wat denk je dat hij over ons zou zeggen?", "no": "Hvis bilen vår kunne snakke, hva tror du den ville sagt om oss?", "sv": "Om vår bil kunde prata, vad tror du att den skulle säga om oss?", "pl": "Gdyby nasz samochód umiał mówić, co twoim zdaniem powiedziałby o nas?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "Have you ever seen wildlife up close that amazed you? (the zoo doesn''t count)", "da": "Har du nogensinde set vilde dyr helt tæt på, som virkelig imponerede dig? (Zoo tæller ikke)", "de": "Hast du schon einmal Wildtiere aus der Nähe gesehen, die dich total fasziniert haben? (Der Zoo zählt nicht)", "es": "¿Has visto alguna vez animales salvajes de cerca que te dejaran alucinado/a? (El zoológico no cuenta)", "pt": "Você já viu algum animal selvagem de pertinho que te deixou maravilhado(a)? (Zoológico não vale)", "ro": "Ai văzut vreodată animale sălbatice de aproape care te-au uimit? (Grădina zoologică nu se pune)", "fr": "As-tu déjà vu des animaux sauvages de près qui t’ont vraiment impressionné(e) ? (Le zoo, ça ne compte pas)", "it": "Hai mai visto da vicino un animale selvatico che ti ha lasciato a bocca aperta? (lo zoo non vale)", "fi": "Oletko koskaan nähnyt villieläimiä läheltä niin, että ne tekivät sinuun vaikutuksen? (eläintarha ei kelpaa)", "nl": "Heb je ooit wilde dieren van heel dichtbij gezien die je echt hebben verbaasd? (de dierentuin telt niet)", "no": "Har du noen gang sett ville dyr på nært hold som virkelig imponerte deg? (dyreparken teller ikke)", "sv": "Har du någonsin sett vilda djur på nära håll som verkligen imponerade på dig? (zoo räknas inte)", "pl": "Czy kiedykolwiek widziałeś/widziałaś dzikie zwierzęta z bliska, które cię zachwyciły? (zoo się nie liczy)"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If we had a van-life year, what’s our route?", "da": "Hvis vi havde et “van-life” år, hvad ville vores rute være?", "de": "Wenn wir ein Van-Life-Jahr hätten, wie sähe unsere Route aus?", "es": "Si tuviéramos un año de vida en furgoneta, ¿cuál sería nuestra ruta?", "pt": "Se tivéssemos um ano de “van life”, qual seria nossa rota?", "ro": "Dacă am avea un an de “van life”, care ar fi ruta noastră?", "fr": "Si on passait un an à vivre en van, quel serait notre itinéraire ?", "it": "Se facessimo un anno da van-life, quale sarebbe il nostro percorso?", "fi": "Jos pitäisimme yhden “van life” -vuoden, mikä olisi meidän reitti?", "nl": "Als we een jaar vanlife zouden doen, wat is dan onze route?", "no": "Hvis vi hadde et vanlife-år, hvilken rute skulle vi kjørt?", "sv": "Om vi hade ett vanlife-år, vilken skulle vår rutt vara?", "pl": "Gdybyśmy mieli rok życia w vanie, jaką trasę byśmy obrali?"}'::jsonb, false, true, true);
