INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What’s one thing that instantly puts you in a good mood, no matter what?", "da": "Hvad får dig straks i godt humør, uanset hvad?", "de": "Was bringt dich sofort in gute Laune, egal was passiert?", "es": "¿Qué cosa te pone instantáneamente de buen humor, sin importar qué?", "pt": "O que te põe imediatamente de bom humor, aconteça o que acontecer?", "ro": "Ce te binedispune instantaneu, indiferent de situație?", "fr": "Qu’est-ce qui te met instantanément de bonne humeur, peu importe quoi ?", "it": "Qual è una cosa che ti mette subito di buon umore, a prescindere da tutto?", "fi": "Mikä yksi juttu tekee sinut heti hyvälle tuulelle, tilanteesta riippumatta?", "nl": "Wat is één ding dat je meteen in een goed humeur brengt, wat er ook gebeurt?", "no": "Hva er én ting som alltid får deg i godt humør, uansett?", "sv": "Vad är en sak som direkt gör dig på gott humör, oavsett vad?", "pl": "Co jest jedną rzeczą, która natychmiast poprawia ci humor, niezależnie od wszystkiego?"}'::jsonb, false, true, true);
