INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What’s our optimal roles on a long drive (e.g. captain, DJ, snack provider, etc)?", "da": "Hvad er vores optimale roller på en lang køretur (f.eks. kaptajn, DJ, snack-ansvarlig osv.)?", "de": "Was sind unsere optimalen Rollen auf einer langen Fahrt (z. B. Kapitän, DJ, Snacklieferant usw.)?", "es": "¿Cuáles son nuestros roles ideales en un viaje largo (por ejemplo, capitán, DJ, proveedor de snacks, etc.)?", "pt": "Quais são os nossos papéis ideais numa longa viagem (por exemplo, capitão, DJ, fornecedor de snacks, etc.)?", "ro": "Care sunt rolurile noastre ideale într-o călătorie lungă (ex: căpitan, DJ, furnizor de gustări etc.)?", "fr": "Quels sont nos rôles idéaux lors d’un long trajet (ex : capitaine, DJ, fournisseur de snacks, etc.) ?", "it": "Quali sarebbero i nostri ruoli ideali in un lungo viaggio in auto (es. capitano, DJ, addetto agli snack, ecc.)?", "fi": "Mitkä ovat meille parhaat roolit pitkällä ajomatkalla (esim. kapteeni, DJ, välipalavastaava jne.)?", "nl": "Wat zijn onze ideale rollen tijdens een lange autorit (bijv. kapitein, DJ, snack-leverancier, etc.)?", "no": "Hva er våre optimale roller på en lang biltur (f.eks. kaptein, DJ, snacksansvarlig osv.)?", "sv": "Vilka är våra optimala roller på en lång bilresa (t.ex. kapten, DJ, snacksansvarig osv.)?", "pl": "Jakie są dla nas idealne role podczas długiej jazdy autem (np. kapitan, DJ, dostawca przekąsek itp.)?"}'::jsonb, false, true, true);
