INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "What’s the strangest or funniest dream you’ve ever had?", "da": "Hvad er den mærkeligste eller sjoveste drøm, du nogensinde har haft?", "de": "Was ist der seltsamste oder lustigste Traum, den du je hattest?", "es": "¿Cuál es el sueño más extraño o divertido que has tenido?", "pt": "Qual foi o sonho mais estranho ou engraçado que já tiveste?", "ro": "Care este cel mai ciudat sau amuzant vis pe care l-ai avut vreodată?", "fr": "Quel est le rêve le plus étrange ou le plus drôle que tu aies jamais fait ?", "it": "Qual è il sogno più strano o più divertente che tu abbia mai fatto?", "fi": "Mikä on oudoin tai hauskin uni, jonka olet koskaan nähnyt?", "nl": "Wat is de vreemdste of grappigste droom die je ooit hebt gehad?", "no": "Hva er den rareste eller morsomste drømmen du har hatt?", "sv": "Vad är den konstigaste eller roligaste dröm du någonsin haft?", "pl": "Jaki jest najdziwniejszy albo najzabawniejszy sen, jaki kiedykolwiek miałeś/miałaś?"}'::jsonb, false, true, true);
