INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Car Talks', '{"en": "If you could time-travel to one specific meal in your life and eat it again, which one would it be ?", "da": "Hvis du kunne rejse tilbage i tiden til ét specifikt måltid i dit liv og spise det igen, hvilket ville det være?", "de": "Wenn du zu einem bestimmten Essen in deinem Leben zurückreisen und es erneut essen könntest, welches wäre das?", "es": "Si pudieras viajar en el tiempo a una comida específica de tu vida y volver a comerla, ¿cuál sería?", "pt": "Se pudesses viajar no tempo até a uma refeição específica da tua vida e comê-la novamente, qual seria?", "ro": "Dacă ai putea călători în timp la o anumită masă din viața ta și să o mănânci din nou, care ar fi?", "fr": "Si tu pouvais voyager dans le temps vers un repas précis de ta vie et le manger à nouveau, lequel serait-ce ?", "it": "Se potessi viaggiare nel tempo a un pasto specifico della tua vita per mangiarlo di nuovo, quale sceglieresti?", "fi": "Jos voisit aikamatkustaa yhteen tiettyyn ateriaan elämässäsi ja syödä sen uudelleen, mikä se olisi?", "nl": "Als je terug in de tijd kon gaan naar één specifieke maaltijd in je leven om die opnieuw te eten, welke zou dat zijn?", "no": "Hvis du kunne tidsreise til ett bestemt måltid i livet ditt og spise det igjen, hvilket ville du valgt?", "sv": "Om du kunde tidsresa till en specifik måltid i ditt liv och äta den igen, vilken skulle du välja?", "pl": "Gdybyś mógł/mogła cofnąć się w czasie do jednego konkretnego posiłku w swoim życiu i zjeść go jeszcze raz, który byś wybrał(a)?"}'::jsonb, false, true, true);
