VALUES ('Do-you-dare Talks', '{"en": "What’s one thing you like about my family, and one thing that’s hard sometimes?", "da": "Hvad er én ting, du kan lide ved min familie — og én ting, der nogle gange er svær?", "de": "Hvem af os tror du mister fokus hurtigere, når flirten starter?", "es": "Wer von uns verliert schneller den Fokus, wenn das Flirten beginnt?", "pt": "O que gostas na minha família — e o que às vezes é difícil?", "ro": "Ce îți place la familia mea și ce e uneori dificil?", "fr": "Qu’aimes-tu chez ma famille — et qu’est-ce qui est parfois difficile ?", "it": "Qual è una cosa che ti piace della mia famiglia e una cosa che a volte è difficile?", "fi": "Mikä on yksi asia, josta pidät perheessäni, ja yksi asia, joka on joskus vaikeaa?", "nl": "Wat is één ding dat je leuk vindt aan mijn familie, en één ding dat soms moeilijk is?", "no": "Hva er én ting du liker med familien min, og én ting som kan være vanskelig noen ganger?", "sv": "Vad är en sak du gillar med min familj, och en sak som kan vara svår ibland?", "pl": "Coś, co lubisz w mojej rodzinie, i coś, co czasami bywa trudne?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Who from your past would make you a little nervous to bump into today — and why?", "da": "Hvem fra din fortid ville gøre dig lidt nervøs, hvis du mødte dem i dag — og hvorfor?", "de": "Hvem af os kan bedst lide at have kontrol?", "es": "Wer von uns mag es mehr, die Kontrolle zu haben?", "pt": "Quem do teu passado te deixaria nervoso se o encontrasses hoje — e porquê?", "ro": "Cine din trecutul tău te-ar face puțin nervos dacă l-ai întâlni azi — și de ce?", "fr": "Qui de ton passé te rendrait un peu nerveux si tu le croisais aujourd’hui — et pourquoi ?", "it": "Chi del tuo passato ti farebbe un po'' nervoso incontrare oggi — e perché?", "fi": "Kuka menneisyydestäsi saisi sinut tuntemaan itsesi hieman hermostuneeksi, jos kohtaisit hänet tänään — ja miksi?", "nl": "Wie uit je verleden zou je een beetje nerveus maken om vandaag tegen te komen — en waarom?", "no": "Hvem fra fortiden din ville gjort deg litt nervøs for å møte i dag — og hvorfor?", "sv": "Vem från ditt förflutna skulle göra dig lite nervös att stöta på idag — och varför?", "pl": "Kto z twojej przeszłości sprawiłby, że poczułbyś się trochę nerwowo, gdybyś go dzisiaj spotkał — i dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you feel about how we each handle money?", "da": "Hvordan har du det med, hvordan vi hver især håndterer penge?", "de": "Hvem af os er mere tilbøjelig til at snige en flirtende besked i løbet af dagen?", "es": "Wer von uns schickt eher eine flirtende Nachricht während des Tages?", "pt": "Como te sentes em relação à forma como cada um de nós lida com o dinheiro?", "ro": "Cum te simți legat de felul în care fiecare dintre noi gestionează banii?", "fr": "Que ressens-tu à propos de notre manière respective de gérer l’argent ?", "it": "Come ti senti riguardo al modo in cui ciascuno di noi gestisce il denaro?", "fi": "Mitä mieltä olet siitä, miten me kumpikin käsittelemme rahaa?", "nl": "Hoe voel je je over hoe wij elk met geld omgaan?", "no": "Hvordan føler du om hvordan vi hver for oss håndterer penger?", "sv": "Hur känner du för hur vi var och en hanterar pengar?", "pl": "Jak się czujesz w kwestii tego, jak każdy z nas radzi sobie z pieniędzmi?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What topic do you think couples should talk about before committing long-term?", "da": "Hvilket emne synes du, par bør tale om, før de forpligter sig langsigtet?", "de": "Hvem af os ville starte et \"fem minutter\"-øjeblik, der bliver til en time?", "es": "Wer von uns würde ein „fünf Minuten“-Moment beginnen, das zu einer Stunde wird?", "pt": "Que assunto achas que os casais deviam falar antes de se comprometerem a longo prazo?", "ro": "Despre ce subiect crezi că ar trebui să discute cuplurile înainte de a se angaja pe termen lung?", "fr": "Quel sujet les couples devraient-ils aborder avant de s’engager à long terme ?", "it": "Quale argomento pensi che le coppie dovrebbero discutere prima di impegnarsi a lungo termine?", "fi": "Mitä aiheita luulet parien pitäisi käsitellä ennen pitkän aikavälin sitoutumista?", "nl": "Over welk onderwerp denk je dat stellen moeten praten voordat ze zich op de lange termijn binden?", "no": "Hvilket tema mener du at par bør snakke om før de forplikter seg på lang sikt?", "sv": "Vilket ämne tycker du att par bör prata om innan de förbinder sig på lång sikt?", "pl": "O jakim temacie uważasz, że pary powinny porozmawiać przed podjęciem długoterminowego zobowiązania?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Have you ever loved two people at the same time?", "da": "Har du nogensinde elsket to personer på samme tid?", "de": "Hvad er din yndlingsmåde for mig at vise, at jeg higer efter dig?", "es": "Wie magst du es am liebsten, wenn ich dir zeige, dass ich dich begehre?", "pt": "Já amaste duas pessoas ao mesmo tempo?", "ro": "Ai iubit vreodată două persoane în același timp?", "fr": "As-tu déjà aimé deux personnes en même temps ?", "it": "Hai mai amato due persone contemporaneamente?", "fi": "Oletko koskaan rakastanut kahta ihmistä samaan aikaan?", "nl": "Heb je ooit twee mensen tegelijk liefgehad?", "no": "Har du noen gang elsket to personer samtidig?", "sv": "Har du någonsin älskat två personer samtidigt?", "pl": "Czy kiedykolwiek kochałeś dwie osoby jednocześnie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What do you think makes conversations about politics or religion go wrong?", "da": "Hvad tror du gør samtaler om politik eller religion så svære?", "de": "Hvor vigtig er øjenkontakt for dig, når vi er tætte?", "es": "Wie wichtig ist dir Augenkontakt, wenn wir uns nah sind?", "pt": "Porque achas que conversas sobre política ou religião correm mal?", "ro": "De ce crezi că discuțiile despre politică sau religie merg prost?", "fr": "Pourquoi penses-tu que les conversations sur la politique ou la religion tournent mal ?", "it": "Cosa pensi faccia andare male le conversazioni su politica o religione?", "fi": "Mitä mieltä olet, mikä saa keskustelut politiikasta tai uskonnosta menemään pieleen?", "nl": "Wat denk je dat gesprekken over politiek of religie verkeerd laten lopen?", "no": "Hva tror du gjør at samtaler om politikk eller religion går galt?", "sv": "Vad tycker du gör att samtal om politik eller religion går fel?", "pl": "Co twoim zdaniem sprawia, że rozmowy na temat polityki lub religii idą źle?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s a fear or insecurity about the future that you rarely talk about?", "da": "Hvilken frygt eller usikkerhed om fremtiden taler du sjældent om?", "de": "Hvad er din yndlingsform for leg?", "es": "Was ist deine Lieblingsform des Spiels?", "pt": "Que medo ou insegurança sobre o futuro raramente falas?", "ro": "Ce frică sau nesiguranță legată de viitor discuți rar?", "fr": "Quelle peur ou insécurité concernant l’avenir évoques-tu rarement ?", "it": "Qual è una paura o un''insicurezza riguardo al futuro di cui parli raramente?", "fi": "Mikä on pelko tai epävarmuus tulevaisuudesta, josta harvoin puhut?", "nl": "Wat is een angst of onzekerheid over de toekomst waar je zelden over praat?", "no": "Hva er en frykt eller usikkerhet om fremtiden som du sjelden snakker om?", "sv": "Vilken rädsla eller osäkerhet om framtiden pratar du sällan om?", "pl": "Jakie jest twoje lęk lub niepewność dotycząca przyszłości, o którym rzadko rozmawiasz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s the biggest trouble you ever got into — and what did it teach you?", "da": "Hvad er den største ballade, du nogensinde har været i — og hvad lærte du af det?", "de": "Hvad betyder \"erfaring\" for dig i sengen – færdigheder, selvtillid, nysgerrighed?", "es": "Was bedeutet „Erfahrung“ für dich im Bett – Können, Selbstvertrauen, Neugier?", "pt": "Qual foi o maior sarilho em que já te meteste — e o que aprendeste com isso?", "ro": "Care e cea mai mare belea în care ai fost — și ce ai învățat din ea?", "fr": "Quelle est la plus grosse galère dans laquelle tu sois tombé — et qu’en as-tu appris ?", "it": "Qual è stato il problema più grande in cui sei mai cacciato — e cosa ti ha insegnato?", "fi": "Mikä on suurin ongelma, johon olet koskaan joutunut — ja mitä se opetti sinulle?", "nl": "Wat is het grootste probleem waar je ooit in terecht bent gekomen — en wat heeft het je geleerd?", "no": "Hva er det største problemet du noen gang har havnet i – og hva lærte det deg?", "sv": "Vad är det största problemet du någonsin hamnat i — och vad lärde det dig?", "pl": "Jakie było największe kłopoty, w jakie kiedykolwiek się wpakowałeś — i czego cię to nauczyło?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "When you think about your past relationships, what stands out most — what you gave or what you lost?", "da": "Når du tænker på dine tidligere forhold, hvad står mest ud — det, du gav, eller det, du mistede?", "de": "Hvad er én ting, du gerne vil lave med mig, som du aldrig har turdet nævne før?", "es": "Was ist eine Sache, die du gerne mit mir tun würdest, dich aber nie getraut hast zu sagen?", "pt": "Quando pensas nas tuas relações passadas, o que se destaca mais — o que deste ou o que perdeste?", "ro": "Când te gândești la relațiile tale anterioare, ce iese cel mai mult în evidență — ce ai oferit sau ce ai pierdut?", "fr": "Quand tu penses à tes relations passées, qu’est-ce qui te marque le plus — ce que tu as donné ou perdu ?", "it": "Quando pensi alle tue relazioni passate, cosa ti colpisce di più — ciò che hai dato o ciò che hai perso?", "fi": "Kun ajattelet menneitä suhteitasi, mikä nousee eniten esiin — se, mitä annoit vai se, mitä menetit?", "nl": "Wanneer je aan je vorige relaties denkt, wat springt er dan het meest in het oog - wat je hebt gegeven of wat je hebt verloren?", "no": "Når du tenker på dine tidligere forhold, hva står mest ut - hva du ga eller hva du mistet?", "sv": "När du tänker på dina tidigare relationer, vad står ut mest — vad du gav eller vad du förlorade?", "pl": "Kiedy myślisz o swoich przeszłych związkach, co najbardziej się wyróżnia — to, co dałeś, czy to, co straciłeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Do you think people today are too easily offended or not careful enough with words?", "da": "Synes du, folk i dag bliver for let fornærmede eller ikke er forsigtige nok med ord?", "de": "Er der en side af dig, jeg stadig ikke har låst op for endnu?", "es": "Gibt es eine Seite von dir, die ich noch nicht entdeckt habe?", "pt": "Achas que as pessoas hoje se ofendem com demasiada facilidade ou não têm cuidado com as palavras?", "ro": "Crezi că oamenii astăzi se simt jigniți prea ușor sau nu sunt suficient de atenți cu vorbele?", "fr": "Penses-tu que les gens aujourd’hui sont trop facilement offensés ou pas assez prudents avec leurs mots ?", "it": "Pensi che le persone di oggi si offendano troppo facilmente o che non siano abbastanza attente con le parole?", "fi": "Luuleeko, että ihmiset tänä päivänä loukkaantuvat liian helposti vai eivät ole tarpeeksi varovaisia sanojensa kanssa?", "nl": "Denk je dat mensen vandaag de dag te snel beledigd zijn of niet voorzichtig genoeg met woorden omgaan?", "no": "Tror du folk i dag blir for lett fornærmet, eller er de ikke forsiktige nok med ordene?", "sv": "Tycker du att folk idag är för lättkränkta eller inte tillräckligt försiktiga med sina ord?", "pl": "Czy uważasz, że ludzie dzisiaj są zbyt łatwo obrażani, czy może nieostrożni w doborze słów?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you feel about earning more or less than your partner?", "da": "Hvordan har du det med at tjene mere eller mindre end din partner?", "de": "Hvilket af dine begær føles stadig \"forbudt\", selvom det tænder dig?", "es": "Welche deiner Begierden fühlt sich immer noch „verboten“ an, obwohl sie dich erregt?", "pt": "Como te sentes por ganhar mais ou menos do que o teu parceiro?", "ro": "Cum te simți dacă câștigi mai mult sau mai puțin decât partenerul tău?", "fr": "Que ressens-tu à l’idée de gagner plus ou moins que ton partenaire ?", "it": "Come ti senti riguardo a guadagnare di più o di meno rispetto al tuo partner?", "fi": "Miltä sinusta tuntuu ansaita enemmän tai vähemmän kuin kumppanisi?", "nl": "Hoe voel je je over het verdienen van meer of minder dan je partner?", "no": "Hvordan føler du om å tjene mer eller mindre enn partneren din?", "sv": "Hur känner du för att tjäna mer eller mindre än din partner?", "pl": "Jak się czujesz w związku z tym, że zarabiasz więcej lub mniej niż twój partner?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What do you think your exes might say they learned from you?", "da": "Hvad tror du, dine eks’er ville sige, de lærte af dig?", "de": "Hvad får et øjeblik til at føles dejligt forkert, men umuligt at modstå?", "es": "Was lässt einen Moment schön falsch, aber unwiderstehlich fühlen?", "pt": "O que achas que os teus ex diriam que aprenderam contigo?", "ro": "Ce crezi că ar spune foștii tăi că au învățat de la tine?", "fr": "Que penses-tu que tes ex diraient avoir appris de toi ?", "it": "Cosa pensi che i tuoi ex possano dire di aver imparato da te?", "fi": "Mitä luulet, mitä entiset kumppanisi voisivat sanoa oppineensa sinulta?", "nl": "Wat denk je dat je exen zouden zeggen dat ze van jou hebben geleerd?", "no": "Hva tror du eksene dine ville sagt at de lærte av deg?", "sv": "Vad tror du att dina ex kan säga att de har lärt sig av dig?", "pl": "Co myślisz, co twoje byłe osoby mogłyby powiedzieć, że się od ciebie nauczyły?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something you sometimes judge others for — but also see a little of in yourself?", "da": "Hvad er noget, du nogle gange dømmer andre for — men også ser lidt af i dig selv?", "de": "Hvilken slags hemmelighed mellem os ville du gerne beholde for evigt?", "es": "Welche Art von Geheimnis zwischen uns würdest du gerne für immer behalten?", "pt": "O que julgas às vezes nos outros mas também vês em ti?", "ro": "Ce este ceva ce judeci la alții, dar recunoști și în tine?", "fr": "Quelle chose juges-tu parfois chez les autres mais reconnais-tu un peu chez toi ?", "it": "Qual è qualcosa per cui a volte giudichi gli altri, ma che vedi anche un po'' in te stesso?", "fi": "Mikä on jotain, jota joskus arvostelet muissa — mutta näet myös siitä pienen osan itsessäsi?", "nl": "Wat is iets waar je anderen soms op beoordeelt, maar waar je ook een beetje van in jezelf ziet?", "no": "Hva er noe du noen ganger dømmer andre for — men også ser litt av i deg selv?", "sv": "Vad är något du ibland dömer andra för — men också ser lite av i dig själv?", "pl": "Coś, za co czasami oceniam innych — ale również dostrzegam to trochę w sobie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something you learned about yourself from your last relationship?", "da": "Hvad har du lært om dig selv fra dit sidste forhold?", "de": "Hvad er det første, dine øjne går efter, når jeg begynder at klæde mig af?", "es": "Wohin gehen deine Augen zuerst, wenn ich beginne, mich auszuziehen?", "pt": "O que aprendeste sobre ti mesmo com o teu último relacionamento?", "ro": "Ce ai învățat despre tine din ultima ta relație?", "fr": "Qu’as-tu appris sur toi-même grâce à ta dernière relation ?", "it": "Qualcosa che hai imparato su te stesso dalla tua ultima relazione?", "fi": "Mitä olet oppinut itsestäsi edellisestä suhteestasi?", "nl": "Wat is iets dat je over jezelf hebt geleerd van je laatste relatie?", "no": "Hva er noe du lærte om deg selv fra ditt forrige forhold?", "sv": "Vad är något du lärde dig om dig själv från ditt senaste förhållande?", "pl": "Coś, czego się nauczyłeś o sobie z ostatniego związku?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "When was a time jealousy actually taught you something about yourself?", "da": "Hvornår har jalousi faktisk lært dig noget om dig selv?", "de": "Hvilken smag ville du gerne smage på mine læber?", "es": "Welchen Geschmack würdest du gerne auf meinen Lippen schmecken?", "pt": "Quando é que o ciúme realmente te ensinou algo sobre ti próprio?", "ro": "Când te-a învățat gelozia ceva despre tine?", "fr": "Quand la jalousie t’a-t-elle réellement appris quelque chose sur toi-même ?", "it": "Quando è stata una volta in cui la gelosia ti ha insegnato qualcosa su te stesso?", "fi": "Milloin kateus oikeasti opetti sinulle jotain itsestäsi?", "nl": "Wanneer was een moment waarop jaloezie je daadwerkelijk iets over jezelf heeft geleerd?", "no": "Når var det en gang sjalusi faktisk lærte deg noe om deg selv?", "sv": "När var en tid då svartsjuka faktiskt lärde dig något om dig själv?", "pl": "Kiedy zazdrość nauczyła cię czegoś o sobie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something your best friend knows about you that I don’t — yet?", "da": "Hvad ved din bedste ven om dig, som jeg ikke gør — endnu?", "de": "Hvad ville du ønske, jeg gjorde oftere under forspil?", "es": "Was würdest du dir wünschen, dass ich beim Vorspiel öfter tue?", "pt": "O que o teu melhor amigo sabe sobre ti que eu ainda não sei?", "ro": "Ce știe cel mai bun prieten al tău despre tine, dar eu nu?", "fr": "Qu’est-ce que ton meilleur ami sait sur toi que je ne sais pas — encore ?", "it": "Qualcosa che il tuo migliore amico sa di te che io non so — ancora?", "fi": "Mikä on jotain, mitä paras ystäväsi tietää sinusta, mutta minä en — vielä?", "nl": "Wat is iets dat je beste vriend(in) over jou weet dat ik nog niet weet?", "no": "Hva er noe din beste venn vet om deg som jeg ikke vet — ennå?", "sv": "Vad är det som din bästa vän vet om dig som jag inte vet — än?", "pl": "Coś, co twój najlepszy przyjaciel wie o tobie, a ja jeszcze nie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s the most impulsive or reckless thing you’ve ever done just for the thrill?", "da": "Hvad er det mest impulsive eller hensynsløse, du nogensinde har gjort bare for spændingens skyld?", "de": "Hvilken slags belysning eller atmosfære tænder dig mest?", "es": "Welche Art von Licht oder Atmosphäre erregt dich am meisten?", "pt": "Qual foi a coisa mais impulsiva ou imprudente que já fizeste só pela emoção?", "ro": "Care este cel mai impulsiv sau nechibzuit lucru pe care l-ai făcut doar pentru adrenalină?", "fr": "Quelle est la chose la plus impulsive ou imprudente que tu aies faite juste pour le frisson ?", "it": "Qual è la cosa più impulsiva o avventata che hai mai fatto solo per il brivido?", "fi": "Mikä on impulsiivisin tai holtittomin asia, jonka olet koskaan tehnyt vain jännityksen vuoksi?", "nl": "Wat is het meest impulsieve of roekeloze dat je ooit hebt gedaan, gewoon voor de spanning?", "no": "Hva er det mest impulsive eller hensynsløse du noen gang har gjort bare for spenningen?", "sv": "Vad är det mest impulsiva eller riskfyllda du någonsin har gjort bara för spänningens skull?", "pl": "Jaką najbardziej impulsywną lub lekkomyślną rzecz kiedykolwiek zrobiłeś tylko dla dreszczyku emocji?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What does financial independence mean to you in a relationship?", "da": "Hvad betyder økonomisk uafhængighed for dig i et forhold?", "de": "Hvordan kan du lide, at jeg viser dig, at jeg er i humør til det?", "es": "Wie magst du es, wenn ich dir zeige, dass ich in Stimmung bin?", "pt": "O que significa independência financeira para ti num relacionamento?", "ro": "Ce înseamnă independența financiară pentru tine într-o relație?", "fr": "Que signifie l’indépendance financière pour toi dans une relation ?", "it": "Cosa significa per te l''indipendenza finanziaria in una relazione?", "fi": "Mitä taloudellinen itsenäisyys merkitsee sinulle suhteessa?", "nl": "Wat betekent financiële onafhankelijkheid voor jou in een relatie?", "no": "Hva betyr økonomisk uavhengighet for deg i et forhold?", "sv": "Vad betyder ekonomisk oberoende för dig i en relation?", "pl": "Co dla Ciebie oznacza niezależność finansowa w związku?"}'::jsonb, false, true, true);
