INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Are you friends with your exes on social media? Why/why not?", "da": "Er du venner med dine ekser på sociale medier? Hvorfor/hvorfor ikke?", "de": "Hvad er din personlige definition af \"forførende\"?", "es": "Was ist deine persönliche Definition von „verführerisch“?", "pt": "És amigo dos teus ex nas redes sociais? Porquê / porquê não?", "ro": "Ești prieten cu foștii tăi pe rețelele sociale? De ce / de ce nu?", "fr": "Es-tu ami avec tes ex sur les réseaux sociaux ? Pourquoi / pourquoi pas ?", "it": "Sei amico dei tuoi ex sui social media? Perché sì o perché no?", "fi": "Oletko ystäviä entisten kumppanien kanssa sosiaalisessa mediassa? Miksi/miksi ei?", "nl": "Ben je bevriend met je exen op sociale media? Waarom wel/niet?", "no": "Er du venn med eksene dine på sosiale medier? Hvorfor/hvorfor ikke?", "sv": "Är du vänner med dina ex på sociala medier? Varför/varför inte?", "pl": "Czy jesteś w przyjaźni z byłymi na mediach społecznościowych? Dlaczego/dlaczego nie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Who’s the last person you looked up online just out of curiosity — and why?", "da": "Hvem er den sidste person, du slog op på online bare af nysgerrighed — og hvorfor?", "de": "Hvis nogen ville lære præcis, hvordan de kunne behage dig, hvad er så det første, de skulle være opmærksomme på?", "es": "Wenn jemand genau lernen wollte, wie man dich befriedigt – worauf sollte er als Erstes achten?", "pt": "Quem foi a última pessoa que pesquisaste online por curiosidade — e porquê?", "ro": "Cine e ultima persoană pe care ai căutat-o online din curiozitate — și de ce?", "fr": "Quelle est la dernière personne que tu as cherchée en ligne par curiosité — et pourquoi ?", "it": "Chi è stata l''ultima persona che hai cercato online per curiosità — e perché?", "fi": "Kuka oli viimeinen henkilö, jota etsit verkosta uteliaisuudesta — ja miksi?", "nl": "Wie is de laatste persoon die je online hebt opgezocht uit nieuwsgierigheid — en waarom?", "no": "Hvem var den siste personen du søkte opp på nettet bare av nysgjerrighet — og hvorfor?", "sv": "Vem var den senaste personen du sökte på nätet av ren nyfikenhet — och varför?", "pl": "Kto był ostatnią osobą, którą wyszukałeś w Internecie z ciekawości — i dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "When’s the last time you got caught in a small lie — and what did you learn from it?", "da": "Hvornår blev du sidst fanget i en lille løgn — og hvad lærte du af det?", "de": "Er der noget, du gerne vil have, at jeg kalder dig, når vi har sex?", "es": "Gibt es etwas, wie ich dich nennen soll, wenn wir Sex haben?", "pt": "Quando foi a última vez que foste apanhado numa pequena mentira — e o que aprendeste?", "ro": "Când ai fost prins ultima dată într-o mică minciună — și ce ai învățat din asta?", "fr": "Quand t’a-t-on pris pour la dernière fois dans un petit mensonge — et qu’en as-tu appris ?", "it": "Quando è stata l''ultima volta che sei stato sorpreso in una piccola bugia — e cosa hai imparato da essa?", "fi": "Milloin viimeksi jouduit kiinni pienestä valheesta — ja mitä opit siitä?", "nl": "Wanneer was de laatste keer dat je betrapt werd op een klein leugentje — en wat heb je daaruit geleerd?", "no": "Når var siste gang du ble tatt i en liten løgn — og hva lærte du av det?", "sv": "När var senaste gången du blev tagen på en liten lögn — och vad lärde du dig av det?", "pl": "Kiedy ostatni raz wpadłeś w małe kłamstwo — i czego się z tego nauczyłeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Have you ever kept someone in your life longer than you should have?", "da": "Har du nogensinde beholdt nogen i dit liv længere, end du burde have?", "de": "Hast du jemals jemanden länger in deinem Leben behalten, als du solltest?", "es": "¿Alguna vez mantuviste a alguien en tu vida más tiempo del que deberías?", "pt": "Já mantiveste alguém na tua vida por mais tempo do que devias?", "ro": "Ai ținut vreodată pe cineva în viața ta mai mult decât trebuia?", "fr": "As-tu déjà gardé quelqu’un dans ta vie plus longtemps que tu n’aurais dû ?", "it": "Hai mai tenuto qualcuno nella tua vita più a lungo di quanto avresti dovuto?", "fi": "Oletko koskaan pitänyt jotakuta elämässäsi pidempään kuin olisi pitänyt?", "nl": "Heb je ooit iemand langer in je leven gehouden dan je eigenlijk zou moeten?", "no": "Har du noen gang hatt noen i livet ditt lenger enn du burde?", "sv": "Har du någonsin haft någon i ditt liv längre än du borde?", "pl": "Czy kiedykolwiek trzymałeś kogoś w swoim życiu dłużej, niż powinieneś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "How do you handle it when you disagree deeply with someone you love?", "da": "Hvordan håndterer du det, når du er uenig med nogen, du elsker?", "de": "Wie gehst du damit um, wenn du mit jemandem, den du liebst, tief uneinig bist?", "es": "¿Cómo manejas el estar en desacuerdo con alguien a quien amas?", "pt": "Como lidas quando discordas profundamente de alguém que amas?", "ro": "Cum gestionezi o neînțelegere profundă cu cineva pe care îl iubești?", "fr": "Comment gères-tu un désaccord profond avec quelqu’un que tu aimes ?", "it": "Come gestisci la situazione quando non sei d''accordo profondamente con qualcuno che ami?", "fi": "Miten toimit, kun olet syvästi eri mieltä jonkun rakastamasi kanssa?", "nl": "Hoe ga je ermee om als je diep van mening verschilt met iemand van wie je houdt?", "no": "Hvordan håndterer du det når du er dypt uenig med noen du elsker?", "sv": "Hur hanterar du det när du har djupa meningsskiljaktigheter med någon du älskar?", "pl": "Jak radzisz sobie, gdy głęboko się z kimś, kogo kochasz, nie zgadzasz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Is there a public figure whose ideas you secretly respect, even if most people around you don’t?", "da": "Er der en offentlig person, hvis ideer du hemmeligt respekterer, selvom de fleste omkring dig ikke gør?", "de": "Gibt es eine öffentliche Person, deren Ideen du heimlich respektierst, auch wenn die meisten um dich herum es nicht tun?", "es": "¿Hay una figura pública cuyas ideas respetas en secreto, aunque la mayoría a tu alrededor no?", "pt": "Há uma figura pública cujas ideias respeitas secretamente, mesmo que a maioria à tua volta não?", "ro": "Există o persoană publică ale cărei idei le respecți în secret, chiar dacă majoritatea nu?", "fr": "Y a-t-il une personnalité publique dont tu respectes secrètement les idées, même si la plupart ne le font pas ?", "it": "C''è una figura pubblica le cui idee rispetti segretamente, anche se la maggior parte delle persone intorno a te non lo fa?", "fi": "Onko olemassa julkista henkilöä, jonka ajatuksia salaa kunnioitat, vaikka useimmat ympärilläsi eivät?", "nl": "Is er een publieke figuur wiens ideeën je stiekem respecteert, ook al doen de meeste mensen om je heen dat niet?", "no": "Er det en offentlig person hvis ideer du hemmelig respekterer, selv om de fleste rundt deg ikke gjør det?", "sv": "Finns det en offentlig person vars idéer du hemligt respekterar, även om de flesta omkring dig inte gör det?", "pl": "Czy jest jakaś postać publiczna, której pomysły potajemnie szanujesz, nawet jeśli większość ludzi wokół ciebie ich nie docenia?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Have you ever felt a little jealous of someone close to me? What made you feel that way?", "da": "Har du nogensinde været lidt jaloux på nogen tæt på mig? Hvad fik dig til at føle sådan?", "de": "Warst du jemals ein bisschen eifersüchtig auf jemanden, der mir nahe steht? Was hat dich so fühlen lassen?", "es": "¿Alguna vez te has sentido un poco celoso de alguien cercano a mí? ¿Qué te hizo sentir así?", "pt": "Alguma vez sentiste um pouco de ciúmes de alguém próximo de mim? O que te fez sentir assim?", "ro": "Te-ai simțit vreodată puțin gelos pe cineva apropiat mie? Ce te-a făcut să simți asta?", "fr": "As-tu déjà été un peu jaloux de quelqu’un de proche de moi ? Qu’est-ce qui t’a fait ressentir ça ?", "it": "Hai mai provato un po'' di gelosia nei confronti di qualcuno a me vicino? Cosa ti ha fatto sentire in questo modo?", "fi": "Oletko koskaan tuntenut pientä kateutta jotakuta läheistäni kohtaan? Mikä sai sinut tuntemaan niin?", "nl": "Heb je ooit een beetje jaloers gevoeld op iemand die dicht bij me staat? Wat deed je zo voelen?", "no": "Har du noen gang følt deg litt sjalu på noen som står meg nær? Hva fikk deg til å føle det slik?", "sv": "Har du någonsin känt dig lite avundsjuk på någon som står mig nära? Vad fick dig att känna så?", "pl": "Czy kiedykolwiek czułeś się trochę zazdrosny o kogoś bliskiego mi? Co sprawiło, że tak się poczułeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "Who from your past do you still think about sometimes — and in what way?", "da": "Hvem fra din fortid tænker du stadig på nogle gange — og hvordan?", "de": "An wen aus deiner Vergangenheit denkst du manchmal noch – und in welcher Weise?", "es": "¿A quién de tu pasado sigues pensando a veces — y de qué manera?", "pt": "Em quem do teu passado ainda pensas às vezes — e de que forma?", "ro": "La cine din trecut te mai gândești uneori — și în ce fel?", "fr": "À qui de ton passé penses-tu encore parfois — et de quelle manière ?", "it": "Chi del tuo passato pensi ancora a volte — e in che modo?", "fi": "Kuka menneisyydestäsi tulee joskus mieleesi — ja millä tavalla?", "nl": "Wie uit je verleden denk je soms nog aan — en op welke manier?", "no": "Hvem fra fortiden din tenker du fortsatt på noen ganger — og på hvilken måte?", "sv": "Vem från ditt förflutna tänker du fortfarande på ibland — och på vilket sätt?", "pl": "Kto z twojej przeszłości czasami przychodzi ci na myśl — i w jaki sposób?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
