INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Do-you-dare Talks', '{"en": "What’s something you’d love for my parents to know about you someday?", "da": "Hvad er noget, du gerne vil have, mine forældre skal vide om dig en dag?", "de": "Was möchtest du, dass meine Eltern eines Tages über dich wissen?", "es": "¿Qué te gustaría que mis padres supieran sobre ti algún día?", "pt": "O que gostarias que os meus pais soubessem sobre ti um dia?", "ro": "Ce ți-ar plăcea ca părinții mei să afle despre tine într-o zi?", "fr": "Qu’aimerais-tu que mes parents sachent de toi un jour ?", "it": "Qualcosa che ti piacerebbe che i miei genitori sapessero di te un giorno?", "fi": "Mikä on jotain, jonka haluaisit vanhempieni tietävän sinusta joskus?", "nl": "Wat is iets dat je zou willen dat mijn ouders ooit over jou weten?", "no": "Hva er noe du gjerne skulle ønsket at foreldrene mine visste om deg en dag?", "sv": "Vad är det som du skulle vilja att mina föräldrar fick veta om dig någon gång?", "pl": "Coś, co chciałbyś, aby moi rodzice kiedyś o tobie wiedzieli?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
