INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You’re in love, but your friends hate your partner. Do you listen to them or follow your heart?", "da": "Du er forelsket, men dine venner hader din partner. Lytter du til dem eller følger dit hjerte?", "de": "Du bist verliebt, aber deine Freunde hassen deinen Partner. Hörst du auf sie oder folgst du deinem Herzen?", "es": "Estás enamorado/a, pero tus amigos odian a tu pareja. ¿Les haces caso o sigues tu corazón?", "pt": "Estás apaixonado(a), mas os teus amigos odeiam o teu parceiro. Ouves-os ou segues o teu coração?", "ro": "Ești îndrăgostit(ă), dar prietenii tăi îți urăsc partenerul. Îi asculți sau îți urmezi inima?", "fr": "Tu es amoureux(se), mais tes amis détestent ton partenaire. Tu les écoutes ou tu suis ton cœur ?", "it": "Sei innamorato, ma i tuoi amici odiano il tuo partner. Ascolti loro o segui il tuo cuore?", "fi": "Olet rakastunut, mutta ystäväsi vihaavat kumppaniasi. Kuunteletko heitä vai seuraatko sydäntäsi?", "nl": "Je bent verliefd, maar je vrienden haten je partner. Luister je naar hen of volg je je hart?", "no": "Du er forelsket, men vennene dine hater partneren din. Lytter du til dem eller følger du hjertet ditt?", "sv": "Du är kär, men dina vänner hatar din partner. Lyssnar du på dem eller följer du ditt hjärta?", "pl": "Jesteś zakochany, ale twoi przyjaciele nienawidzą twojego partnera. Słuchasz ich, czy podążasz za swoim sercem?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "If two of us called you crying at the same time, who would you answer first?", "da": "Hvis to af os ringede grædende på samme tid, hvem ville du svare først?", "de": "Wenn zwei von uns dich gleichzeitig weinend anrufen würden, wem würdest du zuerst antworten?", "es": "Si dos de nosotros te llamáramos llorando al mismo tiempo, ¿a quién responderías primero?", "pt": "Se dois de nós te ligássemos a chorar ao mesmo tempo, a quem responderias primeiro?", "ro": "Dacă doi dintre noi te-ar suna plângând în același timp, cui i-ai răspunde primul?", "fr": "Si deux d’entre nous t’appelaient en pleurant en même temps, à qui répondrais-tu en premier ?", "it": "Se due di noi ti chiamassero piangendo contemporaneamente, chi risponderesti per primo?", "fi": "Jos kaksi meistä soittaisi sinulle itkien samaan aikaan, kenelle vastaisit ensin?", "nl": "Als twee van ons je tegelijkertijd belden en huilend waren, wie zou je dan als eerste beantwoorden?", "no": "Hvis to av oss ringte deg gråtende samtidig, hvem ville du svart først?", "sv": "Om två av oss ringde dig gråtande samtidigt, vem skulle du svara först?", "pl": "Jeśli dwoje z nas zadzwoniłoby do ciebie płacząc w tym samym czasie, kogo byś odebrał pierwszy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "If we all swapped phones for 5 minutes, whose would cause the biggest scandal?", "da": "Hvis vi alle byttede telefoner i 5 minutter, hvis ville skabe den største skandale?", "de": "Wenn wir alle für 5 Minuten die Handys tauschen würden, welches würde den größten Skandal verursachen?", "es": "Si todos intercambiáramos teléfonos por 5 minutos, ¿cuál causaría el mayor escándalo?", "pt": "Se todos trocássemos de telemóvel por 5 minutos, qual causaria o maior escândalo?", "ro": "Dacă ne-am schimba telefoanele timp de 5 minute, al cui ar provoca cel mai mare scandal?", "fr": "Si on échangeait tous nos téléphones pendant 5 minutes, lequel causerait le plus grand scandale ?", "it": "Se tutti noi scambiassimo i telefoni per 5 minuti, il quale causerebbe il maggior scandalo?", "fi": "Jos me kaikki vaihdettaisiin puhelimia viideksi minuutiksi, kenen puhelin aiheuttaisi suurimman skandaalin?", "nl": "Als we allemaal 5 minuten van telefoon zouden wisselen, wiens zou de grootste schandaal veroorzaken?", "no": "Hvis vi alle byttet telefoner i 5 minutter, hvis sin ville skape den største skandalen?", "sv": "Om vi alla bytte telefoner i 5 minuter, vems skulle orsaka den största skandalen?", "pl": "Jeśli wszyscy zamienilibyśmy się telefonami na 5 minut, czyj telefon wywołałby największy skandal?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You win a free vacation — but you can only bring one friend from this room. Who’s your pick and why?", "da": "Du vinder en gratis ferie — men du må kun tage én ven fra dette rum. Hvem vælger du og hvorfor?", "de": "Du gewinnst einen kostenlosen Urlaub – aber du darfst nur einen Freund aus diesem Raum mitnehmen. Wen wählst du und warum?", "es": "Ganas unas vacaciones gratis — pero solo puedes llevar a un amigo de esta sala. ¿A quién eliges y por qué?", "pt": "Ganhas umas férias grátis — mas só podes levar um amigo desta sala. Quem escolhes e porquê?", "ro": "Câștigi o vacanță gratuită — dar poți lua doar un prieten din această cameră. Pe cine alegi și de ce?", "fr": "Tu gagnes des vacances gratuites — mais tu ne peux emmener qu’un ami d’ici. Qui choisis-tu et pourquoi ?", "it": "Vinci una vacanza gratis — ma puoi portare solo un amico di questa stanza. Chi scegli e perché?", "fi": "Voitat ilmaisen loman — mutta voit tuoda vain yhden ystävän tästä huoneesta. Kuka on valintasi ja miksi?", "nl": "Je wint een gratis vakantie — maar je mag maar één vriend uit deze kamer meenemen. Wie kies je en waarom?", "no": "Du vinner en gratis ferie — men du kan bare ta med deg én venn fra dette rommet. Hvem velger du og hvorfor?", "sv": "Du vinner en gratis semester — men du kan bara ta med en vän från det här rummet. Vem väljer du och varför?", "pl": "Wygrywasz darmowe wakacje — ale możesz zabrać tylko jednego przyjaciela z tego pokoju. Kogo wybierasz i dlaczego?"}'::jsonb, false, true, true);
