VALUES ('Plot Twists & Dilemmas ', '{"en": "Who do you think is the most likely to secretly stalk their ex online?", "da": "Hvem tror du er mest tilbøjelig til i hemmelighed at stalke deres eks online?", "de": "Wer ist deiner Meinung nach am ehesten heimlich dazu geneigt, seinen Ex online zu stalken?", "es": "¿Quién crees que es más propenso a espiar en secreto a su ex en línea?", "pt": "Quem achas que seria mais propenso a seguir o ex online em segredo?", "ro": "Cine crezi că e cel mai probabil să-și urmărească fostul pe ascuns online?", "fr": "Qui, selon toi, est le plus susceptible d’espionner secrètement son ex en ligne ?", "it": "Chi pensi sia il più probabile a stalkerare segretamente il proprio ex online?", "fi": "Kuka mielestäsi on todennäköisin salaa vakoilemaan entistä kumppaniaan verkossa?", "nl": "Wie denk je dat het meest waarschijnlijk is om stiekem hun ex online te stalken?", "no": "Hvem tror du er mest sannsynlig til å hemmelig overvåke eksen sin på nettet?", "sv": "Vem tror du är mest benägen att hemligt spionera på sitt ex online?", "pl": "Kto twoim zdaniem jest najbardziej skłonny do potajemnego śledzenia swojego byłego w internecie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who would you nominate to lie for you if you were in trouble?", "da": "Hvem ville du nominere til at lyve for dig, hvis du var i problemer?", "de": "Wen würdest du nominieren, für dich zu lügen, wenn du in Schwierigkeiten wärst?", "es": "¿A quién nominarías para mentir por ti si estuvieras en problemas?", "pt": "Quem nomearias para mentir por ti se estivesses em apuros?", "ro": "Pe cine ai nominaliza să mintă pentru tine dacă ai fi în probleme?", "fr": "Qui nominerais-tu pour mentir pour toi si tu étais dans le pétrin ?", "it": "Chi nomineresti per mentire per te se fossi nei guai?", "fi": "Kenen sinä nimittäisit valehtelemaan puolestasi, jos olisit pulassa?", "nl": "Wie zou je nomineren om voor je te liegen als je in de problemen zat?", "no": "Hvem ville du nominere til å lyve for deg hvis du var i trøbbel?", "sv": "Vem skulle du nominera att ljuga för dig om du hamnade i trubbel?", "pl": "Kogo byś nominował, żeby skłamał w twoim imieniu, gdybyś miał kłopoty?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who’s the most likely to ghost someone and then act confused about it?", "da": "Hvem er mest tilbøjelig til at ghoste nogen og så lade som ingenting bagefter?", "de": "Wer ist am ehesten dazu geneigt, jemanden zu ghosten und dann verwirrt zu tun?", "es": "¿Quién es más probable que haga ghosting y luego se haga el confundido?", "pt": "Quem é mais provável de fazer ghosting e depois fingir que não percebeu?", "ro": "Cine e cel mai probabil să ignore pe cineva și apoi să se prefacă că nu știe?", "fr": "Qui est le plus susceptible de ghoster quelqu’un puis de faire semblant d’être confus ?", "it": "Chi è più probabile che sparisca da qualcuno e poi faccia finta di non sapere cosa sia successo?", "fi": "Kuka on todennäköisin jättämään jonkun huomioimatta ja sitten käyttäytymään hämmentyneesti asiasta?", "nl": "Wie is het meest waarschijnlijk om iemand te ghosten en zich daar vervolgens over te verwonderen?", "no": "Hvem er mest sannsynlig til å ghoste noen og så virke forvirret over det?", "sv": "Vem är mest trolig att spöka någon och sedan agera förvirrad över det?", "pl": "Kto jest najbardziej skłonny do zniknięcia i potem udawania, że nie wie, o co chodzi?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Two of your friends have events on the same night — one is a huge party, the other is a small dinner. Which do you pick, and why?", "da": "To af dine venner har events samme aften — en stor fest, en lille middag. Hvilken vælger du, og hvorfor?", "de": "Zwei deiner Freunde haben am selben Abend Veranstaltungen – eine große Party, ein kleines Abendessen. Welche wählst du und warum?", "es": "Dos de tus amigos tienen eventos la misma noche — una gran fiesta y una cena pequeña. ¿Cuál eliges y por qué?", "pt": "Dois amigos teus têm eventos na mesma noite — uma grande festa e um jantar pequeno. Qual escolhes e porquê?", "ro": "Doi dintre prietenii tăi au evenimente în aceeași seară — o petrecere mare și o cină mică. Pe care alegi și de ce?", "fr": "Deux de tes amis ont des événements le même soir — une grande fête et un petit dîner. Lequel choisis-tu et pourquoi ?", "it": "Due dei tuoi amici hanno eventi nella stessa notte: uno è una grande festa, l''altro è una cena intima. Quale scegli e perché?", "fi": "Kaksi ystävääsi järjestää tapahtumia samana iltana — toinen on iso juhla, toinen pieni illallinen. Kumman valitset ja miksi?", "nl": "Twee van je vrienden hebben op dezelfde avond een evenement — de ene is een groot feest, de andere is een klein diner. Welke kies je, en waarom?", "no": "To venner har arrangementer samme kveld - en er en stor fest, den andre er en liten middag. Hvilken velger du, og hvorfor?", "sv": "Två av dina vänner har evenemang samma kväll — en är en stor fest, den andra är en liten middag. Vilken väljer du, och varför?", "pl": "Dwóch twoich przyjaciół ma wydarzenia w tę samą noc — jedno to ogromna impreza, a drugie to mała kolacja. Które wybierasz i dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You get one million dollars — but you have to ghost everyone in this room for a year. Would you do it?", "da": "Du får en million dollars — men du skal ignorere alle her i et år. Gør du det?", "de": "Du bekommst eine Million Dollar – aber du musst alle hier ein Jahr lang ignorieren. Würdest du es tun?", "es": "Obtienes un millón de dólares — pero tienes que ignorar a todos aquí por un año. ¿Lo harías?", "pt": "Ganas um milhão de dólares — mas tens de ignorar toda a gente aqui durante um ano. Farias isso?", "ro": "Primești un milion de dolari — dar trebuie să ignori pe toată lumea de aici timp de un an. Ai face-o?", "fr": "Tu gagnes un million de dollars — mais tu dois ignorer tout le monde ici pendant un an. Le ferais-tu ?", "it": "Ricevi un milione di dollari — ma devi ignorare tutti in questa stanza per un anno. Lo faresti?", "fi": "Saat miljoona dollaria — mutta sinun täytyy kadota kaikilta tässä huoneessa vuodeksi. Tekisitkö niin?", "nl": "Je krijgt een miljoen dollar — maar je moet iedereen in deze kamer een jaar lang negeren. Zou je het doen?", "no": "Du får en million dollar — men du må kutte kontakten med alle i dette rommet i ett år. Ville du gjort det?", "sv": "Du får en miljon dollar — men du måste spöka alla i det här rummet i ett år. Skulle du göra det?", "pl": "Dostajesz milion dolarów — ale musisz zniknąć dla wszystkich w tym pokoju na rok. Czy byś to zrobił?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "One of your friends gets super rich and changes — do you call them out or stay quiet?", "da": "En af dine venner bliver superrig og forandrer sig — konfronterer du dem eller tier stille?", "de": "Einer deiner Freunde wird superreich und verändert sich – sprichst du es an oder bleibst du still?", "es": "Uno de tus amigos se vuelve muy rico y cambia — ¿lo enfrentas o te quedas callado?", "pt": "Um dos teus amigos fica muito rico e muda — chamas-lhe a atenção ou ficas calado?", "ro": "Unul dintre prietenii tăi devine foarte bogat și se schimbă — îl confrunți sau taci?", "fr": "L’un de tes amis devient très riche et change — tu le confrontes ou tu te tais ?", "it": "Uno dei tuoi amici diventa super ricco e cambia — li affronti o resti in silenzio?", "fi": "Yksi ystävistäsi rikastuu äkisti ja muuttuu — sanotko siitä heille vai pysytkö hiljaa?", "nl": "Een van je vrienden wordt superrijk en verandert — zeg je er iets van of blijf je stil?", "no": "En av vennene dine blir superrik og endrer seg — sier du ifra eller holder du kjeft?", "sv": "En av dina vänner blir superrik och förändras – säger du till dem eller håller du tyst?", "pl": "Jeden z twoich przyjaciół staje się super bogaty i się zmienia — czy zwrócisz mu na to uwagę, czy pozostaniesz cicho?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You find out a friend’s partner is cheating — do you tell them or stay out of it?", "da": "Du finder ud af, at en vens partner er utro — fortæller du det eller blander du dig udenom?", "de": "Du findest heraus, dass der Partner eines Freundes untreu ist – sagst du es oder hältst du dich raus?", "es": "Descubres que la pareja de un amigo engaña — ¿se lo dices o te mantienes al margen?", "pt": "Descobres que o parceiro de um amigo é infiel — dizes-lhe ou ficas de fora?", "ro": "Afli că partenerul unui prieten înșală — îi spui sau te abții?", "fr": "Tu découvres que le partenaire d’un ami le trompe — tu le dis ou tu restes en dehors ?", "it": "Scopri che il partner di un amico ti tradisce — glielo dici o stai fuori da questa situazione?", "fi": "Saat tietää, että ystäväsi kumppani pettää — kerrotko siitä heille vai pysytkö eristyksissä?", "nl": "Je ontdekt dat de partner van een vriend bedriegt — vertel je het hen of blijf je er buiten?", "no": "Du finner ut at en venns partner er utro — sier du ifra eller holder du deg unna?", "sv": "Du får reda på att en väns partner är otrogen – säger du det till dem eller håller du dig utanför?", "pl": "Dowiadujesz się, że partner przyjaciela cię oszukuje — czy powiesz mu o tym, czy lepiej się w to nie mieszać?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You overhear your friend talking behind your back — do you confront them or slowly distance yourself?", "da": "Du overhører en ven tale bag din ryg — konfronterer du dem eller trækker dig langsomt væk?", "de": "Du hörst, wie ein Freund hinter deinem Rücken redet – konfrontierst du ihn oder distanzierst du dich langsam?", "es": "Escuchas a un amigo hablando mal de ti — ¿lo enfrentas o te alejas poco a poco?", "pt": "Ouves um amigo a falar de ti nas tuas costas — confrontas ou afastas-te aos poucos?", "ro": "Îți auzi un prieten vorbind pe la spate — îl confrunți sau te îndepărtezi treptat?", "fr": "Tu entends un ami parler dans ton dos — tu le confrontes ou tu t’éloignes doucement ?", "it": "Senti il tuo amico parlare alle tue spalle — lo affronti o ti allontani lentamente?", "fi": "Kuuntelet ystäväsi puhuvan selkäsi takana — kohtaatko hänet vai etänytätkö itsesi vähitellen?", "nl": "Je hoort je vriend achter je rug om praten — confronteer je ze of neem je langzaam afstand?", "no": "Du overhører vennen din snakke bak ryggen din — konfronterer du dem eller distanserer du deg sakte?", "sv": "Du hör din vän prata bakom din rygg — konfronterar du dem eller distanserar du dig långsamt?", "pl": "Słyszysz, jak twój przyjaciel rozmawia o tobie za twoimi plecami — czy skonfrontujesz się z nim, czy powoli się odsuniesz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You can only tell one person in this room your biggest secret — who do you trust?", "da": "Du kan kun fortælle én person i dette rum din største hemmelighed — hvem stoler du på?", "de": "Du kannst nur einer Person in diesem Raum dein größtes Geheimnis anvertrauen – wem vertraust du?", "es": "Solo puedes contarle tu mayor secreto a una persona en esta sala — ¿en quién confías?", "pt": "Só podes contar o teu maior segredo a uma pessoa nesta sala — em quem confias?", "ro": "Poți spune cel mai mare secret al tău doar unei persoane din această cameră — în cine ai încredere?", "fr": "Tu ne peux confier ton plus grand secret qu’à une seule personne ici — à qui fais-tu confiance ?", "it": "Puoi dire il tuo segreto più grande solo a una persona in questa stanza — di chi ti fidi?", "fi": "Voit kertoa vain yhdelle henkilölle tässä huoneessa suurimman salaisuutesi — kenelle luotat?", "nl": "Je kunt maar één persoon in deze kamer je grootste geheim vertellen — wie vertrouw je?", "no": "Du kan bare fortelle én person i dette rommet din største hemmelighet — hvem stoler du på?", "sv": "Du kan bara berätta för en person i det här rummet din största hemlighet — vem litar du på?", "pl": "Możesz powiedzieć tylko jednej osobie w tym pokoju swój największy sekret — komu ufasz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You’re offered your dream job, but you have to move away from everyone here. Do you go?", "da": "Du får dit drømmejob, men skal flytte væk fra alle her. Gør du det?", "de": "Du bekommst deinen Traumjob, aber musst wegziehen von allen hier. Gehst du?", "es": "Te ofrecen tu trabajo soñado, pero tienes que mudarte lejos de todos aquí. ¿Lo haces?", "pt": "Oferecem-te o teu trabalho de sonho, mas tens de te mudar para longe de todos. Vais?", "ro": "Ți se oferă slujba visurilor tale, dar trebuie să te muți departe de toți de aici. O faci?", "fr": "On t’offre ton job de rêve, mais tu dois déménager loin de tout le monde ici. Tu y vas ?", "it": "Ti offrono il lavoro dei tuoi sogni, ma devi trasferirti lontano da tutti qui. Vai?", "fi": "Sinulle tarjotaan unelmiesi työpaikka, mutta sinun täytyy muuttaa pois kaikista täältä. Lähdetkö?", "nl": "Je krijgt je droombaan aangeboden, maar je moet weg van iedereen hier. Ga je?", "no": "Du får tilbud om drømmejobben din, men du må flytte bort fra alle her. Går du?", "sv": "Du får erbjudande om ditt drömjobb, men du måste flytta bort från alla här. Åker du?", "pl": "Oferują ci pracę marzeń, ale musisz się przeprowadzić z dala od wszystkich tutaj. Czy się decydujesz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "If two of your best friends started dating, would you be happy or secretly terrified?", "da": "Hvis to af dine bedste venner begyndte at date, ville du være glad eller hemmeligt skræmt?", "de": "Wenn zwei deiner besten Freunde anfangen würden, sich zu daten, wärst du glücklich oder heimlich verängstigt?", "es": "Si dos de tus mejores amigos empezaran a salir, ¿estarías feliz o secretamente aterrado?", "pt": "Se dois dos teus melhores amigos começassem a namorar, ficarias feliz ou secretamente aterrorizado?", "ro": "Dacă doi dintre cei mai buni prieteni ai tăi ar începe să se întâlnească, ai fi fericit sau secret îngrijorat?", "fr": "Si deux de tes meilleurs amis commençaient à sortir ensemble, serais-tu heureux ou secrètement terrifié ?", "it": "Se due dei tuoi migliori amici iniziassero a uscire insieme, saresti felice o segretamente terrorizzato?", "fi": "Jos kaksi parasta ystävääsi alkaisi seurustella, olisitko onnellinen vai salaa kauhuissasi?", "nl": "Als twee van je beste vrienden aan het daten gaan, zou je dan blij zijn of stiekem bang?", "no": "Hvis to av dine beste venner begynte å date, ville du vært glad eller hemmelig redd?", "sv": "Om två av dina bästa vänner började dejta, skulle du vara glad eller hemligt skrämd?", "pl": "Jeśli dwójka twoich najlepszych przyjaciół zaczęłaby się spotykać, czy byłbyś szczęśliwy, czy może w tajemnicy przerażony?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You can only choose one: the friend who always makes you laugh or the friend who actually gives good advice.", "da": "Du kan kun vælge én: vennen der altid får dig til at grine, eller vennen der faktisk giver gode råd.", "de": "Du kannst nur einen wählen: den Freund, der dich immer zum Lachen bringt, oder den, der wirklich gute Ratschläge gibt.", "es": "Solo puedes elegir uno: el amigo que siempre te hace reír o el que realmente da buenos consejos.", "pt": "Só podes escolher um: o amigo que te faz rir ou o que dá bons conselhos.", "ro": "Poți alege doar unul: prietenul care te face mereu să râzi sau cel care dă sfaturi bune.", "fr": "Tu ne peux en choisir qu’un : celui qui te fait toujours rire ou celui qui donne de bons conseils.", "it": "Puoi scegliere solo uno: l''amico che ti fa sempre ridere o l''amico che dà davvero buoni consigli.", "fi": "Voit valita vain yhden: ystävän, joka aina saa sinut nauramaan, tai ystävän, joka oikeasti antaa hyviä neuvoja.", "nl": "Je kunt maar één kiezen: de vriend die je altijd aan het lachen maakt of de vriend die echt goed advies geeft.", "no": "Du kan bare velge én: vennen som alltid får deg til å le, eller vennen som faktisk gir gode råd.", "sv": "Du kan bara välja en: vännen som alltid får dig att skratta eller vännen som faktiskt ger bra råd.", "pl": "Możesz wybrać tylko jednego: przyjaciela, który zawsze cię rozśmiesza, czy przyjaciela, który naprawdę daje dobre rady?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "If you could switch lives with one friend for a week, who would it be?", "da": "Hvis du kunne bytte liv med en ven i en uge, hvem ville det være?", "de": "Wenn du für eine Woche das Leben mit einem Freund tauschen könntest, wer wäre es?", "es": "Si pudieras cambiar de vida con un amigo por una semana, ¿quién sería?", "pt": "Se pudesses trocar de vida com um amigo por uma semana, quem seria?", "ro": "Dacă ai putea schimba viața cu un prieten pentru o săptămână, cine ar fi?", "fr": "Si tu pouvais échanger ta vie avec un ami pendant une semaine, qui serait-ce ?", "it": "Se potessi scambiare la vita con un amico per una settimana, chi sarebbe?", "fi": "Jos voisit vaihtaa elämää yhden ystävän kanssa viikoksi, kuka se olisi?", "nl": "Als je een week het leven van een vriend zou kunnen overnemen, wie zou dat dan zijn?", "no": "Hvis du kunne bytte liv med en venn i en uke, hvem ville det vært?", "sv": "Om du kunde byta liv med en vän i en vecka, vem skulle det vara?", "pl": "Gdybyś mógł zamienić się życiem z jednym przyjacielem na tydzień, kto by to był?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who would you least trust with your love life?", "da": "Hvem ville du mindst stole på med dit kærlighedsliv?", "de": "Wem würdest du am wenigsten dein Liebesleben anvertrauen?", "es": "¿En quién confiarías menos con tu vida amorosa?", "pt": "Em quem menos confiarias com a tua vida amorosa?", "ro": "În cine ai avea cea mai puțină încredere când vine vorba de viața ta amoroasă?", "fr": "À qui ferais-tu le moins confiance avec ta vie amoureuse ?", "it": "Chi faresti meno fiducia per la tua vita amorosa?", "fi": "Keneen luottaisit vähiten rakkauselämääsi?", "nl": "Wie zou je het minst vertrouwen met je liefdesleven?", "no": "Hvem ville du minst stole på med kjærlighetslivet ditt?", "sv": "Vem skulle du minst lita på med ditt kärleksliv?", "pl": "Kogo byś najmniej ufał w sprawach swojego życia miłosnego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "If your ex dated one of your friends — who could you actually forgive?", "da": "Hvis din eks datede en af dine venner — hvem kunne du faktisk tilgive?", "de": "Wenn dein Ex mit einem deiner Freunde ausgehen würde – wem könntest du wirklich verzeihen?", "es": "Si tu ex saliera con uno de tus amigos, ¿a quién podrías realmente perdonar?", "pt": "Se o teu ex namorasse com um dos teus amigos — quem realmente perdoarias?", "ro": "Dacă fostul tău ar începe o relație cu unul dintre prietenii tăi — pe cine ai putea ierta cu adevărat?", "fr": "Si ton ex sortait avec un de tes amis — qui pourrais-tu vraiment pardonner ?", "it": "Se il tuo ex uscisse con uno dei tuoi amici — chi potresti realmente perdonare?", "fi": "Jos entinen kumppanisi seurusteli jonkun ystäväsi kanssa — ketä voisit oikeasti antaa anteeksi?", "nl": "Als je ex met een van je vrienden zou daten — wie zou je eigenlijk kunnen vergeven?", "no": "Hvis eksen din datet en av vennene dine — hvem kunne du faktisk tilgi?", "sv": "Om din ex dejtade en av dina vänner — vem skulle du faktiskt kunna förlåta?", "pl": "Jeśli twój były umówiłby się z jednym z twoich przyjaciół — kogo mógłbyś właściwie wybaczyć?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "One of us has to fake-date you for a year for money — who do you pick?", "da": "En af os skal lade som om vi dater dig i et år for penge — hvem vælger du?", "de": "Einer von uns muss für ein Jahr so tun, als würde er mit dir ausgehen – für Geld. Wen wählst du?", "es": "Uno de nosotros tiene que fingir salir contigo por un año por dinero — ¿a quién eliges?", "pt": "Um de nós tem de fingir que namora contigo durante um ano por dinheiro — quem escolhes?", "ro": "Unul dintre noi trebuie să se prefacă că e într-o relație cu tine un an pentru bani — pe cine alegi?", "fr": "L’un de nous doit faire semblant de sortir avec toi pendant un an pour de l’argent — qui choisis-tu ?", "it": "Uno di noi deve fingere di uscire con te per un anno per soldi — chi scegli?", "fi": "Kenen valitset, jos jonkun meistä täytyy teeskennellä deittailua kanssasi vuoden ajan rahan takia?", "nl": "Een van ons moet jou een jaar lang voor geld faken, wie kies je?", "no": "En av oss må late som vi dater deg i et år for penger — hvem velger du?", "sv": "En av oss måste låtsasdejta dig i ett år för pengar — vem väljer du?", "pl": "Jedna z nas musi udawać, że się z tobą spotyka przez rok za pieniądze — kogo wybierasz?"}'::jsonb, false, true, true);
