INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "You have to choose one forever: physical chemistry or emotional connection. Which wins?", "da": "Du skal vælge én for evigt: fysisk kemi eller følelsesmæssig forbindelse. Hvilken vinder?", "de": "Du musst dich für immer entscheiden: körperliche Chemie oder emotionale Verbindung. Was gewinnt?", "es": "Tienes que elegir una para siempre: ¿química física o conexión emocional?", "pt": "Tens de escolher uma para sempre: química física ou ligação emocional.", "ro": "Trebuie să alegi una pentru totdeauna: chimie fizică sau conexiune emoțională. Care câștigă?", "fr": "Tu dois en choisir une pour toujours : alchimie physique ou connexion émotionnelle. Laquelle gagne ?", "it": "Devi scegliere uno per sempre: chimica fisica o connessione emotiva. Quale vince?", "fi": "Sinun täytyy valita yksi ikuisesti: fysikaalinen kemia vai emotionaalinen yhteys. Kumpi voittaa?", "nl": "Je moet er één voor altijd kiezen: fysische chemie of emotionele verbinding. Welke wint?", "no": "Du må velge én for alltid: fysisk kjemi eller følelsesmessig tilknytning. Hvilken vinner?", "sv": "Du måste välja en för alltid: fysikalisk kemi eller känslomässig koppling. Vilken vinner?", "pl": "Musisz wybrać jedno na zawsze: chemia fizyczna czy emocjonalne połączenie. Co wygrywa?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
