INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "Who here would be the last one standing at a night out — and who would tap out first?", "da": "Hvem her ville være den sidste, der stadig stod til en bytur — og hvem ville give op først?", "de": "Wer hier wäre der Letzte, der bei einem Abend ausgeht – und wer würde zuerst aufgeben?", "es": "¿Quién aquí sería el último en irse de una noche de fiesta — y quién se rendiría primero?", "pt": "Quem aqui seria o último de pé numa noite fora — e quem desistiria primeiro?", "ro": "Cine ar rămâne cel mai mult într-o noapte în oraș — și cine ar ceda primul?", "fr": "Qui ici serait le dernier debout lors d’une soirée — et qui abandonnerait en premier ?", "it": "Chi qui sarebbe l''ultimo a rimanere in piedi durante una serata fuori — e chi si arrenderebbe per primo?", "fi": "Kuka täällä olisi viimeinen pystyssä yöelämässä — ja kuka luovuttaisi ensimmäisenä?", "nl": "Wie hier zou de laatste zijn die overeind blijft tijdens een avondje uit — en wie zou als eerste afhaken?", "no": "Hvem her ville vært den siste som stod igjen på en utekveld — og hvem ville gitt seg først?", "sv": "Vem här skulle vara den sista som står kvar på en utekväll — och vem skulle ge upp först?", "pl": "Kto tutaj byłby ostatnią osobą stojącą na nocnym wyjściu — a kto poddałby się jako pierwszy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "If we all lived in one big house, who would be the clean freak, and who would get kicked out first?", "da": "Hvis vi alle boede i ét stort hus, hvem ville være rengøringsfanatikeren, og hvem ville blive smidt ud først?", "de": "Wenn wir alle in einem großen Haus leben würden, wer wäre der Putzfreak und wer würde als Erster rausfliegen?", "es": "Si todos viviéramos en una casa grande, ¿quién sería el maniático de la limpieza y quién sería expulsado primero?", "pt": "Se todos vivêssemos numa casa grande, quem seria o obcecado pela limpeza e quem seria expulso primeiro?", "ro": "Dacă am locui toți într-o casă mare, cine ar fi obsedatul de curățenie și cine ar fi dat afară primul?", "fr": "Si nous vivions tous dans une grande maison, qui serait le maniaque du ménage et qui se ferait virer en premier ?", "it": "Se vivessimo tutti in una grande casa, chi sarebbe l''ossessionato della pulizia e chi verrebbe cacciato per primo?", "fi": "Jos me kaikki asuisimme yhdessä suuressa talossa, kuka olisi se siisteysintoilija ja kuka potkittaisiin ulos ensimmäisenä?", "nl": "Als we allemaal in één groot huis zouden wonen, wie zou dan de schoonmaakfreak zijn en wie zou er als eerste eruit gegooid worden?", "no": "Hvis vi alle bodde i ett stort hus, hvem ville vært den som er besatt av renhold, og hvem ville blitt kastet ut først?", "sv": "Om vi alla bodde i ett stort hus, vem skulle vara den som är besatt av renlighet, och vem skulle bli utkastad först?", "pl": "Jeśli wszyscy mieszkali w jednym dużym domu, kto byłby pedantem, a kto zostałby wyrzucony jako pierwszy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Plot Twists & Dilemmas ', '{"en": "If one of us started dating your ex, who would it hurt the most?", "da": "Hvis en af os begyndte at date din eks, hvem ville det gøre mest ondt på?", "de": "Wenn einer von uns anfangen würde, mit deinem Ex auszugehen, wen würde es am meisten verletzen?", "es": "Si uno de nosotros empezara a salir con tu ex, ¿a quién le dolería más?", "pt": "Se um de nós começasse a namorar o teu ex, quem sofreria mais?", "ro": "Dacă unul dintre noi ar începe o relație cu fostul tău, pe cine ar durea cel mai tare?", "fr": "Si l’un de nous commençait à sortir avec ton ex, qui cela blesserait le plus ?", "it": "Se uno di noi iniziasse a uscire con il tuo ex, chi ne sarebbe più ferito?", "fi": "Jos joku meistä alkaisi seurustella entisen kumppanisi kanssa, kuka kärsisi siitä eniten?", "nl": "Als een van ons zou beginnen met daten met jouw ex, wie zou het dan het meest pijn doen?", "no": "Hvis en av oss begynte å date eksen din, hvem ville det såret mest?", "sv": "Om en av oss började dejta ditt ex, vem skulle det skada mest?", "pl": "Jeśli jedno z nas zaczęłoby spotykać się z twoim byłym, kto by najbardziej ucierpiał?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
