INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What’s the eeriest place you’ve ever been—and what made it feel wrong?", "da": "Hvad er det mest uhyggelige sted, du nogensinde har været – og hvorfor føltes det forkert?", "de": "Was ist der unheimlichste Ort, an dem du je warst – und warum fühlte er sich falsch an?", "es": "¿Cuál es el lugar más inquietante en el que has estado, y qué lo hizo sentir mal?", "pt": "Qual é o lugar mais assustador em que já esteve – e o que o fez parecer errado?", "ro": "Care este cel mai înfiorător loc în care ai fost vreodată – și ce l-a făcut să se simtă „greșit”?", "fr": "Quel est l’endroit le plus étrange où tu sois allé – et pourquoi semblait-il « mauvais » ?", "it": "Qual è il posto più inquietante in cui sei mai stato/a, e cosa lo rendeva così sbagliato?", "fi": "Mikä on oudoinkin paikka, jossa olet ollut – ja mikä teki siitä niin karmivan?", "nl": "Wat is de griezeligste plek waar je ooit bent geweest, en wat maakte het zo fout aanvoelen?", "no": "Hva er det ekleste/merkeligste stedet du noen gang har vært – og hva gjorde at det føltes feil?", "sv": "Vilken är den kusligaste plats du någonsin varit på – och vad gjorde att den kändes fel?", "pl": "Jakie jest najbardziej upiorne miejsce, w którym kiedykolwiek byłeś/byłaś – i co sprawiło, że wydawało się takie złe?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
