INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Share a dream you remember too clearly. Any meaning?", "da": "Del en drøm, du husker alt for tydeligt. Nogen betydning?", "de": "Teile einen Traum, an den du dich zu gut erinnerst. Irgendeine Bedeutung?", "es": "Comparte un sueño que recuerdes con demasiada claridad. ¿Algún significado?", "pt": "Partilha um sonho que te lembras demasiado bem. Tem algum significado?", "ro": "Povestește un vis pe care ți-l amintești prea clar. Ce înțeles are?", "fr": "Partage un rêve dont tu te souviens trop bien. Une signification ?", "it": "Condividi un sogno che ricordi fin troppo bene. Che significato potrebbe avere?", "fi": "Kerro unesta, jonka muistat liian tarkasti. Onko sillä mielestäsi jokin merkitys?", "nl": "Deel een droom die je te goed herinnert. Zie je er een betekenis in?", "no": "Del en drøm du husker altfor godt. Noen mening i den?", "sv": "Dela en dröm du minns alldeles för tydligt. Någon betydelse?", "pl": "Podziel się snem, który pamiętasz aż za dobrze. Ma dla ciebie jakieś znaczenie?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A crime case you can’t stop thinking about—why this one?", "da": "En kriminalsag, du ikke kan glemme – hvorfor netop den?", "de": "Ein Kriminalfall, über den du nicht aufhören kannst nachzudenken – warum dieser?", "es": "Un caso criminal que no puedes dejar de pensar – ¿por qué ese?", "pt": "Um caso criminal que não consegues esquecer – porquê esse?", "ro": "Un caz criminal de care nu te poți desprinde – de ce tocmai acesta?", "fr": "Une affaire criminelle à laquelle tu penses encore – pourquoi celle-là ?", "it": "Un caso di cronaca nera a cui non riesci a smettere di pensare: perché proprio questo?", "fi": "Rikostapaus, jota et saa mielestäsi – miksi juuri tämä?", "nl": "Een strafzaak waar je maar aan blijft denken – waarom juist deze?", "no": "En kriminalsak du ikke klarer å slutte å tenke på – hvorfor akkurat denne?", "sv": "Ett kriminalfall du inte kan sluta tänka på – varför just det?", "pl": "Sprawa kryminalna, o której nie możesz przestać myśleć – dlaczego właśnie ta?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Footsteps outside your door, peephole blocked – what do you do?", "da": "Fodtrin udenfor din dør, kighul blokeret – hvad gør du?", "de": "Schritte vor deiner Tür, Spion blockiert – was tust du?", "es": "Pasos fuera de tu puerta, mirilla bloqueada – ¿qué haces?", "pt": "Passos fora da tua porta, olho mágico bloqueado – o que fazes?", "ro": "Se aud pași în fața ușii tale, vizorul este blocat – ce faci?", "fr": "Des pas devant ta porte, judas bloqué – que fais-tu ?", "it": "Passi fuori dalla porta, spioncino coperto – cosa fai?", "fi": "Askeleita oven ulkopuolella, ovisilmä peitetty – mitä teet?", "nl": "Voetstappen buiten je deur, deurspion afgeplakt – wat doe je?", "no": "Fottrinn utenfor døren din, dørspionen er blokkert – hva gjør du?", "sv": "Fotsteg utanför din dörr, titthålet är blockerat – vad gör du?", "pl": "Kroki za drzwiami, wizjer zasłonięty – co robisz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Motion sensor lights turn on in the empty next room. How do you clear the space?", "da": "Bevægelsessensorlys tænder i det tomme rum ved siden af. Hvordan tjekker du det?", "de": "Bewegungsmelderlicht geht im leeren Nebenraum an. Wie prüfst du den Raum?", "es": "Las luces con sensor se encienden en la habitación vacía de al lado. ¿Cómo revisas el lugar?", "pt": "A luz do sensor de movimento acende-se no quarto ao lado. Como verificas?", "ro": "Lumina cu senzor de mișcare se aprinde în camera goală de alături. Cum verifici spațiul?", "fr": "Les lumières à capteur de mouvement s’allument dans la pièce voisine vide. Comment vérifies-tu ?", "it": "Le luci con sensore di movimento si accendono nella stanza vuota accanto. Come \"ripulisci\" lo spazio?", "fi": "Liiketunnistinvalot syttyvät tyhjään viereiseen huoneeseen. Miten \"puhdistat\" tilan?", "nl": "Bewegingssensorlampen gaan aan in de lege kamer naast je. Hoe \"reinig\" je de ruimte?", "no": "Bevegelsessensorlysene slår seg på i det tomme rommet ved siden av. Hvordan \"renser\" du stedet?", "sv": "Rörelsesensorlampor tänds i det tomma rummet bredvid. Hur \"rensar\" du platsen?", "pl": "Czujniki ruchu zapalają światło w pustym pokoju obok. Jak \"oczyszczasz\" tę przestrzeń?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You can ask a spirit one yes/no question. What do you ask?", "da": "Du kan stille en ånd ét ja/nej-spørgsmål. Hvad spørger du om?", "de": "Du darfst einem Geist eine Ja/Nein-Frage stellen. Was fragst du?", "es": "Puedes hacerle una pregunta de sí/no a un espíritu. ¿Qué preguntas?", "pt": "Podes fazer uma pergunta de sim/não a um espírito. O que perguntas?", "ro": "Poți pune unei spirite o întrebare la care răspunde doar „da” sau „nu”. Ce întrebi?", "fr": "Tu peux poser une question par oui/non à un esprit. Que demandes-tu ?", "it": "Puoi fare a uno spirito una sola domanda a cui si risponde sì/no. Cosa chiedi?", "fi": "Voit kysyä hengeltä yhden kyllä/ei -kysymyksen. Mitä kysyt?", "nl": "Je mag één ja/nee-vraag aan een geest stellen. Wat vraag je?", "no": "Du kan stille en ånd ett ja/nei-spørsmål. Hva spør du om?", "sv": "Du får ställa en ja/nej-fråga till en ande. Vad frågar du?", "pl": "Możesz zadać duchowi jedno pytanie z odpowiedzią tak/nie. O co pytasz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "You find an AirTag/tracker alert on your phone after a night out. What do you do?", "da": "Du får en AirTag/Tracker-advarsel på din telefon efter en aften ude. Hvad gør du?", "de": "Du erhältst nach einer Nacht draußen eine AirTag/Tracker-Warnung auf deinem Handy. Was tust du?", "es": "Recibes una alerta de AirTag/rastreador en tu teléfono después de una noche fuera. ¿Qué haces?", "pt": "Recebes um alerta de AirTag/rastreador no teu telemóvel depois de uma noite fora. O que fazes?", "ro": "Primești o alertă pe telefon despre un AirTag/tracker după o noapte în oraș. Ce faci?", "fr": "Tu reçois une alerte AirTag/suivi sur ton téléphone après une soirée dehors. Que fais-tu ?", "it": "Trovi una notifica di AirTag/tracker sul telefono dopo una serata fuori. Cosa fai?", "fi": "Saat AirTag-/seurantalaitteen hälytyksen puhelimeesi illanvieton jälkeen. Mitä teet?", "nl": "Je krijgt na een avond uit een AirTag-/tracker-melding op je telefoon. Wat doe je?", "no": "Du får varsel om en AirTag/tracker på telefonen etter en kveld ute. Hva gjør du?", "sv": "Du får en AirTag-/tracker-varning på mobilen efter en utekväll. Vad gör du?", "pl": "Po wieczornym wyjściu dostajesz na telefon powiadomienie o AirTagu/trackerze. Co robisz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Rate your belief in ghosts, 0–10, and defend it.", "da": "Bedøm din tro på spøgelser fra 0–10, og forsvar den.", "de": "Bewerte deinen Glauben an Geister von 0 bis 10 und verteidige ihn.", "es": "Califica tu creencia en fantasmas del 0 al 10 y defiéndela.", "pt": "Avalia a tua crença em fantasmas de 0 a 10 e defende-a.", "ro": "Evaluează-ți credința în fantome de la 0 la 10 și apără-ți răspunsul.", "fr": "Évalue ta croyance aux fantômes de 0 à 10 et défends-la.", "it": "Valuta quanto credi ai fantasmi da 0 a 10 e difendi il tuo voto.", "fi": "Arvioi uskosi kummituksiin asteikolla 0–10 ja perustele se.", "nl": "Geef je geloof in geesten een cijfer van 0–10 en verdedig het.", "no": "Ranger troen din på spøkelser fra 0–10, og forklar.", "sv": "Betygsätt din tro på spöken, 0–10, och försvara betyget.", "pl": "Oceń swoją wiarę w duchy w skali 0–10 i ją uzasadnij."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A child in a store waves behind you and says “bye.” There’s no one there. How do you react?", "da": "Et barn i en butik vinker bag dig og siger “hej hej”. Der er ingen der. Hvad gør du?", "de": "Ein Kind im Laden winkt dir hinterher und sagt „Tschüss“. Niemand ist da. Wie reagierst du?", "es": "Un niño en una tienda te saluda y dice “adiós”. No hay nadie allí. ¿Cómo reaccionas?", "pt": "Uma criança numa loja acena-te e diz “tchau”. Não há ninguém. Como reages?", "ro": "Un copil într-un magazin îți face cu mâna și spune „pa.” Nu e nimeni acolo. Cum reacționezi?", "fr": "Un enfant dans un magasin te fait signe et dit « au revoir ». Il n’y a personne. Comment réagis-tu ?", "it": "Un bambino in un negozio saluta dietro di te e dice \"ciao\". Non c’è nessuno. Come reagisci?", "fi": "Lapsi kaupassa vilkuttaa selkäsi takana ja sanoo \"hei hei\". Ketään ei näy. Miten reagoit?", "nl": "Een kind in een winkel zwaait achter je en zegt \"doei\". Er is niemand. Hoe reageer je?", "no": "Et barn i en butikk vinker bak deg og sier \"ha det\". Det er ingen der. Hvordan reagerer du?", "sv": "Ett barn i en butik vinkar bakom dig och säger \"hej då\". Det finns ingen där. Hur reagerar du?", "pl": "Dziecko w sklepie macha za twoimi plecami i mówi \"pa\". Nikogo tam nie ma. Jak reagujesz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "What childhood fear secretly still lives rent-free?", "da": "Hvilken barndomsfrygt bor stadig gratis i dit hoved?", "de": "Welche Kindheitsangst lebt immer noch mietfrei in deinem Kopf?", "es": "¿Qué miedo de la infancia sigue viviendo gratis en tu mente?", "pt": "Que medo de infância ainda vive de graça na tua mente?", "ro": "Ce frică din copilărie încă mai trăiește în tine „cu chirie gratuită”?", "fr": "Quelle peur d’enfance vit encore gratuitement dans ta tête ?", "it": "Quale paura d’infanzia vive ancora di nascosto nella tua testa?", "fi": "Mikä lapsuuden pelko asuu salaa edelleen mielessäsi?", "nl": "Welke kinderangst woont stiekem nog steeds in je hoofd?", "no": "Hvilken barndomsfrykt bor fortsatt i deg, i hemmelighet?", "sv": "Vilken barndomsrädsla bor fortfarande gratis i ditt huvud?", "pl": "Który lęk z dzieciństwa wciąż po cichu mieszka w twojej głowie?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "Create a spooky playlist title and its first three tracks.", "da": "Lav en uhyggelig playliste-titel og dens første tre numre.", "de": "Erstelle einen gruseligen Playlist-Titel und die ersten drei Songs.", "es": "Crea un título de lista espeluznante y sus tres primeras canciones.", "pt": "Cria um título assustador de playlist e as suas três primeiras faixas.", "ro": "Creează un titlu de playlist înfricoșător și primele trei melodii.", "fr": "Crée une playlist effrayante et donne-lui ses trois premiers morceaux.", "it": "Crea un titolo inquietante per una playlist e i suoi primi tre brani.", "fi": "Keksi karmiva soittolistan nimi ja sen kolme ensimmäistä kappaletta.", "nl": "Bedenk een griezelige playlist-titel en de eerste drie nummers.", "no": "Lag en skummel spilleliste-tittel og de tre første låtene.", "sv": "Hitta på en läskig spellisttitel och dess tre första låtar.", "pl": "Wymyśl straszny tytuł playlisty i jej trzy pierwsze utwory."}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('After Dark Talks', '{"en": "A neighbor says your lights flicker every night at the same minute. You weren’t home. Thoughts?", "da": "En nabo siger, at dine lys blinker hver nat på samme tidspunkt. Du var ikke hjemme. Tanker?", "de": "Ein Nachbar sagt, deine Lichter flackern jede Nacht zur selben Minute. Du warst nicht zu Hause. Gedanken?", "es": "Un vecino dice que tus luces parpadean cada noche a la misma hora. No estabas en casa. ¿Pensamientos?", "pt": "Um vizinho diz que as tuas luzes piscam todas as noites à mesma hora. Não estavas em casa. Pensamentos?", "ro": "Un vecin spune că luminile tale pâlpâie în fiecare noapte la aceeași oră. Tu nu erai acasă. Ce crezi?", "fr": "Un voisin dit que tes lumières clignotent chaque nuit à la même heure. Tu n’étais pas chez toi. Des pensées ?", "it": "Un vicino dice che le tue luci sfarfallano ogni notte alla stessa ora. Tu non eri a casa. Pensieri?", "fi": "Naapuri sanoo, että valosi vilkkuvat joka ilta samaan aikaan. Et ollut kotona. Mitä ajattelet?", "nl": "Een buur zegt dat je lampen elke nacht op hetzelfde moment flikkeren. Jij was niet thuis. Mening?", "no": "En nabo sier at lysene dine blinker hver natt på samme minutt. Du var ikke hjemme. Tanker?", "sv": "En granne säger att dina lampor blinkar varje natt på samma minut. Du var inte hemma. Tankar?", "pl": "Sąsiad mówi, że twoje światła migają co noc o tej samej minucie. Nie było cię w domu. Co o tym myślisz?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
