VALUES ('After Dark Talks', '{"en": "If a friendly ghost could do one chore, which would you assign?", "da": "Hvis et venligt spøgelse kunne lave én husopgave, hvilken ville du vælge?", "de": "Wenn ein freundlicher Geist eine Hausarbeit erledigen könnte, welche würdest du wählen?", "es": "Si un fantasma amable pudiera hacer una tarea doméstica, ¿cuál elegirías?", "pt": "Se um fantasma simpático pudesse fazer uma tarefa, qual escolherias?", "ro": "Dacă o fantomă prietenoasă ar putea face o treabă în locul tău, ce i-ai da?", "fr": "Si un gentil fantôme pouvait faire une corvée, laquelle lui confierais-tu ?", "it": "Se un fantasma amichevole potesse svolgere una faccenda domestica per te, quale gli affideresti?", "fi": "Jos ystävällinen kummitus voisi hoitaa yhden kotityön, minkä antaisit sen tehtäväksi?", "nl": "Als een vriendelijke geest één klus voor je kon doen, welke zou je hem geven?", "no": "Hvis et vennlig spøkelse kunne gjøre én husoppgave for deg, hvilken ville du gitt det?", "sv": "Om ett vänligt spöke kunde göra en syssla åt dig, vilken skulle du ge det?", "pl": "Gdyby przyjazny duch mógł wykonać jedno domowe zadanie, które byś mu zlecił(a)?"}'::jsonb, true, true, false);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
