INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "If a cloud had a taste, what flavor would it have? Why?", "da": "Aplicația ta de încuietoare arată că cineva a încercat patru coduri la 2:41 dimineața. Ce faci?", "de": "Wenn eine Wolke einen Geschmack hätte, welchen hätte sie? Warum?", "es": "Si una nube tuviera sabor, ¿a qué sabría? ¿Por qué?", "pt": "Se uma nuvem tivesse sabor, qual seria? Por quê?", "ro": "Dacă un nor ar avea gust, ce gust ar avea? De ce?", "fr": "Si un nuage avait un goût, quel serait-il ? Pourquoi ?", "it": "Se una nuvola avesse un sapore, quale sarebbe? Perché?", "fi": "Jos pilvellä olisi maku, mikä se olisi? Miksi?", "nl": "Als een wolk een smaak had, welke smaak zou het dan hebben? Waarom?", "no": "Hvis en sky hadde en smak, hvilken smak ville den hatt? Hvorfor?", "sv": "Om ett moln hade en smak, vilken smak skulle det ha? Varför?", "pl": "Gdyby chmura miała smak, jaki by miała? Dlaczego?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "If your backpack could talk, what story would it tell?", "da": "Ton application de serrure connectée montre que quelqu’un a essayé quatre codes à 2h41. Que fais-tu ?", "de": "Wenn dein Rucksack sprechen könnte, welche Geschichte würde er erzählen?", "es": "Si tu mochila pudiera hablar, ¿qué historia contaría?", "pt": "Se a tua mochila pudesse falar, que história contaria?", "ro": "Dacă ghiozdanul tău ar putea vorbi, ce poveste ar spune?", "fr": "Si ton sac à dos pouvait parler, quelle histoire raconterait-il ?", "it": "Se il tuo zaino potesse parlare, quale storia racconterebbe?", "fi": "Jos repussasi olisi ääni, mitä tarinaa se kertoisi?", "nl": "Als je rugzak kon praten, welk verhaal zou hij vertellen?", "no": "Hvis sekken din kunne snakke, hvilken historie ville den fortelle?", "sv": "Om din ryggsäck kunde prata, vilken historia skulle den berätta?", "pl": "Gdyby twój plecak mógł mówić, jaką historię by opowiedział?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "If your shoes could take you anywhere, where would you go first?", "da": "Hvis dine sko kunne tage dig hvor som helst hen, hvor ville du tage hen først?", "de": "Wenn deine Schuhe dich überallhin bringen könnten, wohin würdest du zuerst gehen?", "es": "Si tus zapatos pudieran llevarte a cualquier lugar, ¿a dónde irías primero?", "pt": "Se os teus sapatos te pudessem levar a qualquer lugar, para onde irias primeiro?", "ro": "Dacă pantofii tăi te-ar putea duce oriunde, unde ai merge mai întâi?", "fr": "Si tes chaussures pouvaient t’emmener n’importe où, où irais-tu d’abord ?", "it": "Se le tue scarpe potessero portarti ovunque, dove andresti per primo?", "fi": "Jos kenkäsi voisivat viedä sinut minne tahansa, minne menisit ensin?", "nl": "Als je schoenen je overal naartoe konden brengen, waar zou je dan als eerste naartoe gaan?", "no": "Hvis skoene dine kunne ta deg hvor som helst, hvor ville du gått først?", "sv": "Om dina skor kunde ta dig vart som helst, vart skulle du gå först?", "pl": "Gdyby twoje buty mogły zabrać cię wszędzie, dokąd poszedłbyś najpierw?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "Which is heavier: a pillow or a book? How do you know?", "da": "Hvad er tungest: en pude eller en bog? Hvordan ved du det?", "de": "Was ist schwerer: ein Kissen oder ein Buch? Woher weißt du das?", "es": "¿Qué es más pesado: una almohada o un libro? ¿Cómo lo sabes?", "pt": "O que é mais pesado: uma almofada ou um livro? Como sabes?", "ro": "Ce este mai greu: o pernă sau o carte? Cum știi?", "fr": "Qu’est-ce qui est plus lourd : un oreiller ou un livre ? Comment le sais-tu ?", "it": "Qual è più pesante: un cuscino o un libro? Come lo sai?", "fi": "Mikä on painavampi: tyyny vai kirja? Mistä tiedät?", "nl": "Wat is zwaarder: een kussen of een boek? Hoe weet je dat?", "no": "Hvilket er tyngre: en pute eller en bok? Hvordan vet du det?", "sv": "Vilket är tyngre: en kudde eller en bok? Hur vet du det?", "pl": "Co jest cięższe: poduszka czy książka? Skąd to wiesz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What would happen if parents had to go to kid school for one day?", "da": "Hvad ville der ske, hvis forældre skulle gå i børneskole i en dag?", "de": "Was würde passieren, wenn Eltern für einen Tag in die Kinderschule gehen müssten?", "es": "¿Qué pasaría si los padres tuvieran que ir a la escuela de los niños por un día?", "pt": "O que aconteceria se os pais tivessem de ir à escola das crianças por um dia?", "ro": "Ce s-ar întâmpla dacă părinții ar trebui să meargă la școala copiilor pentru o zi?", "fr": "Que se passerait-il si les parents devaient aller à l’école des enfants pendant une journée ?", "it": "Cosa succederebbe se i genitori dovessero andare a scuola con i bambini per un giorno?", "fi": "Mitä tapahtuisi, jos vanhempien pitäisi mennä lapsen kouluun yhdeksi päiväksi?", "nl": "Wat zou er gebeuren als ouders één dag naar de school van hun kinderen moesten gaan?", "no": "Hva ville skjedd hvis foreldre måtte gå på barneskolen i en dag?", "sv": "Vad skulle hända om föräldrar var tvungna att gå till barnens skola en dag?", "pl": "Co by się stało, gdyby rodzice musieli pójść do szkoły swoich dzieci na jeden dzień?"}'::jsonb, false, true, true);
